INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106353,"Siaga ubat buat sapuan,","Hilang bisa keliling pinggang;","Afiat berbebat hilang keruan,","Esok lusa tegap berlenggang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106354,"Terung pipit molek dilumat,","Pinang muda dikisar sama;","Jika disepit duga kesumat,","Mendabik dada jangan berhuma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106355,"Kunyit hidup dilumat-lumat,","Bedak sejuk dicampur gaul;","Moga dilitup berkat selamat,","Makbul dipujuk hajat berkaul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106356,"Angkut-angkut buat sarang,","Ambil sarangnya dibuat ubat;","Patuh ikut berpantang larang,","Hilang sakitnya sihat berbebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106357,"Hilang nyawa badan musnah,","Baru ular telan mangsa;","Tenanglah jiwa hidup bertanah,","Elok gelar citra wangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106358,"Tunggu mangsa masuk jerat,","Hilang nyawa badan musnah;","Esok lusa tidak melarat,","Tenanglah jiwa hidup bertanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106359,"Burung puyuh terbang rendah,","Susup lari masuk semak;","Walaupun kuyup berhujan gundah,","Didiklah diri singkirkan tamak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106360,"Ada ular susur akar,","Tunggu mangsa masuk jerat;","Elak tular menjadi mekar,","Esok lusa tidak melarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106361,"Durian dirasa berdusun-disusun,","Koyan sekian kenyang sendawa;","Berpangsa-pangsa molek tersusun,","Kisah durian senandika jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106362,"Kenyang letih tupai pun pening,","Durian dirasa berdusun-dusun;","Isinya putih ada yang kuning,","Berpangsa-pangsa molek tersusun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106363,"Tupai mengendap ditebuk lari,","Kenyang letih tupai pun pening;","Rasanya sedap menjilat jari,","Isinya putih ada yang kuning.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106364,"Pohon bertuah dilipur lara,","Tupai mengendap ditebuk lari;","Raja buah punya angkara,","Rasanya sedap menjilat jari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106365,"Dari bukit hingga ke baruh,","Pohon bertuah dilipur lara;","Timbun berdikit harga ditaruh,","Raja buah punya angkara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106366,"Ada yang tinggi juga nan rendang,","Dari bukit hingga ke baruh;","Gugurlah lagi peraih berdendang,","Timbun berdikit harga ditaruh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106367,"Melaka Pindah ke Tanjung Ara,","Arah bukan ke pekan Pilah;","Siaplah sudah si kerang sira,","Jemput makan baca bismillah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106368,"Tanah nanas jangan berlumpur,","Salah kira kelaknya sesak:","Kuali panas sudah di dapur,","Kerang sira siap dimasak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106369,"Sulam lerang bunga selasih,","Kudup pecah lima kembangan;","Basuh kerang biar bersih,","Siapkan rencah untuk hidangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106370,"Tumbuh liar pohon berangan,","Lereng bukit di hutan Jelai;","Sambil bayar mula berangan,","Apa menu hendak digulai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106371,"Jangan kalih purnama mengambang,","Pungguk merayu berselang-seli;","Dipilih-pilih cukup timbangan,","Seluk saku mampu dibeli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106372,"Tukang pagar dengan tukul,","Paku berderai jatuh di tangga;","Kerang segar ada di bakul,","Dalam kedai Taman Tenaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106373,"Allah Maha baik dgn hamba,","Dianugerah ni'kmat tiada terperi,","Bukanlah ia cerita direka,","Memang ia suatu realiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106374,"Diberi secupak dibalas segantang,","Mulia hidup rezeki berpanjangan,","Syukurlah hamba tiada kepalang,","Menerima ni'kmat anugerah Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106375,"Selut di lopak dari permatang,","Mentari redup samar bayangan,","Diberi secupak dibalas segantang,","Mulia hidup rezeki berpanjangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106376,"Jangan rengkuh asam di paya,","Asam bukit dari seberang,","Usahlah angkuh menjadi manusia,","Terlantar sakit berharap orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106377,"Pohon keranji pohon belangkan,","Di tengah hutan si pokok petai,","Disiplin diuji saling mengingatkan,","Sepakat putuskan wabak merantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106378,"Terbang burung terbang ke bukit,","Pohon keranji pohon belangkan,","Perintah berkurung dilonggar sedikit,","Disiplin diuji saling mengingatkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106379,"Membuat rakit diikat buluh,","Buluh dikerat disusun sesikat,","Terlantar sakit semoga sembuh,","Sedang dirawat semoga sihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106380,"Tinggal sejemput nasi sedulang,","Pulut sejemput sebungkus tapai,","Yang menjemput belumlah pulang,","Yang dijemput telahpun sampai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106381,"Atas pelantar tersusun dulang,","Kain semutar batik terampai,","Yang menghantar belumlah pulang,","Yang dihantar telahpun sampai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106382,"Indah beragam bunyinya balam,","Di pohon mengkudu sedang berbunga,","Masjid tersergam mengindah alam,","Jiwa merindu sujud di lantainya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106383,"Indah beragam bunyinya balam,","Berlagu serak di pinggir sawah,","Masjid tersergam mengindah alam,","Merapat jarak mengakrab ukhuwwah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106384,"Indah beragam bunyinya balam,","Unggun berasap di tunggul senduduk,","Masjid tersergam mengindah alam,","Bagaikan mengusap ketika duduk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106385,"Adab santun molek bicara,","Resam dara berperi laku;","Mari berpantun mengikat saudara,","Jalin saudara jiran sekuku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106386,"Jangan layu pokok kedangsa,","Bulan sura berbuah nanti;","Bahasa Melayu penyatu bangsa,","Tiada tara bargalang ganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106387,"Pinggir belukar tumbuh berangan,","Tumbuh betik rimbunan lalang,","Bunga nan mekar dipetik jangan,","Layu dipetik serinya hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106388,"Covid bermula di satu kota,","Datang menyapa tidak kelihatan;","Rindu terasa yang jauh dimata,","Di kejauhan kita saling bermaafan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106389,"Dahan berangguh di tepi kuala,","Terbit fajar menjelang Subuh,","Perangai angkuh masih dibela,","Laba dikejar kemanusiaan rapuh,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106390,"Terisi perut terbang merata,","Dahan berangguh di tepi kuala,","Biadab di mulut hatinya buta,","Perangai angkuh masih dibela.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106391,"Dari dahan terbang ke perahu,","Sisa ikan segera dimakan,","Tika ditahan berbohong tak tahu,","Tika dijelaskan polis dibodohkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106392,"Terbang burung melayang rendah,","Dari dahan terbang ke perahu,","Perintah berkurung tidak diendah,","Tika ditahan berbohong tak tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106393,"Di rumpun benta si kumbang jati,","Terbang burung melayang rendah,","Beraja di mata bersultan di hati,","Perintah berkurung tidak diendah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106394,"Berlagu jelatik di dahan pala,","Di rumpun benta si kumbang jati,","Sukar dididik angkuh segala,","Beraja di mata bersultan di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106395,"Padam pelita angin hembusan,","Berlagu jelatik di dahan pala,","Sikap kita yang keterlaluan,","Sukar dididik angkuh segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106396,"Silap langkah tersadung akar,","Padam pelita angin hembusan,","Bukan salah virus menular,","Sikap kita yang keterlaluan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106397,"Coronavirus peringatan tuhan,","Wabak menguji sekalian umat;","Jagalah jarak duduk berjauhan,","Satu meter menjamin, hidup selamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106398,"Dilaman maya menular sakan,","Habis dikongsi dusta maklumat;","Berita palsu segera elakkan,","Rujuklah MKN tepat dan cermat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106399,"Asal petaka di pekan Wuhan,","Segenap jagat lekas merebak;","Duduklah di rumah patuhi arahan,","Mari leraikan rantaian wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106400,"Andai arahan rakyat tak endah,","Polis dan askar siap siaga;","Buatlah dirumah pekara faedah,","Mengisi masa bersama keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106401,"Prihatin Nasional membantu selalu,","Ringankan beban semua bangsa;","Bijaklah belanja barang yang perlu,","Rezeki secupak segantang terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106402,"Layari KKM segenap jaringan,","Info kesihatan tersedia disana;","Ikutilah langkah membasuh tangan,","Basmikan kuman sabun diguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106403,"Kepada pencipta kusemai harapan,","Lenyapkan wabak seluruh buana;","Marilah membantu 'Barisan Hadapan',","Duduklah dirumah jangan kemana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106404,"Tepung Pelita dijual cik Normah,","Pak Ramhat menjual roti;","Marilah kita duduk dirumah,","Semoga selamat tidak dijangkiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106405,"Di tepi cerana si kuih lepat,","Teh halia dihidang mak dara;","Virus corona merebaknya cepat,","Seluruh dunia menular sengsara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106406,"Terjatuh dahan di tengah pamah,","Cuba elakkan burung terjebak;","Patuh arahan duduk di rumah,","Bersama hentikan tularan wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106407,"Keluar jangan hingga ke Pekan,","Terasa lama kuarantinnya masa;","Dengar arahan kawal pergerakan,","Muafakat bersama tak kira bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106408,"Tersohor kari di kedai Mamu,","Dibeli mertua si gulai serati;","Pejarakkan diri kalau bertemu,","Tu petua menyekat dijangkiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106409,"Menunduk lemah zaitun dibeli,","Urus semua belian kaktus;","Duduk di rumah santun famili,","Virus bahaya rantaian putus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106410,"Enak rasanya si madu lebah,","Jual di pekan ramai peminatnya;","Ingkar PKP undang musibah,","Sesal kemudian tiada berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106411,"Setiap pagi mawar di jirus,","Tumbuh mekar tepi sawah;","Duduk di rumah putuskan virus,","Demi sekeluarga ikatkan ukhwah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106412,"Dirikan khemah tepi daik,","Buluh sumpitan bukan riak,","Duduk dirumah amalan baik,","Virus jangkitan tidak membiak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106413,"Ikan siakap lekat di pukat,","Anak kelah masuk di sangkar;","Demikian sikap segelintir masyarakat,","Makin ditegah seronok diingkar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106414,"Banyak hikmah dari musibah,","Aurat tertutup keluarga muafakat,","Sahut cabaran duduk dirumah,","Putuskan rantaian penyebaran virus tersekat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106415,"Jala ditebar tidak berlengah,","Lihat haruan menghilir sana;","Reda dan sabar diam di rumah,","Putus rangkaian virus durjana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106416,"Anak Raja memanah burung,","Burung tersangkut didahan mati;","Bukan sakit kita berkurung,","Untuk kebaikan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106417,"Berdua Fatin di tepi paya,","Ke warung Salmah makan martabak;","Covid 19 virus bahaya,","Duduk dirumah mencegah wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106418,"Ikat seluar simpul mati,","Seluar basahan sangkut di dahan;","Usah keluar sesuka hati,","Patuhi arahan ikut panduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106419,"Pagi hari menjala ikan,","Menjala pula dihujung hulu;","Patuhilah arahan yang diberikan,","Semoga semuanya selamat selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106420,"Terung bulat di hujung dahan,","Pokoknya tumbuh tepi perigi;","Tolonglah patuh pada arahan,","Semoga virus segera pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106421,"Jalan-jalan bersama Cik Limah,","Terus terpandang pohon meranti;","Jika kita duduk di rumah,","Virus jangkitan boleh dihenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106422,"Hinggap kelawar di pohon kelapa,","Senjanya hujan terbang kelkatu,","Dicari penawar belum berjumpa,","Menyedarkan insan Allah penentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106423,"Di pohon sena hinggapnya bubut,","Hinggap kelawar di pokok kelapa,","Seluruh dunia kelam-kelibut,","Dicari penawar belum berjumpa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106424,"Pohon keranji dahan teduhan,","Di pohon sena hinggapnya bubut,","Allah menguji virus dititipkan,","Seluruh buana kelam-kelibut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106425,"Usah direngkuh dahan di tebat,","Pohon keranji dahan teduhan,","Angkara angkuh berasa hebat,","Allah menguji virus dititipkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106426,"Ambil belati ke paya rawa,","Mata meliar tersadung akar,","Membersih hati menyuci jiwa,","Insafi iktibar wabak menular.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106427,"Pohon senduduk tidak berduri,","Ambil belati ke paya rawa,","Sujud tunduk mengabdi diri,","Membersih hati menyuci jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106428,"Buluh sigai di batang mengkudu,","Pohon senduduk tidak berduri,","Hatiku bagai dirasuk rindu,","Sujud tunduk mengabdi diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106429,"Jiran meninjau mengintai di dalam,","Di luar pagar di penjuru halaman,","Indah menghijau serata alam,","Udaranya segar damai dan nyaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106430,"Kuat hentakan bingit setaman,","Jiran meninjau mengintai di dalam,","Titipkan hujan suburkan tanaman,","Indah menghijau serata alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106431,"Bagai mengipas hembusan bayu,","Paku pakis hiasan rimba,","Hutan ditebas sukar merayu,","Tanah terhakis banjir melanda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106432,"Sayap camar tampak berbalam,","Bagai mengipas menghembus bayu,","Sungai dicemar merajuklah alam,","Hutan ditebas sukar merayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106433,"Terbang rendah si burung camar,","Sayap camar tampak berbalam,","Sungai nan indah jangan dicemar,","Andai dicemar merajuklah alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106434,"Tanam lada dan bunga puding,","Petai be lalang pucuk mengkudu,","Masakan bonda tiada tertanding,","Dipanggil pulang diseru rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106435,"Rumpun bayam tumbuh di hiliran,","Tanam lada dan bunga puding,","Disembelih ayam gulai kegemaran,","Masakan bonda tiada tertanding.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106436,"Bunga melati pohon jenjulang,","Rumpun bayam tumbuh di hiliran,","Rangtua menanti anak tersayang,","Disembelih ayam gulai kegemaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106437,"Bunyi tekukur hari meninggi,","Di banir renta di hujung taman,","Diri berundur melangkah pergi,","Lebur cinta pengantin idaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106438,"Air tenang tidak berkocak,","Bunyi tekukur hari meninggi,","Dihulur pinang kacip ditolak,","Diri berundur melangkah pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106439,"Indah tasik hijau pohonan,","Air tenang tidak berkocak,","Bercinta asyik pelamin impian,","Dihulur pinang kacip ditolak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106440,"Tinggi pinang akar selerak,","Pohon nangka di pinggir paya,","Andai meminang hasrat ditolak,","Wajah berduka pudar cahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106441,"Tinggi pinang akar selerak.","Pohon nangka di pinggir paya,","Air tenang tidak berkocak,","Usah disangka tiada buaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106442,"Digoreng tilan buat bekalan,","Sarapan pagi karipap kentang,","Rezeki nelayan dihalang hakisan,","Mengais pagi makannya petang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106443,"Tembakau kretek lintah terlepas,","Digoreng tilan buat bekalan,","Sungainya cetek kapal terkandas,","Rezeki nelayan dihalang hakisan,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106444,"Sesekali katak melompat tersasar,","Tembakau kretek lintah terlepas,","Sampah keladak mendap ke dasar,","Sungainya cetek kapal terkandas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106445,"Menangguk belut dan ikan keli,","Sesekali katak melompat tersasar,","Hanyut ke laut tak mungkin kembali,","Sampah keladak mendap ke dasar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106446,"Asyik mengail dari permatang,","Menangguk belut dan ikan keli,","Kuala sungai ke laut terbentang,","Hanyut ke laut tak mungkin kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106447,"Banjaran gunung biru berbalam,","Pohon kelapa pelepah melambai,","Sendirian menung terkurung di dalam,","Mata berkaca badan dirantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106448,"Seberang titi menghijau bendang,","Banjaran gunung biru berbalam,","Gelisahnya hati teringin pulang,","Sendirian menung terkurung di dalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106449,"Dua titi antara tebing,","Lantai buluh tiangnya keras,","Andainya hati bakhil kepiting,","Usah mengeluh orang membalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106450,"Sungai mengalir antara tebing,","Jalan berjengket atas permatang,","Nasib bergilir jangan kepiting,","Berkongsi sedikit usah berhutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106451,"Belah buluh melantai titi,","Sungai mengalir antara tebing,","Usah mengeluh gusar di hati,","Nasib bergilir jangan kepiting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106452,"Melodi syahdu liriknya lengkap,","Malam kelam pasang pelita,","Igin mengadu tidak terucap,","Kata terendam dek air mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106453,"Pemetik kecapi di tepi paya,","Melodi syahdu liriknya lengkap,","Terasa sepi berteman cahaya,","Ingin mengadu tidak terucap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106454,"Terbang rendah burung ke bendang,","Pemetik kecapi di tepi paya,","Mimpikan indah igau bertandang,","Terasa sepi berteman cahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106455,"Burung tekukur di dahan kerdas,","Merayap musang di pohon kekabu,","Cendawan kukur digulai pedas,","Jantung pisang dibuat kerabu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106456,"Burung kedidi terbang merata,","Murai batu terbang berlegar,","Sayur keladi berulamkan pegaga,","Pucuk paku tumbuhnya meliar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106457,"Riuhnya murai nyaring berlagu","Burung kedidi terbang merata,","Pucuk cemperai si pucuk labu,","Sayur keladi berulamkan pegaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106458,"Teduhan leban di banir mempelam,","Riuhnya murai nyaring berlagu","Ayam di reban ikan di kolam,","Pucuk cemperai si pucuk labu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106459,"Kutip terung di penjuru halaman,","Teduhan leban di banir mempelam,","Keliling kampung sumber makanan,","Ayam di reban ikan di kolam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106460,"Burung pipit di rumpun lengkuas,","Kutip terung di penjuru halaman,","Hidupnya sempit dunianya luas,","Keliling kampung sumber makanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106461,"Terbang burung ke pohon kelepung,","Burung pipit di rumpun lengkuas,","Biar terkurung hidup di kampung,","Hidupnya sempit dunianya luas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106462,"Taghik upih polopah pinang,","Bobunyi balam bobunyi taktibau,","Lum bulih nak balik bawak botonang,","Tokughung lam ghumah pueh molilau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106463,"Jatoh ko payo keq tobeng maman,","Taghik upih polopah pinang,","Tokonang yo keq kampong laman,","Lum bulih nak balik bawak botonang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106464,"Buah compodak buah bidagho,","Jatoh ko payo keq tobeng maman,","Dighi tocampok nun ko utagho,","Tokonang yo keq kampong laman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106465,"Kain pelikat dibuat sampin,","Batik lepas ikat di pinggang,","Bangsa tergugat kerana pemimpin,","Kuasa terlepas ke tangan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106466,"Gumpalan tanah guna tenggala,","Burung tekukur di hujung halaman,","Nikmat dan hikmah di sebalik bala,","Redha dan syukur tanda beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106467,"Menitis gerimis di celah batu,","Hinggap burung terbang cempera,","Di meja tulis menelaah buku,","Badan terkurung minda kembara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106468,"Ramai orang di tengah pekan,","Disedari awal persiapan di beli;","Jika ambo ada kesalahan,","Mohon maaf menjelang Aidilfitri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106469,"Di media-cetak perkhabaran awal,","Pertemuan Jemaah jauh dan dekat;","Kemenangan mutlak menjelang Syawal,","Terterap istiqomah mengerjakan yang sunat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106470,"Ramai orang pergi kepekan,","Sedari awal persiapan dibeli;","Ramadhan bakal meninggalkan,","Semoga tahun depan berjumpa lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106471,"Ramai orang di tengah pekan,","Sedari awal persiapan dibeli;","Sucikan hati ikhlas memaafkan,","Indahnya syawal kemenangan dirai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106472,"Dilebuh raya banyak kenderaan,","Masing masing menuju tujuan;","Syawal diraikan penuh kegembiraan,","Maaf bermaafan sesama insan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106473,"Ramainya orang pergi kepasar,","Membeli pelbagai barangan harian;","Lebaran diraikan kecil dan besar,","Pelbagai juadah di hidang disajikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106474,"Bunga cempaka bunga raya,","Cantik hiasan kebun taman;","Hulurkan salam dipagi raya,","Sambut salam dengan senyuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106475,"Lalai lidah menbawa cela,","Lalai hati membawa luka;","Terucap kata mohon di pinta,","Sucikn hati di hari yg mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106476,"Ramai orang ditengah pekan,","Sibuk membeli persiapan raya;","Dihari raya kita bermaafan,","Sucikan hati hapuskan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106477,"Pungguk semalaman bermenung di pohon,","Rembulan di angkasa dirindu disebut;","Berjumpa bersalaman maaf dipohon,","Sempurna puasa Aidilfitri disambut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106478,"Ramai orang di tengah pekan,","Sedari pagi persiapan di beli;","Di pagi raya kita berjamu makan,","Tanda menyambut kemenangan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106479,"Ramai orang di tengah pekan,","Sedari awal persiapan di beli;","Di Aidilfitri saling bermaafan,","Bertambah erat persaudaraan sejati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106480,"Pergi ke pasar beli ikan patin,","Di dalam raga ada belacan;","Selamat Hari Raya maaf zahir batin,","Salah dan silap harap di maaf kan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106481,"Bergema takbir meruntun jiwa,","Tanda syawal sudah tiba;","Bermaaf-maafan sesama kita,","Di pagi syawal syahdu terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106482,"Ketupat palas jadi pilihan,","Untuk hidangan dihari raya;","Marilah kita bermaaf-maafan,","Semoga hubungan bertambah mesra.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106483,"Jemur tepung atas dulang,","Dulang diperbuat dari emas;","Syawal di tunggu hampir menjelang,","Nantikan anak bulan rase cemas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106484,"Jalan jalan ditegah kota,","Masa lalu jadi kenangan;","Saling memaafi antara kita,","Ikatan silaturahim satu kemenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106485,"Satu syawal menjelang tiba,","Takbir bergema mengusik jiwa;","Sekiranya ada salah dan dosa,","Ampun ku pinta dihari mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106486,"Keluar beriadah meredah panas;","Membeli bawal di Pekan Maran:","Pastikan ibadah, fitrah dilunas,","Raikan Syawal penuh kesyukuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106487,"Santan diperah santan kelapa;","Kelapa mawa kelapa pandan:","Bayarlah fitrah jangan dilupa;","Suci jiwa sempurna Ramadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106488,"Mana terkoyak dijahit ditampung,","Bumi dipijak langit dijunjung;","Indahnya Syawal balik kampung,","Jumpa saudara saling berkunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106489,"Lemang ketupat tepung pelita,","Air sirap dihidang juga;","Pohon ampun maaf dipinta,","Indahnya Syawal beraya sekeluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106490,"Melepas rindu terlalu lama,","Sambil makan bergurau senda;","Indahnya Syawal diraikan bersama,","Walaupun ketiadaan ayah dan bonda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106491,"Hari siang padam pelita,","Mandi awal gunakan timba;","Sayunya Ramadhan meninggalkan kita,","Indahnya Syawal menjelang tiba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106492,"Marah bukan kerana kebencian,","Tegur disulam kasih dan sayang;","Indahnya Syawal menghilangnya Ramadhan,","Tangan bersalam silang bersilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106493,"Merdunya suara baca AlQuran,","Membaca bertadarus beramai ramai;","Tiada makna indahnya lebaran,","Andai zakat fitrah tidak ditunai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106494,"Menjelang raya memasang pelita,","Pelita dipasang sigadis jelita;","Salam dihulur kemaafan di pinta,","Luhurkan hati leraikan sengketa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106495,"Indahnya langit kerana awan,","Indahnya lautan kerana kebiruan;","Indahnya Ramadan kerana keinsafan,","Tibanya Syawal kerana kemaafan dan kemampuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106496,"Kala Ramadhan telah pergi,","Takbir berkumandang diAidilfitri;","Lapar dahaga yang kita lalui,","Semoga berbalas pahala Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106497,"Sinar mentari dicelah pohon,","Datang hujan pohon sejahtera;","Tiga puluh hari dalam setahun,","Allah beri ganjaran berlipat ganda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106498,"Ketupat, lontong menjadi juadah,","Kueh mueh dan duit raya;","Puasa sebulan berakhir sudah,","Maaf bermaafan di Hari Raya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106499,"Nafsu amarah dapat di kawal,","Madrasah Ramadan di beri berkat;","Meraikan kemenangan di pagi shawal,","Bahgia di dunia untung nya di akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106500,"Air pergi sejuk ditimba,","Buat mandian jangan dihairan;","Raya sudah menjelang tiba,","Sambutlah dengan penuh kesyukuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106501,"Kuih koci berisi inti,","Rasanya enak peninggalan warisan;","Ramadan berlalu Syawal menanti,","Tangan dihulur saling bermaafan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106502,"Sunat berbuka memakan kurma,","Menjamah juadah yang telah tersedia;","Indahnya syawal disambut bersama,","Jangan dilupa pada yang tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106503,"Pergi ke pekan membayar zakat,","Rezeki yang ada harus dikongsi;","Menyambut syawal penuh berkat,","Ramadhan yang pergi perlu diinsafi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106504,"Pergi ke pasar membeli ikan,","Dapat sepotong ikan tenggiri;","Kepada semua saya ucapkan,","Selamat menyambut hari raya aidilfitri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106505,"Menderu bayu dipagi hari,","Harum bau nya daun nilam;","Kian hampir si Aidilfitri,","Bermaaf-maafan salam menyalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106506,"Masak gulai campur halia,","Gulai dimasak dalam kuali;","Maaf bermaafan dihari raya,","Salah silap harap dimaafi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106507,"Beduk dipalu menjelang pagi,","Bersihkan diri supaya berseri;","Ramadan berlalu dosa diampuni,","Fitrahnya suci umpama bayi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106508,"Sorong papan tarik papan,","Buah keranji diatas peti,","Hari raya bermaaf maafan,","Jangan disimpan didalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106509,"Mari brkampung di tengah laman,","Berhimpun sdara sembang kepagi;","Raya ini bermaaf-maafan,","Pnjang umur tahun hadapan jumpa lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106510,"Ramai orang pergi pekan,","Lihat orang jual beli;","Salah silap dapat maafkan,","Hutang duit tetap ditagihi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106511,"Bila dikenang terasa hiba,","Tidak sedar menitis airmata;","Kini Syawal menjelang tiba,","Bermafaan sesama kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106512,"Jangan lewat baik awal,","Segala amal perlu diutamakan;","Perginya Ramadhan muncul Syawal,","Merendah diri saling bermaafan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106513,"Tetak buluh panjang sepuluh,","Hendak membuat lemang raya;","Ku hulur tangan jari sepuluh,","Mohon maaf dan Selamat Hari Raya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106514,"Cantik sulam sulam sembilan,","Bunga emas bunga kecubung;","Ilmu dicurah menjadi bekalan,","Jasa guru tetap disanjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106515,"Cantik sulam sulam sembilan,","Bunga emas bunga kecubung;","Ajaran guru jadi amalan,","Jasanya guru tetap disanjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106516,"Cantik sulam sulam sembilan,","Bunga emas bunga kecubung;","Guru pendidik demi kejayaan,","Jasa dan bakti tidak terhitung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106517,"Cantik sulam sulam sembilan,","Benang emas bunga kecubung;","Perkara yg baik jadi amalan,","Ajaran guru sambung bersambung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106518,"Cantik sulam sulam sembilan,","Bunga emas bunga kecubung;","Jasa bakti guru jadikan amalan,","Tahun ke tahun berganti bersambung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106519,"Cantik sulam sulam sembilan,","Bunga emas bunga kecubung,","Setitik dakwat jadikan bekalan,","Kepada guru harapanku menggunung.", 4, 6);
