INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106602,"Bunga matahari kembang berseri,","Pergi ke sawah menabur baja;","Senyum diberi seikhlas hati,","Cara mudah beroleh pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106603,"Merah merah mawar sekuntum,","Petik sekuntum hiasan meja;","Bangun pagi jangan lupa senyum,","Rezeki murah hidup pun ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106604,"Sepohon palas dahannya sekah,","Pelanduk pula melindung diri;","Senyuman ikhlas jadi sedekah,","Dapat pahala Tuhan memberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106605,"Merah merah mawar sekuntum,","Dipetik dari kuala langat;","Bangun pagi jangan lupa senyum,","Sepanjang hari lebih semangat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106606,"Cantik keris sikayu jati,","Letak di kiri duduk di denai;","Senyum manis memikat hati,","Membawa rezeki disenangi ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106607,"Harum wangi bunga sekuntum,","Cantik dihias dalam jambangan;","Biar diri sentiasa tersenyum,","Rezekikan bertambah tiada kekurangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106608,"Tambah sekuntum bunga mas cotek,","Nampaklah tenang sudut hiasan;","Senyumlah senyum kupandang cantik,","Hati pun senang buat urusan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106609,"Memang cantik mawar berkuntum,","Di bawa Saudagar masuk kapal;","Pagi-pagi semangat dan senyum,","Kerana senyum tak perlu modal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106610,"Panas mentari memancarkan cahaya,","Ke kampung halaman di tengah hari;","Hendak memberi hamba tak kaya,","Cukuplah senyuman ikhlas ku beri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106611,"Pahlawan desa bersenjatakan lembing,","Digoyang-goyang dilontar melayang;","Ukirkan sentiasa senyuman kambing,","Pasti ramai yang suka yang sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106612,"Mawar merah mekar di taman,","Dihurung merbah bergoyang-goyang,","Kalau murah dengan senyuman,","Rezeki bertambah orang pun sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106613,"Air secangkir beserta gelas,","Dihidang pada ku bersama keladi;","Senyuman ku ukir tidak berbalas,","Mengertilah aku orang tak sudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106614,"Naik kebukit cuaca panas,","Konon mengambil udara segar;","Senyum sedikit tetapi ikhlas,","Sedekah kecil pahalanya besar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106615,"Senyum manis bak delima merkah,","Daunnya jatuh di laman orang;","Banyak kan senyum umpama sedekah,","Tapi jgn le senyum seseorang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106616,"Bentang permaidani sebagai hamparan,","Takar tempayan umpama hiasan;","Jika senyuman dizahir luaran,","Itulah senyuman dalam tangisan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106617,"Buah sentul dikerat-kerat,","Buah belimbing busuk mereput;","Senyum simpul ikatannya erat,","Senyum kambing meragut rumput.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106618,"Sirih bergambir coklat dikulum,","Buah manggis masak merekah;","Lebarkan bibir lemparkan senyum,","Senyuman manis umpama sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106619,"Mata lembing rosak sumbing,","Golok diasah berlandas meranti;","Senyum sumbing atau senyum kambing,","Senyumlah dengan keikhlasan di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106620,"Misai tumbuh terlalu lebat ,","Rambut disisir menggunakan sikat;","Senyuman manis umpamanya ubat,","Senyuman juga sebagai pemikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106621,"Jangan Sokong membawa rebah,","Sirih disokong di hujung tanjung;","Senyuman raja merafak sembah,","Senyuman rakyat daulat di junjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106622,"Daun kesum sibunga kantan,","Rumpun kunyit daun pandan;","Senyuman manis lambang perasaan,","Senyuman diukir tanpa paksaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106623,"Sayor lemak gulai kari,","Terlebih santan tercurah cuka;","Senyuman ada pelbagai kategori,","Mengikut keadaan suasana dan ketika.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106624,"Bergerak kereta berpusingnya roda,","Hidupnya enjin minyak dituang;","Berwaspada dengan senyuman peggoda,","Habisnya manis sepah dibuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106625,"Pagi hari turun kesawah,","Bawa bekal nasi dalam kaca;","Bila hati rasa gundah,","Senyumlah, baru hidup tambah ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106626,"Limau manis limau kasturi,","Penuh berisi dalam dulang;","Saya senyum untuk sendiri,","Bukan tujuan memikat orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106627,"Bila dahaga rasa nak minum,","Minum sedikit di dalam gelas;","Hendak senyum tidak tersenyum,","Senyum sedikit tiada berbalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106628,"Putih-putih Melati,","Merah-merah delima;","Teman-teman yang baik hati,","Senyum selalu pasti awet muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106629,"Menekut merbah ditepi taman,","Tersedak liur ketawa berdekah;","Jangan kedekut dgn senyuman,","Senyum kita adalah sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106630,"Kalau tuan ke Alor Gajah,","Belikan saya kuih keria;","Senyum itu penyeri wajah,","Gambaran hati penuh ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106631,"Kumpulkan di pangkin daun palas,","Palas berduri kemas tergantung;","Menakan mungkin senyum berbalas,","Senyum diberi rupanya patung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106632,"Disusun selari batu berbongkah,","Aturkan baldi bertanam halia;","Senyum diberi ibarat sedekah,","Terserlah budi pekerti mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106633,"Pohon belimbing tepi tebing,","Tebing berbata tidak rata;","Senyum kambing senyumnya sumbing,","Senyum kita elakkan nista.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106634,"Sedapnya pulut dibuat mak Siti,","Gembira si Mehat makan di belanga;","Senyum di mulut tafsiran di hati,","Orang melihat jiwa berbunga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106635,"Bunga mawar dipetik sekuntum,","Letak Dihias diatas kepala;","Dianjurkan kita supaya senyum,","Kerana ianya amalkan berpahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106636,"Di petik dari Kuala Langat,","Habis madu di hisap lebah;","Senyum selalu mesti bersemangat,","Satu adat amalan sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106637,"Pergi kedai beli cuka,","Jatuh ke batu habis berkecai;","Beri senyum maniskan muka,","Segala masalah akan selesai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106638,"Batu Kikir tempatnya aman,","Menanam manggis di kampong hulu;","Tetap terukir dengan senyuman,","Hati menangis tak siapa tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106639,"Pergi ke kebun siram tanaman,","Terasa lapang suasana pun damai;","Bersama bugarkan kempen senyuman,","Hidup ceria sahabat pun ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106640,"Anak dara bergurau senda,","Atas pangkin menjahit baju;","Senyuman itu awet muda,","Siapa melihat rasa sesejuk salju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106641,"Kembang pagi, bunga seroja,","Harum semerbak cuit hati;","Senyum itu salam bicara,","pengikat jiwa antara insani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106642,"Watak antagonis jahat berkarat,","berwajah koya memberi kesan;","senyuman manis tanpa bersyarat,","bawa ceria sesama insan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106643,"Berduyun melihat aksi sigajah,","Bermacam aksi hebat dan lancar;","Senyum itu penyeri wajah,","Hanya ikhlas tak perlu dibayar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106644,"Kail ikan dapatnya pari,","Hajat mencari tengiri batang;","harus tersenyum sepanjang hari,","Walau gigi tinggal sebatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106645,"Gadis cilik mendaki gunung,","Dikejar lanun jatuh terluka;","Telah ku tilik puas ku renung,","Senyum Dek Non penawar duka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106646,"Buah rambutan dah masak ranum,","Dipetik dijual dikedai papa;","Manisnya Tuan bila tersenyum,","Terpaut hati ingin bersapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106647,"Tiup bara bakar tuka,","Buka selera asam jawa;","Senyum mesra penawar duka,","Lenyap sengsara cengkam jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106648,"Segar bunga berkuntum kuntum,","Merah kuning menarik hati;","Walau pun sedeh tetap tersenyum,","Nikmat Allah tidak terperi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106649,"Bangun pagi pasti senyum,","Jangan lupa petik belimbing;","Dah pasti kita senyum,","Walau pon senyum kambing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106650,"Anak cina mencari galah,","galah panjang celahan perdu;","Apalah guna berpecah belah,","Banyak hikmahnya bersatu-padu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106651,"Anak cina mencari galah,","Galah panjang celahan perdu.","Bersepakat teruskan melangkah,","Sehingga tiba matlamat di tuju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106652,"Anak cina mencari galah,","Galah panjang celahn perdu;","Hidup memang kena mengalah,","Jika mahu hidup bersatu padu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106653,"Anak cina mencari galah,","Galah panjang celahan perdu;","Jangan kita buat masalah,","Kelak negara jadi haru biru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106654,"Campur madu dengan kurma,","Buat minuman gadis jelita;","Bersatu padu masyarakat bersama,","Membawa kemakmuran negara tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106655,"Sungguh indah bunga Melati,","Kembang mekar di taman;","Jika kita saling menghormati,","Pasti hidup penuh aman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106656,"Jauh dimata dekat dihati,","Hati bertaut si anak luncai;","Marilah kita saling menghormati,","Membina nagara aman dan damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106657,"Mengumpul huma menjadikan perdu,","Jika tak larat rehatkan tangan;","Bila bersama bersatu padu,","Kerja yang berat menjadi ringan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106658,"Masak kicap campur hati","Di bawa bekal ketika berkelah","Sikap tidak hormat menghormati","Umpama retak menanti belah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106659,"Mekar indah bunga Raya,","Terkenal digelar bunga kebangsaan;","Rakyat bersatu malaysia berjaya,","Buangkan jauh sifat perkauman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106660,"Anak cina mencari galah,","Galah panjang celahan perdu;","Nikmat kemakmuran negara bertuah,","Hasil perjuangan insan terdahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106661,"Belayar malam merentas selat,","Singgah di pulau tiada penghuni;","Malaysia tercinta negara berdaulat,","Mari bersatu kekalkan harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106662,"Isi bekalan sampai muat,","Untuk bekalan satu perkelahan;","Bila bersatu kita kuat,","Bila perpecahan bawa kemusnahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106663,"atah arang dipijak kera,","Atas kayu menggintai kumbang;","Akar tunjang bercanggah arah,","Pohon layu menanti tumbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106664,"Kampungku jauh indah dan permai,","Tempat indah duduk dan beradu;","Hidup bersatu rukun damai,","Semua bangsa bersatu padu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106665,"Hujan rahmat di Selat Melaka,","Kapal berlabuh di waktu subuh;","Pepatah keramat jangan dilupa,","Bersatu teguh bercerai roboh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106666,"Anak cina mencari galah,","Ke gunung dicari dapat sebatang;","Tiada guna berpecah belah,","Untung secupak rugi segantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106667,"Pergi ke Pedu rehatkan mata,","Dapat dirasa udara nyaman;","Bersatu-padu amalan kita,","Warisan bangsa zaman-berzaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106668,"31 Ogos Hari Merdeka,","Umurnya kian berlanjutan;","Bersatu hati kehendak kita,","Kemakmuran negara diutamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106669,"Galah khemah celahan perdu,","Bayu nyaman sejuk udara;","Banyak hikmah bersatu padu,","Negara aman hidup sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106670,"Hati-hati kalau memandu,","Jalan berbendul banyak di taman;","Semua rakyat bersatu padu,","Barulah negara akan jadi aman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106671,"Pergi taman bersama sama,","Anak ratu tiada yang tahu;","Hidup aman pelbagai agama,","Mari bersatu berganding bahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106672,"Membeli-belah terasa lelah,","Bahan yang mentah pilihan tentu;","berpecah belah undang masalah,","mudah dipatah lidi yang satu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106673,"Anak Cina mencari ketam,","Ketam di masak kari cengkudu;","Apa guna kita berantam,","Marilah kita bersatu padu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106674,"Ditepi pantai duduk berkelah,","Makan pisang habis sesikat;","Jangan kita berpecah belah,","Hidup muafakat membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106675,"Setiap pakatan kita setuju,","Itulah yang baik kita tahu;","Kalau tak bersatu padu,","Dah tentu untung yang satu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106676,"Anak Cina memasang lukah,","Luka dipasang bercampur lumpur;","1957 Malaysia merdeka,","Rakyatnya hidup aman dan makmur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106677,"Anak Cina mencari galah,","Galah diguna menjuluk kelapa;","Jgn lh kito mcari silap dn salah,","Bersangka baik hidup bahgia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106678,"Buah durian di buat pengat,","Sedap di makan di waktu petang;","Kita semua mesti berpakat,","Musuh yang datang kita tentang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106679,"Pergi ke timur membeli batu,","Membuat galah si dahan ara;","Malaysia makmur berkat bersatu,","Berpecah belah merosakkan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106680,"Apa guna menebang nipah,","Kalau tidak mendapat sagunya;","Apa guna berpecah belah,","Di bawah satu rumpun aman semuanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106681,"Tepi taman tambat perahu,","Di batang bidara di Tasik Chini;","Hidup aman bahu membahu,","Negara sejahtera rakyat harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106682,"Anak Cina mencari galah,","Galah di cari buat mengukur;","Jangan sekali kita mengalah,","Jom berusaha tingkatkan prestasi dan bersyukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106683,"Pergi ke kedai membeli baju,","Mari disangkut di belakang pintu;","Untuk menuju berbangsa maju,","Bersama lah kita berganding bahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106684,"Anak angsa berkaki satu,","Bawa ke kota darinya desa;","Berbilang bangsa hidup bersatu,","Negara kita aman sentosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106685,"Susu cair beli di Bentung,","Petai seikat dan kicap pekat;","Bulat air kerana pembentung,","Bulat masyarakat kerna muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106686,"Buluh kecil di atas bukit,","Bukit diredah termengah-mengah;","Daging kancil sama dicubit,","Daging gajah sama dilapah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106687,"Anak Cina memasang lukah,","Lukah dipasang ditepi paya;","Apa guna asyik bertelagah,","Hidup bersatu aman Malaysia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106688,"Angin bertiup arah ke barat,","Langit mendung di tutupi awan;","Negeri makmur rakyat sihat,","Bersatu padu eratkan perpaduan.", 4, 6);
