INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106774,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Kita mesti selalu bersedekah,","Setahun sekali lakukan korban. [3]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106775,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Tenang jiwa beramalan sedekah,","Semoga kekal dalam ingatan. [4]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106776,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Ibu & ayah bersusah payah,","Untuk kejayaan ank kesayangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106777,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Jiwa kental tiada lemah,","Pengorbanan hidup tetap semadikan. [1]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106778,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Pengorbanan mak dan ayah bersusah payah,","Tak nak hidup anaknya susah di masa hadapan. [2]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106779,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Hiduplah penuh langkah,","Agar kita senantiasa dalam ingatan, [3]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106780,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Untuk rezeki sentiasa berkah,","Bersedekah selalu jangan dilupakan. [4]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106781,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Pengorbanan itu satu ibadah,","Keikhlasan Ibrahim dan Ismail jadi ikutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106782,"Orang jawa memasang lukah,","Bawa bekal pukut bersantan;","Dalam hidup aku melangkah,","Jasa ibu bapa tersemat di ingatan. [1]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106783,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Hidup akan menjadi berkah,","Pengorbanan guru usah dilupakan. [2]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106784,"Orang Jawa memasang lukah,","Bawa bekal pulut bersantan;","Ibrahim Ismail tidak membantah,","Nyawa dan badan rela dikorbankan. [3]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106785,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Qurban harta membentuk uhkwah,","Tuntutan agama eratkan perpaduan. [4]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106786,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Suka tak suka menjadi fitrah,","Selama hidup wajib berkorban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106787,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Memang sudah jadi lumrah,","Setiap apa berlaku mesti ada pengorbanan. [1]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106788,"Orang Jawa memasang lukah,","Bawa bekal pulut bersantan;","Kerjakan korban satu ibadah,","Suruhan Agama di turutkan. [2]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106789,"Orang Jawa memasang lukah,","Bawa bekal pulut bersantan;","Hidup biar mencari barakah,","Banyakkan amalan dan pengorbanan. [3]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106790,"Orang Jawa memasang lukah,","Bawa bekal pulut bersantan;","Dengan bertakwa korban disedekah,","Perlu tawakkal mohon keberkatan. [4]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106791,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Jangan risau menempuh hidup susah,","Untuk kesenangan masa depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106792,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Biar diuji dengan hidup susah,","Pasti ada hikmah dan ganjaran. [1]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106793,"Orang jawa memasang lukah,","Bawa bekal pulut bersantan;","Membahang hawa kulit menadah,","Mengharap baka mendapat intan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106794,"Putih seroja merah tiada,","Susun diatur elok terhimpun;","Makan semeja bergurau senda,","Biar berbeza tetap serumpun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106795,"Jalan ke pekan memikul batu,","Sakit badan terasa berat;","Hidup berjiran saling membantu,","Membawa kerukunan kawasan setempat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106796,"Raja ditabal penuh istiadat,","Atas singgahsana duduk bertakhta;","Berlainan budaya berlainan adat,","Hidup makmur tiada sengketa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106797,"Kalau ruboh kota Melaka,","Tinggal lah ia sebagai kenangan;","31 Ogos sambutan hari merdeka,","Berbagai kaum berganding tangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106798,"Tunang Pak Dukun disukai ramai,","Kerap disiar di ruang udara;","Hidup rukun aman dan damai,","Negara makmur rakyat sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106799,"Pohon kemboja ditaman subur,","Bunga kembang cantik dipandang;","Ikatan silatulrahim biar subur,","Persengkataan usah diundang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106800,"Pergi ke Lundu beli bidara,","Cari kelisa juga bambangan;","Bersatu padu demi negara,","Berbilang bangsa bukan halangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106801,"Mencair gangsa bingkai tirani,","Biarlah cerna sepanjang hayat;","Berbilang bangsa hidup harmoni,","Warnai rona perpaduan rakyat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106802,"Di Anjung Batu tercipta kenangan,","Terlerainya ikatan bujang dan dara;","Perbezaan itu bukannya halangan,","Malah kekuatan rakyat dan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106803,"Gagah mematuk ayam Serama,","Sambil memagut daun Pegaga;","Perpaduan dipupuk sekian lama,","Jangan dikeruh air di telaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106804,"Harum mewangi bunga di para,","Disusun tinggi di alas bata;","Perpaduan rakyat agenda negara,","Pembangunan dikongsi rakyat jelata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106805,"Gunung bernama Jajaran Titiwangsa,","Tegak kukuh dalam wawasan;","Perpaduan jalinan berbilang bangsa,","Ibarat tenunan tekat warisan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106806,"Cendawan tumbuh tepian jalanan,","Bunga tumbuh di sudut anjung;","Perpaduan dipupuk berturutan keturunan,","Bumi dipijak langit dijunjung. [4]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106807,"Bunga disusun gadis remaja,","Cantik gubahan mewangi haruman;","Negara berpelembagaan institusi Beraja,","Rukunegara penghayatan mengeratkan perpaduan. [5]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106808,"Perbedaan Agama tiada halangan,","Tidak sungguh tiada melata;","Bersama kita menyambut kemerdekaan,","Slogan sayangilah Malaysia kita. [6]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106809,"Putih kemboja tanda setia,","Pelita di taman meyala terang;","Mari eratkan jiwa bersaudara,","Eratkan perpaduan bangsa gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106810,"Tegap berdiri tiang bendera,","Berdiri teguh di padang merdeka;","Aman dan makmur dalam negara,","Bunga raya lambang kebangsaan kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106811,"Hospital Selayang tempat dituju,","Buat rawatan tetap berkala;","Doktor Sarawak pesakitnya Melayu,","Itulah indahnya perpaduan di Malaysia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106812,"Mengasah kuku singa dewasa,","Di dalam hutan suara mengaum;","Berpadu suku antara Bangsa,","Mesra ikatan berbilang kaum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106813,"Gula kelapa dari Melaka,","Santan ditambah warna berubah;","Bertegur sapa tanpa prasangka,","Rasa muhibbah kukuh tak rebah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106814,"Rapuh dahan bila dipaut,","Jangan sampai jatuh kebawah,","Kukuhkan perpaduan biar selaut,","Sama digapai negara bertuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106815,"Makan piza enak dirasa,","Beli karipap dan kuih keria;","Biarpun berbeza agama dan bangsa,","Kita tetap rakyat Malaysia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106816,"Tumbuh berhimpun anak papaya,","Diletak baja di akar akasia;","Duduk serumpun berbilang budaya,","Adat berbeza tetap se Malaysia. [1]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106817,"Banyak khasiat buah markisa,","Masak bertauhu berempah halia;","Kita rakyat berbilang bangsa,","Bahu membahu pertahan Malaysia. [2]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106818,"Pokok celagi si pokok zaitun","Di lembah Tahan tumbuh bidara","Masuk lagi serangkap pantun","Mengajak pertahan harmoni negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106819,"Terbang pagi sirama rama,","Hinggap mari dipohon kelapa;","Biar tidur bantal tak sama,","Asal mimpi kita serupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106820,"Tumbuh melata buah bidara,","Didalam acuan berbagai warna;","Elakkan sengketa demi negara,","Barulah perpaduan dapat dibina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106821,"Pergi menjala di tengah tasik,","Dapat Patin dimasak Nyonya;","Jawi diperkasa tulisan klasik,","Semua bangsa dapat memahaminya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106822,"Bila bersalah mintak lah ampun,","Keharmunian rakyat tetap bersama;","Bahasa di jaga kekal serumpun,","Walaupun kita berbeza agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106823,"Tumis kangkung sambal sotong,","Juadah tengahari untuk dimakan;","Hidupkan amalan bergotong royong,","Eratkan silaturrahim kukuhkan perpaduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106824,"Si gadis ayu anak Pak Musa,","Manis lagi bila tertawa;","Walaupun saling berlainan bangsa.,","Satu bahasa tetap di jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106825,"Putih-putih merpati,","Merah-merah delima;","Pupuk semangat sejati,","Eratkan silaturrahim tak kira agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106826,"Hijau-hijau daun pandan,","Boleh membuat masakan harum;","Teguhkan amalan dalam perpaduan,","Malaysia terdiri pelbagai kaum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106827,"Pucuk paku buah mengkudu,","Dikerat-kerat buat ramuan;","Berat dipikul sama membantu,","Berganding bahu erat perpaduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106828,"Air kelapa gula madu,","Bila di campur bertambah pekat;","Bila hidup bersatu padu,","Musuh keliling takut nak dekat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106829,"Ayak tepung kelapa dikukur,","Beras dibasuh sambil berseloka;","Tegak serumpun umpama aur,","Menjadi teguh bangsa merdeka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106830,"Putik kemboja baru mengembang,","Wangi mengharum di tengah laman;","Erat perpaduan bangsa berbilang,","Kemakmuran bersama satu tujuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106831,"Putih sudah tentu suci dan bersih,","Merah melambangkan kuat dan berani;","Tak kira kaum kita sayang dan kasih,","Negara aman sejahtera dan harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106832,"Pergi ke hutan memburu rusa,","Dapat saekor di bagi rata;","Eratkan perpaduan tak kira bangsa,","Menjaga keamanan bumi tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106833,"Memakai tudung yang merah warna,","Bersulam renda berlekatkan manik;","Kulit dan hidung jelas berbeza,","Kepelbagaian Bangsa identiti unik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106834,"Burung kedidi dipohon nangka,","Anak bayan hinggap di dahan;","Orang berbudi kita berbahasa,","Mesra ikatan erat perpaduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106835,"Ada pagi adanya senja,","Datang malam waktu berehat;","Kuncinya ada satu sahaja,","Bersatu hati dan bermaufakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106836,"Ibadah Haji di bulan Zulhijah,","Umat Islam berhimpun di Mekah;","Sopan santun tidak menerjah,","Malaysia di puji rakyat terujah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106837,"Tanam padi luas terbentang,","Wangi harum haba rerumputan;","Mari julang bangsa terbilang,","Perpaduan kaum kita eratkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106838,"Emas perak intan suasa,","Cantik bersinar cahaya berkilauan;","Biar berbeza agama dan bangsa,","Mimpi bersama kukuhkan perpaduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106839,"Padi huma di Seri Aman,","Terlihat buaya seronok mengawan;","Beza agama hidup setaman,","Lain budaya setia berkawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106840,"Kepala pening rasa nak rebah,","Dudu tersandar di tepi pagar;","Berbilang kaum hidup muhibah,","Budaya Malaysia tetap segar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106841,"Cincin emas cincin suasa,","Dilihat orang tiada tertahan;","Dalam kehidupan berbilang bangsa,","Utamakan perpaduan jauhkan pertelingkahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106842,"Malar dibaja kerap berdaun,","Duku, markisa disapu rusa;","Biar berbeza tetap serumpun,","Satu bangsa satu bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106843,"Membeli karipap hari selasa,","Dibeli bersama si gula sira;","Bersatu hati tak kira bangsa,","Agar terpelihara Islam sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106844,"Pagi hari pergi ke muara,","Hendak menjual seguni padi;","Hidup kite berbilang bangsa,","Jaga adap budi dan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106845,"Berarak mega di Gunung Daik,","Pelangi terbias di hujung desa;","Ajaran agama berbuatlah baik,","Sesama manusia tak kira bangsa. [1]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106846,"Berarak mega di Gunung Daik,","Pelangi terbias di hujung desa;","Ikuti contoh nabi Muhammad tauladan baik,","Sesamalah kita amalkan sentiasa. [2]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106847,"Berarak mega digunung daik,","Kaki terjuntai digunung bentan;","Dari kecil disemai berbuat baik,","Nilai umpama emas dan intan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106848,"Anak muda membaca kitab,","Selepas sebuah sebuah pula;","Rakyat memikul tanggungjawab,","Menjaga agama dan bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106849,"Di sana sini kepulan awan,","Riang gembira burung berkawan;","Nilai murni teras perpaduan,","Jalinan mesra tonggak penyatuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106850,"Pohon nipah pohon rumbia,","Batang diguna buat titi;","Buat baik sesama manusia,","Dikenang daripada hidup sampai ke mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106851,"Burung terbang melintas padang,","Padang ditanam pokok peria;","Bercakap siang memanglah pandang,","Tapi yang terserlah buruknye sifat manusia. [2]", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106852,"Pohon rumbia rendang merimbun,","Merimbun lagi pohon kelapa;","Kalau bersalah mintaklah ampun,","Semoga hubungan erat semula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106853,"Mencari rerama ditepi hutan,","Awas terkena ranting berduri;","Hormat sesama agama satu tuntutan,","Ikrar rukun negara menjadi bukti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106854,"Beli pakaian di kedai butik,","Bila di pakai jalan bersama;","Jangan kita jadi pelik,","Sesama kita lain agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106855,"Berarak mega di gunung daik,","Daku duduk diatas tangga;","Tak rugi kita berbuat baik,","Balasan menanti di pintu syurga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106856,"Ke Gunung Daik si anak Desa,","Berbelah bagi tetapi akur;","Berbuat baik tak kira bangsa,","Nilainya tinggi tidak terukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106857,"Duduk bersembang hingga terleka,","Bersama teman di muka pintu;","Hidup bermasyarakat berbaik sangka,","Bekerjasama saling membantu.", 4, 6);
