INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107023,"Dodol sekancah sayangnya tumpah,","Ralat rasanya tersalah angkat;","Sama dicicah sama dilapah,","Agar nikmatnya membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107024,"Kaki tercubih mencari pukat,","Terbahak dema ditepi hutan;","Rezeki selebih diberi zakat,","Hak sesama satu tuntutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107025,"Mari membeli kain pelikat,","Dari benang tenunan lama;","Usahakan lebih mencari berkat,","Di bagi rezeki konsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107026,"Petik rambutan di ikat ikat,","Untuk dijual di Kuala Kedah;","Kalau kita sentiasa muafakat,","Yang susah menjadi mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107027,"Bawah bukit pokok delima,","Dekat perigi ditanam rapat;","Biar sedikit rezeki diterima,","Pandai bahagi semua dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107028,"Tahan pukat tak dapat ikan,","Rupa-rupanya ada mahkluk Allah yang makan;","Ada masalah kita bincangkan,","Supaya tidak menjadi isu dihari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107029,"Melekit daki beraksi di huma,","joran patah sepat bercanda;","sedikit rezeki kongsi bersama,","Ganjaran Allah berlipat ganda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107030,"Jangan lupa bawalah pukat,","Angkat bersama jikalah berat;","Hidup sekata membawa berkat,","Bahagia dunia hingga akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107031,"Pagi raya makan ketupat,","Lebih enak makan bersama rendang;","Hidup sepakat membawa berkat,","Kerja yang susah menjadi senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107032,"Joran diikat redup kuini,","Gugur cermai di rumpun halia;","Jiran sepakat hidup harmoni,","Aman damai terasa bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107033,"Berkayoh rakit menuju muara,","Bawa muatan buah delima;","Rezeki yang ada jangan lah berkira,","Banyak atau sikit kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107034,"Ke Tanjung Dawai membawa pukat,","Membawa bersama sipadi huma;","Aman dan damai hidup bermuafakat,","Susah dan senang dirasa bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107035,"Makan sedulang duduk berlima,","Dapat merasa ikan gelama;","Ilmu sedikit kongsi bersama,","Semoga hidup bersa bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107036,"Nampak berkarat si mata cangkul,","Terkena lengan si anak anjing;","Apa yang berat sama dipikul,","Mana yang ringan sama dijinjing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107037,"Dari Samarahan pergi ke Bau,","Di lereng bukit membeli buah;","Kesian jiran tercium bau,","Hulurlah sedikit biar pun kuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107038,"Nangka dibelah ambil seulas,","Sepiring biskut pula disua;","Rahmat Allah rezeki meluas,","Usah kedekut bolot semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107039,"Laman dibarat lama berlampu,","gelap redup resah di desa;","Ringan dan berat sama di tampu,","berkat hidup sejahtera dirasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107040,"Tali direntang bukannya rawai,","Dihujung tali terngangnga pukat;","Serabut berbilang caranya selesai,","Berbincanglah cari kata sepakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107041,"Kain direntang jemur dirembang,","Hendak dijaga jangan merekah;","Rezeki datang pelbagai cabang,","Berkongsi agar mendapat berkah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107042,"Buah salak di dalam kantung,","Kulitnya keras isi nya kelat;","Bulat air kerana pembentung,","Bulat manusia kerana muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107043,"Duduk di tangga memakan laksa,","Rambutan seikat berwarna warni;","Jiran tetangga pelbagai bangsa,","Saling sepakat hidup harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107044,"Jangan lupa membawa pukat,","Pukat dipasang di muara;","Besar nikmatnya kita sepakat,","Segala masaalah selesai segera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107045,"Makan nasi ikut sukatan,","Agar tidak datangkan mudarat;","Pupuklah hubungan baik sesama jiran,","Jiran sepakat membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107046,"Ke Kuala sungai mendayung sampan,","Membawa bekal untuk si Dara;","Bermuafakat kita supaya mampan,","Hidup berkat bertambah aman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107047,"Bukan jauh tapi dekat,","Walau dekat masih jauh;","Ringan tulang membawa berkat,","Bantulah andai ada yang terjatuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107048,"Asal kenderaan dari besi,","Tapi sayang tidak berkaki;","Andai sedikit elok dikongsi,","Andai banyak itulah rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107049,"Mendaki bukit org berlima,","Bekal diikat tali rupiah;","Biar sedikit kongsi bersama,","Rezeki berkat pasti bertambah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107050,"Terbang kedidi di pagi hari,","Riuh berkicau burung kenari;","Walau sedikit sama di rasai,","Andai terlebih sama mensyukuri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107051,"Duduk berbincang berpakat pakat,","Hendak menziarah encik Ibrahim;","Andai kita saling bermuafakat,","Bertambat eratlah hubungan silaturahim.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107052,"Ikan kikik ikan gelama,","Ikan bilis terkena pukat;","Rezeki sikit kongsi bersama,","Supaya akan membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107053,"Mendayung rakit ke tepi tebing,","Sambil mendayung memasang pukat;","Hidup seperti aur dan tebing,","Jiran sepakat membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107054,"Potong sayur hemat berhati,","Bubuh puan sirih berpandan;","Bertolak ansur hormat menghormati,","Kukuh perpaduan penuh keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107055,"Depan halaman tanahnya bencah,","Tanah ditambak disapu sampah;","Hati kuman sama dicicah,","Hati gajah sama dilapah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107056,"Potong sotong gaul tepung,","Cicahnya pekat ada bergula;","Gotong royong hidup berkampung,","Hidup berpakat jaminan bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107057,"Bukan lekat sebarang lekat,","Gam melekat dengan segera;","Seluruh rakyat hidup sepakat","Negara damai aman sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107058,"Terbang berkawan si rama-rama,","Tersangkut seekor terkena pukat;","Rezeki yg sedikit dikongsi bersama,","Semoga kehidupan mendapat berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107059,"Naik rakit menjala ikan,","Redah muara menuju haluan;","Biar sedikit yang kita kongsikan,","Pada yang Esa besar ganjaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107060,"Pergi ke bukit sambil memikat,","Pergi bersama rosli dan arjuna;","Rezeki sedikit adalah berkat,","Harus dikongsi bersama-sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107061,"Tahan pukat dapat gelama,","Di simpul pukat siikan duri;","Jiran sepakat kongsi bersama,","Semoga berkat hidup berdikari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107062,"Warna warni rama-rama,","Terbang tinggi di sini sana;","Makan setalam kongsi bersama,","Dapat pahala amalan sunnah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107063,"Di pasar lama barang dilelong,","Bubu jala dan juga lukah;","Amalkan sikap tolong menolong,","Rezeki berlebihan kita sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107064,"Di dalam bakul buah delima,","Di isi bersama buah langsat;","Rezeki yang ada dikongsi bersama,","Bermuafakat membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107065,"Lembing ditujah terkena pelepah,","Pisang di taman gugur ke sisi;","Hati gajah bersama dilapah,","Hati kuman dicecah dikongsi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107066,"Anak semut sebulat kata,","Atas petunjuk pangkat dewasa;","Tidak luput sepakat kita,","Asas dibentuk berkat dirasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107067,"Pergi ke sawah berjalan kaki,","Bawa bekal pulut panggang;","Setiap hari mencari rezeki,","Banyak atau sikit di. Bawa pulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107068,"Kalau berakit saudara berlima,","Jangan dilupa bawalah pukat;","Ada sedikit kongsi bersama,","Besarnya nikmat membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107069,"Asalnya kosong jadi satu,","Lama-lama menjadi empat;","Kalau hidup sentiasa bersatu,","Keluarga dirahmati anak-anak selamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107070,"Kuih talam dalam takung,","Kuih keria tepung melekat;","Bulat air kerana pembetung,","Bulat manusia kerana muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107071,"Cantik sungguh sulaman tekat,","Bantal tekat dijahit sembat;","Jiran terdekat seharusnya berkat","Hidup bermuafakat sebagai sahabat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107072,"Kuah kari kuah laksa,","Kuih samosa makanan si dara;","Jiran memberi kita merasa,","Selamat sentosa hidup sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107073,"Dibuat kancing emas yang asli,","Lurusnya peniti tidaklah bengkok;","Bapanya kencing sambil berdiri,","Anak lelaki kencing di tembok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107074,"Inai dilurut luka jarinya,","dengar mengaduh ketika lalu;","Anak menurut baka ayahnya,","Corakkan contoh gilap selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107075,"Dalam kolam ada ikan,","Ada banyak ikan haruan;","Ayah ibu beri didikan,","Didikan terbaik itulah tuntutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107076,"Burung Merbah burung Kenari,","Hijau berbulu si burung Punai,","Kemana tumpahnya sikuah kari,","Kalau tidak ke roti canai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107077,"Rasa hati jangan dipandang,","Seperti jati menjadi hiasan;","Hisap rokok boleh ditentang,","Akhir usia penuh kesihatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107078,"Kayu dilorek coraknya cantik,","Coraknya diolah si mega mendung;","Bapanya borek anaknya rintik,","Mengapa salahkan ibu mengandung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107079,"Ramai orang pergi mendaki.","Ada yang terjatuh sampai terlentang;","Kalau bapa makan berlari,","Anak makan sambil kuak lentang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107080,"Buah kelapa d atas peti,","Anak rusa mati terjerat;","Ibu bapa perlu d hormati,","Sejahtera hidup d akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107081,"Membeli belah di pekan Kunak,","Bersiar siar pula menaiki kereta;","Jadilah model terbaik bagi anak anak,","Anak anak peniru gelagat kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107082,"Ehh malam-malamlah mengilap suasa,","Sambil mengilap melihat anak dara;","Hai encik abang adik hendak bertanya,","Mengapa hari ini bertugas pagi-pagi buta?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107083,"Asap berkepul berbunga bunga,","Puntung dibuang di tepi daik;","Itu hanya bidalan sahaja,","Semoga tauladan contoh terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107084,"Kusemai padi tumbuhnya lalang,","Padi huma atas bukit;","Ibu bapa teladan untuk tersayang,","Rezeki dibahagikan walaupun sedikit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107085,"Hendak dijamu kepada rakan,","Air selasih berbuah tiada;","Tidaklah jemu memberi didikan,","Tanda kasih duanya tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107086,"Susah payah nasi ditanak,","Lapar dahaga hidup disini;","Pekerti ayah ikutan anak,","Kebangaan keluarga hidup harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107087,"Korek halia dapatnya seraga,","Petik kelapa dapat setandan;","Pekerti mulia didikan keluarga,","Ibu dan bapa jadi teladan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107088,"Ilmu dan amal menjadi bekalan,","Budi bahasa jangan dilupa;","Ibu dan ayah mencorak masa depan,","Anak cemerlang maruah terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107089,"Kalau ada menanam tebu,","Boleh dijual dipasar desa;","Biarpun wang beratus ribu,","Budi bernilai sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107090,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Jasa baik sepanjang waktu,","Jadi kenangan setiap masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107091,"Kalau ada menanam tebu,","Boleh dijual dipasar desa;","Terlalu besar jasamu guru,","Mendidik, membimbing anak bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107092,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Dengar baik nasihat ibu,","Budi yang baik diingat sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107093,"Yang korek itu kendi,","Yang merah itu saga;","Bapa borek usah diikuti,","Jadilah anak yang berjasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107094,"Cincin emas gelang suasa,","Di pakai oleh dara jelita;","Ibu ayah menjaga kita,","Bila dewasa jangan derhaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107095,"Pergi menekut hala ke daik,","Jalan balak tepi lembah;","Perlu diikut perkara baik,","Buruk dielak undang musibah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107096,"Daun cermai bawa kemudik,","Tempat berpaut tak ambil hirau;","Kalau disemai benih yang baik,","Jatuh kelaut menjadi pulau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107097,"Sarapan pagi maggie kari,","Makanan kegemaran anak sekarang;","Anak melihat bapa makan berdiri,","Ibu menjeling tanda melarang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107098,"Pagi hari masak kari,","Masak kari si ikan pari;","Kalau bapa kencing berdiri,","Anak pula kencing berlari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107099,"Gaharu bau macam setanggi,","Dibakar perasap di tengahari;","Guru jika kencing berdiri,","Murid akan kencing berlari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107100,"Ikan seluang dimasak lemak,","Ikan pari dalam kuali;","Perangai buruk jangan tunjuk pada anak,","Anak dah dewasa akan mewarisi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107101,"Pi pekan Kangar beli baja,","Untuk dibaja pokok durian;","Jiwa anak kenalah jaga,","Supaya tak menyesal dihari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107102,"Malam hari merenung Sang bulan,","Merindu dia sambil merintih;","Ibarat ketam ajar anaknya berjalan,","Sendiri jalan terhegeh-hegeh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107103,"Pagi-pagi menggilap suasa,","Sambil digilap emas dan perak:","Ibu ayah dan warga dewasa,","Contoh terbaik tauladan anak-anak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107104,"Dalam dulang ada selasih,","Buat minuman jika dahaga;","Ibu penyayang bapa pengasih,","Membawa kebahgiaan dalam keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107105,"Pagi-pagi menggilap suasa,","Sambil digilap emas dan perak;","Perilaku anak cerminan kita,","Terjaga agama terjagalah akhlak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107106,"Manis berona isinya buah,","Hendak pula belimbing besi;","Ke mana tumpahnya kuah,","Kalau tidak ke atas nasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107107,"Kalau berjalan ke hujung desa,","Baik berdua atau bertiga;","Walau yang muda atau yang dewasa,","Gaya hidup Islam amalan mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107108,"Apa dikorek di tanah lembik,","Hendak menyemai benih selasih;","Bapa borek si anak rintik,","Semoga rintik secantik cenderawasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107109,"Indah sempurna seluruh buana,","Laman permainan dewa dan dewi;","Cakap sahaja bukan bermakna,","Tauladani pekerti murnikan pertiwi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107110,"Hendak pergi ke pulau daik,","Ribut, dan taufan di tempuh jua;","Didikan ibu bapa biar yang terbaik,","Menjadi kebangaan bila anak dah berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107111,"Terbang bebas si burung undan,","Tiada sempadan seluas lautan;","Ibu dan ayah contoh teladan,","Amalan yang baik jadi ikutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107112,"Pokok rambai di tepi telaga,","Buah nangka baru dipetik;","Bapa sebagai ketua keluarga,","Memimpin keluarga ke arah terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107113,"Emas dan perak usah dihimpun,","Ada lebih hulurkan sedekah;","Ibu dan ayah perlu besopan santun,","Jangan hanya berharap cikgu di sekolah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107114,"Pagi hari kita bersarapan,","Juadah enak pembuka selera;","Pekerti yang baik jadi ikutan,","Cerminan kita martabat bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107115,"Mari petik buah delima,","Tersangkut kaki terkena jerat;","Amalan baik diasuh bersama,","Selamat didunia selamat akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107116,"Pagi pagi menggilap suasa,","Sambi menggilap emas dan perak;","Kalau mak ayah sudah biasa,","Anak mengikut apa yang nampak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107117,"Keluar pagi berenang di kolam,","Sampai ke tengah tersangkut paku;","Cantik diluar cantik di dalam,","Memang layak dijadikan menantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107118,"Burung tiung burung jelatik,","Burung belatuk di pokok pinang;","Budi bahasa eluk rupa pun cantik,","Membuat hati terkenang-kenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107119,"Anak ayam berbunyi itik,","Rupanya itik baru menetas;","Luar dalam kenalah cantik,","Perangai cantik barulah kemas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107120,"Goreng nasi dalam kuali,","Di tambah petai juga belacan;","Cantik di mata cantik di hati,","Barulah sempurna menjadi insan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107121,"Sakit bukan kerana saka,","Tak dapat di ubat lalu mati;","Jangan cantik saja di muka,","Pasti kan cantik juga di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107122,"Dalam pagar si pohon betik,","Daun pandan tidak berduri;","Cantik d luar dalam pun cantik,","Bertuah badan empunya diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107123,"Lukis rajah ilusi kendiri,","Sambil meniti bawa halia;","Cantik wajah hiasan diri,","Molek pekerti amalan mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107124,"Lentiknya jari menumbuk padi,","Si padi huma dari utara;","Cantikkan diri cantikkan budi,","Seiring bersama maruah terpelihara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107125,"Pasang pelita di malam hari,","Ikan sepat enak dimakan;","Cantik di mata cantik hati,","Antara sifat insan beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107126,"Manis rasanya si buah betik,","Betik di tanam di hujung halaman;","Cantik di mata di hati pun cantik,","Itulah ciri-ciri menantu idaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107127,"Cantik bukan sebarang cantik,","Cantik lagi puteri bunian;","Jangan terlalu pandang pada yang cantik,","Belum tentu jadi idaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107128,"Kuih keria dari kelapa,","Buat hidangan satu keluarga;","Apa adanya cantik pada rupa,","Andai tiada berbudibahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107129,"Bintik seluar warnanya kelam,","Dipakai nyonya masa bercuti;","Cantik di luar cantik di dalam,","Manis wajahnya sopan pekerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107130,"Kalau makan semangkuk laksa,","Jangan lupa perah limau kasturi;","Rupa cantik berilmu berbudi bahasa,","Sah boleh jadi calon isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107131,"Diracik umbut reja melantun,","Beserta temu juadah dihidang;","Berwajah lembut bersopan santun,","Tidak jemu mata memandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107132,"Itik di kolam mencari teman,","Betik Tuaran tumbuh melata;","Cantik di dalam penyeri iman,","Cantik di luaran memukau mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107133,"Serumpun kunyit serumpun halia,","Kepasar pagi buat jualan;","Berbudi tinggi berhati mulia,","Wajah cantik bukan ukuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107134,"Itik berjalan ibu ikut bersama,","Mandi di kolam nampak seronoknya;","Cantik luaran hiasan cuma,","Cantik dalaman menyerlah serinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107135,"Kudup ketumbar secekak kesum,","Baru dipetik serak di tikar;","Senyuman lebar ikhlas menguntum,","Semakin cantik semakin mekar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107136,"Itik berenang didalam kolam,","Terjah berlari itik serati;","Cantik diluar cantik di dalam,","Wajah berseri cerminan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107137,"Asam kandis dalam tempayan,","Kulit lokan jatuh di laman;","Wajah yang manis nampak rupawan,","Maniskn lagi dengan senyuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107138,"Pohon meranti tumbuh melata,","Ditepi perigi rimbunan bata;","Cantik dihati cantik dimata,","Kemana pergi orang tak kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107139,"Pelbagai warna corak batik,","Benang disulam bermacam ragam;","Apa guna berwajah cantik,","Kalau di dalam hitam legam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107140,"Gelang emas gelang suasa,","Dipakai oleh sigadis sunti;","Cantik rupa cantik budi bahasa,","Pandang dimata sejuk dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107141,"Dalam reban ada itik,","Keluar pagi berenang dikolam;","Hiasi diri dengan akhlak yang baik,","Barulah cantik diluar, cantik didalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107142,"Itik berenang di tepi kolam,","Kolam buatan di dalam taman;","Cantik di luar cantik di dalam,","Bukti sempurna ciptaan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107143,"Rerama bersayap warna menarik,","Hinggap di daun sungguh ceria;","Apa guna berwajah cantik,","Utamakan budi berakhlak mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107144,"Pandai bicara menyusun kata,","Bagaikan mekar kembang pagi;","Bukan sekadar cantik di mata,","Sopan santun berbudi tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107145,"Pergi kedah makan laksa,","Bubuh jeruk ikan sepat;","Cantik wajah tutur bahasa,","Hati sejuk orang melihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107146,"Bersiar-siar di dalam taman,","Sambil duduk di pohon cermai;","Cantik luaran cantik dalaman,","Sifat terpuji di sukai ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107147,"Sungai Sarawak airnya tohor,","Tempat Tuan mencari nafkah;","Andai di hati teserlah kotor,","Manakan cantik bakal terserlah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107148,"Beli telur itik di pasar raya,","Beli juga ubi keladi;","Bagaimana cantik di mata,","Begitu juga cantik di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107149,"Bintik seluar warnanya kelam,","Dipakai nyonya masa bercuti;","Cantik di luar cantik di dalam,","Manis wajahnya sopan pekerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107150,"Sopan nyonya duduk bersila,","Di atas tikar berwarna cerah;","Cantik orangnya beriman pula,","Sehingga terpancar seri kewajah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107151,"Tercarik seluar ditampal sulam,","Ditampal lekap tidak menjadi;","Cantik di luar cantik di dalam,","Pakej lengkap menghias peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107152,"Itik berlari di atas bata,","Mengejar kedidi di hujung titi;","Cantiknya diri memukau mata,","Indahnya budi menambat hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107153,"Manis rasa nya buah mempelam,","Rasa rugi kalau tak makan;","Cantik luar cantik di dalam,","Itulah insan yang kami idamkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107154,"Tepian lata pohon meranti,","Dekat perigi murai bersarang;","Cantik di mata senang di hati,","Kemana pergi di sukai orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107155,"Dalam reban ada itik,","Itik jantan itik betina;","Bersyukurlah kalau cantik,","Kalo tidak terimalah apa adanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107156,"Kalau makan semangkuk laksa,","Jangan lupa perah limau kasturi;","Rupa cantik berilmu berbudi bahasa,","Sah boleh jadi calon isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107157,"Jalan bergaya tangan berlengang,","Kuih kapit sedap dimakan;","Berbaju kebaya cantik dipandang,","Berlesung pipit cantik menawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107158,"Anak ayam berbunyi itik,","Rupanya itik baru menetas;","Luar dalam kenalah cantik,","Perangai cantik barulah kemas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107159,"Pohon langsat ditepi tasik,","Batang semambu berjuntai dilaman;","Kalau berhasrat kekal menarik,","Amalkan ilmu cantik budiman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107160,"Dari Kertih ke Kayu Hitam,","Membawa betik dan rambutan;","Berbaju putih bercelana hitam,","Rupanya cantik jadi rebutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107161,"Itik di kolam mencari teman,","Betik Tuaran tumbuh melata;","cantik di dalam penyeri iman,","Cantik di luaran memukau mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107162,"Pagi pagi memotong betik,","Hendak di jual di Pekan selasa;","Untunglah berwajah cantik,","Cantik pulak budi bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107163,"Dalam belukar banyak unggas,","Bunyinya sungguh riang ria;","Mau cantik bukanya susah,","Amalkan senyum serta ketawa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107164,"Mari dibakar ubi keladi,","Sama dilenyek ubi keledek;","Cantik biar semulajadi,","Bukan cantik kerana bersolek.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107165,"Hujan rintik di malam Selasa,","Lama menanti payung dicari;","Orangnya cantik berbudi bahasa,","Hasrat di hati kujadikan isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107166,"Menanam betik bersama halia,","Di hujung halaman pisang mentalon;","Berwajah cantik berhati mulia,","Menjadi idaman sebagai calon.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107167,"Menjual buah di Pekan Mantin,","Laris perniagaan luar biasa;","Berseri wajah zahir dan batin,","Itulah kebanggaan setiap manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107168,"Pecah kaca pecah gelas,","Hias mawar di dalam cawan;","Luaran dalaman senyuman ikhlas,","Hati tergoda ramai kawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107169,"Ibarat kapal dengan nakhoda,","Penentu arah menuju destinasi;","Raja dan rakyat berpisah tiada,","Kasihnya dijunjung titahnya ditaati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107170,"Mudik ke hulu sampan didayung,","Bila sampai lalu berlabuh;","Raja berdaulat ibarat payung,","Rakyat jelata menumpang teduh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107171,"Masak lemak terung bulat,","Ikan masin menambah perisa;","Raja kita raja berdaulat,","Menjadi lambang kebanggaan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107172,"Membaca hikayat wali kota,","Lalu berlinangan siair mata;","Raja dan rakyat berpisah tiada,","Lambang kemakmuran Negara tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107173,"Elok rupa elok bahasa,","Bahasa istana penuh santun;","Raja &amp; rakyat saling berbahasa,","Membudayakan sentiasa bahasa dan santun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107174,"Kapal belayar menuju sabah,","Kapal kargo membawa barang;","Raja di sembah raja di sangah,","Kaseh nya raja bukan kepalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107175,"Belah rotan bertahta belian,","Sireh pinang pembuka bicara;","Raja berdaulat tiada tepian,","Adat dikenang rakyat sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107176,"Warna putih si itik angsa,","Terbang hingap di hujung kota;","Raja lambang kekuatan bangsa,","rakyat bernaung di payung mahkota.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107177,"Berlabuh kapal di hujung muara,","Singgah berlabuh berdagang bijih;","Tuanku ibarat ayah dan bonda,","Tempat rakyat menumpang kasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107178,"Pergi kahutan jumpa kera,","Kera melompat dipohon mangga;","Raja berdualat payung negara,","Taat setia tidak terhingga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107179,"Baja dan berzat tentulah petanda,","Subur sungguh cemara bertumbuh;","Raja dan rakyat berpisah tiada,","Syukur kukuh negara seluruh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107180,"Raja dan rakyat berpisah tiada,","Sudah tertulis sejak berzaman;","Usah digugat kesetiaan yang ada,","Demi Raja kami rela pertahankan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107181,"Taming sari keris sakti,","Semat di pinggang pahlawan tuah;","Raja kita bertukar ganti,","Setianya rakyat tidak berubah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107182,"Pagi hari memetik ulam raja,","Untuk dicicah bersama sambal kelapa;","Raja lambang negara kita,","Hendaklah kita taat dan setia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107183,"Rasa gayat teruja diredah,","Waktu senja kait pedada;","Setia rakyat begitu indah,","Bersama raja berpisah tiada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107184,"Baju Kedah berkain sutera,","Kain pelikat dibuat tudung;","Raja adalah payung negara,","Menjadi tempat rakyat bernaung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107185,"Ikan pelata dimasak cuka,","Dicampur dgn cili padi;","Raja dan rakyat berpisah tiada,","Segala perintah wajib ditaati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107186,"Angin lembut meniup ke barat,","Waktu senja beralih ke timur;","Negara beraja lagi berdaulat,","Rakyat hidup penuh makmur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107187,"Sayur peria banyak khasiat,","Tak kurang juga daun pegaga,","Kasih raja berjiwa rakyat,","Tolong menolong di mana saja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107188,"Beriring kilat guruh bergema,","Terbang merpati pulang ke sarang;","Raja berdaulat payung agama,","Tertib pekerti menjadi sanjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107189,"Irama dan lagu berpisah tiada,","Lemah gemalai rentak tarinya;","Raja berdaulat payung negara,","Aman dan damai rakyat jelata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107190,"Ibarat kapal dengan nakhoda,","Penentu arah menuju destinasi;","Raja di daulat tinggi di persada,","Setia nya bangsa menjadi tradisi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107191,"Sarapan pagi bubur jagung,","Jagung dibeli dihujung tanjung;","Raja disembah raja diagung,","Kepada tuanku agama dijunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107192,"Raja dan rakyat berpisah tiada,","Sejak dahulu hinggalah sekarang;","Kami rakyat berasa bangga,","Dibawah duli yang kami sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107193,"Pergi memancing diBukit Jugra,","Sambil memetik daun pegaga;","Baginda Tuanku payung negara,","Tempat berlindung rakyat semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107194,"Tuju Selat nampak duyung,","Kapal dilabuh pancing pelata;","Raja berdaulat ibarat payung,","Tempat berteduh rakyat jelata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107195,"Raja berdaulat ibarat payung,","Rakyat jelata menumpang teduh;","Negara kita kita yg menyanjung,","Hapuskan petualang segala musuh.", 4, 6);
