INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107369,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Berniaga jujur pasti berkat,","Rezeki yang halal dapat untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107370,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Perniagaan mesti dibayar zakat,","Barulah berkat mendapat untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107371,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Kacau rakan bagi mudarat,","Takkan seri hitung untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107372,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Bila menimbang lebihkan berat,","Hasil barakah penuh untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107373,"Kalau tuan pergi ke darat,","Jalan mari ke hujung tanjung;","kalau berniaga ingin kan berkat,","Jangan lupa yg maha AGUNG.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107374,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Kalau hendak rezeki berkat,","Niat dihati hendaklah jujur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107375,"Kalau Tuan pergi ke darat,","Jalan mari hujung tanjung,","Urus perniagaan mengikut syarat,","Sedikit rugi beroleh untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107376,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Berniaga usah bak lintah darat,","Ambil untung tak berpenghujung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107377,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Urusan Muamalah di ambil berat,","Dihisab Allah akan diperhitung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107378,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Jangan pembeli hendak dijerat,","Sekali terkena tak teman kunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107379,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Bukan kekayaan yang diperhambat,","Peniaga dan pembeli sama beruntung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107380,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Diam penggali cangkul berkarat,","Diam pedagang mengira untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107381,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Amal Makruf Perniagaan bersyarat,","Nahi Mungkar tidak menguntung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107382,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Perniagaan haram tiada syubahat,","Insyaa Allah berkat lagi beruntung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107383,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung,","Jual jering hancingnye pekat,","Jual pekasam baunya melantung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107384,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Risaukan untung hanya sekerat,","Sanggup menipu dan mengelintung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107385,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Tipu helah membawa mudarat,","Perniagaan terhenti tiada berhubung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107386,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Perniagaan berakad mengikut adat,","Bukan sekadar mengisi kantung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107387,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Perniagaan harus mengikut syarat,","Bukan perniagaan kung kalikung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107388,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Perniagaan berlandaskan agama sejagat,","Dunia akhirat akan disanjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107389,"Dalam reban berbunyi itik,","Keluar pagi berenang di kolam;","Cantik bukan sebarang cantik,","Cantik luar cantik dalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107390,"Anak kana pergi sekolah,","Berehat sekejap di pohon cempa;","Rupa cantik kurniaan Allah,","Budi bahasa jangan lah lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107391,"Pergi ke kota membeli itik,","Buah kelapa hanyut ke hulu;","Cantik dimata dihati pun cantik,","Jangan lupa senyum selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107392,"Buah kuini di dalam guni,","Buah durian di mana-mana;","Cantik rohani cantik jasmani,","Mendapat pujian di sini sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107393,"Pergi Tuaran mencari huma,","Merentas desa perut pun sebu;","Cantik luaran tak kekal lama,","Sampai masa berkedut seribu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107394,"Petik ubi mengirai padi,","Sarat bekal ke kebun cermai;","Cantik budi cantik peribadi,","Sahabat kekal taulan pun ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107395,"Kain batik dari seberang,","Bawa belayar ke Kuala Pilah;","Biar tak cantik dimata orang,","Asal mulia disisi Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107396,"Pagi-pagi pergi ke taman,","Bunga cantik menawan hati;","Manis kan senyuman manis kan iman,","Agar sentiasa cantik berseri seri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107397,"Bunga berputik terlihat indah,","Berputik pula pada tangkainya;","Sudah lah cantik diwajah,","Cantik pula di dalam hatinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107398,"Pohon langsat ditepi tasik,","Batang semambu berjuntai dilaman;","Kalau berhasrat kekal menarik,","Amalkan ilmu cantik budiman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107399,"Dalam reban berbunyi itik,","Keluar pagi berenang berteman;","Cantik bukan sebarang cantik,","Cantik hakiki disulami iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107400,"Cerdik bukan sebarang cerdik,","Cerdik belajar dengan adik;","Kalau barang yang baik,","Kemana pergi nampak cantik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107401,"Kalau tuan membeli itik,","Jangan lupa membeli angsa;","Kalau punya wajah yang cantik,","Sertakan juga budi bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107402,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Rezeki dan kurniaan dijaga dikendong,","Jangan sampai jatuh berderai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107403,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Tiada untungnya bersikap sombong,","Jauh saudara sahabat handai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107404,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Harta banyak jgnlah sombong,","Kejap nanti akan tergadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107405,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Sentiasalah tolong menolong,","Walaupun poket kita selalu pokai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107406,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Indahnya hidup tolong menolong,","Disukai semua sahabat handai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107407,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jadikan nasihat sebagai pendorong,","Baru terserlah bijak pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107408,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Patuh suruhan Allah yang Agong,","Di dunia bahagia akhirat digapai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107409,"Tanam limau tanam kedondong.","Tanam mari ditepi pantai;","Berbaik baik warga sekampong,","Agar silaturahim terus aman damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107410,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Berilmu tinggi janganlah buat sombong,","Ramai lagi orang-orang yang lebih pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107411,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan kedekut belanja wang,","Pasti menjadi menantu disayangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107412,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Kenanglah jasa ibu mengandung,","Insafi diri jangan diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107413,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Waktu kecil kita di kendong,","Tunjuk ajar ibu bapa jangan di abai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107414,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Sungguh sedap buah kedondong,","Duduk di pantai makan ramai ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107415,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Suka berkalau perlu dibendung,","Elakkan diri sebelum dirantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107416,"Tanam limau tanam kedondong,","Tanam mari ditepi pantai;","Setiap yang baik mau dikendong,","Usah berasa kita dah pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107417,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Biar pun langit nampaknya mendong,","Kain baju tetap ku sidai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107418,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Janganlah suka berbual kosong,","Sesaat masa itu amat dihargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107419,"Tanam limau tanam kedondong,","Tanan mari tepi pantai;","DiSajian klasik kita berkampung,","Sambil sarapan kita bersantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107420,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Kalau bercakap suka berbohong,","Rakan dan sahabat semuanya lari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107421,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Tanam kasih harus dikendong,","Tanam cinta takut makan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107422,"Membeli barang dikedai si nyonya,","Memasak untuk berkelah di pantai;","Rezeki kita Allah yg punya,","Hampir dimulut terhempas ke lantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107423,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Mana tak mahu kita bendung,","Pahala dicari kita pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107424,"Tanam limau tanam kedondong,","Tanam mari di tepi pantai;","Hidup harus tolong menolong,","Bagaikan laut dengan pantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107425,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Tak nak hidup berlagak sombong,","Takut nanti memakan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107426,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Habis tercicir semua dikendong,","Masihkah mahu berduduk santai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107427,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan hidup suka berlagak sombong,","Kelak rugi merana diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107428,"Tanam limau tanam kedondong,","Tanam mari ditepi pantai;","Anak harimau jangan dikendong,","Nampak ngeri ibunya melambai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107429,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jiwa kacau setelah berbohong,","Resah hati hilang damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107430,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Orang susah elok ditolong,","Jadikan amalan hidup santai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107431,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Hidup jangan suka menyombong,","Orang benci kawan pun tak ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107432,"Tanam mari tepi pantai,","Dihias cantik pagar berduri;","Orang bistari bijak dan pandai,","Membuat keputusan menguntungkan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107433,"Taman limau tanam ke dinding,","Tanam mari tepi pantai;","Kalau hidup kering dan kontang,","Akan memakan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107434,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Sayangkan anak mintak di dukung,","Kasih kan isteri kenalah belai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107435,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan leka dek puji dan sanjong,","Agar tidak lupa diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107436,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Dengki khianat harus dibendong,","Jati diri menjadi perisai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107437,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Duhai harimau usah sombong,","Sedar dirimu telah dirantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107438,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan sedara berlagak sombong,","Kelak nanti tidak di sukai ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107439,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jauhi sikap angkuh sombong,","Nanti dibenci sahabat handai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107440,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan suka cakap bohong,","Kemana pergi melikat mengintai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107441,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan lah hidup suka berbohong,","Rezeki sempit kawan tak ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107442,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jaga aib tak pandang serong,","Selisih faham mari berdamai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107443,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Buang jauh sifat sombong,","Baru kita kawan ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107444,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Kesalahan lampau jgn dihitung,","Sikap pemaaf sama-sama disemai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107445,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Kalau mahu hidup disanjung,","Jangan dicari bercerai-berai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107446,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Bila rindu datang mendorong,","Lepas dendam dapat diintai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107447,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jahanam pokok menjadi condong,","Gajah menggaru belakangnya gatai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107448,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan kedekut bila menolong,","Ibarat jering dipokoknya kutai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107449,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Jangan sesekali kita berbohong,","Habis lari sahabat handai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107450,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Kalau hidup selalu menyombong,","Pasti akan menjadi rugi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107451,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Apa digaru miangnya rebong,","Keladi kemahang memangnya gatai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107452,"Tanam limau tanam kedondong,","Tanam mari tepi pantai;","Ikan gelama siakap senohong,","Enak digulai bersama petai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107453,"Waktu kecil dipakai bedak,","Kulitnya pecah nampak dipipi;","Sewaktu kecil gelarnya budak,","Meningkat usia itulah lelaki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107454,"Kelapa di korek jari melentik,","Lokan di belah labu di kendong;","Bapa borek aku rintik,","Bukan salah ibu mengandung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107455,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Jangan kita berbohong selalu,","Hanya boleh menambah dosa.", 4, 6);
