INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107475,"Kalau tuan menanam tebu,","Boleh dijual di pasar desa;","Hancurnya iman kerana bicara,","Budi disanjung sampai kemati.", 4, 6);
