INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107480,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Ingatlah selalu pesanan ibu,","Berbudi bahasa setiap masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107481,"Kalau ada menanam tebu,","Boleh dijual dipasar desa;","Jangan lalai mencari ilmu,","Semoga hidupmu berjaya.", 4, 6);
