INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107483,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Kalau kita jadi tetamu,","Janganlah lupa tutur budi bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107484,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Biar jasa menjadi debu,","Dalam ingatan jelas terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107485,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Begitu besar jasa mu ibu,","Akan ku kenang setiap masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107486,"Kalau ada menanam tebu,","Boleh dijual dipasar desa;","Budimu Tuan ku ingat selalu,","Tak pernah dilupa disetiap masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107487,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Budi berganda pahala beribu,","Elakkan dibual sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107488,"Kalau ada menanam tebu,","Boleh dijual dipasar desa;","Andai sudah miliki sesuatu,","Jangan lupa membalas jasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107489,"Kalau ada menanam tebu,","Boleh dijual dipadar desa;","Tidak derhaka terhadap ibu,","Hidup selamat sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107490,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Sungguh besar pengorbananmu ibu,","Tak terbalas dgn wang walau berjuta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107491,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Diangin lalu ku kirim rindu,","Kerna sudah lama tidak bersua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107492,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Kalau di hati terasa rindu,","Tetapkan hari kita bertemu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107493,"Kalau ada menanam tebu,","Boleh dijual dipasar desa;","Kalau kita sayangkan ibu,","Janganlah lupa setiap jasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107494,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Tabur jasa janganlah jemu,","Orang kenang sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107495,"Apa dikelek si labu cantik ,","Labu diletak di dalam dulang;","Bapa borek si anaknya rintik,","Lebih baik dari anaknya berbelang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107496,"Ikan belanak dan ikan timah,","Beli dipasar dibawa balik;","Didikkan anak bermula dirumah,","Ibu dan ayah contoh terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107497,"Tebang buluh buat tabung,","Dibawa pulang kerumah atok;","Melentur buluh sedari rebung,","Bila dah keras susah nak bentuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107498,"Manis rasanya si buah Salak,","Salak dipetik di padi huma;","Bukan tidak mahu mengelak,","Sudah terjebak sekian lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107499,"Pagi hari pergi ke sawah,","Membawa bakul berisi ragi;","Ibu Bapa jalankan amanah ALLAH,","Semoga keluarga kita diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107500,"En.Mustafa membeli gelama,","Masak halia kari berempah;","Ibu bapa contoh utama,","Kerana ia bermula dirumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107501,"Merah-merah buah rambutan,","Masak di tangkai baik dipetik;","Perkara baik jadikan teladan,","Agar peribadi berakhlak baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107502,"Sang kancil binatang yg cerdik,","Boleh perdaya sang buaya;","Ibu mengasuh ayah mendidik,","Jasa mereka usah dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107503,"Kalau ada menanam tebu,","Boleh dijual di pasar desa;","Bantu seada ikhlas di kalbu,","Terpuji diamal setiap masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107504,"Tanam cili rapat-rapat,","Banyak cili dalam negeri;","Sudah mengorat tak dapat-dapat,","Terpaksalah aku hidup sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107505,"Saya dari Kolej Tun Hussien Onn,","Saya ingin jadi budak baik;","Kolej Tun Hussien Onn sebelah Dato Onn,","UKM institut yang terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107506,"Ikan dijual di dalam pasar,","Dibeli oleh Cik Izah;","Harapan keluarga amatlah besar,","Untuk membawa segulung ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107507,"Dari Johor pergi ke Bangi,","Niat dihati menuntut ilmu;","Pergi ke kelas berbau wangi,","Menimba ilmu hanya keranaMu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107508,"Bunga raya mekar dikota,","Lambang kecantikan uniknya Malaysia;","Tradisi budaya kebanggan kita,","Imej perpaduan rakyat Malaysia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107509,"Angin bertiup dilindungi papan,","Buah cempedak dikait menggunakan galah;","Jika kejayaan menjadi harapan,","Janganlah kita sesekali mengalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107510,"Kakak memanggil si tukang besi,","Yang datang hanyalah gergasi;","Ku datang membawa bagasi,","Ku pilang meninggalkan legasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107511,"Dimana ada gula disitu ada semut,","Ada bunga adalah bau harum;","Berusahalah mencapai matlamat,","Demi mengharumkan nama UKM.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107512,"Hala ke perigi membawa cucuian,","Hala ke sana tanpa bangga;","Harapan tinggi menggapai kejayaan,","Agar tercapai impian keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107513,"Baju melayu berwarna merah,","Warna merah seperti bunga raya;","Jangan malu jangan marah,","Semoga menjadi anak berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107514,"Membeli barang di pasar selasa,","Terjumpa dengan Pak lang;","UKM maju dan sentosa,","Saya pasti berjaya dengan terbilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107515,"Abang membeli kereta Perodua,","Untuk dihadiah kepada papa;","Dari muda sampai tua,","Budaya kita jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107516,"Diberi ilmu tanpa dipinta,","Tanpa mengenal usia dan rupa;","Wahai guru yang dicinta,","Jasa ditabur tidak dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107517,"Dibawa hati dibawa jiwa,","Maju melangkah lautan direnang;","Kegagalan diri tidak dibiar kecewa,","Agar masa depan diidamkan senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107518,"Masak meranum buah ara,","Jatuh sebiji diatas papan;","Jika berjimat setiap perkara,","UKM merupakan penentu masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107519,"Satu dua tiga empat,","Mari kita lompat sana sini;","Saya datang dari suatu tempat,","Untuk belajar di UKM ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107520,"Pergi ke kedai membeli roti,","Roti dimakan dengan kami;","Terima kasih UKM yang dihormati,","Menjadi tempat belajar kami setiap hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107521,"Jalan-jalan sakit dada,","Duduk tepi meja jumpa buku;","Baca-baca membuka minda,","Siapa lagi tak kenal aku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107522,"Ikan keli dimasak lemak,","Basikal dikayuh perlahan-lahan;","Anak sentiasa hormatkan emak,","Moga sentiasa dirahmati tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107523,"Duduk dibawah pokok yang besar,","Orentasi UKM memang parah;","Pengalaman diberi lembut dan kasar,","Terima kasih UKM payung merah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107524,"Tidaklah rata kulit sang buaya,","Umpama nanas tak terima hujan;","Cantik-cantik permata indah lagi budaya,","Tak lekang dek panas tak lusuh dek hujan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107525,"Tinggallah Ali dirumah lama,","Rumah lama di kampung tamu;","Susah senang kita bersama,","Aku sayang kamu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107526,"Dari sarawak jauh datangnya,","Kerana ingin menimba ilmu;","Saya baik elok orangnya,","Memandang saya takkan jemu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107527,"Cermin guna mestilah baru,","Lupa memang sudah begitu;","Ada masalah cari guru,","Guru menyelesaikan masalah itu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107528,"Pergi ke pasar di pagi hari,","Pasar bertapak di Batang Kali;","Kata ibu jangan diingkari,","Agar hidup sentiasa dirahmati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107529,"Jika dahaga minumlah soya,","Kerana soya dapat menyegarkan kita;","Tidak semua kita harus percaya,","Percayalah orang yang benar kata-kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107530,"Ke Pulau Pandan membawa perahu,","Untuk melihat si anak malu;","Datang ke UKM cerita baru,","Harap saya OK selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107531,"Membawa bekal pergi berlayar,","Pokok bunga sudah di sirami;","Bila berhutang harus dibayar,","Semoga cinta kita sentiasa bersemi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107532,"Burung kenyalang tanda Sarawak,","Sarawak dihuni pelbagai bangsa;","Pelbagai makanan sedap di Sarawak,","Datanglah Sarawak bila masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107533,"Pergi pasar sebelah petang,","Pergi bersama adik-beradik;","Kalau anda orang yang lantang,","Mungkin salah seorang yang cerdik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107534,"Di sekolah naik tangga,","Sehari-hari belajar BM;","Ibubapa gembira tak terhingga,","Kerana diriku dapat UKM.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107535,"Buah durian buah berduri,","Bersama dengan buah longan;","Barang orang jangan dicuri,","Akibatnya dipotong tangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107536,"Cantik sunggu pengantin ini,","Bank bunga mawar di taman;","Martabatkan bahasa ibunda negara ini,","Universiti Kebangsaan Malaysia menjadi idaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107537,"Pergi ke laut menangkap ikan,","Tapi jangan lupa membawa teman;","Amalan budaya jangan dilupakan,","Tidak pupus dimakan zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107538,"Remaja kini menjulang nusa,","Bagai cahaya menyeri pandangan;","Malaysia harmoni aman sentiasa,","Dengan adanya pemimpin sanjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107539,"Alahai comelnya bunga melati,","Cantik juga kain sutera;","Saya kini penat menanti,","Mohon copkan dengan segera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107540,"Mencedok air di tepi telaga,","Enggan ke sungai takutkan buaya;","Adat dan budaya wajib dijaga,","Menjadi warisan anak bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107541,"Hari ini hari raya,","Rumah Ali ramai tamu;","Ibu berpesan kepada saya,","Pergi universiti menimba ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107542,"Bersiar-siar bersama Mimi,","Bersiar dengan hati-hati;","Terima kasih menerima kami,","UKM tetap dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107543,"Pergi ke kedai membeli roti,","Roti dimakan bersama kaya;","Kalau baik budi pekerti,","Disayangi orang dimana jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107544,"Pergi ke pulau bercuti sakan,","Buah tangan tidak dilupa;","Berhati-hati memilih kawan,","Kelak merana hatimu hampa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107545,"Tumbuh lada dipohon bidara,","Tumbuh dengan pokok kuini;","Kibar bendera cintakan negara,","Rakyat aman hidup harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107546,"Bunga melati bunga di darat,","Bunga seroja ditepi kali;","Hina besi kerana karat,","Hina manusia tidak berbudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107547,"Anak dara cantik bergaya,","Berjalan seiring ke rumah mama;","Selamat datang ke kampung budaya,","Acara pelbagai dirai bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107548,"Terasa lapar pergi ke pasar,","Pergi membeli pelbagai makanan;","Niat kita jangan pernah tersasar,","Untuk kebaikan masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107549,"Gadis jelita di atas titi,","Bertutup litup berbaju raya;","Varsiti kita sentiasa di hati,","Moga hidup di mata dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107550,"Dari Kelantan ke Kuala Lumpur,","Datang mencari kereta Exora;","Semangat dihati untuk bertempur,","Dapat berbakti untuk negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107551,"Baju saya hitam,","Tapi hatinya bersih;","Kalau makan ketam,","Minum sekali ginger teh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107552,"Anak dara manis jelita,","Beli kain bercorak buaya;","Segulung ijazah matlamat kita,","Berusaha tawakal sehingga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107553,"Hidup insan dalam agama,","Manusia beriman menjadi pilihan;","Kehidupan masyarakat bersama-sama,","Janganlah kita melupakan tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107554,"Antara kemahiran dara yang utama,","Belajar membuat jahitan sembat;","Tidak ku sangka dapat diterima,","Menjadi mahasiswa di UKM terhebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107555,"Bunga Melor bunga melati,","Dibawa belayar ke negeri cina;","Jika iman tiada di hati,","Nescaya sengsara diakhirat sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107556,"Pergi ke pasar membeli delima,","Delima dibeli oleh Pak Maun;","Ilmu diajar saya terima,","Sebagai hutang seribu tahun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107557,"Pagi-pagi makan halwa,","Makan halwa penuh bergaya;","Minggu ini minggu mesra mahasiswa,","Semoga menjadi penuntut berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107558,"Sopannya gadis bila berkata,","Cantik langkahnya bila ke pekan;","Indahnya sungguh adat tradisi kita,","Namun mengapa kian dilupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107559,"Pergi ke pekan sibuk membeli,","Bersua pula di tepi kota;","Budaya kita indah sekali,","Itulah dia Malaysia tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107560,"Manusia yang tidak berputus asa,","Berasal dari kuasa yang terkawal;","Kalau akhirnya ditakdirkan seksa,","Tidak sepatutnya bermula dari awal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107561,"Pergi ke kedi membeli jamu,","Balik ke rumah terjumpa si Dayang;","Pergi ke sekolah mencari ilmu,","Kejayaanku buat sekolah tersayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107562,"Pergi senggarang membeli kerang,","Singgah sebentar di pekan Raja;","Jangan dicari salah orang,","Perlu ambil yang positif sahaja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107563,"Usaha dikerah dengan harapan,","UKM di cita idaman hati;","Visinya dipegang dengan mapan,","Demi membina hidup sejati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107564,"Halia ini tanam-tanaman,","Ke barat juga akan condongnya;","Dunia ini pinjam-pinjaman,","Akhirat juga akan sesungguhnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107565,"Bunga mawar berwarna merah,","Dipetik sekuntum Pak Hairi;","Jangan dilepas perasaan marah,","Nanti menyesal dikemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107566,"Malam ini pergi ke pesta muda,","Dengan kawan lama kita bersua;","Kami berlapan berpisah tiada,","Sering bersama ke mana jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107567,"Negara Malaysia aman sentosa,","Kunjungan pelanggan tak putus;","Sambutan merdeka membaru sentiasa,","Pastikan kita tidak putus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107568,"Pergi ke kota membeli kereta,","Kereta biru pilihan ramai;","Jika toleransi amalan kita,","Sudah pasti hidup damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107569,"Pak Mayu menangkap ikan,","Buat lauk isterinya ita;","Bahasa Melayu jangan dilupakan,","Budaya kita harta kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107570,"Jepun, Hong Kong negara Asia,","Madu bunga dimakan rama-rama;","Wahai pelajar Universiti Kebangsaan Malaysia,","Marilah kita berjaya bersama-sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107571,"Kayu jati dibuat kerusi,","Buat dagangan di Pulau Jawa;","Nikmat merdeka sama dikongsi,","Peliharalah dengan sepenuh jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107572,"Bunga indah si bunga temu,","Disimpan si puteri di dalam peti;","Sejujurnya aku merindui kamu,","Ikhlas dariku si penghuni hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107573,"Jauh merantau hingga ke Kajang,","Hendak meminang Puteri Hajar;","Perjalanan hidup amatlah panjang,","Amatlah rugi tidak belajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107574,"Sudilah tuan jalan berlapan,","Jalan berlapan dimalam kelam;","Sudilah tuan beri ucapan,","Kata aluan pemanis kalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107575,"Tuai padi antara masak,","Esok jangan layu-layuan;","Intai kami antara nampak,","Esok jangan rindu-rinduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107576,"Hari ini sungguh ceria,","Riang ria suka hati;","Saya berasa sungguh gembira,","Dapat menjadi pelajar Universiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107577,"Pergi balairung berjumpa sultan,","Jumpa sultan untuk berpantun;","Kecil-kecil perlu dirotan,","Agar dewasa sopan-santun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107578,"Baik-baik makan keladi,","Keladi itu ada kebaikannya;","Baik-baik termakan budi,","Budi itu ada hutangnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107579,"Tikar pucuk tikar mengkuang,","Alas nikah raja Melayu;","Ikan busuk jangan dubuang,","Buat perencah si daun kayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107580,"Di tanah lata tumbuh kiambang,","Bersimpuh si sunti hatinya cekal;","UKM tercinta ilmuan berkembang,","Gembira dihati tidak disangkal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107581,"Bangi termasyhur kerana UKM,","Lahirnya pelajar berjati diri;","Bagi memenuhi aspirasi UKM,","Izinkan saya memperkenalkan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107582,"Pengantin tiba kompang dipalu,","Sesudah bersanding dijemput makan;","Hidup didunia perlu usaha selalu,","Namun jangan akhirat dilupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107583,"Anak ke kedai membeli ikan,","Dibeli pula di kedai Abu;","Jadilah anak yang boleh dibanggakan,","Hormatlah mereka ayah dan ibu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107584,"Mendaki bukit teramatlah payah,","Bertemu pula dengan Si Nyonya;","Kalau tidak menghormati ibu dan ayah,","Kelak nanti sesal selamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107585,"Sarapan pagi minum teh tarik,","Kena sentiasa beringat;","Hari ini panas terik,","Habis basah baju ini dengan keringat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107586,"Pergi ke kedai beli kelapa,","Terjumpa pula Si Duda;","Adat tradisi jangan dilupa,","Menjadi pengalaman orang muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107587,"Bekerja sehari tubuh lelah,","Baca buku indah irama;","Bila engkau masih bersekolah,","Belajar adalah tugas utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107588,"Harum semerbak bunga matahari,","Harum lagi si bunga raya;","Awak nampak cantik berseri,","Cantik lagi bersama si dia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107589,"Ambilkan saya daun keladi,","Lurutkan saya daun selasih;","Kasih keluarga kasih abadi,","Tiada kasih semurni kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107590,"Dua tiga kucing berlari,","Mana nak sama Si Kucing Belang;","Dua tiga dapat dicari,","UKM kita memang cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107591,"Rumah bunian sangat padat,","Terik matahari merasa dahaga;","Busana tradisi lambang adat,","Menjadi harta paling berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107592,"Ilham puteri impikan dewa,","Setia menanti saban hari;","Merak kayangan sangat istimewa,","Tersemat disini pelbagai memori.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107593,"Angin petang bertiup sepoi-sepoi,","Pelajar UKM terlibat domain;","Panggung seni memang sempoi,","Pelajar aktif seronok bermain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107594,"Belajar lukis dengan pensil,","Mengapa kertas tidak rata;","Semoga engkau berhasil,","Meraih segala cita-cita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107595,"Burung-burung terbang berkicauan,","Angin pagi menegur sapa;","Harapan saya tidak keterlaluan,","Segulung ijazah dari UKM untuk ibubapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107596,"Anak Melayu bernama Ali,","Pergi jumpa Kak Bedah;","Buat jahat jangan sekali,","Kelak nanti buruk padah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107597,"Jalan-jalan di hari raya,","Dalam perjalanan melalui titi;","Negara kita banyak budaya,","Semua budaya tetap di hatiku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107598,"Asam sambung jangan dipaut,","Jika ditambat kalah laga;","Asam di gunung ikan di laut,","Dalam belanga bertemu juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107599,"Naik ke bukit membeli lada,","Lada sebiji dibelah tujuh;","Apanya sakit berbini janda,","Anak tiri boleh disuruh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107600,"Pergi ke pasar membeli ikan,","Beli sekali nila dan jerami;","Sebelum pantun kami tampilkan,","Jawab dulu salam kami.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107601,"Pergi ke hutan kapak diguna,","Kapak diguna memotong balak;","Walaupun tidak menarik mana,","Harap pantun tidak ditolak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107602,"Ada burung dalam sangkar,","Burung lepas susah tangkapnya;","Buat apa kita bertengkar,","Bertengkar tiada untungnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107603,"Dengar lawak tertawa-tawa,","Hingga kepala terhantuk papan;","Datang UKM demi minggu Mesra Mahasiswa,","Ingin saya membina masa depan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107604,"Anak kucing di dalam rumah,","Merayau-rayau di pagi ahri;","Jika ingin menjadi peramah,","Jangan sesekali menyendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107605,"Burung pipt hinggap di atap,","Hinggap dipohon bunga merah;","Kalau adik ingin menyantap,","Sopan santun jangan marah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107606,"Si adik suka membaca kamus,","Ingin mengenal terjemahan Malaysia;","Dua tahun bertungkus lumus,","Diterima masuk ke Universiti Kebangsaan Malaysia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107607,"Membawa bekal segelas susu,","Makan kari bersama disaji;","Bijak berakal mengawal nafsu,","Menjaga diri sifat terpuji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107608,"Pergi ke pasar membeli senohong,","Ikan senohong banyak sekali;","Kalau suka bercakap bohong,","Akhlak mulia jauh sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107609,"Dalam tasik ada ikan,","Ikan dijual di kedai Mamat;","Kini UKM talah mengharumkan,","Dari mula pengajian hinggalah tamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107610,"Selamat datang diucapkan,","Kepada saudara saudari sekalian;","Kalau ada salah harap dimaafkan,","Ikhlas dari saya Si Intan Berlian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107611,"Buat sesuatu jangan sia-sia,","Nanti tidak dapat nasi;","Minggu pertama di Universiti Kebangsaan Malaysia,","Rindu ibu dan ayah yang tiada disisi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107612,"Anak lembu merah,","Tambat di pokok asam;","Adik kena marah,","Tarik muka masam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107613,"Pagi-pagi sudah berjaga,","Pergi bersenam bersama rakan;","Hati ibu mesti dijaga,","Nasihat ibu jangan dilupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107614,"Terbang tinggi banyak kelokan,","Helang terbang jauh melayang;","Selamat pagi ku ucapkan,","Untuk adinda yang tersayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107615,"Dimasak belacan sayur kailan,","Hidangan dimasak dengan hati;","Natijah ilmu dengan amalan,'","Natijah budi dengan bakti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107616,"Pergi kedai balik pukul tiga,","Berseronok sampai lupa beli roti;","Minggu mesra mahasiswa amat berharga,","Domain kebudayaan UKM no.1 di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107617,"Domain kebudayaan di seni panggung,","Datang untuk kekalkan budaya;","Hati senang makan jagung,","Agar budaya kita sentiasa kaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107618,"Cantik sungguh tuan puteri,","Apabila tersenyum tambah manisnya;","Kalau tuan puteri bijak bestari,","Kuih dimakan hanya kopinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107619,"Berjalan datuk pergi ke kota,","Pergi bersama Tuan Ali;","Bukan pelita sebarang pelita,","Pelita dimakan sedap sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107620,"Kucing Parsi baru lahir,","Tapi sayang dimakan buaya;","Ini pantun yang berakhir,","Tepuk tangan untuk saya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107621,"Buah ciku buah kesukaanku,","Perkahwinan adalah sebuah dambaan;","Sudah habis masa lapangku,","Kini tiba masanya aku mencipta kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107622,"Anak Melayu makan tauhu,","Tauhu dibuat daripada soya;","Kalau awak nak sangat tahu,","Hanya awak seorang di hati saya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107623,"Menyeberangi sungai melalui titi,","Singgah sebentar untuk bermain taman;","UKM tempat impian menjadi realiti,","Usaha, doa dan tawakal menjadi pedoman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107624,"Berterbangan tinggi si burung helang,","Singgah sebentar di atas titi;","UKM merupakan universiti gemilang,","Kerana itu UKM dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107625,"Di pagi hari berwangi-wangi,","Ingin berjumpa rakan dikota;","Panas terik sanggup diharungi,","Demi UKM universiti tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107626,"Kolej KIY UKM di atas bukit,","Langsir kuning hitam berupa baru;","Kami dan semua PC janganlah sakit,","Supaya kami tenang seperti laut biru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107627,"Anak dara membuka pandan,","Membuat kuih hidangan untuk kenduri;","Ilmu yang baik jadikan teladan,","Ilmu yang kurang bagaikan duri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107628,"Tanam padi berkain pelikat,","Ditanam bersama pohon markisa;","Hidup masyarakat perlu muafakat,","Supaya negara kekal sentosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107629,"Pergi ke sungai untuk bermandi,","Airnya cetek sejuk sekali;","Kalau tuan orangnya berbudi,","Pasti akan dihormati berkali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107630,"Buah berangan masaknya merah,","Kelekati diletak dalam perahu;","Luka di tangan nampak berdarah,","Luka di hati siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107631,"Terang bulan terang paya,","Anak kambing memakan rumput;","Jika tak kenang kepada saya,","Nama yang baik tidak tersebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107632,"Tarik papan dalam pasar,","Bagi sikit kepada Mak Jah;","Harapan keluarga amatlah besar,","Untuk membawa segulung ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107633,"Membina rumah berdindingkan papan,","Bising penghuninya pelbagai ragam;","Bukan kecil impian dan harapan,","Segulung ijazah ingin digenggam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107634,"Balik kampung jumpa imam,","Ingin kstsnys mengenai raya;","Pertama kali menjejak UKM,","Terpesona dengan perkampungan budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107635,"Burung merpati terbang ke hulu,","Terbang-terbang ke Pengkalan Chepa;","Kalau sayang tradisi dahulu,","Sayang-sayanglah jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107636,"Tidur malam sangat lena,","Lagi-lagi lepas berangan;","Hidup kita biar sempurna,","Supaya dapat tinggalkan kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107637,"Petang hari pergi ke pekan,","Pulang membawa sebungkus laksa;","Hidup muhibah perlu diamalkan,","Di negaraku Malaysia berbilang bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107638,"Pergi Jepun jumpa raksasa,","Makan sushi tak sedar diri;","Pelajar UKM memang luar biasa,","Tapi jangan sampai lupa diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107639,"Jalan-jalan pergi ke kota,","Berhasrat ingin membeli peti;","Bahasa Melayu bahasa kita,","Jangan dilupa sehingga mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107640,"Dayung sampan hingga ke muara,","Singgah sebentar membeli peti;","Berada di perkampungan kebudayaan amat gembira,","Dapat menghidangkan rasa hiba dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107641,"Pergi kedai beli soya,","Soya dibeli bersama beri;","Minggu depan sudah raya,","Duit raya boleh diberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107642,"Pergi ke taman bercucuk tanam,","Bercucuk tanam bunga raya;","Terima kasih pihak UKM,","Menerima saya seadanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107643,"Bunga mawar sungguh wangi,","Dua mempelai ingin bertemu;","Dari jauh datang ke Bangi,","Untuk berjahat menuntut ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107644,"Terbang tinggi si burung helang,","Hinggap di dahan bersama rerama;","Generasi cemerlang budaya gemilang,","Budi pekerti amalan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107645,"Hujan renyai kemudiannya pawana,","Sinar mentari mencecah pipi;","Jika ingin bijak wacana,","Molek bahasa disusun rapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107646,"Hias terendak songket kemboja,","Milik mualim saudagar berlayar;","Meluncur todak, mengancam raja,","Setia Hang Nadim bagaimana terbayar?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107647,"Letak pedada raga disisi,","Sapa Sina tepian taman;","Teratak ibunda syurga firdausi,","Mengapa sirna tepian laman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107648,"Duduk andika bersama puteri,","Berteman sahaja membaiki sampan;","Siti Aisyah nama diberi,","Datang UKM membawa harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107649,"Asal kupas menjadi benang,","Asal benang menjadi kain;","Perkara lepas usah dikenang,","Kalau diri sudah punya yang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107650,"Telur ayam itik menetas,","Dikacau telur pasti mara;","Ayuh semua bergerak pantas,","Agar semua siap segera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107651,"Pergi ke kedai membeli kain,","Kain dibeli di kedai nyonya;","Kami datang bukan hendak bermain,","Ingin bertanya si dara sudah berpunya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107652,"Pergi ke kedai membeli asam jawa,","Membawa sekali kayu cemeti;","Jangan lupa pesanan orang tua,","Akhlak yang mulia orang hormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107653,"Satu rangkap puisi diminta,","Berjuta-juta harta saya sembahkan;","Anak-anak perlulah mendengar kata,","Beban ibu bapa boleh dikurangkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107654,"Membawa bekal sebotol susu,","Kari dan nasi putih disaji;","Bijak berakal mengawal nafsu,","Menjaga diri sifat terpuji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107655,"Pergi ke kedai membeli ikan,","Membeli ikan dengan kacang bitam;","Kesenian budaya jangan dilupakan,","Agar tidak lupakan generasi muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107656,"Pergi ke Pusanika membeli dadih,","Cuaca harini sangatlah hangat;","Jangan marah jangan sedih,","Domain kebudayaan best sangat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107657,"Mengilhamkan harapan cipta masa depan,","UKM pelengkap aset negara;","Ilmu dimapan sentiasa dihadapan","Menjunjung amanah semangat membara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107658,"Ke puncak gading menuntut ilmu,","Seperti terbang burung merpati;","Jagalah adat, sopan santunmu,","Semoga hidup selalu diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107659,"Dipuput bayu melentur si lalang,","Induk dipuput dibalik cemara;","Mahasiswa cemerlang gemilang,","Kita pemdaulat amanah negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107660,"Batang selasih patah dipulas,","Dibuang dayang ditepi kali;","Kusangka kasih tulis dan ikhlas,","Rupanya sayang dijual beli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107661,"Pergi ke kedai membeli kudapan,","Pergi ke kedai yang baru dibina;","Kami datang membawa harapan,","Supaya menjadi insan berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107662,"Bulat air kerana pembuluh,","Bulat manusia kerana muafakat;","Minggu mesra banyak berpelah,","Pelajar harmoni kerana sepakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107663,"Jalan-jalan ke Kota Paris,","Hujan lebat berlecak-lecak;","Biar mati di hujung keris,","Asalkan dapat kanda kacak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107664,"Bendera berkibar dirumah Pak Agos,","Di halaman rumah pasang pelita;","Menjelang tiba 31 Ogos,","Tarikh kemerdekaan negara kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107665,"Hobi saya menunggang kuda,","Kuda ditambat di pokok ara;","Bukan sekadar sarjana muda,","Tapi penentu masa depan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107666,"Makan jamu menjadi indah,","Anak bulan disinggahsana;","Menuntut ilmu satu ibadah,","Menjadi bekalan diakhirat sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107667,"Pergi ke kedai membeli ikan,","Membeli ikan dengan Pak Rahman;","Warisan dan budaya usah dilupakan,","Agar Malaysia sentiasa aman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107668,"Belajar untuk menjadi pandai,","Harapan daripada kedua orang tua;","Belajar bukan sekadar pandai,","Bawa ilmu hingga ke tua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107669,"Gadis Melayu manis rupanya,","Bersiar-siar di hujung desa;","Janganlah kita ingkar perintahnya,","Kelak hidup kita binasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107670,"Berjalan-jalan di Kota Lama,","Meluangkan masa bersama tersayang;","Timba ilmu biarlah lama,","Asalkan kita bersifat penyayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107671,"Jalan-jalan ke tanah Bali,","Hendak mencari sekarung mainan;","Kepada tuhan tempat kembali,","Jangan lupa mohon ampunan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107672,"Tak dengar kata ibu marah,","Ibu mendidik anak menjadi manusia;","Tudung putih suci berbaju merah,","Selamat datang ke Universiti Kebangsaan Malaysia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107673,"Memakai baju butang suasa,","Dilihat si dia hati terpana;","Jangan kita mengikut rasa,","Kelak diri akan merana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107674,"Pergi ke kedai membeli ubat ketuat,","Pergi bersama dengan Pak Mail;","Kami hebat kami kuat,","Itulah kami Kolej Tun Dr. Ismail.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107675,"Mandi-manda di Pualau Lang Tengah,","Hati-hati terjumpa ular berbisa;","Penat lelah termengah-mengah,","UKM memang luar biasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107676,"Seri kembangan tempat asalku,","Membesar bersama saudaraku Izzah;","Terpaksalah aku tinggalkan temanku,","Demi membawa pulang segulung ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107677,"Duduk teruna dipohon cemara,","Bersama puteri cantik berseri;","Assalamualaikum pembuka bicara,","Muhammad Nue Haizan nama diberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107678,"Nasi goreng masakan isteri,","Beras dibawa dari kedah;","Bersyukur rezeki Allah beri,","Ringankan tangan hulur sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107679,"Minum petang di rumah Khairullah,","Disaji dengan pulut lepa;","Kita khalifah dibumi Allah,","Jangan pula membuat-buat lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107680,"Cantik bergugus buah papaya,","Masak dipetik satu persatu;","Walaupun berlainan bangsa budaya,","Kerana muhibah hidup bersatu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107681,"Petang-petang makan pegaga,","Main congkak atas jerami;","Adat resam harus dijaga,","Sebelum hilang ditelan bumi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107682,"Abang keluar membeli ampaian,","Bertemu dengan para siswazah;","Aku datang membawa impian,","Pulangku pasti bersama ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107683,"Ibu memakai sebuah gelang,","Perut lapar segera makan;","Lima waktu janganlah hilang,","Dimanapun selalu tunaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107684,"Hari malam gelap gelita,","Pasang lilin jalan ke taman;","Sopan santun budaya kita,","Jadi kebanggaan zaman berzaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107685,"Buah berangan masak sebaldi,","Disimpan kemas didalam peti;","Sahabat sejati kekal abadi,","Bersama hingga ke syurga nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107686,"Apa guna berkain batik,","Kalau tidak dengan sucinya;","Apa guna beristeri cantik,","Kalau tidak dengan buainya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107687,"Bersungguh benar menyedia bekal,","Bekal disedia untuk ayahnya;","Jangan kita panjang akal,","Kleak menyesal tiada sudahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107688,"Cahaya suria menyinar biara,","Tolong teguh pasak terhebat;","Generasi belia harapan negara,","Imannya kukuh minda hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107689,"Pulau Pandan jauh ke tengah,","Gunung Daik bercabang tiga;","Menuntut ilmu jangan mengah,","Semoga menjadi insan terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107690,"Daun hijau jatuh ke lantai,","Senior UKM bagi satu peta;","Sekarang saya sedang bersantai,","Di UKM saya yang tercintai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107691,"Pergi ke bandar membeli barang,","Membeli barang bersama Cik Ana;","Hidup jangan menghina orang,","Kelak nanti hidup merana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107692,"Orang Jawa mencari benang,","Mencari benang diatas bukit;","Orang jauh jangan dikenang,","Lama-lama jadi penyakit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107693,"Berubah sehingga ke hujung zaman,","Sehingga ke zaman Si Ali Baba;","Nama hamba Amirul Aiman,","Seorang warga di kolej Zaaba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107694,"Buah rambutan sungguh lebat,","Di ambil Nona di kebun Haka;","Pelajar UKM pelajar hebat,","Kata juara itulah mereka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107695,"Songket terbelah sopan tingkahnya,","Indah mempelai dipohon bambu;","Penat lelah tiada hiraunya,","Tiada nilai kasihnya ibu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107696,"Di waktu petang turun ke paya,","Turun ke paya bersama Hajar;","Hamba kemari bukan untuk berfoya,","Hamba ke sini untuk belajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107697,"Orang Melayu Melayu dan Penan,","Sungguh hebat membuat drama;","Sudah lama hamba berkenan,","Dengan Cik adik bersama-sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107698,"Gerimis turun rintik-rintik,","Guruh datang selang menyelang;","Bunga ditaman ingin ku petik,","Sayang jauh tiada kepalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107699,"Hujan lebat dipohon duri,","Burung nuri di Tanjung Bidara;","Megah hebat UKM berdiri,","Siswa siswi tonggak negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107700,"Rumah terbuka pelbagai hidangan,","Dihidang rendang dan roti jala;","Harapan ayah ibu jadi dorongan,","Demi ijazah ku koban segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107701,"Bunga mawar si bunga melati,","Dipetik pula si anak teruna;","Azam dan ikhlas pengiring sejati,","Ilmu ditimba hidup bermakna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107702,"Langit biru terbentang luas,","Terik cahaya mengintai hari;","Ilmu didada sangatlah luas,","Tetaplah semangat meniti hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107703,"Bunga mawar bunga selasih,","Menjadi hiasan di taman permai;","Jangan membenci tapi berkasih,","Hiduplah kita aman dan damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107704,"Limau manis limau kasturi,","Penuh berisi dalam dulang;","Saya senyum untuk sendiri,","Bukan tujuan menghalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107705,"Duduk di hutan menjaga bara,","Nyalanya kuat apinya nyata;","UKM penjaga maruah negara,","Maruah warga Malaysia tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107706,"Mencari timba si anak dara,","Dalam sarang burung tempua;","Salam sembwhpembukan,","Assalamualaikum semua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107707,"Kecil comel namanya Etun,","Selalu makan ikut rasa;","Hidup haruslah bersopan santun,","Supaya hidup senang sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107708,"Ayuk pergi minum jamu,","Ingatlah kenangan manis sesungguhnya;","Wahai kawan jagalah persahabatanmu,","Supaya dapat mengekalkan selama-lamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107709,"Sore-sore memancing ikan,","Duduk lama pun tiada jemu;","Belajar itu mengasyikkan,","Kita pandai banyak ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107710,"Kuala Lumpur ibu negara,","Negeri Selangor Darul Ehsan;","Mati dihibur hati dan lara,","Sama berhinur hilangkan bosan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107711,"Hujan turun badan pun basah,","Batang galah haluan perahu;","Niat di hati tak mahu berpisah,","Kehendak Allah siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107712,"Sedap sungguh kuih keria,","Makan bersama rakan-rakan;","Mari kita bersuka ria,","Karnival kebudayaan kita raikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107713,"Hebatnya bandar Johor Bharu,","Kemajuan dan rakyat membangun sejajar;","Saya ini pelajar baru,","Salah silap tunjuklah ajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107714,"Negeri Johor merupakan negeri tersohor,","Kaya dengan pelbagai adat dan budaya;","Saya merupakan warganegara Johor,","Insan budi bahasa yang kaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107715,"Kalau ikan dalam kolam,","Bunga tumbuh ditengah taman;","Kalau cinta sudah dalam,","Jarak jauh terasa nyaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107716,"Indahnya suasana di halaman desa,","Ternampak anak kecil berlari-larian;","Janganlah kita tergesa-gesa,","Nescaya berakhir dengan kekecewaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107717,"Duduk termenung bawah pohon ara,","Sambil menghirup udara nyaman;","Kesenian harus dipelihara,","Agar kekal sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107718,"Makan rambutan warna merah,","Pilih yang harum baunya;","Perjuangan tumpah darah,","Cintailah Malaysia kita punya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107719,"Pergi ke kedai membeli baju,","Untuk dipakai dihari raya;","Jika ingin sentiasa maju,","Harus belajar sehingga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107720,"Mak Milah pergi berkelah,","Pergi berkelah dengan Johan;","Tidak pernah kenal erti mengalah,","Itulah kami dari Kolej Burhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107721,"Udang galah masak kari,","Dihantar jauh ke rumah ustazah;","Harapan tinggi saya ke mari,","Ingin pulang dengan ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107722,"Pergi ke kedai membeli roti,","Roti dimakan bersama kaya;","Kalau baik buah pekerti,","Disayangi orang dimana jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107723,"Ada burung dalam sangkar,","Burung lepas susah ditangkap;","Buat apa kita bertengkar,","Bertengkar tiada untungnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107724,"Kalau tuan hendak ke pasar,","Tolong belikan suasa;","Wahai tuan janganlah kasar,","Kelak badan tuan binasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107725,"Berlayar Tanggang ke laut dalam,","Merana ibu anak ditunggu-tunggu;","Warisan jangan pernah dibiarkan tenggelam,","Budaya pula jangan ditinggalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107726,"Sungguh cantik anak Che Ta,","Lahir ditengah Bandar Tangkak;","Apa guna sibuk bercinta,","Lebih baik bermain congkak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107727,"Tanam padi berkain pelikat,","Ditanam bersama pohon markisa;","Hidup masyarakat perlulah muafakat,","Supaya negara kekal sentosa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107728,"Terkena hujan bajunya basah,","Airnya mengalir terus ke kakiku;","Jika kamu tak ingin salah,","Maka ikutilah cakap ibuku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107729,"Buah berangan masak merah,","Kelekatu dalam perahu;","Luka ditangan nampak berdarah,","Luka dihati siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107730,"Terang bulan terang di paya,","Anak kambing memakan rumput;","Jika tak kenang kepada saya,","Nama yang baik tidak tersebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107731,"Tarik papan dalam pasar,","Bagi sikit kepada Mak Jah;","Harapan Keluarga amatlah besar,","Untuk membawa segulung ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107732,"Membina rumah berdindingkan papan,","Bising penghuninya pelbagai ragam;","Bukan kecil impian dan harapan,","Segulung ijazah ingin digenggam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107733,"Gembira bersiar ke Putrajaya,","Sayang ditinggalkan rasa hatinya;","Bukan didunia sahaja ingin berjaya,","Di akhirat juga adalah semestinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107734,"Balik kampung jumpa imam,","Ingin bertanya tentang raya;","Pertama kali memjejak UKM,","Terpesona dengan perkampungan budaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107735,"Burung merpati terbang ke hulu,","Terbang-terbang ke Pengkalan Chepa;","Kalau sayang tradisi dahulu,","Sayangkanlah jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107736,"Tidur malam sangat lena,","Lagi-lagi lepas berangan;","Hidup kita biar sempurna,","Supaya dapat tinggalkan kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107737,"Petang hari pergi ke pekan,","Pulang membawa sebungkus laksa;","Hidup muhibah perlu diamalkan,","Di negaraku Malaysia berbilang bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107738,"Pergi Jepun jumpa raksasa,","Makan sushi tak sedar diri;","Pelajar UKM memang luar biasa,","Tapi jangan sampai lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107739,"Pergi Jepun jumpa raksasa,","Makan sushi tak sedar diri;","Pelajar UKM memang luar biasa,","Tapi jangan sampai lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107740,"Jalan-jalan pergi ke kota,","Berhasrat ingin membeli peti;","Bahasa Melayu bahasa kita,","Jangan dilupa sehingga mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107741,"Dayung sampan hingga ke Muara,","Singgah sebentar membeli peti;","Berada di perkampungan kebudayaan amatlah gembira,","Dapat menghilangkan rasa hiba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107742,"Pergi kedai beli soya,","Soya dibeli bersama beri;","Minggu depan sudah raya,","Duit raya boleh diberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107743,"Pergi ke taman bercucuk tanam,","Bercucuk tanam bunga raya;","Terima kasih pihak UKM,","Menerima saya seadanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107744,"Bunga mawar sungguh wangi,","Dua mempelai ingin bertemu;","Daru jauh datang ke Bangi,","Untuk berhajat menuntut ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107745,"Malam hari timbul bulan,","Pergi kedai beli maruku;","Dari Kelantan ku jalan,","UKM di hatiku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107746,"Jelingan manis seri bercelak,","Keris tersisip kemas di sisi;","Masyarakat jaya menjungjung akhlak,","Terjulangkan matlamat mencapai visi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107747,"Bersukan hingga ke negara asia,","Mendapat pinggat kebanggan sultan;","Assalamualaikum Universiti Kebangsaan Malaysia,","Hulurkan tangan tanda ikatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107748,"Buah durian jatuh di tanah,","Isinya tebal sedap dimakan;","Harapan UKM tidak akan punah,","Budaya kita sentiasa diamalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107749,"Makan kerabu bersama budu,","Elok dijamah di hari murung;","Biarlah kita menjadi berudu,","Janganlah katak dibawah tempurung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107750,"Pergi ke kedai membeli dadih,","Membeli dadih bersama nyonya;","Janganlah kita berasa sedih,","Moga hikmah sentiasa disebaliknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107751,"Permainan tradisional dimainkan sudah,","Budak kecil suka bermain guli;","Bangunan UKM tersemai indah,","Menjadi pelajarnya bertuah sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107752,"Dari Kelang lalu ke Jawa,","Naik singa ke atas atap;","Ikan kering lagikan ketawa,","Mendengar tupai membaca kitab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107753,"Pergi ke kedai membeli ikan sembilang,","Membeli ikan pagi-pagi;","UKM adalah universiti yang gemilang,","Ku nantikan sejak dahulu lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107754,"Dua tiga kucing berlari,","Naik turun bersama-sama;","Dua tiga boleh ku cari,","Universiti Kebangsaan Malaysia universiti utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107755,"Pergi ke Bangi di hari sabtu,","Berjumpa dengan rakan yang baru;","Perasaanku teruja nombor satu,","Apabila bertemu rakan-rakan baharu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107756,"Aman dan tenang suasana,","Semoga kita bangun bersama;","Berdirinya abang disana,","Harap kita bergelip seperti Purnama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107757,"Tang Ting Tang Tung,","Akulah inan paling peruntung;","Hajatku ke UKM kini disanjung,","Kerana akulah insan yang dijunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107758,"Berakit-rakit kita ke hulu,","Berenang-renang kita ketepian;","UKM megah seperti dahulu,","Melestarikan impian para graduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107759,"Pergi pasar beli ikan dan udang,","Masak lemak cili padi di idamkan;","Denda dikenakan jika langgar undang-undang,","Kursus yang diingini telah diberikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107760,"Mengalir tenang sungau alur ilmu,","Menberi ketenangan di fikiran;","Tersemat kemas matlamat dihatimu,","Untuk membawa UKM di perantauan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107761,"Cikgu Bedah ajar budak,","Budak suka Cikgu Bedah;","Satu hari budak tersedak,","Kena siku dengan Cikgu Bedah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107762,"Pak Ali membawa kapak,","Untuk dibawa ke kedai Baba;","UKM universiti yang sangat gempak,","Kolej terhebat pendeta Za'ba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107763,"Burung helang, burung kenyalang,","Terbang sekawan tujuh lautan;","Gembira hati bukan kepalang,","Masuk UKM tercapai impian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107764,"Pagi-pagi ayam berkokok,","Berkokok itu berkali-kali;","Masuk UKM ingatkan kekok,","Rupanya UKM hebat sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107765,"Langit biru dilindungi awan,","Langit biru menyambut perayaan;","Program meriah pakaian menawan,","Itulah dia Pusat Kebudayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107766,"Bertemunya kita di bumi Miri,","Sungguh indah menjadi nostalgia;","Ku cari rupa ku bingung sendiri,","Ku cari budi aku bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107767,"Senyum terukir di bibir ibunda,","Melihat anak baik pekerti;","Terasa sungguh riang di dada,","Semangat UKM bertakhta di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107768,"Warna-warni si rama-rama.","Hinggap sebentar diatas batu-bata;","UKM dipohon pilihan pertama,","Teruja rasanya tidak terkata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107769,"Hendak belajar ke Teluk Betong,","Sambil mencuba labuhan pukat;","Masuk UKM rasa beruntung,","Semog perjuangan membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107770,"Sekolah ibarat rumah,","UKM yang saya cintai;","Terlupa resah sudah,","Impian akan saya capai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107771,"Buah langsat di luar pagar,","Dapatkan buah baru berasa;","Jalan ke UKM belajar,","Sentiasa gembira dan ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107772,"Dari Kota Bharu berhijrah ke Bangi,","Demi ilmu menerangi yang gelita;","Harapan bonda ayahanda yang ku sayangi,","Moga bermanfaat buat UKM tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107773,"Pergi ke dusun memetik ceri,","Duduk di pondok mengasah parang;","Jika selalu meninggi diri,","Kelak akan dibenci orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107774,"Beras segantang sudah disukat,","Lalu disimpan diatas para;","Andai pelajar bermuafakat,","UKM menjadi universiti perpaduan dan sejahtera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107775,"Pergi pekan beli nanas,","Nanas diambil dari raga;","Cuaca di UKM amat panas,","Memberi semangat jiwa raga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107776,"Burung merpati burung kenari,","Burung singgah di pokok jati;","UKM uiverisiti pilihan,","Teruja rasanya hadir dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107777,"Pokok rimbun banyak dahan,","Tinggi pokoknya disini;","Mencipta impian mengilhamkan harapan,","itulah visi universiti ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107778,"Universiti Kebangsaan Malaysia aman sentosa,","Kaya dengan budi pekerti;","Niat di hati nak buat jasa,","Dibangga rakyat selama-lamanya nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107779,"Bangun pagi ayam berkokok,","Duduk bersila diatas atap;","Hujan lebat bergegar pokok,","UKM Bangi memang mantap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107780,"Indah rupa gadis bangsawan,","Menjeling manja tujuh putera;","Hati adinda sudah tertawan,","Pada UKM kebanggaan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107781,"Peron ke kebun mengutip pinang,","Pinang diberi kepada Wawa;","UKM hebat ceria dan tenang,","Menjadi impian setiap mahasiswa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107782,"Merdu sungguh buluh perindu,","Pemilik suara si gadis Jawa;","Semangat Keris Mas sangatlah padu,","Bersatu kami sehati sejiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107783,"Dayung sampan sampai ke hulu,","Hati senang jiwa gembira;","Jangan lupa adat yang dulu,","Kerana ia identiti negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107784,"Deruan bayu menyapa deria,","Riuh nyanyian si murai emas;","seruan UKM ku terima ria,","Satu tujuan ku genggam kemas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107785,"Indah tersusun bunyi gamelan,","Handal perawan tiada bersuara;","Langkah ku mula tidak terkilan,","Bakal ku daulat amanah negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107786,"Redup pandangan teruna didepan,","Gagah terbuka sayapnya helang;","UKM terus mengilham harapan,","Mencipta masa depan dengan gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107787,"Pergi ke pantai memancing ikan,","Sambil memancing memakai gelang;","Kami bersungguh mendapatkan dekan,","Siswa cemerlang UKM gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107788,"Kanak-kanak riang ria,","Bermaim di padang sangat cemerlang;","Panggung seni sangat ceria,","UKM sangat gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107789,"Pergi ke pasar mencari kuini,","Terjumpa pula si anak Raihan;","Sembah hamba datang ke sini,","Untuk mendapatkan satu pengesahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107790,"Kami datang dari Miri,","Sambil membawa buah-buahan;","Tujuan kami datang ke mari,","Untuk dapatkan pengesahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107791,"Pergi ke pekan beli cincin suasa,","Nak di pinang anak Cik Siti;","Masuk UKM gembira ku rasa,","Bersama semangat membara di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107792,"Dua tiga kucing berlari,","Mana nak sama si kucing belang;","Pergi kelas hari-hari","InshaAllah di UKM kami cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107793,"Bulan Ramadan bersahur kurma,","Berbuka puasa ikan siakap;","Menjadi siswazah universiti ternama,","Perasaan gembira tidak terungkap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107794,"Berdiri megah si merak kayangan,","Tampak berseri di petang hari;","Datang saya bukan angan-angan,","Ingin memaku ilmu di sanubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107795,"Kerisauan hati anak si dara,","Melihat jendela tekun memerhati;","Nilai ilmu dan budaya terpelihara,","Tatkala UKM tersemat dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107796,"Beli timun di kedai Siti,","Kedai Siti sangat laku;","Hati gembira senang hati,","Pihak UKM memanggil aku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107797,"Setiap hari makan roti,","Roti diletak ditepi lilin;","Undang-undang negara perlu dipatuhi,","Supaya hidup lebih berdisiplin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107798,"Anak kambing merah,","Tambat di pokok asam;","Ibu adik marah,","Adik buat muka masam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107799,"Menaiki kapal ke barat daya,","Nakhoda kapal menempuh halangan;","Di sebuah kampung ada pelbagai budaya,","Disitu terletaknya kampung merak kayangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107800,"Bunga mawar harum mewangi,","Di petik elok oleh Cik Salmah;","Dari Port Dickson daerah Bangi,","Harapan ayahanda segulung ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107801,"Duduk di luar menghirup udara nyaman,","Melihat pemandangan yang sangat indah;","UKM mengilham harapan,","Demi masa depan yang cerah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107802,"Indah mewangi memekar cinta,","Cinta buat jejaka pujangga;","Hati gembira tidak terkata,","Semoga amanah ku pikul berbangga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107803,"Enak sungguh buah kuini,","Masak meranum enak dimakan;","Gah berdiri UKM di sini,","Moga diriku mampu pertahankan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107804,"UKM Bangi dihuni kera,","Kelihatan kera disana-sini;","Hati ini sangat gembira,","Dapat menimba ilmu disini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107805,"Pisang emas dibawa berlayar,","Masak sebiji diatas peti;","Masuk ke sini untuk belajar,","UKM sentiasa dekat dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107806,"Tempatnya nampak kehijauan-hujauan,","Tenangnya mata melihat sentiasanya;","UKM adanya segala keperluan,","Tidak susahnya tinggal selamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107807,"Menyinari perkampungan budaya UKM,","Sinaran suria yang terang;","Alunan muzik bergema di UKM,","Menjadi perhatian orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107808,"Jalan ke pekan ternampak mamu,","Bajunya elok tidak tertahan;","Berlumba-lumba mencari ilmu,","UKM yang megah menjadi pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107809,"Langit senja terlihat sendu,","Keluarlah sudah si kunang-kunang;","Jarak jauh tumbuhkan rindu,","Hatiku kini makin terkenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107810,"Pergi ke rumah Nek Maya,","Di rumah nenek ada ayam laga;","Senang hati sudah berjaya,","Akhirnya dapat universiti juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107811,"Ayam itik rusa ikan,","Sambil makan buah mangga;","Belajar selalu jangan lupakan,","Agar menjadi UKM bangga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107812,"Di hutan banyak kera,","Ia pula dipantau dan dicegah;","Perasaan saya sungguh gembira,","Dapat belajar di UKM yang megah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107813,"Samudera terbentang semangat nakhoda,","Tidak gentar maut yang bakal tiba;","banyak-banyak citra yang ada,","Citra kebudayaan pilihan hamba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107814,"Makan nasi goreng yang sedap,","Sedap sangat menjilat jari;","UKM di depan mata ku hadap,","Dengan impian meningkatkan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107815,"Universiti Kebangsaan Malaysia pilihan utama,","Menjadi rebutan si masa depan cerah;","Tidak tergambar perasaan diterima,","Itulah kami bakal produk payung merah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107816,"Pergi ke kedai membeli gambar,","Singgah di tepi makan laksa;","Ku fikir UKM hambar,","Rupanya UKM luar biasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107817,"Wahai Universiti Kebangsaan Malaysia ku,","Cantik-cantik pemandangannya;","Hidupan UKM dihatiku,","Pasti memori menjadi ingatannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107818,"Tinggi-tinggi si matahari,","Burung terbang laju di awan;","Sungguh gembira setiap hari,","Kerna UKM banyak kawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107819,"Mendayung sampan ke Pulau Daik,","Ingin membeli gelang suasa;","Benarlah UKM yang terbaik,","Menjadi idaman setiap bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107820,"Hinggap didahan si burung merpati,","Hendak mencari air dikota;","Gembira dihati tidak terhenti,","Mendapat rezeki di varsiti tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107821,"Terang bulan terang di paya,","Anak kambing memakan rumput;","Buat orang yang tidak terdaya,","UKM pasti tidak dapat dituntut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107822,"Pergi ke kedai membeli santan,","Santan dibeli bersama roti;","Ke timur, barat, utara atau selatan,","UKM akan sentiasa dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107823,"Pagi-pagi makan roti,","Roti dimakan bersama bahulu;","Empat tahun cabaran menanti,","Hidup di UKM ceria selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107824,"Hidangan enak sudah dijamu,","Di makan bersama ulam kasih cinta;","UKM tempat kami menuntut ilmu,","Agar hasrat dihati dapat dicipta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107825,"Jalan-jalan pergi ke kedai,","Singgah untuk membeli kopi;","Kalau siapa bijak pandai,","Sudahlah jangan main dengan api.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107826,"Indah sungguh suasana di UKM,","Ingin pula mencari teman;","Seronoknya jadi warga UKM,","Tersergam indah bak bunga di Taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107827,"Pergi berjalan bersama saudara,","Terjumpa gadis berkaca mata;","Hati berasa sungguh gembira,","Diterima masuk ke UKM tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107828,"Pergi ke UKM naik kereta kenari,","Mencari ilham di pokok meranti;","Orientasi berhari-hari,","Membawa kejayaan sampai ke mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107829,"Kalau tuan pergi ke pekan,","Jangan lupa membeli roti;","Salah silap harap dimaafkan,","Semoga hidup terus diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107830,"Amanah kebangsaan varsiti kita,","Anak bangsa kritis dan kreatif;","Menjadi harapan perintis pendeta,","Mengamalkan budaya kelestarian objektif.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107831,"Merantau sudah kesana sini,","Berharap keselamatan sentiasa baik;","Senangnya hati dipilih kesini,","UKM pastinya universiti terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107832,"Gadis cina berkebaya nyonya,","Duduk bersimpuh ditepi nisan;","Universiti Kebangsaan Malaysia gah namanya,","Menjadi idaman setiap insan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107833,"Indera Sakti laksamana Betara,","Hendak meminang si anak Jebat;","Rasa dihati sungguh gembira,","Mendapat tawaran di varsiti terhebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107834,"Jauh dari rumah beratus-ratus perjalanan,","Bertemu kawan dari timur barat;","Belajar bersungguh tiada kesukaran,","Banyak guna otak tiada karat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107835,"Naik ke bukit menaiki kereta,","Jumpa saudara bertegur sapa;","Hati gembira tidak berkata,","Mampu membanggakan ibubapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107836,"Sultan bertakhta di balairung seri,","Agama rasmi Malaysia agama Islam;","Datang ke UKM muka berseri-seri,","Syukur tuhan berada jua di Pengajian Islam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107837,"Dibawah pokok mangga,","Main kompang bertalu-talu;","Pelajar UKM duduk berbangga,","Realiti hidup dialu-alu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107838,"Diumpamakan bodih ialah keldai,","Sentiasa tidur di waktu pagi;","Dimanakah mahasiswa bijak pandai,","Kalau bukan di UKM dimana lagi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107839,"Ilmu di dunia seluas laut,","Ilmu di dada hanya setitik;","Namun tak dilupa sehingga maut,","Semoga amal setip detik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107840,"Masuk UKM bersama Mamat,","Menunggu tiba harinya nanti;","Kepada tuhan bersyukur nikmat,","Kurasa riang sungguh di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107841,"UKM Bangi tersergam indah,","Membuat hati terasa bangga;","Waktu belajar kini tiba sudah,","Pastikan hidup menjadi berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107842,"Jangan sering berduka lara,","Langit indah di bandar kota;","UKM pendaulat amanah negara,","Dan inilah varsiti kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107843,"Dari Perlis sampailah ke Sabah,","Jlan jauh hingga hujan lebat;","Saya ini orang Sabah bah,","Gembira teruja masuk universiti hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107844,"Pergi ke kedai membeli jamu,","Berjalan bergerak dibawah pelangi;","Niat hati menuntut ilmu,","Dari Kuantan turun ke Bangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107845,"Merdu suara alunan puisi,","Terbang bebas burung pedada;","Pekan kebudayaan warung tradisi,","Nostalgia diangkat diagung persada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107846,"Masakan ibu memang teliti,","Membuat kari bukannya senang;","Semua manusia haruslah berbakti,","Orang yang baik haruslah dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107847,"Dua tiga kucing berlari,","mana nak sama si comel Ara;","Dibumi UKM ilmu ku cari,","Demi nusa, bangsa dan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107848,"Pergi ke gua mencari bakti,","Mulut pahlawan tak terkata;","Tanpa UKM resah dihati,","Sungguh indah rasa cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107849,"Pergi hospital membeli ubat,","tetap sakit walaupun badan sasa;","Menyambung belajar di tempat hebat,","Inilah dia UKM yang luar biasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107850,"Pagi-pagi makan mi,","Pergi tasik untuk mengail;","Siapalah diri kami,","Kamilah dari Kolej Tun Dr. Ismail.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107851,"Dua tiga kucing berlari,","Mana nak sama si kucing belang;","Dua tiga menunggu hari,","Memasuki UKM untuk dijulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107852,"Pergi kedai membeli laici,","kulit laici berwarna merah;","Kalau anda tertinggal kunci,","Mesti hati berasa marah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107853,"Merak kyangan tinggalan siswazah,","Tempat monyet tumpang berhenti;","Pulanglah dikau dengan ijazah,","Ayah dan ibu tak sabar menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107854,"Gendang dipalu di pohon cemara,","Pohon cemara tempat si manis melati;","Hajat tercapai ingin mengembara,","Menjadi warga UKM dihati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107855,"Hujan hari ini sangat lebat,","Kelihatan nelayan dilaut nan berani;","UKM sebuah universiti yang hebat,","Selamanya tersemai dihati ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107856,"Berlari si arjuna bersama anak gajah,","Jatuh tersungkur di tepian bendang;","Riak gembira tampil di wajah,","Siswa siswi UKM gelaran disandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107857,"Atas, bawah, kanan dan kiri,","Ke dalam, keluar, ke sini dan kesana;","Masuk ke UKM banyak bermanfaatkan diri,","Tidak kiralah kos yang mana-mana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107858,"Api membara ditanah Jawa,","Bukannya dendam bukannya dengki;","Rasa gembira dalam jiwa,","Apabila UKM ku jejaki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107859,"Rumah tradisional berbumbung atap,","Atapnya kukuh kepunyaan petani;","UKM hebat UKM mantap,","Kami bangga berada disini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107860,"Dari pagi belajar menari,","Menunggu masa untuk bercuti;","Kolej B, Kolej Bestari,","Riang dihati suka hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107861,"Anak cina di tepi titi,","Bermain congkak disiang hari;","Jangan kita bersusah hati,","Pasti kejayaan akan dimiliki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107862,"Malaysia berada di Asia Tenggara,","Haiwan kebanggaan harimau belang;","Terima kasih pahlawan negara,","Malaysia merdeka terus gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107863,"Harum sungguh bunga melati,","Hiasan sanggul amat memikat;","Bertolak ansur, hormat menghormati,","Mengukuhkan perpaduan ikatan muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107864,"Berterbangan di laut si burung camar,","Penat berjalan duduk di bangku;","Niat abang hendak melamar,","Melamar awak si pencuri hatiku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107865,"Kucing berlari membawa benang,","Benang dicuri dari Iran;","Memori UKM pasti dikenang,","Walaupun hidup sudah berkurun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107866,"Uda dara bermadah pujangga,","Tanda ikatan teman dan kawan;","Disiplin diri kekal terjaga,","Andai nafsu selalu dilawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107867,"Pergi ke kedai membeli ikan,","Singgah sebentar bertemu Pak Ali;","Amalan mulia mestilah diamalkan,","Supaya hidup aman harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107868,"Duduk sopan bersimpuh si dara,","Mendengar bonda menyampai pesan;","UKM maju terus mara,","Demi menggilap permata warisan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107869,"Jalan-jalan di Batu Pahat,","Singgah sebentar di Bandar Senggarang;","Jangan pernah berperangai jahat,","Kemudian hari dibenci orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107870,"Dua tiga kucing berlari,","Mana nak sama si kucing belang;","Anak-anak bila nak mari,","Sudah lama kalian tak pulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107871,"Pergi mendaki di Gunung Daik,","Penat lelah sehingga terpengsan;","Domain kebudayaan memang terbaik,","Mengimbau kembali keindahan warisan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107872,"Indah sungguh suasana barat,","Menghirup segar udara nyaman;","Harapan orang tua ku genggm erat,","Segulung ijazah menjadi idaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107873,"Negeri Sarawak asalnya dinda,","Bertandang ke UKM mengejar cita-cita;","Walau keluarga jauh di sana,","Daku di sini takkan berputus asa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107874,"Ku lihat langit di tengahari,","Panas hari baju ku sidai;","Takkan ku mundur walau sekali,","Andai harapan ibu ayah belum tercapai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107875,"Kemalangan berlaku di jalan raya,","Pandulah kereta berhati-hati;","Tersegam indah bangunan di bandar raya,","Universiti Malaysia Kelantan tetap dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107876,"Burung merpati terbang jauh disana,","Indah dilihat dari sini;","UKM ibarat sebuah singgahsana,","Dipenuhi insan berseni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107877,"Bunga orkid berwarna biru,","Sungguh cantik di taman jaya;","Niat dihati mencari teman baru,","Untuk menjadi teman berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107878,"Bintang terang ditemani bulan,","Menaikkan seri dimalam hari;","Mendaftar rasa untuk membawa diri,","Gementar rasa untuk membawa diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107879,"Emak berpesan kepada Lili,","Agar pulut itu ditampi;","UKM universiti terhebat sekali,","Semoga tercapai hajat diimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107880,"Panjat pokok dengan tangga,","Lebah datang terus menyengat;","Perasaan bangga yang tidak terhingga,","Berjaya memasuki UKM dengan penuh semangat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107881,"Dari Perlis sampai ke Sabah,","Mengilhamkan harapan mencipta masa depan;","Semangat jitu yang tidak pernah berubah,","UKM kebanggaanku mara kehadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107882,"Ribuan pokok banyak bunganya,","Semuanya mekar gembiranya aku;","Berdebar hati sering tertanya,","Adakah UKM pilihan hatiku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107883,"Kasih ibu terlihat dimata,","Ketika aku dinobatkan juara;","Tertanya tuhan menyayangi kita,","Hatiku disini sentiasa gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107884,"Gadis jelita gadis UKM,","Sungguh senang melihatkamu;","Guru berjasa dialah UKM,","Tunggu masa ku balas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107885,"Pandai berceloteh dalam berdebat,","Orangnya cantik sungguh bergaya;","UKM universiti terhebat,","Melahirkan ramai graduan berhaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107886,"Keluar ke pekan hendak berniaga,","Mencari rezeki harapnya diberkati;","Walau jauh dimata keluarga,","UKM tetap dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107887,"Menuntut ilmu jangan berahsia,","Mengajar rakan jangan berkira;","Banggakanlah Universiti Kebangsaan Malaysia,","Agar hidup aman gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107888,"Menuntut ilmu di negeri orang,","UKM pilihan di dihati;","Anak Mak Cik daftar seorang,","Sudahku jumpa pilihan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107889,"Pergi ke kebun pokok ciku,","Pergi bersam Zain dan Che Ta;","Masuk UKM adalah impianku,","Menjadi keluarga UKM tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107890,"Masuk ke hutan terjumpa merak,","Merak sedang pulang kota;","UKM bersinar bagaikan perak,","Sehingga memukau dek mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107891,"Hari malam gelap gelita,","Pasang lilin jalan ke taman;","Bahagia di hati tidak terkata,","UKM terbaik sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107892,"Tersenyum indah Kota Laksamana,","Menjadi tempat tarikan Camai;","Sungguh bangga belajar di sana,","UKM universiti yang permai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107893,"Sejak subuh harum mewangi,","Gemuruh jiwa si darah muda;","Dari Seremban ke UKM bangi,","Untuk menimba ilmu di dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107894,"Berjalan-jalan di Andalusia,","Berjalan bersama Encik Hasan;","UKM memang tersohor di Malaysia,","Menjadi impian setiap insan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107895,"Angin melambai si payung merah,","Membawa utusan dari kehormat;","Jika berlari biarlah searah,","Agar di hati UKM tersemat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107896,"Puteri berlima turun ke bumi,","Terlihat si tampan menjolok kelapa;","Indah saujana varsiti kami,","Banyak pengalaman takkan ku lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107897,"Apakah faedah bijak bestari,","Jika tidak baik budinya;","Apakah rahsia UKM berseri,","Jikalau tidak disiplin pelajarannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107898,"Makan coklat sebesar bata,","Jual sebongkah pasti kaya;","UKM hasrat rakyat kita,","Membina negara ke arah jaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107899,"Tak kisah jauh atau dekat,","Warga UKM semua kawan;","Siswa siswi semua berbakat,","UKM universiti yang paling menawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107900,"Datang ke mari membawa berita,","Berita dibawa dari perantauan;","Saya ke sini mengejar cita-cita,","Untuk menjadi seorang usahawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107901,"Ligat berousing sepasang roda,","Habat lagi teruna perkasa;","Besar hati membawa cita bonda,","Menggulung ijazah gembira terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107902,"Bunga peria di atas sampan,","Dahan gedangsa bersilang-silang;","Di tangan belia terletak harapan,","Membawa nusa ke mercu gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107903,"Cahaya suria menyinar biara,","Tolong teguh pasak terhebat;","Generasi belia harapan negara,","Imannya kukuh mindanya hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107904,"Tepian biara pohon pulasan,","Kelopak bahlia diatas papan;","Aspirasi negara mencapai wawasan,","Di UKM belia menaruh harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107905,"Cantik sunggu kerongsang didada,","Dipakai indah di siang hari;","Walau jauh kami berada,","Bakti UKM tetap di memori.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107906,"Rindu dihati datang bertamu,","Membawa jiwa sukma yang punah;","Bangkit berdiri maju berilmu,","UKM terbilang pendaulat amanah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107907,"Makan roti duduk di kedai,","Melihat gasing berputar-putar;","Belajar banyak baru pandai,","Rajin belajar otak pintar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107908,"Menuntut ilmu biarlah sehingga ke negara Cina,","Tangan digenggam demi sebuah misi;","UKM menyediakan persekitaran kondusif dan prasarana,","Untuk melahirkan warga berinspirasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107909,"Jalan-jalan di negeri Sarawak,","Singgah sebentar di kubu rentap;","Pensyarah UKM pandai berlawak,","Siswa siswi terdidik mantap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107910,"Tarik papan sorong papan,","Buah keranji dalam perahu;","48 tahun UKM ditubuhkan,","Menjadi tempat sumber ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107911,"Sepinggan nasi ditambah kuab,","Sambil sepiring makan sekali;","Seronoknya hati terasa indah,","Syukur saya berada di sini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107912,"Berkaca mata manis jelita,","Memakai kasut berwarna biru;","UKM menerangi seperti pelita,","Berakhlak mulia diajar guru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107913,"Medan ini penuh dengan kehidupan,","Semangat patriotik itu yang diutamakan;","Disinilsh tempat membina masa hadapan,","Kurnia ini janganlah disiakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107914,"Berehat sebentar makan kubis,","Selepas itu menggosok gigi;","Minggu 3M dan nak habis,","Semoga kita berjumpa lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107915,"Merantau sendiri diri ini,","Di negeri seberang sana;","Menuntut ilmu di UKM ini,","Diharapkan menjadi bijaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107916,"Mencari sesuatu diimpi,","Melangkah kaki ini;","Menuju pusat ilmu UKM diimpi,","Mencapai kejayaan di sini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107917,"Kembang mewangi bunga melati,","Tumbuh segar di tengah kota;","Adat dan warisan Melayu diingati,","Itulah tinggalan nenek moyang kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107918,"Terang mentari menyentuh muka,","Bermula hari di taman senyuman;","Berjaya tidak hadir dengn leka,","Biar berjaya bersama teman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107919,"Hari Selasa domain kebudayaan,","Segala aktivit dilakukan gembira;","UKM harapan pulang kejayaan,","Dengan bismillah pembuka kembara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107920,"Pergi ke laut menaiki sampan,","Membawa roti berperisa kaya;","Masuk UKM dengan harapan.","Semoga pendidik yang berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107921,"Datang ke jeti tarik papan,","Kalau tak tarik Along tak percaya;","Datang UKM dengan harapan,","Bawa segulung ijazah petanda berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107922,"Memakai busana yang indah,","Pergi ke rumah membawa raga;","Menuntut ilmu bukanlah susah,","Jika rajin tanam di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107923,"Buah cenduku buah cendana,","Termakan pula buah ciku;","Citra kebudayaan tempat bergurau senda,","Mungkin menjadi citra pilihanku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107924,"Ranting kayu jatuh merata,","Buah peria pahit terasa;","Bahasa Melayu bahasa kita,","Gunakan ia sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107925,"Membawa pasu sambil menyusung,","Menyusung bersama kakak Fazura;","Majlis kini telah berlangsung,","Assalamualaikum pembuka bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107926,"Pak Abu menanam labu,","Mak Minah menanam kelapa;","Saya sayang akan ibu,","Saya sayang akan bapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107927,"Bagai melutut di tepi gantang,","Mengharap kasih dari tuan;","Dari Sabah aku datang,","Untuk menimba ilmu pengetahuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107928,"Letih berjalan di tengah pekan,","Singgah membeli coklat M&amp;M;","Terima kasih saya ucapkan,","Kerana diterima oleh UKM.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107929,"Buah mengkudu ku sangkakan kandis,","Kandis terletak di dalam puan;","Gula madu ku sangka manis,","Manis lagi senyuman tuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107930,"Asap api di tanjung tuan,","Kuala Linggi bakaunya rendah;","Harap hati kepada tuan,","Langit tinggi ku pandang rendah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107931,"Pakai baju baru,","Pergi sekolah gemilang;","Murid harus hormati guru,","Kerana mereka penyampai ilmu nan cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107932,"Anak dara mandi manda,","Sambil membasuh kain basahan;","Jika hidup suka bercanda,","Takut nanti jadi tomahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107933,"Kalau ikan di dalam kolam,","Bunga tumbuh di tengah taman;","Kalau cinta sudah dalam,","Jarak jauh terasa nyaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107934,"Belajar lukis dengan pensil,","Mengapa kertas tidak rata;","Semoga engkau berhasil,","Meraih segala cita-cita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107935,"Pergi berjalan ke Taman Ria,","Pergi bersama ibu dan ayah;","Kalau Cik Adik orangnya ceria,","Sentiasa senyum tidaklah payah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107936,"Datang ke UKM dari desa,","Demi mencapai cita-cita;","Untuk menjadi harapan bangsa,","Juga kerana ibu bapa tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107937,"Dalam rokok mengandungi tar,","Jika dibakar menghasilkan jelaga;","Sungguh indah alam sekitar,","Lagi indah sekiranya dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107938,"Rindu rumah tak sudah-sudah,","Pasti terkenang saat bicara;","Rindu pada emak dan ayah,","Rindulan juga sanak saudara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107939,"Sirap bandung sirap selasih,","Dimakan bersama mee tarik;","Hati kita perlulah bersih,","akhirat nanti neraka tak tarik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107940,"Burung terkukur berbunyi balam,","Riuh berkicau di pagi hari;","Ramah menegur hulurkan salam,","Tiada musuh sahabat mencari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107941,"Sibuk di dapur memasak itik,","Untuk hidangan para tetamu;","Saya datang untuk belajar linguistik,","Agar dapat menambah ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107942,"Pergi ke kota menaiki sampan,","Berbaju cantik segak bergaya;","Datang UKM dengan harapan,","Supaya hidup akan berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107943,"Jadilah seorang yang berwibawa,","Agar diri menjadi disiplin setiap saat;","Ilmu itu ibarat nyawa,","Kerana tanpa ia kita jadi sesat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107944,"Jalan-jalan di Dataran Lang,","Lalu di depan rumah cik siti;","Hajatku ini tinggi menjulang,","Ingin menjadi insan berbakti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107945,"Budak-budak berkejar-kejar.","Berasa riang dan gembira setiap hari;","Hendaklah rajin belajar,","Percaya kejayaan akan dicapai suatu hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107946,"Pergi memancing menaiki sampan,","Mendayung sampah bersama Faiza;","Aku keluar dengan satu harapan,","Dapat membawa segulung ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107947,"Buah cempedak di luar pagar,","Ambil galah tolong jolokkan;","Saya budak baru belajar,","Kalau salah tolong tunjukkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107948,"Ikan kelisa di Kuala Rakan,","Menjadi santapan Raja Muda;","Rumah selesa hamba tinggalkan,","Demi ijazah sarjana muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107949,"Tanam pokok rapat-rapat,","Banyak sungguh dalam negeri;","Masuk universiti sudah dapat,","Tinggallah aku hidup berdikari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107950,"Pergi kedai membeli balang,","Tiba-tiba hujan lebat;","Saya pasti berjaya dengan cemerlang,","Di UKM yang hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107951,"Pergi kedai membeli balang,","Tiba-tiba hujan lebat;","Saya pasti berjaya dengan cemerlang,","Di UKM yang hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107952,"Jauh terkenang kampung halaman,","Mencari ilmu pahatkan dihati;","Capaian untuk hajat idaman,","Kepada keluarga yang menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107953,"Daun pegaga daun kasturi,","Di petik dara untuk ibunda;","Tidak terhingga ilmu diberi,","Hargailah guru selagi ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107954,"Buah peria masak setangkai,","Buah cermai lebat dipangkal;","Beza manusia dengan bangkai,","Hidup beriman bersendi akal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107955,"Burung nuri tidak digerun,","Makan bersama Cik Jurita;","Budi pekerti jangan menurun,","Biar menjadi amalan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107956,"Layang-layang terputus tali,","Ditiup angin jatuh ke bumi;","Masa muda cuma sekali,","UKM menjadi pilihan kami.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107957,"Baju mahal jenama Padini,","Jenama tempatan memang laku;","Sayangi Malaysiaku tema kali ini,","Semoga berjaya tanah airku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107958,"Masuk UKM hati waja,","Belajar tujuan utama;","Buat pantun senang saja,","Susun hujung bagi sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107959,"Membawa bekal segelas susu,","Makan kari bersama disaji;","Bijak berfikir mengamal nafsu,","Mengapa diri sifat dipuji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107960,"Cikgu sibuk menurus hal,","Loceng berbunyi tidur awal;","Bangun pagi tidur awal,","Sentiasa bersenam badan sihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107961,"Pulang ke kampung mencari perigi,","Terletak jauh di pinggir hutan;","Walau ke mana aya pergi,","UKM tetap menjadi ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107962,"Bertudung merah si manis cantik,","Mengambil air di tepi perigi;","UKM mengekalkan warisan antik,","Ilmu dan akhlak menjulang tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107963,"Beli keladi di Pekan Lidup,","Sayang rasa lemaknya kurang;","Taburlah budi ketika hidup,","Kelak jasa dikenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107964,"Kemilau bersepuh intan permata,","Ditambah serigala berdiduri;","Universiti UKM tercinta,","Membuat hatiku bertambah seri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107965,"Cantik mani anak Pak Meon,","Duduk bersimpuh di pohon bakau;","Kepada penghuni Kolej Dato' Onn,","Bersukalah engkau berbanggalah engkau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107966,"Tumbuh menjalar pohon palma,","Singgah sebentar si merpati;","Bergelar siswa tujuan utama,","Dijemput UKM riang hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107967,"Gadis Melayu cantik kulitnya,","Berjalan-jalan di tepi muara;","UKM tercinta indah sekitarnya,","Terletak salah satu hutan simpan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107968,"Cantik sari indah rupanya,","Dipakai kemas si putri negeri;","UKM dihati tersemat selamanya,","Dari kecil itulah impian diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107969,"Kelapa besar pada dahan,","Akhirnya jatuh ke lantai;","Orang sombong jangan tahan,","Kerana tuhan sedang mengintai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107970,"Bangun pagi makan sarapan,","Sarapan dimasak oleh Cik Pina;","Datang UKM membaw harapan,","Agar menjadi orang berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107971,"Kamu merupakan matahari,","Saya merupakan bulanan;","Saya berharap bersamamu ditiap hari,","Berharap saya akan dapat hatimu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107972,"Tangkap ikan gunakan tuba,","Turunnya nelayan di tepi jeti;","Panggilan di tunggu sudah tiba,","Akhirnya jejak UKM dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107973,"Burung helang dua mata,","Duduk sebenar di atas balkoni;","Hati gembira tak terkata,","Sudi menerima saya di sini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107974,"Cantik si gadis berkebaya merah,","Pergi ke pekan beli mempelam;","UKM pasti menentukan arah,","Masa depanmu tak akan kelam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107975,"Ilmukah sireh berpinang tidak,","Kondisi mangsa telah dipulih;","Makan sireh mengenyang tidak,","Tak ku sangka diriku terpilih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107976,"Angin segar datang di sini,","Semua pelajar telah datang;","Suara gembira wujud di sini,","Tidak hilang hingga petang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107977,"Anak Pak Ahmad pergi ke hutan,","Pergi ke hutan memburu rusa;","UKM merupakan institusi pendidikan rebutan,","Kerana berjaya melahirkan graduan harapan bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107978,"Langit biru awan cerah,","Terik mentari panas membara;","UKM ada payung merah,","Buat hati senang gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107979,"Universiti Kebangsaan Malaysia tercinta,","Tempat pertemuan semua bangsa;","Mencari teman seakan permata,","Mengeratkan silaturahim sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107980,"Halia ini tanam-tanaman,","Dibarat saja akan tumbuhnya;","Dunia ini pinjaman-pinjaman,","Akhirat juga akan sesungguhnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107981,"Lari manusia kerana sang sawa,","Datang segera bomba Bangi;","Pelusuk Malaysia hijrahlah siswa,","Varsiti UKM sama junjungi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107982,"Wahai si bijak kekanda adinda,","Dengarlah nasihat pahatlah di ingitanmu;","Nama UKM menjadi lagenda,","Bangga kami menjunjung namamu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107983,"Terbentang luas si laut biru,","Seronok bermain si anak ikan;","UKM lama tak nampak baru,","Harapan ku tetap mendapat dekan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107984,"Masuk menara gading tuntut ilmu,","Menuntut ilmu bersama cikgu bedah;","Janganlah bergaduh sesama kamu,","Kerana berkawan adalah tak sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107985,"Pergi ke hutan diserang helang,","Terjumpa pula seorang nyonya;","UKM terbilang juga gemilang,","Melahirkan siswa holistik semuanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107986,"Pergi ke bandar naik kereta,","Singgah sebentar membeli ikan;","Hati gembira sukar dikata,","Teria kasih saya ucapkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107987,"Burung murai burung terkukur,","Pucuk ubi pucuk paku;","Perasaan di hati sangat syukur,","UKM kini menjadi gedungku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107988,"Taman bunga berwarna-warni,","Tempat bermain kumbang dan lebah;","Harumkan namamu di UKM ini,","Jangan hampakan mama dan abah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107989,"Pergi ke pasar membeli gula,","Membeli gula esok dan lusa;","Saya masuk UKM dengan sukarela,","Moga dalam Rahmat Allah sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107990,"Tuan puteri pergi ke Tasah,","Pulangnya pula di petang hari;","Disinilah ilham dan bakatku diasah,","UKM ku kini sentiasa dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107991,"Indahnya santai di Gunung Kinabalu,","Duduk termenung jauh pemikiran;","Jikalau sudi ingin beetanya dahulu,","Adakah awak tinggal berhampiran?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107992,"Pergi ke dusun memetik buah,","Buah dipetik enak rasanya;","Jikalau kita mahukan tuah,","Eloklah kita berusaha sepenuhnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107993,"Pak Hussien memburu rusa,","Menembak harimau hingga mati;","Pelajar UKM berbilang bangsa,","Universiti Kebangsaan Malaysia sentiasa di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107994,"Gadis Jawa melambai malu,","Bawa celepa dihias suasa;","Harta manusia dikejar selalu,","Hingga lupa yuhan yang esa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107995,"Buah cempedak bentuknya bujur,","Sangat ranum manis isinya;","Kita hendaklah bersikap jujur,","Agar hidup sejahtera sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107996,"Satu dua tiga,","Abu pergi beli papan;","Bilangan kampus UKM tiga,","Mengilham harapan mencipta masa depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107997,"Berjalan-jalan di Batu Pahat,","Liha burung-burung merpati;","Indahnya UKM daku lihat,","Sememangnya UKM si hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107998,"Panjat bangunan merasa gayat,","Seronak memanjat kasut hilang;","Dibangun oleh ihsan rakyat,","Itulah UKM hebat lagi gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (107999,"Pergi ke kelas jalan kaki,","Setiap langkah itu diukur;","Kolej tercinta kolej Aminuddin Baki,","Masuk belajar ke UKM aku syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108000,"Budak-budak kejar-mengejar,","Layang-layang putus tali;","Para siswa kenalah belajar,","Masa muda cuma sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108001,"Baju kurung baju kebangsaan,","Dipakai semasa hari raya;","UKM ku universiti kebangsaan,","Anugerah dekan impian saya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108002,"Banyak gadis berlari-lari,","Yang cantik hanya di Melaka;","Dua tiga dapat ku cari,","Pelajar UKM manis-manis belaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108003,"Membawa bekal segelas susu,","Makan kari bersama disaji;","Bijak berakal mengawal nafsu,","Menjaga diri sifat terpuji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108004,"Buah rambutan di luar pagar,","Ambil galah tolong jolokan;","Den budak baru belajar,","Kalau salah tolong biarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108005,"Buah durian jatuh di tanah,","Isinya tebal sedap dimakan;","Harapan UKM tidak akan punah,","Budaya kita sentiasa diamalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108006,"Naik kapal pergi mencari teknik,","Teknik susah untuk dibelajar;","Saya budak elektrik dan elektronik,","Mohon tunjuk ajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108007,"Air panas dalam gelas,","Makan roti bersama keju;","Niat belajar biar ikhlas,","UKM Bangi tetap di tuju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108008,"Tanam pinang rapat-rapat,","Biar senang puyuh berlari;","Ku pinang-pinang tapi tak dapat,","Ku pujuk-pujuk ku bawa lari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108009,"Wajah kacak tinggi lampai,","Menjadi tarikan setiap wanita;","Banyak bidang perlu dicapai,","Untuk mendapatkan sebuah cita-cita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108010,"Pergi ke kedai membeli permata,","Permata dibeli dijaga berhati;","Keindahan alam didepan mata,","UKM sentiasa di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108011,"Haus di tengahari inginkan nira,","Nira yang muda terasa manisnya;","Datang ke UKM dengan gembira,","Syukur ke Ilahi setinggi-tingginya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108012,"Pagi ini menuai padi,","Petang nanti meneruskan usaha;","Hari ini kita kekal abadi,","Esok kita terus berusaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108013,"Ramai orang membeli jamu,","Di bawah pokok cuaca redup;","Bersungguh-sungguh mencari ilmu,","Ilmu dicari penyuluh hidup.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108014,"Masuk ke UKM pilihan pertama,","Melangkah bangga memasuki UKM ini;","Orang lain ada misi yang sama,","Bersama menempuh nasib begini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108015,"Bunga mawar merah warnanya,","Kelopaknya merah seperti hati;","Payung merah itulah gelarannya,","Universiti Kebangsaan Malaysia tetap dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108016,"Belia muda serata negeri,","Jelajah jauh menimba ilmu;","Seberang laut Sabah diberi,","Jatuh bangun demi kamu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108017,"Pulau labuan diberi nama lelayang,","Anak Sulung memcuba nasibnya;","Harapan tinggi oleh orang tersayang,","Ilmu dicari di UKM lah tempatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108018,"Anak Cik Ita tercekik ikan,","Ikan dibeli di pasar Pak Man;","Budaya kita jangan dilupakan,","Agar tidak ditelan zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108019,"Bunga telur cantik sekali,","Pengantin baharu kita raikan;","Bahasa dan budaya indah sekali,","Daulatkan selalu jangan dilupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108020,"Limau kasturi berbau wangi,","Pergi ke pekan berjumpa tamu;","Dari Gemas hingga Bangi,","Pergi jelajah menuntut ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108021,"Berjalan-jalan di pekan rantau,","Ditegur pula oleh Kak Zah;","Kami anak muda jauh merantau,","Ingin menggenggam segulung ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108022,"Khadijah dilahir secantik bidadari,","Datang Mak Lijah berkata dusta;","Lebih baik berdiam diri,","Daripada berkaat nista.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108023,"Asalnya aku dari Subang,","Jalan ke sini terlalu berliku;","Idolaku adalah kakak abang,","Menjadi dekan itu tujuan aku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108024,"Negara makmur indah nian,","Pilihan raya selesai tanggungjawab;","Malaysia memilih jadi tumpuan,","Hasrat rakyat terungkai terjawab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108025,"Pagi petang main dengan Hajah,","Main sehingga ke hujung titi;","Walaupun jauh saya menjelajah,","UKM universiti tetap dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108026,"Bunga putih di pohon melati,","Mulanya dipetik hingga ke Tok Kadi;","Akanku semadikan UKM dihati,","Melakar memori untuk abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108027,"Saya orang kampung Jeram,","Hidup disana berkasih sayang;","UKM tempat yang tenteram,","Makin tambah cinta dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108028,"Pergi ke gua untuk bertapa,","Menghirup udara segar dan nyaman;","Adat Melayu jangan dilupa,","Haruslah dijaga sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108029,"Anak kuda pakai sari,","Sari dari kulit memerang;","Kalau mahu bijak bestari,","Masuklah ke UKM sekarang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108030,"Main-main ke Madina,","Makan kurma ketemu raja;","Kalau pengen jadi sarjana,","Jangan cuma mimpi saja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108031,"Dayung perahu dayung sampan,","Dayung sampai Sungai Tualang;","Datang UKM segulung harapan,","Harap ijazah dibaw pulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108032,"Siang hari memanjat pokok kelapa,","Berhati-hati ketika turun;","Budaya kita jangan dilupa,","Lambang kasih turun-temurun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108033,"Beli celana beli permata,","Sungguh menarik dipakai raja;","Apa guna bermain cinta,","Lebih baik belajar saja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108034,"Pergi ke warung membeli samosa,","Dijual oleh Cik Mek Lisa;","Semangat bersatu sentiasa,","Mahasiswa UKM luar biasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108035,"Riang sungguh hatiku ini,","Dapat masuk Universiti Kebangsaan Malaysia;","Kita semua berwarna-warni,","Pendaulat amanah negara Malaysia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108036,"Keluar rumah berjumpa Zali,","Ayah didalam terintai-intai;","Citra seni UKM menarik sekali,","Bertuahnya diri dapat menyertai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108037,"Baju biru tudung putih,","Lima hari tak tukar-tukar;","Menimba ilmu tanpa letih,","Burga ilmu kembang mekar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108038,"Bertemu rakan terus menyapa,","Sambil menyiang ikan keli;","Biar muka tidak berupa,","Asalkan hati baik sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108039,"Berjalan-jalan di pekan lama,","Berjalan bersama Pak Amin;","Hidup berunding muafakat bersama,","Bangsa mulia budaya dijamin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108040,"Anak balik ditunggu-tunggu,","Untuk nikmat hari libur;","Petang terik dengarkan lagu,","Hati girang rasa terhibur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108041,"Pergi ke kuala menaiki sampan,","pergi bersama Kaka Limaj;","Di tangan saya terletak harapan,","Membawa segulung ijazah pulang ke rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108042,"Pak Ali mendayung sampan,","Untuk mengail udang galah;","Datang ke UKM dengan harapan,","Tidak mengerti makna kalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108043,"Cik Aminah pergi membeli keli,","Di temani si suami berambut tebat;","Berbuat baik biar berkali-kali,","Itulah tanda cinta seorang sahabat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108044,"Masuk ke kamar mencari pendeta,","Pendeta pergi ke Tasik Bera;","Menuntut ilmu varsiti kita,","UKM pendaulat amanah negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108045,"Anak ayam belajar berkokok,","Meniru suara ayam jantan;","Anak kecil jangan merokok,","Kalau merokok pasti penyakitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108046,"Pergi ke hutan jumpa buaya,","Usah dibuat tempat bertapa;","Pakaian Melayu gambaran budaya,","Anggun dipakai janganlah dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108047,"Buah manggis buah nangka,","Makan bersama Pak Isa;","Usahlah kita menarik muka,","Orang berbudi kita berbahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108048,"Lompat si katak lompat,","Lompat di tepi pagar;","Sekolah lekas dapat,","Mesti rajin belajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108049,"Tepian biara pohon pulasan,","Kelopak dahlia diatas papan;","Aspirasi diri mencapai wawasan,","Di tangan UKM terletak harapan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108050,"Anak Pak Atan mencari peta,","Petanya hilang di dalam saku;","Jika Cik Abang dikejar cinta,","Bersembunyi saja di dalam hatiku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108051,"Bumi dipijak lagit dijunjung,","Pucuk dicita ulam mendatang;","UKM universiti yang ku sanjungi,","Kejayaan kita yang akan datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108052,"Ikan di laut garam di Darat,","Dalam kuali bertemu jua;","Hati terpaut janji terikat,","Di UKM bertemu jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108053,"Kalau tuan pergi ke pekan,","Jangan lupa membeli roti;","Terima kasih saya ucapkan,","Semoga UKM terpahat dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108054,"Pergi ke kedai membeli asam,","Asam dibeli bersam gula;","Semangat patriotik harus ditanam,","Supaya diri cintakan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108055,"Di anjung istana termenung puteri,","Menanti Kekasih putera perkasa;","Di persada dunia gah berdiri,","UKM dihati ku junjung sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108056,"Pohon mangga lebat buahnya,","Mangga dipetik disimpan di peti;","UKM kepadaku besar budinya,","Tidak akan ku lelah menyumbang bakti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108057,"Panas terik cuaca hari ini,","Adat Melayu jangan kita lupai;","Tinggi-tinggi pokok kelapa,","Tinggi lagi adab kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108058,"Perahu belayar ke muara,","Di dayung perlahan Mak Hajar;","Kata nasihat umpama mutiara,","Memberi dorongan anak belajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108059,"Buah bidara di dalam kaca,","Enak dimakan bersama tetamu;","Janganlah kita membuang masa,","Kelak nanti masyarakat malu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108060,"Pergi ke kedai membeli limau,","Pergi bersama anak Leman;","Biarlah muka seganas harimau,","Asalkan dalam hati ada taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108061,"Pagi-pagi beli pisang,","Pisang disimpan di atas para;","Saya megat masih bujang,","Ingin mencari yang si dara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108062,"Adabnya wanita berakhlak sopan,","Ditegah sendiri apabila di jalan;","Mengilhamkan harapan, mencipta masa hadapan,","Salam UKM, salam perkenalan.", 4, 6);
