INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108064,"Orang kaya naik pedati,","Ketawa jangan tidak berhenti;","Saya berjaya masuk universiti,","Awak tinggal cekalkan hati.", 4, 6);
