INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108073,"Bunga melor tumbuh berangkai,","Pohon manggis tumbuh berbuah;","Mustahil biawak menolak bangkai,","Takkanlah saya menolak tuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108074,"Penat lelah tidak mengapa,","Menuntut ilmu sampai kemati;","Harapan dari ibu dan bapa,","Menjadi pembakar semangat dihati.", 4, 6);
