INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108136,"Mentari memancar di lembah indah,","Burung berkicau memecah sepi;","Azam dan tekat ku semat sudah,","Biar halangan dihadapan menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108137,"Terbang tinggi si burung merpati,","Hinggap sebentar dipohon tinggi;","Kenal budi sesama hati,","Hingga ke puncak tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108138,"Tikar mengkuang dianyam rapi,","Terbentang elok diruang tamu;","Teruskan berjuang cekalkan hati,","Segulung ijazah menanti kamu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108139,"Dedaun gugur diarus ke hulu,","Angin mengoncang pohon cemara;","Adat belajar usaha perlu,","Tersungkur jangan teruslah mara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108140,"Anak-anak bermain batu,","Batu dilempar satu per satu;","Berbadan lurus matanya satu,","Hujungnya tajam apakah itu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108141,"Sampan kecil dibawa berlayar,","Srbiji pisabg masak di peti;","Jika hutang boleh dibayar,","Hutang budi dibawa mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108142,"Anak kecil bermain di tepian,","Tempat si angsa bermain juga;","Mencari ilmu bermati-matian,","Sanggup berkorban jiwa dan raga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108143,"Daun nipah di atas para,","Buat anyaman dikala senja;","Rumah bersepah hai anak dara,","Penuh senyuman selamba saja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108144,"Enak sungguh makan masakan,","Tidaklah cukup untuk merasa;","Harapan ibu bapa saya tunaikan,","Tidaklah kenal berputus asa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108145,"Harum sungguh bunga kemboja,","Buat hiasan pelbagai benda;","Demi ijazah yang ku puja,","Terpaksa tinggalkan ayahanda Bonda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108146,"Durian disusun dibuat serawa,","Ditambah gula manis rasanya;","Kejar impian harapan dibawa,","Pulau ilmu UKM tempatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108147,"Bangun pagi pukul lapan,","Menjalankan tugas menguruskan jenazah;","Datang UKM penuh harapan,","Harapan agar menggenggam ijazah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108148,"Burung helang burung merpati,","Berkicau-kicau di siang hari;","UKM menjadi idaman hati,","Pengalaman berharga ingin ku cari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108149,"Indah padi melambai saujana,","Kerbau berpanas terus membajak;","Doa ibu bapa akhirnya sempurna,","Bumi UKM telah ku jejak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108150,"Tuai padi antara masak,","Esak jangan layu-layuan;","Kalau padi belumlah masak,","Janganlah tuai, taklah layu-layuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108151,"Burung pipit terbang merata,","Hinggap sebentar di dahan rapuh;","Puncak jaya yang ingin ku cita,","Segala cabaran harus ditempuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108152,"Petik bunga berwarna merah,","Orang panggil bunga raya;","Berjuang walaupun tumpah darah,","Dapat ijazah dengan jaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108153,"Awal pagi pergi ke kelas,","Berusaha keras bersaing sengit;","Menuntut ilmu dengan ikhlas,","Demi cita-cita setinggi langit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108154,"Daun pandan daun kari,","Paku lama akan berkarat;","ilmu itu perlu dicari,","Sebagai bekalan dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108155,"Dari jauh datang kemari,","Untuk mencari ilmu yang tinggi;","Menjadi pelajar harus rendah diri,","Untuk menjadi insan yang disanjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108156,"Anak burung anak lebah,","Hari-hari mengangkut sarang;","Dek zaman pantai berubah,","Inikan pula hati orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108157,"Beli celana beli permata,","Sungguh cantik dipakai raja;","Apa guna bermain cinta,","Lebih baik datang UKM belajar sahaja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108158,"Cantik rerama dipuji ratu,","Bagai gemala istana syurgawi;","Mari bersama kita bersatu,","Semarakkan semula permainan tradisi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108159,"Lancang kuning lancang pusaka,","Nampak dari Tanjung Pulan;","Belajar di UkM meninggalkan keluarga,","Pasti akan rasa kerinduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108160,"Berkelana lanang menuju desa,","Singgah berdagang di Tanjung Jati;","Bahasa Melayu membina nusa,","Generasi terbilang kukuh berdiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108161,"Biar hari ditelan malam,","Suria pasti menjelma lagi;","Biar hati di cengkam suram,","Masalah pasti beransur pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108162,"Ketupat ubi ketupat palas,","Boleh dicari dimana-mana;","Kepercayaan diberi tanggungjawab digalas,","Moga menjadi graduan berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108163,"Jauh belayar ke hujung tanjung,","Angkatan perang pejuang negara;","Adat Melayu tetap di sanjung,","Takkan hilang pantun di jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108164,"Cantik bidadari turun dari kayangan,","Putih berseri cantik wajahnya;","Jadi mahasiswa harus ada wawasan,","Supaya nanti kelak berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108165,"Kelip-kelip ku sangka api,","Mana nak sama si kucing belang;","Diam-diam ubi berisi,","Impian nak jadi pelajar cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108166,"Sayangkan kain buangkan baju,","Kerana baju panjang sembilan;","Saya berharap dapat maju,","Menjejak kaki di Kolej Dato Onn", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108167,"Lipat kain lipat baju,","Lipat kertas dalam puan;","Dari air menjadi batu,","Sedikit tak lupa budi tuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108168,"Lipat kain lipat baju,","Lipat kertas dalam puan;","Dari air menjadi batu,","Sedikit tak lupa budi tuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108169,"Anak merak Kampung Cina,","Singgah berhenti kepala titi;","Emas perak kebesaran dunia,","Budi bahasa tak dapat dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108170,"Saya datang dari Penang,","Dari jauh meredah lautan;","Memori UKM mesti dikenang,","Semoga berkekalan dalam ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108171,"Pergi ke kedai membeli roti,","Singgah sekejap di rumah Pak Haji;","Aku sudah masuk ke Universiti,","Atas usaha menabur bakti,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108172,"Tinggal di kota Ali dan Siti,","Sahabat rapat jarang ditemu;","UKM ada banyak varsiti,","Dapat juga sambung ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108173,"Universiti Kebangsaan Malaysia menjadi pilihan,","Tempat mencari ilmu dan tuntutan;","Semoga ilmu diberkati tuhan,","Segulung ijazah menjadi rebutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108174,"Dua tiga kanak-kanak berlari,","Mana nak sama si anak sulung;","Dua tiga boleh ku cari,","Mana nak sama UKM sayang,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108175,"Lukisan cantik menghiasi dinding,","Mata terpegun hati berangan;","Hendak berjaya di menara gading,","Menggenggam ijazah erat ditangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108176,"Luas lautan dibumi kita,","Indah bagaikan rembulan;","Jejakku disini menggapai cita-cita,","Universiti Kebangsaan Malaysia menjadi rebutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108177,"Ucapan bismillah pembuka bicara,","Pantun dibuku dengan senyuman;","tuntutlah ilmu hingga ke negeri China,","Agar bermanfaat di masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108178,"Belum punah semangat diri,","Baru bermula ikutkan hati;","Siti Aminah nama diberi,","Salam UKM salam diskusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108179,"Pujangga Melayu indah berkata,","Istana Melayu berbentang puri;","Bukan berhasrat bermain cinta,","Meningkatkan ilmu matahari diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108180,"Berkaca mata orangnya buta,","Suaranya lunak tapi disimpam;","Emas permata tiada dipinta,","Kejayaan anak menjadi harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108181,"Pisang emas dibawa belayar,","Masak sebiji di atas peti;","Anak yang baik membawa sinar,","Sejuk mata nyaman hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108182,"Sungguh cantik kebaya Nyonya,","Walau dipakai ke mana jua;","Puisi tradisional tiada kurangnya,","Menjadi kemegahan UKM semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108183,"Berkibar megah jalur gemilang,","Luahan belia dihujung pena;","Semangat gagah umpama helang,","Akhirat dunia tiada bencana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108184,"Pergi ke sungai memancing ikan,","Ikannya lari ke tengah sawah;","Ilmu jika tidak diamalkan,","Bagaikan pohon tidak berbuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108185,"Pergi ke hutan mencari rotan,","Dapat rotan di jual di pekan;","Budi yang baik jadi ikutan,","Disayang guru dan juga rakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108186,"Bersiar-siar keliling taman,","Sambil mencari teman pengganti;","Jangan kita bermalas-malasan,","Berjaya kelak bahagia pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108187,"Duduk bersimpuh si anak dara,","Sambil melihat si anak ikan;","UKM pendaulat amanah negara,","Menjadi idaman setiap insan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108188,"Pantai Mersing Kuala Johor,","Pantainya bersih sangat mashyur;","Pohonkan doa kita bersyukur,","Negara kita aman dan makmur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108189,"Terbang tinggi si burung nuri,","Singgah hinggap di dalam hutan;","Jangan segan untuk mencari,","Malu bertanya sesat jalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108190,"Pergi ke kedai membeli baja,","Singgah sebentar di kedai Cik Limah;","Datang ke UKM hendak berjaya,","Membawa harapan ibu dan ayah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108191,"Permatang rimba bersuluh tenang,","Ranting bersilang taut pegaga;","Dagang hamba jauh di seberang,","Membanting tulang buat keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108192,"Kalau ada sumur di ladang,","Boleh kita menumpang mandi;","Datang UKM penuh harapan,","Kelak berjaya di puancak nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108193,"Terbang tinggi burung merpati,","Hinggap di pepohon ara;","Pergi ke pantai membawa diri,","Mengubat hati yang lara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108194,"Burung terbang diatas pokok,","Sambil gembira berkicau-kicau;","Ilmu datang jangan kata tidak,","Sekiranga ingin menggapai impian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108195,"Bangun pagi berseri-seri,","Mimpi malam begitu nyata;","UKM harus kita sanjungi,","Disini hasrat kita terbina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108196,"Indah sungguh burung merpati,","Singgah bertenggek diatas dahan;","Menuntut ilmu sepenuh hati,","Agar jiwa bertatah iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108197,"Anak raja makan sirih,","Makan sirih diatas bangku;","Bila dibantu ucaplah terima kasih,","Barulah elok indah perilaku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108198,"Lari sini lari sana,","Dapatkan penitisan di singgahsana;","Jika kemenangan menjadi harapan,","Pastikan sifat rajin dalam genggaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108199,"Anak Pak Dollah berbadan sasa,","Naik beca menghirup udara;","Menuntut ilmu luar biasa,","UKM pendaulat amanah negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108200,"Bunga kemboja bunga telekup,","Masa depan corak hidup;","Hidup jangan terlalu leka,","Kelak binasa harapan bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108201,"Jika kamu pergi ke dusun,","Janganlah lupa membawa beras;","Belajarlah kita dengan fokus,","Agar akhirnya kejayaan dibalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108202,"Apa tanda batang pandan,","Tumbuh di pantai banyak pelepah;","Apalah tanda orang bertuah,","Elok perangai hati pun rendah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108203,"Anak semilang di tapak tangan,","Tertusuk duri bisanya terasa;","Datang UKM penuh harapan,","Jangan sekali berputus asa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108204,"Pandu kereta jenama Lamborghini,","Jalan sesak di Pantai Johor;","Negara aman dan harmoni,","Panjatkan doa rasa syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108205,"Aidilfitri hari kemenangan,","Kejayaan menempuh halangan;","Mengenali erti kesenangan,","Bagi mereka yang kekurangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108206,"Bunga mawar berbatang duri,","Melilit kemas ditepi papan;","Permata bernilai sukar dicari,","Menjadi tonggak masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108207,"Anak itik terenang-renang,","Air pasang setenang-tenangan;","Tunjuk ajar sikit takkan hilang,","Itu menjadi kenang-kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108208,"Kelapa muda bawa sepeti,","Gula Jawa beli seraga;","Sama-sama menjaga hati,","Demi keluarga nun disana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108209,"Pulau Pandan jauh ke tengah,","Gunung Daik bercabang tiga;","Hancur badan dikandung tanah,","Budi yang baik dikenang juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108210,"Pohon Jati kuat kayunya,","Pohon nyiur tinggi batangnya;","Belajar rajin banyak ilmunya,","Masuk UKM dengan bangganya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108211,"Anak kumbang mencari makan,","Terbang seiring ditepi kaki;","Bersama kita mengeratkan hubungan,","Di UKM yang semua sanjungi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108212,"Tersenyum manis si anak dara,","Duduk bersimpuh tampak sopan;","Berbekal semangat kian membara,","Mengilham harapan, mencipta masa depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108213,"Bangun pagi bermuka ceria,","Dengan riang bernyanyi lagu;","Jangan malu untuk bertanya,","Bersama kita berkongsi ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108214,"Asal kapas menjadi benang,","Dari benang dibuat kain;","Barang yang lepas jangan dikenang,","Kelak mengundang penyakit lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108215,"Dari mana punai melayang,","Dari sawah turun ke kali;","Dari mana datangnya sayang,","Dari mata turun ke hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108216,"Kalau menyanyi perlahan-lahan,","Berjalan tepi lalu lintas;","Dibawa matahari yang besar,","Rindu ibu bapa di rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108217,"Dari Tambun ke pekan,","Menggunakan tenaga buruh;","Sederhana dan sabar asas kejayaan,","Rajin dan usaha asas kesenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108218,"Harimau rimba datang kemari,","Harimau bintang mencari mangsa;","Pejuang bahasa tetap dicari,","Harapan Malaysia dijunjung bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108219,"Puteri Cina berbaju merah,","Menuju ke pekan menaiki sampan;","Hendaklah usaha jangan menyerah,","Moga berjaya di masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108220,"Hari-hari hidup bertapa,","Terasa ujian begitu berat;","Berbuat baik janganlah lupa,","Semoga berjaya dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108221,"Dari Baling ke Melaka,","Waktu balik membeli bedak;","Alam sekeliling membentuk mereka,","Jadi baik atau tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108222,"Dunia kini penuh pancaroba,","Hidup mewah idaman manusia;","Jika gagal teruskan mencuba,","Kejayaan kita kebanggaan semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108223,"Pakai baju warna biru,","Pergi ke sekolah pukul satu;","Murid sentiasa hormatkan guru,","Kerana guru pembekal ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108224,"Pakai baju warna biru,","Pergi ke sekolah pukul satu;","Murid sentiasa hormatkan guru,","Kerana guru pembekal ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108225,"Dari Pahang datang ke Bangi,","Mencari ilmu duniawi dan ukhrawi;","Moga tercapai hasrat dihati,","Ingin mencapai sebuah impian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108226,"Datang kebun memetik longan,","Terlajak petik hingga ke petang;","Disiplin asas kecemerlangan,","Kejayaan bakal datang bergolek.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108227,"Bunga melur dalam jambangan,","Di tepi kolam menjadi hiasan;","Kami datang dengan harapan,","Harapnya pulang bersama kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108228,"Dektar indah di tepi jalan,","Menjadi pilihan seorang pujangga;","Hidup mahasiswa penuh dugaan,","Tetapkan hati kentalkan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108229,"Angin utara dibawa ke selatan,","Telah menjadi seorang perantau;","Akan dia hadapi segala cabaran,","Untuk menjadi seorang yang berilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108230,"Berjalan-jalan di tepi pagar,","Berapa cantiknya bunga-bunga;","Jadilah orang yang sentiasa bersyukur,","Jangan sekali-kalinya lupa jasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108231,"Pohon selasih subur di tanjung,","Dicampur diramu buah bidara;","Belia dikasih harapan dijunjung,","Mencanai ilmu membina negeri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108232,"Aku lihat langit berbatu-bata,","Lihat dengan kaca mata;","Mari kita usaha satu,","Supaya UKM maju jaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108233,"Pak Samad membawa galah,","Galah diletak di atas papan;","Jangan sesekali mudah mengalah,","Ingatlah kejayaan ada di hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108234,"Bunga mawar berwarna putih,","Berwarna putih dan juga berduri;","Hati yang baik haruslah bersih,","Supaya kita sentiasa disenangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108235,"Pergi ke sungai memancing ikan,","Memasang umpan agar terkena;","Jika kau ingin berwawasan,","Maka rajinlah membaca.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108236,"Pergi pasar naik Ducati,","Singgah sebentar membeli laksa;","Mencari ilmu untuk berkati,","Semoga mendapi berkat tuhan yang Esa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108237,"Jauh bertandang ke rumah Ira,","Niat utama untuk bersantap;","Generasi belia harapan negara,","imannya kukuh ilmunya mantap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108238,"Wan Leman nama diberi,","Kecil molek cerdik akalnya;","Kejayaan hidup hasrat dihati,","Semoga Ilahi memperkenankannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108239,"Buah cempedak di luar pagar,","Ambil galah tolong jolokkan;","Saya budak baru belajar,","Kalau salah tolong tunjukkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108240,"Bunga peria diatas sampan,","Pohon gedangsa bersilang-silang;","Di tangan belia terletak harapan,","membawa nusa ke mercu gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108241,"Membawa diri jauh melangkah,","Tabahkan hati menanti-nanti;","Bertanya diri pada bilakah,","Sabarlah ijazah pasti miliki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108242,"Buruk baju tampal seribu,","Lusuh kain koyak rabak;","Buruk laku menanggung malu,","Pengaruh kawan jangan terjebak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108243,"Bunga mawar mekar merahnya,","Berbau harum sekeliling taman;","Berbuatlah baik kepada semua,","Amalan yang baik sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108244,"Dayung perahu tuju haluan,","Membawa rokok bersama rempah;","kalau ilmu tidak diamalkan,","Ibarat pokok tidak berbuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108245,"Anak perantau ke kuala,","Naik basikal ke Pekan Bahau;","Wahai sang anak jagalah diri,","Tempat baru jadikan memori.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108246,"Membina ilmu di Universiti,","Azam disemat kukuh dihati;","Impian yang tinggi harus dikejari,","Agar beruntung di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108247,"Setiap tahun menyambut merdeka,","Hidup sejahtera bersatu padu,","Sejarah negara jangan dilupa,","Rakyat terbilang negara maju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108248,"Pergi ke pekan beli peria,","Jangan lupa beli ubi;","Hari ini rasa ceria,","Akhirnya dapat masuk universiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108249,"Hidup ini perlu berjasa,","Walau dimana kita berada;","Janganlah mudah berputus asa,","Teruskan usaha pasti berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108250,"Muzik dimainkan dengan irama,","Alunan lagu memeriahkan suasana;","Ibu bapa tidak dapat bersama,","Sentiasa rindu nan jauh disana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108251,"Tinggi sekali Gunung Kinabalu,","Tingi lagi harapan bonda;","Buang malas, buang malu,","Kecapi impian binalah nakhoda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108252,"Rupanya ada si kura-kura,","Besar dari seekor labi;","Gunanya ilmu tidak terkira,","Bekalan manusia bertemu ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108253,"Rupanya ada si kura-kura,","Besar dari seekor labi;","Gunanya ilmu tidak terkira,","Bekalan manusia bertemu ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108254,"Puteri Rokan berakit ke hulu,","Di nanti sayup seorang putera;","Sifat prihatin hendaklah selalu,","Supaya hidup aman sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108255,"Terang bulan terang di paya,","Anak kambing memakan rumput;","Jika tak kenang kepada saya,","Nama yang baik tidak disebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108256,"Burung-burung terbang tinggi,","Berterbangan laju lewati pintu;","Dari kecil bercita tinggi,","Terus berjuang menuntut ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108257,"Burung terbang di tepi perigi,","Lurus terbang lewati batu;","Masa kecik bercita tinggi,","Kini dewasa menuntut ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108258,"Rancak bersolek si anak raja,","Solekan dihias mengagumi sang putera;","Harapan hamba ringkas sahaja,","Cukuplah kejayaan dinikmati semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108259,"Sorong papan tarik papan,","Papan dari kampung Bera;","Sungguh tinggi menaruh harapan,","untuk agama bangsa dan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108260,"Ke pekan naik jambatan,","Singgah gerai minum air kelapa;","Segulung ijazah menjadi impian,","Untuk membalas jasa ibu bapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108261,"Bila hidup jangan selesa,","Ingatlah diri wahai saudara;","Jangan dilupa warisan bangsa,","Menjadi pedoman untuk negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108262,"Sarung ketupat berisi inti,","Di bawa budak sambil berlari;","Harapan ibu bapa kepada kami,","Semoga mampu kita kecapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108263,"Angin teluk mengisir pantai,","Hanyut sampai dibawah titi;","Biarlah buruk kain dipakai,","Asal pandai mengambil hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108264,"Anak itik terenang-renang,","Air pasang tenang- tenangan;","Budi sedikit nilakan hilang,","Itu menjadi kenang- kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108265,"Bangun pagi bersiap sedia,","Gosok gigi jangan lupa;","Hormat-menghormati amalan mulia,","Mendapat berkat ibu bapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108266,"Kusangka nanas di tengah padang,","Rupanya pandan yang tumbuh berduri;","Kusangka panas hingga ke petang,","Rupanya hujan di tengah hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108267,"Hijau daun pokok meranti,","Duduk berehat di bawah pohon;","walaupun jauh tetap ku nanti","ampun maaf daripadaku mohon", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108268,"Mak Esah pergi ke kedai,","Membeli roti untuk dimakan;","Jika anda ingin bijak pandai,","membaca adalah kunci kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108269,"Pohon kelapa tumbuh menjajar,","Tumbuh berjajar ditepi pantai;","Bersungguh-sungguh untuk belajar,","Pasti kita lekas pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108270,"Bunga dahlia jangan dibuang,","Buat tatapan puteri pangeran;","Membina belia bersemangat juang,","Sanggup berhadapan pelbagai cabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108271,"Letih berjalan di tengah Universiti,","Singgah sebentar di domain kebudayaan;","Terima kasih daun keladi,","Kerana sudi memberi bantuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108272,"Tumbuh melata si pokok tebu,","Pergi pasar membeli daging;","Banyak lautan tak ada ilmu,","Bagai rumah tidak berdinding.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108273,"Sungguh elok ciptaan manusia,","Lagi indah ciptaan ilahi;","Sungguh elok budi bahasa,","Jika dihias akhlak terpuji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108274,"Ke Semarang bersama dibina,","Membeli oleh-oleh dan boneka;","Bergeraklah para jiwa muda,","Bekerja dengan semangat membara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108275,"Anak ayam turun sembilan,","Hilang satu tinggal delapan;","Di UKM, ilmu boleh sedikit ketinggalan,","Tapi jangan sampai putus harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108276,"Belajar lukis dengan pensil,","Mengapa kertas tidak rata;","Semoga engkau berhasil,","Meraih segala cita-cita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108277,"Rumput hijau dipandang cantik,","Pokok bunga berwarna-warni;","Pulau Langkawi panas terik,","apatah lagi UKM Bangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108278,"Meleret senyum manis dibibirmu,","Hilang duka terpancar rindu;","ku ukir namamu di dasar kalbu,","Ukhwah fillah memjadi dambaku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108279,"Anak rusa masuk rumah,","Dihalau bapa lari terkial;","kalau kita malas telaah,","Bodoh yang dirasa jangan dipersoal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108280,"Membeli kain pergi ke pekan,","Kain dibelikan dibuat baju;","Datang kemari, tidak lain tidak bukan,","Menimba pengalaman, menuntut ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108281,"Buah berangan masaknya merah,","Kelekati dalam perahu;","Luka di tangan nampak berdarah,","Luka dihati siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108282,"Buah berangan masaknya merah,","Kelekati dalam perahu;","Luka di tangan nampak berdarah,","Luka dihati siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108283,"Hijau flora Gunung Helang,","Cantik pepasir di Tanjung Karang;","Jangan kita menunjuk belang,","Kelak hidup di benci orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108284,"Bunga raya, bunga kebangsaan,","Dipetik oleh si gadis pemalu;","Perpaduan rakyat jaminan keamanan,","Menjadi kunci negara maju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108285,"Anak ayam turun lima,","Mati seekor tinggal empat;","Kita hidup mesti beragama,","Supaya hidup tidaklah sesat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108286,"Kelopak dahlia berseri warna,","Penyeri hiasan Puteri Tempawan;","Generasi belia pengukuh agama,","Teras landasan jalan perpaduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108287,"Dari Sarawak datang kemari,","Lautan China sanggup ku rentasi;","Datang ke UKM ilmu ku cari,","Sebagai bekalan ukharawi dan duniawi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108288,"Ditengah hutan pokok sebatang,","Merantau jauh rujuk peta;","Besar harapan aku datang,","Untuk berlari mengejar cita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108289,"Mekar sejambak bunga gubahan,","Jatuh sekuntum di atas peti;","Segulung ijazah hamba idamkan,","harapan keluarga akan ku tunaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108290,"Riuh suasana kampung halaman,","Burung berkicauan terlihat indah;","Segulung ijazah menjadi idaman,","Pemberian kepada ibu dan ayah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108291,"Anak ayam turun sepuluh,","Mati satu tinggal sembilan;","Tuntutlah ilmu bersungguh-sungguh,","Jangan ada yang ketinggalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108292,"Harimau Malaya, harimau rimba,","Anak ayam turun sepuluh;","Dari muda sampai ke tua,","Ajaran baik jangan diubah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108293,"Ramai orang menggali perigi,","Menggali perigi guna papan;","Ilmu dicari tidak akan rugi,","Kelak senang masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108294,"Datang ke UKM bersemangat waja,","Dengan membawa harapan yang besar;","Menuntu ilmu perkara yang utama,","Janganlah kita lupa untuk belajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108295,"Dari Sarawak terbang ke Bangi,","Datang membawa sejuta harapan;","UKM kan sentiasa tersemat dihati,","Agar segulung ijazah berada di dakapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108296,"Burung Enggang tercantik benua,","Lambang sabah dulu dan kini;","Kerjasama antara semua,","Membina masyarakat aman harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108297,"Berkejar-kejar ombak dilaut,","Menyentuh pantai berpasir bersih;","Budi pensyarah disebut-sebut,","Kami berjaya dan terima kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108298,"Pergi ke kota berkaca mata,","Berbaju segak penuh bergaya;","Hidup kita punya cita-cita,","Demi menjadi insan berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108299,"Manis sungguh anak manakah,","dilihat ayu pada mata;","hasrat rakyat disuarakan,","UKM pendaulat amanah bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108300,"Memetik gambus di tepi kali,","Diteman bersama puteri yang sopan;","Tidak akan pupus ilmu dicari,","Kelak berguna dimasa depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108301,"Dari kampung pergi ke pekan,","Bersama-sama kaum muda;","Rajin belajar dapatlah dekan,","malas berusaha duduklah desa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108302,"Manisnya rasa buah rambutan,","Dicuri tupai terbang melayang;","Terima kasih UKM atas jemputan,","Hati teruja bertambah sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108303,"Bunga raya, bunga melati,","Buat hiasan tman di kota;","UKM mengilhamkan inspirasi,","Menjana harapan anak bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108304,"Berjalan-jalan di tepi pantai,","Bersama pula si janda baru;","Pergi kesana mestilah pandai,","Jangan pula tergaru-garu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108305,"Anak semilang di tapak tangan,","Tertusuk duri bisanya terasa;","Jika kejayaan menjadi harapan,","Jangan sesekali berputus asa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108306,"Anak kambing lintas titi,","Burung nuri menjadi harapan;","Harapan sudah menjadi realiti,","Realiti akan menjadi kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108307,"Ayam boleh, ikan pun boleh,","Yang penting ada nasinya;","Hitam boleh, putih pun boleh,","Yang penting baik hatinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108308,"Burung belatuk terbang tinggi,","Singgah sebentar diatas para;","Ukhwah dibentuk memangkin keamanan,","Agar hidup kekal sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108309,"Elok disarung cincin suasa,","Sekali dipakai tampak bergaya;","Hidup gembira aman sentosa,","Agar sentiasa kekal bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108310,"Asyik berjalan terjumpa perawan,","Berbudi bahasa baik hati;","Tujuan kemari tak lain tak bukan,","Menuntut ilmu dibawa mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108311,"Sudah kering air di telaga,","Bencana alam terus ditangisi;","Masuk ke UKM harapan keluarga,","Kejayaan ditunggu bakal dikecapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108312,"Berjalan kaki Fatimah dan Yaya,","Menuju ke kedai membeli serunding;","Belajar di UKM impian saya,","Harapnya sampai ke menara gading.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108313,"Pergi ke kedai membeli lada,","Lalu diletak di dalam raga;","Bergeraklah para jiwa muda,","Bekerja dengan semangat membara.", 4, 6);
