INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108398,"Jari sepuluh susun di kepala,","Kata diucap Assalamualaikum,","Hati bahagia Saudara tiba,","Bersama kita berbalas pantun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108399,"Alhamdulillah penyambut kata,","Syukur bahagia dapat bersua,","Acara berpantun menjalin mesra,","Inilah jambatan serumpun sebangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108400,"Tegaknya rumah kerana sendi,","Sendi binasa dik kayu lama,","Tegaknya bangsa kerana budi,","Budi bahasa tunjang utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108401,"Tari piring kemas bertingkah,","Menyambut mesra tetamu mulia,","Ayuh bersama mengatur langkah,","Memartabatkan bahasa ke persada dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108402,"Hendak belayar ke Teluk Betong,","Singgah di muara melabuh pukat,","Bulat air kerana pembetung,","Teguh bangsa kerana muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108403,"Belayar ke hulu menuju muara,","Sauh dilabuh di tengah hari,","Sepakat berpadu satu suara,","Ikatan kukuh masyarakat bistari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108404,"Baju kebaya berkain kasa,","Baju biru berbatu hiasan,","Kepelbagaian budaya satu bahasa,","Namun bersatu dalam gagasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108405,"Puncak cemara menyapu awan,","Asap berkepul di ruang angkasa,","Rancangan ke udara sebagai panduan,","Biasakan yang betul, betulkan yang biasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108406,"Usah ditampi padi haluan,","Biarkan saja kering di jemuran,","RTM-DBP sering sehaluan,","Satu Malaysia satu impian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108407,"Orang Barat berbenteng besi,","Orang Melayu berbenteng adat,","Perjuangan Datuk Seri Utama kami sanjungi,","Semoga Allah memberi berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108408,"Tanjung Rumbia ombak melantun,","Redup suasana di pinggir gurun,","Orang Malaysia sopan dan santun,","Pintar bijaksana turun temurun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108409,"Dari permatang ke tanah gambut,","Menghijau desa rimbun pedada,","Tuan datang kami menyambut,","Membudayakan bahasa, warisan ibunda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108410,"Nak buah ciku dah dapat,","Nak dibelah belum lagi,","Nak baju baru dah dapat,","Nak berziarah bila lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108411,"Naik sampan ke Indergiri,","Muatan lada hendak dijaja,","Buat persiapan di Aildilfitri,","Harus waspada ketika berbelanja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108412,"Hendak disalai pohon gaharu,","Asap cincin berbau dupa,","Meskipun pakai baju baru,","Insan miskin jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108413,"Ikan terapung dalam paya,","Itik angsa menyelam menyudu,","Balik kampung tradisi raya,","Panggilan desa menggamit rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108414,"Jangan menuba sebarang racun,","Takut terkena udang galahnya,","Jangan cuba bermain mercun,","Ditimpa bencana buruk padahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108415,"Buah pala atas para,","Buah rambutan di dalam talam,","Dari musola menziarah pusara,","Tanda ingatan dekat dan dalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108416,"Awan berpuput ke seberang takir,","Bayu berarak ke Bukit Larut,","Salam bersambut senyum terukir,","Lukapun tidak lagi berparut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108417,"Burung kuang di dahan kundang,","Ayam serama makan di tangan,","Lontong, lemang, ketupat dan rendang,","Menu utama di tikar hidangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108418,"Ikan picu dalam sawah,","Pokok serai tumbuh merimbun,","Pelukan cucu ciuman opah,","Rindu terlerai dendam setahun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108419,"Berbuka di Ipoh bersahur dirantau,","Singgah di Kajang membeli belida,","Berlagu riuh dendang perantau,","Berkejar pulang ke laman ibunda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108420,"Puding santapan berisi perisa,","Rencah digarap adunan di dulang,","Saling bermaafan menyuci dosa,","Salah dan silap jangan diulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108421,"Pelikat merah alas celana,","Songket Betawi tenun bersegi,","Zakat fitrah ikhlas terlaksana,","Membersih diri setahun lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108422,"Malam raya aneka busana,","Tambah gemilang puspa jambangan,","Dalam ceria meriah suasana,","Mereka yang malang dilupa jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108423,"Rendang berempah masak bersangai,","Bubur caca pengat keladi,","Pakaian indah ragam pelbagai,","Syukur terasa berkat abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108424,"Baulu pandan masak di laman,","Jual di kedai bawa ke sawah,","Berlalu Ramadhan semarak iman,","Lebaran melambai memarak ukhwah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108425,"Ranting buluh rebah di hutan,","Batang jerami gugur merata,","Setitik peluh segunung intan,","Makmurkan bumi ibunda tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108426,"Orang Daik ke Pulau Rokan,","Singgah Selama rumah Cik Dolah,","Kerja yang baik kita percepatkan,","Moga- moga diterima Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108427,"Jalan sunyi titi bergalah,","Menebang jati di tengah huma,","Amalan murni diberkati Allah,","Tingkatkan kualiti, kewajipan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108428,"Angkat titi batang rumbia,","Alas papan buat lintasan,","Khidmat berkualiti amalan mulia,","Asas kejayaan negara berwawasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108429,"Radio Irama Melayu Asli,","Pendokong warisan budaya bangsa,","Suara bergema merdu sekali,","Bersahut-sahutan kota dan desa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108430,"Daun baru dahan pun baru,","Basah gelam meredah hutan,","Tahun baru azam pun baru,","Kisah semalam jadikan ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108431,"Orang Barat berbenteng besi,","Orang Melayu berbenteng adat,","Bersungguh-sungguh tuan menghulur budi,","Semoga Allah memberi berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108432,"Sungguh kami menyemai keladi,","Keladi bergenang di pinggir tebat,","Sungguh kami terhutang budi,","Budi dikenang sepanjang hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108433,"Orang Daik ke Bukit Rokan,","Singgah Selama mengambil bilah,","Kerja yang baik kita percepatkan,","Mudah-mudahan diterima Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108434,"Kajang tuan kajang berumbut,","Tumbuh selari di tepi pekan,","Datang tuan datang bersambut,","Sepuluh jari kami hulurkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108435,"Bunga selasih harum bersemi,","Gugur melati di tapal batas,","Terima kasih ucapan kami,","Dengan hati yang tulus ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108436,"Ada ubi adalah batas,","Ada padi buahnya lebat,","Budimu tuan tidak terbalas,","Menjadi kenangan sepanjang hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108437,"Sungguh besar Istana Melaka,","Tempat bermain si putera raja,","Sungguhlah benar bagai dikata,","Hadirnya tuan bawa cerita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108438,"Kelat sekali buah peria,","Kelatnya kurang buah dibelah,","Hadirnya kami bawa ceria,","Kerna tuan berbudi indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108439,"Buah jentik manis rasanya,","Terjumpa ia di negeri utara,","Sejuk hati melihat saudara,","Apa rahsia berwajah ceria?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108440,"Gagah si badang gagah berbeza,","Punya kisah berhati duka,","Rahsia kami berwajah ceria,","Dapat berjumpa di RTM Melaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108441,"Bunga lalang cantik rupanya,","Tapi sayang dimakan kumbang,","Mahukah tuan kami bertanya,","Adat melayu datang bertandang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108442,"Mencari keli ditepi bendang,","Mengunyah ulam buat kudapan,","Melayu kami datang bertandang,","Berbudi bahasa bersulam sopan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108443,"Bermain kata di pohon ceri,","Renung mata si gadis menawan,","Sudi tuan jawab ke kami,","Apa adat menyambut kawan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108444,"Rindu si dia berhati rawan,","Bagai pungguk rindu si bulan,","Dengarkan adat menyambut kawan,","Hulurkan tangan maniskan senyuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108445,"Pergi ke hutan mencari meranti,","Tersepak pokok si kayu mati,","Tertanya kami rasa di hati,","Apa hajat tuan ke mari?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108446,"Mencari tempat datang bercuti,","Dari Melaka ke Pulau Tioman,","Hajat kami datang kemari,","Ingin bertandang ke majlis tuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108447,"Pasir Putih di pinggir kali,","Pekan penyabung ayam berlaga,","Kasih tak boleh di jual beli,","Bukannya benda buat berniaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108448,"Selasih rimba memanjat rotan,","Kerat rotan tali pedati,","Kasihnya hamba kepada tuan,","Bagai ikatan tersimpul mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108449,"Pasang pelita di senja redup,","Menanti sinaran cahaya purnama,","Bahagia derita mainan hidup,","Hadapi dugaan dengan bijaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108450,"Setianya Qais kepada Laila,","Bekorban jiwa kasih sejati,","Tawa dan nangis sama dirasa,","Disitulah terbina ketulusan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108451,"Buah keranji dari kuala,","Gugur selasih di batas padi,","Sudah berjanji kita berdua,","Menjalin kasih setia abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108452,"Guruh berbalam hutan di redah,","Bertemu dupa di Tanjung Jati,","Seluruh alam ku cari sudah,","Baru berjumpa si jantung hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108453,"Selasih terlerai di bawa angin,","Harum jambangan kembang melati,","Kasih dibelai sayang terjalin,","Kukuhkan dengan cinta sejati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108454,"Sutera cindai bertatah permata,","Kain dilipat sutera halus,","Gelora badai, menerpa cinta,","Genggam erat janji nan tulus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108455,"Tegaknya rumah kerana sendi,","Runtuh sendi rumah binasa,","Sendi bangsa adalah budi,","Runtuh budi, bangsa binasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108456,"Bertanam padi di pinggir desa,","Pepohon permai hijau merimbun,","Orang berbudi kita berbahasa,","Rukun damai bangsa serumpun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108457,"Jika berladang menyemai padi,","Carilah padi benih seberang,","Jika dagang pandai berbudi,","Ke mana pergi dikenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108458,"Desa permai bersih dan indah,","Hasil berganda petani berbakti,","Jasa disemai kasih tercurah,","Bijak nak muda menyulam budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108459,"Buah manggis di atas papan,","Manggis dibeli dusun Laksemana,","Wajah manis tingkah bersopan,","Manis sekali hati budinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108460,"Hias dupa halus ukiran,","Berseri melur jambangan bertingkat,","Paras dan rupa bukan ukuran,","Budi yang luhur hamba terpikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108461,"Jika padi tidak tergalas,","Bawa ke bukit naik pedati,","Jika budi tidak terbalas,","Kenang sedikit di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108462,"Di sana padi di sini padi,","Padi yang mana hendak di galas,","Di sana budi di sini budi,","Budi yang mana, hendak ku balas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108463,"Padi nan halus sukar dituai,","Benih diganti, padi Malinja,","Budi nan tulus sukar dinilai,","Semoga diberkati Allah yang Esa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108464,"Banyak padi bertanam padi,","Padi nan halus, sukar dituai,","Banyak budi perkara budi,","Budi nan tulus, sukar dinilai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108465,"Keris dihunus silang bersilang,","Mainan perwira putera perkasa,","Silat berseni pencak terbilang,","Warisan pertiwi pusaka bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108466,"Datuk Laksamana menjunjung duli,","Segak bertanjak berkeris dipinggang,","Gayung dan lincah teratur rapi,","Tangkas berpencak ditengah gelanggang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108467,"Kelapa pandan dibuat inti,","Makan berama pulut sekaya,","Kesucian Ramadhan sudah dinikmati,","Kini menjelma Syawal yang mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108468,"Serumpun padi jemur di titi,","Tangkainya kering gugur berderai,","Seranum budi seluhur hati,","Silaturrahim subur dibelai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108469,"Rumah kecil tiang seribu,","Rumah besar tiang Sebatang,","Masa kecil di belai ibu,","Menyambut lebaran kenapa tak pulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108470,"Merdu berlagu buluh perindu,","Mengubat resah di perantauan,","Ku dakap ibu menghilang rindu,","Tak sanggup berpisah menyambut lebaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108471,"Mengail kelis memasang joran,","Sambil bersiar di Pulau Tioman,","Asyik leka mengejar kemasyuran,","Terlenakah penyiar hilang pedoman?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108472,"Dari Beluran menuju Tuaran,","Berdagang logam beserta kendi,","Bukan kemasyhuran menjadi ukuran,","Pedoman ku genggam menghias peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108473,"Buah jering enak dimakan,","Pembuka selera di tengahari,","Sudah sering kami ingatkan,","Ramai yang cedera dik mercun sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108474,"Kelopak merah sibunga raya,","Bunga kebanggaan lambang pertiwi,","Lalai dan ghairah menyambut raya,","Akibatnya memakan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108475,"Di sana gunung di sini gunung,","Tengah tengah pohon meranti,","Di sana bingung di sini pun bingung,","Yang mana satu terpikat di hati?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108476,"Aman desa tenang dan hening,","Duduk di bangku memetik kecapi,","Tak kuasa nak pening pening,","Sampai waktunya tahulah nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108477,"Merdu mendayu suara takbir,","Dendang Perantau menggamit kenangan,","Kueh Melayu banyak terpamer,","Selera terpukau yang mana pilihan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108478,"Runtuh mahligai cinta terhalang,","Cari yang baru sebagai teman,","Biar pelbagai kuih terhidang,","Dodol dan bahulu tetap pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108479,"Ikan gelama si ikan duri,","Ikan cencaru dalam belanga,","Sekian lama menghilangkan diri,","Album terbaru bilakah agaknya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108480,"Duri onak tepi gelanggang,","Luka berdarah nampak kesannya,","Menghiburkan anak keliling pinggang,","Album di rumah lebih bermakna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108481,"Kain batik berlapik baldu,","Kain dilipat belum berbayar,","Wajah cantik suara pun merdu,","Begitukah syarat jadi penyiar?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108482,"Dari utara angin berpesan,","Membawa hasrat teruna desa,","Wajah dan suara sekadar hiasan,","Yang penting bakat bijak berbahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108483,"Perahu dagangan di badai taufan,","Sarat muatan si daun palas,","Hulurkan tangan bermaaf maafan,","Dengan ingatan tulus dan ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108484,"Mudik kuala ke Tanjung Jati,","Membawa kelapa di dalam sampan,","Jernihkan segala kekeruhan dihati,","Di hari mulia bermaaf maafan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108485,"Buah kepaya dalam tapisan,","Kupas kulitnya dibuat jeruk,","Menyambut raya berhabis habisan,","Lepas raya mengapa terduduk?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108486,"Tidak ku lutut pucuk rambutan,","Banyak penyengat racun berbisa,","In sya Allah, tidak ku turut nafsu syaitan,","Beringat ingat setiap masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108487,"Berdondang sayang Baba dan nyonya,","Berpantun bermadah bergurau senda,","Apakah rahsia apakah petuanya,","Usia bertambah suaranya muda?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108488,"Ayam hitam asyik berkokok,","Mencari makan tunggul berkulat,","Selalu bersenam tidak merokok,","Banyakkan zikir dan berselawat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108489,"Kena penyengat semut merah,","Bengkak jari sampai bertaut,","Masihkah ingat rentak dan langkah,","Kalau menari Serampang Laut?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108490,"Jelingan mata hati terpaut,","Itu tandanya cinta meleleh,","Jangankan kata Serampang Laut,","Limbo dan caca pun saya boleh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108491,"Rumah baharu di Kota Lama,","Di bina oleh Panglima Bertiga,","Tiada yang baharu tiada yang lama,","Bagaimana boleh berganding tenaga?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108492,"Kembang melati semerbak wangi,","Ikat jambangan si bunga raya,","Yang lama dihurmati yang baru disayangi,","Sepakat sehaluan memperkasa budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108493,"Ikan picu dalam sawah,","Pohon serai tumbuh merimbun,","Pelukan cucu ciuman opah,","Terleraikah rindu dendam setahun?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108494,"Hanyut pelepah di bawa air,","Buah berangan jatuh melayang,","Rindu opah tak pernah berakhir,","Menunggu kepulangan anak cucu tesayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108495,"Bayu berpuput ke seberang Takir,","Dari Selama ke Bukit Larut,","Salam bersambut senyum terukir,","Luka yang lama masihkah berparut?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108496,"Sudah ku semai si padi huma,","Hasil bertambah musim berganti,","Sudah ku lupakan kisah yang lama,","Kiniku sudah ada pengganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108497,"Tanah subur jalan ke paya,","Banyak tanaman orang berpamah,","Sibuk berhibur di hari raya,","Tidakkah demikian melekakan ummah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108498,"Tumbuh sejajar pohon kedondong,","Pohon pulasan pucuknya condong,","Dunia di kejar akhirat di kendong,","Menyedarkan insan dalam senandung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108499,"Anak siamang melompat lompat,","Jatuh bertingkah dipohon kasturi,","Tanpa lemang tanpa ketupat,","Masih berserikan raya Aidilfitri?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108500,"Pasang pelita menjelang malam,","Cahaya gemilang menyambut raya,","Eluknya kita mengikut resam,","Agar tak hilang akar budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108501,"Kuih kemboja di potong empat,","Kuih bahulu kita hidangkan,","Jemputan keluarga jemputan peminat,","Yang mana satu puan dulukan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108502,"Nasi lemak buah pedada,","Kuahnya pekat bercampur santan,","Sayang emak sayang saudara,","Jemputan peminat menyusul kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108503,"Pucuk pauh si asam pauh,","Putik yang muda gugur di laman,","Ibu jauh saudara pun jauh,","Dengan siapa teman berteman?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108504,"Kalau rebah si pohon pauh,","Jangan tesekat dahan kuini,","Apa dikisah keluarga yang jauh,","Saudara angkat ramai disini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108505,"Orang Tanjung pintar berniaga,","Bawa dagangan sibuah kurma,","Kunjung mengunjung di hari raya,","Apakah hidangan dibawa bersama?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108506,"Pohon rendang di tepi huma,","Tempat teduhan si pohon jati,","Ketupat dan rendang ku bawa,","Bersamaberulamkan dengan sekeping hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108507,"Patah cemara rendah di muara,","Indah molek mawar seuntai,","Megah bahtera membelah samudera,","Resahkah kolek terdampar di pantai?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108508,"Sembilu berputik di perdu teratai,","Rendah seroja merimbun kasturi,","Andai kolek terdampar di pandai,","Rujuklah saja malim bistari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108509,"Berpaut beringin puspa besemi,","Mencelah melati ranting bersilang,","Kemelut si miskin meranjau pribumi,","Lesukah simpati korporat terbilang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108510,"Seri berikat kuntuman melur,","Kayu beringin condong ke lembah,","Simpati korporat sedia dihulur,","Membantu si miskin wujudlah muhibbah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108511,"Terpegun awan meruntun purnama,","Sendu kejora direkah perindu,","Anggun perawan mengalun irama,","Merdu suara terubatkah rindu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108512,"Indah tembusu menceceh bidara,","Lampai serumpun nyiur di hulu,","Terubatlah rindu mendengar suara,","Andai dialun mesranya lagu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108513,"Bertabur seri embun titisan,","Suria memancar menambah ceria,","Kabur seni mencorak lukisan,","Di mana sinar menyuluh budaya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108514,"Mawar tepus di pohon rendah,","Tampak melati hujung kekisi,","Sinarkan terus memancar indah,","Di cermin sakti mengukir ilusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108515,"Desir bersela bayu di gunung,","Tingkah bergema menular rasa,","Di tabir jendela bonda termenung,","Terguriskah sukma penawar berbisa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108516,"Mencelah pedada ditiup bayu,","Semarak di hulu bertanam rendah,","Takkan bonda berhati sayu,","Anak ditunggu pulanglah sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108517,"Kapas berbunga di tabir lurah,","Mencelah kelopak seri cempaka,","Lemas remaja di laut sejarah,","Bisakah semarak semangat merdeka?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108518,"Pohon mengkudu di balik pedada,","Merimbun pauh tepian tualang,","Semangat merdeka membara di dada,","Maju pertiwi bangsa cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108519,"Tatah suasa gelang bersendi,","Mustika bermata karangan kurnia,","Megah bangsa dijulang budi,","Mengapa harta sanjungan dunia?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108520,"Di antara seroja berseri dahlia,","Palas ditanam di hujung kota,","Takkan harta pandangan dunia,","Andai ikhlas tertanam di mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108521,"Langit membiru seri melambai,","Mencelah mentari sinar mengimba,","Ringgit diburu hutang terabai,","Agungkah pekerti dek emas melimpah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108522,"Dahan melati tegak di dataran,","Seri melur di pinggir tempua,","Masakan pekerti jadi ukuran,","Budi nan luhur kukenang jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108523,"Malam nan indah menyinar padi,","Rumpun sekata basah terendam,","Bertikam lidah menilai budi,","Ampun diminta masihkan berdendam?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108524,"Rendangan kesumba pinggiran delima,","Wangian bidara tampak berderai,","Tidakkah hamba berdendam lama,","Talian saudara tidak sanggup kulerai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108525,"Berbuah padi menguning cahaya,","Terbentang indah di tanah Betawi,","Manakah budi suluhan kerjaya,","Menyimpang amanah memandang duniawi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108526,"Pohon tepus di pohon mawar,","Pohon dahlia tepian mandi,","Budikan terus mantap di jiwa,","Membawa bahagia harmoni abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108527,"Angsana mashyur naungan kedidi,","Jati mempelas usah dibuang,","Ke mana luhur sekalung budi,","Menanti balas menagih peluang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108528,"Kasturi gugur di rumpun palas,","Mencelah berbalam kuntum melati,","Budi ditabur tak minta dibalas,","Sudah kutanam ikhlas di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108529,"Sedili di Selat Tamu,","Pulau sebarang hiliran kuala,","Luhurkah budi nak watan berilmu,","Di rantau orang peristiwa dicela?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108530,"Kilau seberang pohon keranji,","Sekata sawah batas berselang,","Dirantau orang pertiwi dipuji,","Menjaga maruah bangsa cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108531,"Tinggi cengkih tepi halaman,","Merekah kesumba rendang cemara,","Teknologi canggih meniti zaman,","Hijaukah rimba di Sahara gersang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108532,"Melambai kesumba di pohon bidara,","Mencelah cemara ranting belinjau,","Andai rimba di gersang Sahara,","Jagalah flora biar menghijau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108533,"Kenari di hulu turun ke tanah,","Merbah sekawan menyusur kuala,","Teknologi maju membantu jenayah,","Unggulkah ciptaan mengundang gejala?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108534,"Bergugur salju di pinggir desa,","Tampak pudar pepohon pauh,","Ciptaankan maju menggerak bangsa,","Jenayah dihindar sengketa dijauh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108535,"Hujan menitis tika di kuala,","Tingkah kelisa kilau merintih,","Ilmuwan saintis cipta segala,","Beranjakkah masa walau sedetik?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108536,"Rendangan tembusu bunga memutik,","Kesuma hiasan tampak di hulu,","Tidakkan waktu beranjak sedetik,","Cuma insan beranjak maju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108537,"Gemerlap cantik sinaran membiru,","Menambah seri saujana angkasa,","Setiap detik insan terburu,","Benarkah teknologi menjimat masa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108538,"Seri kerlipan bintang di desa,","Sayu purnama indah berseri,","Teknologi mapan menjimatkan bangsa,","Waktu diguna ilmuan bistari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108539,"Baris puitis elok sahaya,","Madah bistari pujangga ilusi,","Arif saintis menakluk maya,","Tersingkapkah misteri syurga firdausi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108540,"Semarak kandis pinggir perigi,","Melati pedada menghias paya,","Bijak saintis mencipta teknologi,","Pasti minda menjangkau maya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108541,"Jerih gelora di hening pagi,","Samudera terhampir biru merata,","Canggih senjata mempamer teknologi,","Mengapa sekadar menghangat sengketa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108542,"Tanam pegaga tumbuh melata,","Siantan tegak hujung semalu,","Masakan senjata hangat sengketa,","Watan nan bijak mengguna pilu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108543,"Negara merdeka sesudah perang,","Gugur pahlawan di medan laga,","Ada narkotik dan obat terlarang,","Hancurkan kehidupan anak manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108544,"Buruk besi dililit kawat,","Buruk tangan berbuat jahat,","Mabuk dan judi perbuatan maksiat,","Dikutuk Tuhan dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108545,"Masak ikan dicampur garam,","Makan berkuah pindang kunyit,","Kalau dimakan uang haram,","Jadi musibah dan penyakit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108546,"Burung merpati bulunya putih,","Terbang hinggap diatas pintu,","Kalau suami sedang letih,","Jangan pula minta yang itu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108547,"Bakar setanggi harum sekampung,","Baunya sampai ke tengah ladang,","Kalau suami sedang bingung,","Peluklah ia diatas ranjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108548,"Langit cerah hujan tak turun,","Awan gelap disapu angin,","Jika salah saya berpantun,","Mohon maaf lahir dan bathin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108549,"Anak raja menunggang kuala,","Kuda diregang tali kendali,","Kami terima calon mempelai pria,","Seperti anak kandung sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108550,"Pergi haji kekota Mekkah,","Tawaf ifadah keliling Kaabah,","Sebentar lagi akad nikah,","Semoga mendapat ridha Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108551,"Jika kapal muatan sarat,","Jangan berlayar di musim barat,","Kalau salah kami berbuat,","Mohon maaf dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108552,"Jemurlah padi di hamparan,","Daun pisang dibuat alas,","Kami serahkan pula barang antaran,","Mohon diterima dengan ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108553,"Tanah gambut lahannya retak,","Tanah datar tanam kenari,","Sambutlah calon menantu Bapak,","Anggap seperti anak sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108554,"Tanam sirih di tengah halaman,","Jangan dilalap di musim hujan,","Terima kasih atas penerimaan,","Mohon maaf atas segala kekurangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108555,"Pohon kelapa tumbuh berjajar,","Anak Tupai melompat-lompat,","Tidak lama mereka berlayar,","Akhirnya sampai ke suatu tempat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108556,"Buat cempedak jangan dikupas,","Belah saja ambil isinya,","Sungai Landak Sungai Kapuas,","Dua muara saling berjumpa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108557,"Pasanglah atap kayu belian,","Kalau tak cepat hujanpun datang,","Setelah tetap satu pilihan,","Hutan lebat segera ditebang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108558,"Salai pisang diatas bara,","Pisang batu bukan berangan,","Selesai menebang hutan belantara,","Lalu didirikan dua bangunan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108559,"Ambil balok ikatkan tali,","Jadi mainan anak-anak,","Mesjid dan keraton sebagai bukti,","Awal berdirinya Kota Pontianak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108560,"Baju berukat kain pelekat,","Elok dipakai pengantin baru,","Niat berangkat sudahlah bulat,","Hendak membuka negeri baru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108561,"Gunung kapur si batu cadas,","Hinggap bertelur burung elang,","Perahu meluncur di Sungai Kapuas,","Lalu singgah di Batu Layang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108562,"Anak beruang jangan diburu,","Anak kijan boleh dimakan,","Batu layang di sarang hantu,","Kuntilanak mereka namakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108563,"Burung elang hinggap di dahan,","Burung murai bermandi hujan,","Batu layang bukan pilihan,","Hanya sesuai untuk kuburan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108564,"Asah parang diatas batu,","Jangan senjata disalahgunakan,","Setelah berperang melawan hantu,","Perjalanan mereka pun dilanjutkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108565,"Rama-rama banyak sekali,","Terbang kehutan di siang hari,","Setelah lama dicari-cari,","Petunjuk Tuhan sudahlah pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108566,"Air mancur di dalam taman,","Air di kolam jangan keruhkan,","Akhirnya Mandor ditemukan,","Tulang belulang pun dimakamkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108567,"Putar haluan kapal berlabuh,","Lempar sauh turunkan barang,","Dua puluh delapan juni tujuh-tujuh,","Mandor menjadi makam juang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108568,"Delima merah bukannya Intan,","Batu permata disukai orang,","Pemerintah Daerah telah menetapkan,","Para suhada adalah Pejuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108569,"Buah keranji didalam peti,","Alangkah subur si asam paya,","Abang berjanji sehidup semati,","Satu kubur kita berdua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108570,"Kalau roboh kota Malaka,","Pandan di Jawa saya rebahkan,","Kalau sungguh Tuan berkata,","Badan dan nyawa saya serahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108571,"Tanjung Katong airnya biru,","Tempat nak dara mencuci muka,","Sedang sekampong hatiku rindu,","Konon pula jauh di mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108572,"Kalau abang mudik ke hulu,","Belikan saya ikan belida,","Kalau abang mati dahulu,","Tunggu saya di pintu surga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108573,"Selat Malaka laut berlimpah,","Sampan kolek menuju muara,","Tujuh purnama kita berpisah,","Abang datang membawa cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108574,"Bunga tanjung kembang bermakna,","Jatuh berderai di waktu pagi,","Kalau abang sayangkan dinda,","Jangan ditinggalkan adinda sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108575,"Negeri cina ditutup mendung,","Gunungnya tinggi di puncak biru,","Kalau adinda menjadi burung,","Abang menjadi si dahan kayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108576,"Petik kurma di tanah Mekkah,","Pohonnya tumbang tertutup debu,","Adinda rela dibawa susah,","Asalkan abang sayang selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108577,"Ikan biawan di dalam bakul,","Anak kepiting di dalam kuali,","Kalau kita pandai bergaul,","Jangan banyak caci dan maki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108578,"Batulayang makam keramat,","Tempat ziarah sambil berdoa,","Sembahyang itu kewajiban umat,","Menyembah Allah wajib hukumnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108579,"Bulan mengambang di langit biru,","Awan berarak bergumpal-gumpal,","Jangan biarkan hatimu pilu,","Kepada Allah kita tawakkal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108580,"Burung camar terbang melayang,","Burung merpati hinggap didahan,","Ajarkan anak ngaji dan sembahyang,","Agar menjadi orang beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108581,"Ambillah jala lalu tebarkan,","Ikan belanak tolong tangkapkan,","Hamba rela abang ceraikan,","Nasib anak tolong pikirkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108582,"Ikan betutu didalam sampan,","Ikan jelawat memakan umpan,","Anak itu titipan Tuhan,","Tanggung jawab di hari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108583,"Bakar dupa bakar setanggi,","Dupa Arab harum baunya,","Dahulu cinta sekarang benci,","Hamba tak tahu apa sebabnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108584,"Basah kain jangan di gantung,","Cepat jemur diatas atap,","Orang lain mendapat untung,","Hamba seorang menggantang asap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108585,"Burung nuri hinggap digunung,","Asyik makan buah cempedak,","Putus tali tempat bergantung,","Runtuh bumi tempat berpijak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108586,"Riang gembira katak melompat,","Menyambut hujan turun ke bumi,","Sembahyang Isya empat rakaat,","Tunailah sudah kewajiban sehari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108587,"Kain sutera baju berukat,","Dipakai anak gadis penari,","Rukun ketiga wajib berzakat,","Dibayar sebelum Idul Fitri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108588,"Buah langsat tidak bersisa,","Banyak dimakan anak jalanan,","Rukun ke empat ibadah puasa,","Tiga puluh hari di bulan Ramadhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108589,"Buah kurma didalam peti,","Rasanya manis seperti gula,","Rukun ke lima mengerjakan haji,","Kalau mampu pergi ke sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108590,"Terang purnama dikeliling bintang,","Lembah ngarai tersiram cahaya,","Barang bermakna tanda kasih sayang,","Kedua mempelai saling mencinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108591,"Gelombang lautan tiada bertepi,","Tengah samudera langit nan tinggi,","Barang antaran lambang ikatan hati,","Dua keluarga bersilaturahmi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108592,"Pasang joran diujung tali,","Ikan di sungai berhati hati,","Barang antaran mengandung arti,","Semoga mempelai sehidup semati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108593,"Irama Melayu senandung Deli,","Lagu Mas Merah merdu sekali,","Pengendali nafsu syahwat birahi,","Terhindar dari zinah yang keji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108594,"Air samudera rasanya asin,","Tiang bahtera berlapis baja,","Agar merasakan derita si miskin,","Marilah kita berzakat harta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108595,"Pengantin baru Raja sehari,","Didalam pesta wajah berseri,","Menolak serakah nafsu hewani,","Agar manusia berakhlaq tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108596,"Masak ketan pengganti beras,","Duduk makan beratap langit,","Perut laksana lautan luas,","Banyak makan jadi penyakit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108597,"Kota besar penduduknya padat,","Lalu lintas tiada hentinya,","Agar badan selalu sehat,","Makan sedikit tepat waktunya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108598,"Anak muda menjadi Khatib,","Rajin berdakwah siang dan malam,","Puasa itu ibadah wajib,","Diantara lima rukun islam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108599,"Selasa Rabu bilangan hari,","Hari ke enam Jumat namanya,","Puasa itu mengandung arti,","Enam hikmah ada di dalamnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108600,"Bubur putih bubur merah,","Dibuat untuk upacara,","Tanda bersyukur kepada Allah,","Karena nikmatnya tak terkira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108601,"Angin berputar badaikan tiba,","Layar terbentang bertiang gagah,","Menahan lapar haus dahaga,","Memegang teguh amanah Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108602,"Bahtera bertambat tali diikat,","Nahkoda muda cerah wajahnya,","Segala sarat adat istiadat,","Kami serahkan apa adanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108603,"Gendang rancap tembang kenangan,","Alun irama serampang dua belas,","Uang asap dan barang antaran,","Mohon diterima dengan ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108604,"Hujan senja hujan sehari,","Hujan berderai tiada hentinya,","Dengan segala kerendahan hati,","Jangan dinilai dari harganya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108605,"Anak udang dibalik batu,","Jangan ditangkap dengan jala,","Dirikan sembahyang lima waktu,","Itu dia rukun ke Dua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108606,"Kapal berlabuh lalu di tambat,","Air surut terlalu cepat,","Sembahyang subuh dua rakaat,","Doa Qunut terasa nikmat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108607,"Tidaklah payah bertanak nasi,","Arang dapur mudah didapat,","Bila matahari telah meninggi,","Sembahyang Dzuhur empat rakaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108608,"Banyak pedagang di dalam pasar,","Harga naik terlalu cepat,","Menjelang senja sembahyang Ashar,","Empat rakaat jangan terlambat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108609,"Baju kemeja leher bersulam,","Pakaian Khatib di hari Jumat,","Ketika senja berganti malam,","Sembahyang Maghrib tiga rakaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108610,"Pasang pelita di waktu malam,","Isi minyak bakar sumbunya,","Kalau kita mengaku Islam,","Tunaikan lima rukunnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108611,"Belah ketupat makan bersama,","Alangkah sedap isi dan kuahnya,","Kalimah syahadat rukun pertama,","Lidah berucap hati saksinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108612,"Patah dahan pata pelepah,","Ditiup angin pohonnya rebah,","Tiada Tuhan yang wajib disembah,","Tiada yang lain, melainkan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108613,"Burung merpati terbang tinggi,","Membawa surat bersampul merah,","Hamba bersaksi di dalam hati,","Nabi Muhammad Rasul Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108614,"Buah pepaya isinya merah,","Dibuat manisan nyaman sekali,","Percaya kepada Kitab Allah,","Haqqul Yakin di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108615,"Orang kaya banyak sedekah,","Rezeki datang dibagi-bagi,","Percaya kepada Rasul Allah,","Dari hidup sampailah mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108616,"Keris Panglima sarung berukir,","Buat menghadang bala seribu,","Dunia fana akan berakhir,","Kiamat datang menunggu waktu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108617,"Perahu terbalik mencapai tepi,","Disambar petir dimalam hari,","Buruk dan baik jika terjadi,","Namanya takdir ketentuan Illahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108618,"Putera Mahkota mandi di taman,","Memakai kain sutera bersulam,","Jika mengaku hamba beriman,","Harus yakin kepada yang enam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108619,"Kalau berat isi pikulan,","Tahanlah dengan kedua tangan,","Enam syarat Allah tentukan,","Itulah dia Rukun Iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108620,"Bibir laksana delima merekah,","Peribahasa mengandung arti,","Pertama percaya kepada Allah,","Sedikit tidak ragu di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108621,"Surat merah mengandung arti,","Suatu niat di dalam hati,","Malaikat Allah adanya pasti,","Tugasnya sudah dibagi-bagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108622,"Pria: Burung gelatik terbang tinggi,","Hinggap sepasang diatas dahan,","Wahai adik yang baik hati,","Bolehkah abang berkenalan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108623,"Wanita: Burung gelatik basah bulunya,","Hinggap sebentar di atas dahan,","Bermaksud baik apa salahnya ,","Boleh saja kita berkenalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108624,"Pria: Tinggi gelombang di laut China,","Perahu Bugis berlayar senja,","Kalau abang boleh bertanya,","Adik yang manis siapa namanya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108625,"Wanita: Buah kurma di Negeri Arab,","Enak dimakan berbuka puasa,","Abang bertanya, adik menjawab,","Nama adik Khairunnisa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108626,"Pria: Angin berhembus di waktu pagi,","Hawa sejuk nyaman terasa,","Ingin abang bertanya lagi,","Apakah sudah ada yang punya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108627,"Wanita: Jika bulan tertutup awan,","Mengapa bintang tiada kilauan,","Abangkan hanya ingin berkenalan,","Mengapa itu yang ditanyakan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108628,"Pria: Alangkah mulia bulan puasa,","Bulan ibadah penuh pahala,","Alangkah cantik Khairunnisa,","Hati abang jadi tergoda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108629,"Wanita: Emas suasa logam mulia,","Perak dan loyang lain harganya,","Adik ini si perawan desa,","Belum ada orang yang punya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108630,"Banyak kumbang di taman bunga,","Bunga sekuntum jadi rebutan,","Banyak sudah abang bertanya,","Nama abang belum disebutkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108631,"Pria: Alangkah terang bulan purnama,","Tetapi sayang tertutup awan,","Maafkan abang sampai terlupa,","Nama abang Khairuzaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108632,"Wanita: Punai terbang ke rumpun padi,","Padi terserang tikus dan hama,","Wahai abang yang baik budi,","Sekarang giliran adik bertanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108633,"Bakar mabang menjadi arang,","Arang habis besi binasa,","Kalau abang suami orang,","Jangan berlagak seperti perjaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108634,"Pria: Menebang hutan jangan sembarang,","Kayu jati mahal sekali,","Abang bukan suami orang,","Perjaka asli belum beristeri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108635,"Wanita: Alangkah besar ombak samudera,","Haluan kemudi harus dijaga,","Kalau benar abang berkata,","Adik pasti usul periksa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108636,"Pria: Orang buta jalan dituntun,","Orang bisu menari-nari,","Lelah sudah kita berpantun,","Lain waktu berjumpa lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108637,"Pohon pandan akar terjuntai,","Dipetik anak, anak sekolah,","Meskipun jenggot menyapu lantai,","Tak ada manusia yang tak salah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108638,"Anak cina membeli bedak,","Bedak dibeli dari tarakan,","Kalau tuan orang yang bijak,","Hamba salah tolong maafkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108639,"Bila ada jarum yang patah,","Jangan patahkan jarum yang lain,","Bila hamba terlanjur salah,","Janganlah diberitahu orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108640,"Ikan belanak si ikan sepat,","Dalam tempayan makan jerami,","Hamba salah sudahlah tepat,","Insya Allah tidak terulang lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108641,"Hancur tikar, dimakan rayap,","Tikar pandan, murah harganya,","Dada terbakar, darah meruap,","Mendengar abang, berbini muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108642,"Pohon tumbang di jalan baru,","Menimpa atap memecah jendela,","Ingin terbang ke langit biru,","Sayap patah apalah daya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108643,"Rusa betina masuk kekampung,","Anaknya hilang satu persatu,","Derita jiwa tidak tidak tertanggung,","Ingin kembali ke perut ibu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108644,"Buah kuini di negeri Cina,","Buah petai di tanah seberang,","Hamba ini ibarat bunga,","Segar dipakai layu dibuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108645,"Ambil benang jahitlah tilam,","Tilam berisi kabu-kabu,","Kalau dikenang masa yang silam,","Air mata jatuh ke pangku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108646,"Anak raja pergi berburu,","Melepas panah dengan Bismillah,","Untung nasib siapa yang tahu,","Semua tergantung kepada Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108647,"Jika hujan lebat sekali,","Biarkan hamba berselimut kain,","Kalau tuan lepas sekali,","Biar hamba cari yang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108648,"Angin berhembus di malam gelap,","Sayup terdengar dendang dan lagu,","Hangus kampung nampak asap,","Terbakar hati siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108649,"Ambil cermin kacalah muka,","Rambut basah keringkan segera,","Surat Yaasin kalau dibaca,","InsyaAllah hapus segala dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108650,"Buruk muka cermin dibelah,","Itu dia kata pepatah,","Jangan tuan membuat fitnah,","Mendatangkan dosa dan musibah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108651,"Masak pulut dicampur santan,","Masukkan gula dalam kuali,","Kalau takut gangguan setan,","Baca selalu Ayatul Kursi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108652,"Tanam jagung ditanah subur,","Sawah ladang ditanam padi,","Kalau tuan sombong takabur,","Orang lain menjauhkan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108653,"Banyak awan di langit tinggi,","Pertanda hari kan turun hujan,","Banyak kawan banyak rezeki,","Tinggi hati harap dijauhkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108654,"Angin malam mengusir senja,","Bintang Timur memancar cahaya,","Rukun islam adalah lima,","Sembahyang itu tiang agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108655,"Takkan balik hujan ke langit,","Biar matahari membakar bumi,","Perawan sekarang jangan dipingit,","Biar cepat mendapat laki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108656,"Jangan dimakan pepaya mentali,","Kulitnya hijau bergetah-getah,","Supaya hartamu menjadi berkah,","Rajin-rajinlah engkau sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108657,"Sepatu bola telapak getah,","Dipakai bertanding sepak raga,","Menolak bala dengan sedekah,","Jadi pendingin api neraka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108658,"Adat tua menanggung ragam,","Adat muda menanggung rindu,","Kalau wanita sedang mengidam,","Mertua lewat disangka hantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108659,"Pohon limau tanam di galang,","Daun saga dibuat obat,","Harimau marah terjun ke jurang,","Mendengar dunia akan kiamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108660,"Pisang rebus didalam panci,","Panci sumbing jangan di jerang,","Semenjak tikus naik haji,","Banyak kucing yang sembahyang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108661,"Kemeja hitam digantung dasi,","Baru dipinjam dipakai pesta,","Ketika ayam jadi polisi,","Musang dihutan masuk penjara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108662,"Pohon manggis batangnya patah,","Kilat menyambar di pagi hari,","Tikus menangis di tengah sawah,","Melihat ular terbang tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108663,"Apa guna pasang pelita,","Kalau tidak tersumbu kain,","Apa guna menjalin cinta,","Jika tidak segera kahwin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108664,"Hujan emas di negeri orang,","Hujan batu di negeri sendiri,","Jangan cemas melepas abang,","Takkanlah abang beristeri lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108665,"Tiram dan siput di tepi karang,","Ketam mati atas perahu,","Karam di laut boleh direnang,","Karam di hati siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108666,"Pangeran muda pergi merantau,","Laut luas tiada bertepi,","Hati adinda rindu dan risau,","Bilakah abang datang kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108667,"Anak tikus lari kehutan,","Takut disengat si kumbang jati,","Hamba kurus bukan tak makan,","Karena mengingat si jantung hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108668,"Bunga melati mekar di taman,","Bunga cempaka dalam jambangan,","Patah hati tumbang harapan,","Cinta hampa tinggal kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108669,"Malam ini malam Jumat,","Bakar dupa diatas peti,","Ku tepok bantal manggil semangat,","Agar menjelma di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108670,"Kalau padi katakan padi,","Jangan aku tertampi-tampi,","Kalau jadi katakan jadi,","Jangan aku ternanti-nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108671,"Galah bambu tajam ujungnya,","Galah kecil dibuat joran,","Kalu tahu begini jadinya,","Menyesal rasnya berkenalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108672,"Hati-hati menanam kelapa,","Ulat sembunyi dalam pelepah,","Hati-hati memadu cinta,","Kalau tak jodoh, jadi musibah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108673,"Sekapur sirih seulas pinang,","Pinang muda lebat daunnya,","Putus kasih jangan dikenang,","Cari yang lain lain buat gantinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108674,"Layang jatuh di rakit,","Rakit ditambat tali kawat,","Kasih sayangku bukan sedikit,","Dari dunia sampai akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108675,"Tupai beranak di lumbung padi,","Satu jantan satu betina,","Wahai adik yang baik hati,","Apakah obat penyakit cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108676,"Naik perahu mudik ke hulu,","Parahu karam di tengah sungai,","Tak sanggup hati menahan rindu,","Bantal dipeluk hancur berderai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108677,"Putus benang boleh dirajut,","Benang dibuat tali kelayang,","Hati terkenang mulut menyebut,","Dalam mimpi terbayang-bayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108678,"Menangkap ikan denganlah tanggok,","Supaya tak lepas si ikan teri,","Nasib badan bagaikan pungguk,","Rindukan bulan di langit tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108679,"Pohon mempelam tanam di kebun,","Anak tengkuyung memanjat dahan,","Malam hari berselimut embun,","Bila siang berpayung awan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108680,"Naik pedati ke batu jajar,","Barang diangkut dengan kereta,","Takkan lari gunung dikejar,","Hilang kabut tampaklah dia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108681,"Buang sabut ambil tempurung,","Tempurung kelapa bermata tiga,","Garam di laut asam di gunung,","Dalam belanga saling berjumpa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108682,"Lomba sampan boleh bertaruh,","Sampan naga mendapat juara,","Bukan tanaman kami tak mau tumbuh,","Kiranya bumi tak mau menerima", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108683,"Jalan jalan ke Tanjung Pandan,","Disana banyak gadis pingitan,","Kalau untung tuah di badan,","Pasir digenggam menjadi intan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108684,"Anak raja berpasang pasangan,","Batu pualam dibuat gelang,","Kalau memang rezeki musang,","Takkan ayam disambar elang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108685,"Kuda pedati menapak-napak,","Kuda di kandang menendang-nendang,","Kalau padi hanya secupak,","Takkan menjadi beras segantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108686,"Ambil galah disambung-sambung,","Untuk menjolok pohon berbuah,","Bukan salah bunda mengandung,","Memang badan tak bertuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108687,"Habis rotan ambillah akar,","Akar jati dilapis perak,","Mengayun tongkat membunuh ular,","Ular tak mati rumput berserak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108688,"Tuanku Pangeran berbadan gemuk,","Datok panglima dadanya lebar,","Kalau marah kepada nyamuk,","Jangan pula kelambu dibakar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108689,"Bunga melati tumbuh di tanjung,","Bunga raya tumbuh di pantai,","Niat hati memeluk gunung,","Apa daya tangan tak sampai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108690,"Beras pulot masak dikukus,","Gula dan wijan ditabur-tabur,","Hujan lebat pancorpun bagus,","Sayang pula tempayan bocor.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108691,"Tuan puteri risau menanti,","Rindu ditahan, menjadi penyakit,","Biar mentari membakar bumi,","Takkan hujan kembali ke langit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108692,"Dari Pahang ke negeri Kedah,","Sampai di Kedah berjalan kaki,","Kalau engkau sudah meludah,","Jangan pula dijilat kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108693,"Lebat daunnya si kapuk randu,","Kapuk dipakai mengisi tilam,","Adat muda menanggung rindu,","Adat tua menanggung ragam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108694,"Kupas nenas kupaslah bawang,","Untuk membuat sayur paceri,","Kusangka panas sampai ke petang,","Rupanya hujan di tengahari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108695,"Itik serati pulang ke kandang,","Anaknta tinggal di tepi sawah,","Pipi seperti pauh dilayang,","Bibir laksana delima merekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108696,"Ikan betutu bukanlah toman,","Ikan belanak harganya murah,","Kasih ibu sepanjang zaman,","Kasih anak sepanjang galah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108697,"Dari Malaka ke Negeri Kedah,","Singgah di pekan membeli nasi,","Kalau berkata-kata peliharalah lidah,","Bila berjalan peliharalah kaki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108698,"Orang Arab menghitung duit,","Duit disimpan hemat-hematkan,","Jangan mengharap guntur di langit,","Air tempayan dicurah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108699,"Biduk lalu kiambang bertaut,","Angin berhembus layar terkembang,","Setetes embun jatuh ke laut,","Tidak disangka jadi gelombang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108700,"Udang galah di dalam bakul,","Udang ebi di buat sambal,","Sayang anak dipukul-pukul,","Sayang isteri ditinggal-tinggal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108701,"Buah labu tolong kupaskan,","Sayur santan coba buatkan,","Anak dipangku dilepaskan,","Kera di hutan di susukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108702,"Juadah talam diatas pahar,","Pahar terletak diatas meja,","Isi di dalam biji di luar,","Buah apa cobalah terka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108703,"Ikan gabus memakan umpan,","Kail diikat tali dan joran,","Dibungkus bukan barang kiriman,","Ditanam bukan tumbuh-tumbuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108704,"Telor biawan mahal harganya,","Jarang dimasak bercampur rempah,","Kalau tuan pandai menerka,","Binatang apa tidak berlidah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108705,"Carilah sabut kelapa bali,","Kelapa parut di dalam talam,","Sekali sebut ia berlari,","Dua kali sebut ia pun diam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108706,"Bunyi guruh angin menderu,","Petir kilat di waktu petang,","Kalau jauh ia pun diburu,","Setelah dapat lalu ditendang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108707,"Dibalik awan bulan mengambang,","Bila dipandang hatipun bimbang,","Rupanya Tuan bertanam kembang,","Kembang yang satu memikat kumbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108708,"Puncak bukit menjulang tinggi,","Jalannya licin sulit didaki,","Kami diutus datang kemari,","Maksud memetik sekuntum melati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108709,"Bulan mengintip di balik awan,","Mari bernyanyi sahut-sahutan,","Kalau tuan tidak keberatan,","Bolehlah kami mendapat jawaban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108710,"Alangkah tinggi pohon kelapa,","Lebih tinggi pohon durian,","Kalau kami salah menyapa,","Kami mohon tuan maafkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108711,"Dupa mengalun di bara panas,","Asapnya harum naik ke atas,","Bertahun-tahun Jepang mengganas,","Akhirnya Tuhan menentukan batas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108712,"Mata air di dalam telaga,","Jatuh jarum terlihat nyata,","Petanda akhir angkara murka,","Bom atom jatuh di Hiroshima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108713,"Buah nona, buah kecapi,","Sekarang sudah langka sekali,","Hiroshima dan Nagasaki,","Musnah jadi lautan api.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108714,"Hari petang matahari surut,","Senja tiba malam menyambut,","Ketika Jepang bertekuk lutut,","Indonesia merdeka sudahlah patut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108715,"Samudera dan lautan tiada bertepi,","Bahtera meluncur di malam hari,","Kemana korban akan dicari,","Hutan Mandor masih misteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108716,"Besar berudu menjadi katak,","Katak sawah ditangkap orang,","Mereka dituduh sebagai pemberontak,","Hendak melawan serdadu Jepang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108717,"Ikan belanak di celah batu,","Ikan jelawat dimasak kari,","Sultan Pontianak di saat itu,","Almarhum Syarif Muhammad Alkadrie.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108718,"Hari panas hujan berlalu,","Jalan berlumpur kering dan rata,","Jepang mengganas tak pandang bulu,","Sultan gugur bersama keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108719,"Kalau bertanak, masaklah nasi,","Makan berkuah sayur nangka,","Tinggal anak, tinggallah isteri,","Yatim berurai air mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108720,"Awan kelabu langit berguruh,","Guntur dan hujan terus mengucur,","Puluhan ribu rakyat terbunuh,","Banyak terkubur di hutan Mandor.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108721,"Ambil sarung ayunkan anak,","Biar tertidur dalam buaian,","Kalau berkunjung ke Kota Pontianak,","Makam Mandor jangan lupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108722,"Kalau gugur buah yang merah,","Pohonnya jangan sering dipanjat,","Makam Mandor bukti sejarah,","Perjuangan rakyat Kalimantan Barat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108723,"Masuk kehutan menebang kayu,","Lalu muatkan ke kapal dagang,","Puluhan ribu korban terbujur kaku,","Akibat keganasan serdadu Jepang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108724,"Anak kelinci melompat-lompat,","Bulunya basah tersiram hujan,","Itu terjadi tahun empat-empat,","Negeri tercinta dalam jajahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108725,"Burung elang paruhnya tajam,","Berputar-putar diatas atap,","Serdadu Jepang sangatlah kejam,","Orang pintar semua ditangkap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108726,"Belah ketupat di pagi hari,","Hujan pagi perutpun lapar,","Setelah empat tahun beristeri,","Abdurrahman pergi berlayar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108727,"Ikan bakar disukai orang,","Baunya sampai ke awang-awang,","Jauh berlayar ke Negeri Sebrang,","Hingga ke Banjar dan Palembang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108728,"Ada Pahlawan Datok Hang Tuah,","Tanah Melaka punya cerita,","Ketika Abdurrahman pulang ke Mempawah,","Ayahandanya Habib Husin telah tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108729,"Di rumpun sawah katak melompat,","Mencari makan seekor lalat,","Setahun sudah Ayahandanya wafat,","Abdurrahman dan keluarga bermuafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108730,"Makan berkuah si gulai ikan,","Ikan lulu dimasak santan,","Kerajaan Mempawah akan ditinggalkan,","Enam belas perahu disiapkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108731,"Kalau sawah dijadikan kebun,","Tanam kelapa barang selusin,","Raja mempawah Daeng Menambon,","Gembira menerima Habib Husin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108732,"Delima merah batu bertuah,","Diikat cincin si Tuan Puteri,","Berberapa lama di Kota Mempawah,","Habib Husin diangkat sebagai Patih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108733,"Isi dadar gula kelapa,","Nyaman dimakan berbuka puasa,","Waktu berputar berganti masa,","Syarif Abdurrahman meningkat dewasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108734,"Tepung beras dibuat sagon,","Jangan dimakan sambil berdiri,","Ketika berusia delapan belas tahun,","Abdurrahman diberi isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108735,"Delman dan pedati kereta kuda,","Kuda pedati bulunya putih,","Abdurrahman menjadi menantu Raja,","Isterinya Utin Canderamidi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108736,"Jepin menggetar lantai papan,","Tari dan nyanyi lagu bergema,","Habib Husin berlayar ke negeri Matan,","Jadi mufti dan guru agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108737,"Masak santan sayur paceri,","Kari dan dalca hidangan pesta,","Di negri Matan Habib Husin beristri,","Puteri Raja bernama Nyai Tua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108738,"Jangan salah meningkah irama,","Tari jepin budaya lama,","Setelah menikah sekian lama,","Habib Husin dikurniai beberapa putera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108739,"Alangkah masin air samudera,","Ketentuan Tuhan Yang Maha Kuasa,","Habib Husin berputera lima,","Abdurrahman anak tertua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108740,"Dalam rumah baca Al Quran,","Surah Yaasin dan doa Aqasyah,","Tidak lama di Negri Matan,","Habib Husin pun pindah ke Mempawah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108741,"Onta beranak di Tanah Mekkah,","Anaknya jantan, bulunya jingga,","Kota Pontianak punya sejarah,","Sultan Abdurrahman yang membukanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108742,"Padang pasir berselimut debu,","Panas membakar gurun sahara,","Kalau Tuan kepingin tahu,","Cobalah dengar jalan ceritanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108743,"Batu pualam mahal harganya,","Disimpan anak Raja Malaka,","Kampung dalam sekarang namanya,","Di situ Pontianak asal mulanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108744,"Akek zaman batu permata,","Ikat di cincin indah rupanya,","Abdurrahman Sultan yang mulia,","Habib Husin nama Ayahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108745,"Ikat cincin batu delima,","Dalam gelap berkilau cahayanya,","Habib Husin penyebar agama,","Negeri Arab asal usulnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108746,"Pisau yang tumpul sudah diasah,","Dipakai memotong daging sapi,","Setelah berkumpul kita berpisah,","Semoga dapat berjumpa lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108747,"Makan sahur berlauk pindang,","Ikan merah atau tenggiri,","Kalau ada umur panjang,","Insya Allah berkumpul lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108748,"Ibarat bulan dibelai awan,","Pucat cahaya muka perawan,","Selamat jalan aduhai tuan,","Selamat tiba di tempat tujuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108749,"Kawat dipintal buat pengangkat,","Suruh menimba air kali,","Selamat tinggal wahai sahabat,","Jauh dimata dekat dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108750,"Dari hulu mengangkat kayu,","Sampai ke kota lalu dipikul,","Dahulu kita pernah bertemu,","Lama pula kita berkumpul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108751,"Kalau ada pisau yang tumpul,","Cepat-cepatlah diasah,","Memang ada masa berkumpul,","Ada pula saat berpisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108752,"Ditempa diasah besi waja,","Lalu dibuat pisau belati,","Berjumpa berpisah sudah biasa,","Jangan pula bersedih hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108753,"Nasi kebuli warnanya merah,","Nyaman dimakan di waktu pagi,","Barangkali saya banyak bersalah,","Tolong tuan maafkan kami.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108754,"Kawat direntang buat jemuran,","Ambil galah tolong tegakkan,","Selamat datang kami ucapkan,","Alhamdulillah, tak ada halangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108755,"Buah kelapa dibelah belah,","Tempurung dan sabut semua berguna,","Sudah jodoh kehendak Allah,","Kami menyambut dengan gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108756,"Buah cermai coba carikan,","Campur gula buat manisan,","Calon mempelai Bapak serahkan,","Insya Allah segera kita nikahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108757,"Burung Enggang hidup di hutan,","Burung Dara hidup di taman,","Kami terima barang antaran,","Kecil tapak tangan, nyiru kami tadahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108758,"Bukan kacang sembarang kacang,","Lain kacang lain kedelai,","Bukan datang sembarang datang,","Datang membawa calon mempelai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108759,"Pucuk pauh ranting bercabang,","Kumbang merayap riang gembira,","Dari jauh calon mempelai pria datang,","Terbang dengan Sayap Asmara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108760,"Kering laut keringlah samudera,","Jarak yang jauh menjadi dekat,","Hati terpaut cinta asmara,","Kasih sayang tak dapat digugat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108761,"Buah nangka buah pepaya,","Campur santan dengan es batu,","Kami serahkan calon mempelai pria,","Mohon diterima sebagai menantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108762,"Cenderawasih si burung Irian,","Konon hinggap berpasang-pasangan,","Terima kasih atas perhatian,","Mohon maaf atas segala kekurangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108763,"Plih jerami derai-deraikan,","Biji padi menjadi beras,","Terima kasih kami sampaikan,","Dari hati yang tulus ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108764,"Padi ketan padi simpanan,","Pilih padi,jerami sisihkan,","Budi Tuan takkan dilupakan,","Terima kasih kami ucapkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108765,"Bunyi gendang mari berdendang,","Hati sedih coba lenyapkan,","Kami mengundang Tuan pun datang,","Terima kasih kami ucapkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108766,"Pintas jalan ke Tanjung Intan,","Di sana petani tanam selasih,","Atas perhatian dan kehadiran,","Kami ucapkan terima kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108767,"Tanam sirih diatas bukit,","Bukit berselimut embun pagi,","Terima kasih kami bukan sedikit,","Dari mulut sampai ke hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108768,"Anak cina dayung perahu,","Perahu bidar muatan sarat,","Wahai Anakda pengantin baru,","Cobalah dengar ini nasihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108769,"Ikan teri dari bengkulu,","Ikan tenggiri dalam laut,","Kalau isteri pandai merayu,","Tentu suami bertekuk lutut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108770,"Nasi lemak santan berminyak,","Dimakan pagi, hari minggu,","Beranak boleh, banyak banyak,","Kalau beristri cukuplah satu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108771,"Tuan puteri berbaju merah,","Sayang ditimpa hujan gerimis,","Jika suami sedang marah,","Cepatlah pakai baju yang tipis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108772,"Hitam lumut di batu karang,","Ketam mati dilanda gelombang,","Karam dilaut dapat kurenang,","Karam di hati dinda kupinang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108773,"Tepi laut,tepi samudera,","Air pasang kapal merapat,","Hati terpaut cinta asmara,","kasih sayang sudah melekat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108774,"Sungguh jauh ikan berenang,","Berenang sampai ke muara,","Dari jauh kakanda datang,","Terbang dengan Sayap Asmara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108775,"Bukan lenggang sembarang lenggang,","Lenggang Mainang Sri Langkat,","Bukan datang sembarang datang,","Datang membawa cincin pengikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108776,"Tari dan jepin dimalam hari,","Pukul rebana berganti-ganti,","Terimalah cincin pakai di jari,","Sebagai tanda ikatan di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108777,"Bunga melati dalam jambangan,","Bunga kenanga kembang merekah,","Hari ini, hari pertunangan,","Tak lama lagi tentu menikah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108778,"Kupas-kupas kelapa puan,","Kelapa parut jadikan santan,","Minuman keras diharamkan Tuhan,","Masuk ke perut memabukkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108779,"Duduk-duduk di kursi goyang,","Minum susu dalam gelas,","Kalau mabuk hilang ingatan,","Tingkah laku jadi tak waras.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108780,"Lautan luas, ombak bergulung,","Petir dan kilat sambung menyambung,","Minuman keras merusak jantung,","Pikiran sesat khayal membumbung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108781,"Jika rakit hendak ditambat,","Tambatlah dengan tali dan rotan,","Jika sakit pergi berobat,","Obat jangan salah digunakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108782,"Dari hulu pergi kedusun,","Naik perahu membawa barang,","Orang Melayu pandai berpantun,","Dari dahulu hingga sekarang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108783,"Tenun Sutera selendang delima,","Buat menari si Bujang Dara,","Pantun adalah Sastera Nan Lama,","Alat komunikasi nenek moyang kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108784,"Banyak ukiran dalam galeri,","Dipajang-pajang bersusun-susun,","Buah pikiran dan isi hati,","Diucapkan orang dengan pantun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108785,"Bangun rumah dinegeri asal,","Banyak biaya mendirikannya,","Pantun adalan seni tradisional,","Perlu kita melestarikannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108786,"Intan permata menghias puadai,","Diikat erat emas dan suasa,","Bukan hamba cerdik dan pandai,","Hasrat mengangkat budaya bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108787,"Kain pelekat berwarna-warna,","Tenun sutera coba serahkan,","Ingin mengangkat budaya bangsa,","Pantun pusaka hamba sembahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108788,"Gunung daik bercabang tiga, anak sepat di dalam kuali,","Dalam berseri bulan, suka benar pungguk hendak terbang,","Buat baik berpada-pada, buat jahat jangan sekali,","Manis jangan terus di telan, pahit jangan terus dibuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108789,"Intan permata menghias puadai","Diikat erat emas dan suasa","Bukan hamba cerdik dan pandai","Hasrat mengangkat budaya bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108790,"Kain pelekat berwarna-warna","Tenun sutera coba serahkan","Ingin mengangkat budaya bangsa","Pantun pusaka hamba sembahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108791,"Pantun pusaka hamba sembahkan","Hasrat mengangkat budaya bangsa","Khilaf dan salah tolong maafkan","Hamba yang daif manusia biasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108792,"Kita hidup mestilah sabar,","Putar belit jangan didengar,","Perpecahan kita jadi gusar,","Segala pembangunan akan hambar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108793,"Cakap jangan berdegar-degar,","Pendapat orang tidak di dengar,","Rukun negara mestilah di war-war,","Supaya kesatuan tidak berputar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108794,"Sebagai pemimpin hatinya jernih,","Fikiran lurus hatinya bersih,","Memandang rakyat matanya jernih,","Memberi pertolongan tidak selisih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108795,"Bersatu datangnya dari hati,","Jangan pura-pura puas hati,","Ikutlah hati akan mati,","Ikutkan rasa tidak akan dipeduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108796,"Negara kita menuju maju,","Itulah matlamat yang kita tuju,","Buang tabiat yang tidak perlu,","Supaya kehidupan tidak bersetoruh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108797,"Apa lagi tidak puas hati,","Sehingga kebaikan tidak dipeduli,","Mohon bersihkan hati dan diri,","Supaya hidup sentiasa berbakti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108798,"Ikut suka, luka,","Ikut hati, mati,","Ikut rasa, binasa,","Ikut mata, lata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108799,"Anak harum mudik berenam,","Lumba-lumba menyusul tebing,","Sama mencecah si hati kuman","Susah senang sama membimbing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108800,"Anak dara mengandam dahi,","Rambut ditanam di tepi telaga,","Anjing biasa makan tahi,","Kalau tak makan cium juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108801,"Anak buaya pergi bertenung","Singgah ke lubuk memakan bangkai","Hendak hati memeluk gunung","Gunung dipeluk tangan tak sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108802,"Air pasang menanam bayam","Buyung diisi di dalam loyang","Jangan diambil resmi ayam","Bertelur sebiji riuh sekampung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108803,"Bersabar giliran adalah setia","Menyimpan kuku tajam perkasa","Andainya hilang dua perkara","Hilanglah bangsa rosaklah Negara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108804,"Duhai hati,","Bantulah aku utk terus begini,","Menjadi hamba yang tidak mengerti,","Keseronokan dunia yg hanya ilusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108805,"Duhai hati,","Tangkislah segala godaan duniawi,","Hindarlah dari api yang menanti,","Agar aku peroleh syurgawi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108806,"Ya Allah Tuhan yang kudrat","Jauhkan kami dari maksiat","Minta senangkan dunia akhirat","Alhamdulillah nazam pun tamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108807,"Telah selesai kami syairkan","Kami meminta serta selamatkan","Nasihat kami jangan dimudahkan","Seumur hidup anak ingatkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108808,"Tamatlah sudah nazam disyairkan","Sanak saudara kami mengayunkan","Kepada Allah kami pohonkan","Supaya bahagiaanak budiman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108809,"Ya Allah Malikul Rabbi","Limpah dan makmur sehari-hari","Sihatkan badan terangkan hati","Budak ini dimudahkan rezeki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108810,"Ya Allah Malikul Rabbi","Limpah dan makmur sehari-hari","Sihatkan badan terangkan hati","Budak ini dimudahkan rezeki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108811,"Ya Allah Malikul Manan","Doa kami minta perkenankan","Siang dan malam sepanjanglah zaman","Bala dan fitnah minta jauhkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108812,"Ya Allah Malikul Rahman","Budak ini tetapkan iman","Amal ibadat minta kuatkan","Syaitan dan iblis minta jauhkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108813,"Ya Allah Malikul Izzati","Budak ini tetapkan hati","Minta kurniakan pangkat yang tinggi","Negeri akhirat engkau terpuji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108814,"Ya Allah Malikul Jabbar","Budak ini cepatlah besar","Jauhkan daripada kata-kata mungkar","Dunia akhirat supaya pintar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108815,"Ya Allah Malikul Rabbi","Limpah dan makmur sehari-hari","Sihatkan badan terangkan hati","Budak ini dimudahkan rezeki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108816,"Wahai anakku kami ayunkan","Engkaulah ini kami doakan","Umur yang pendek minta panjangkan","Rezeki yang halal minta mudahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108817,"Ya Allah Malikulidah","Budak ini bersarnya tuah","Siang dan malam makin bertambah","Sehinggalah sampai ia berpindah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108818,"Wahai anakku muda jauhari","Pesanan kami engkau ingati","Engkau masukkan ke dalam hati","Jangan ditaruh di ibu kaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108819,"Kami mengayun terlalu banyak","Supaya tidurmu bertambah nyenyak","Kami berpesan masukkah ke otak","Dibawa berjalan jangan tercampak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108820,"Wahai anakku di dalam ayunan","Kami berpesan engkau ingatkan","Di atas kepalamu engkau junjungkan","Di dalam hatimu engkau taruhkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108821,"Wahai anakku hendaklah ingat","Janganlah diikut iblis laknat","Kerjakan olehmu amal dan ibadat","Engkau jauhkan sekelian maksiat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108822,"Jikalau anak tidak mengikut","Pengajaran ibumu muka berkerut","Masa nak mati kita terkejut","Di dalam Al Quran sudah tersebut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108823,"Jika anak tanda derhaka","Amanah dan pesan dipalingkan muka","Mengikut pekerjaan iblis celaka","Seteru Allah isi neraka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108824,"Jikalau ada dosa kesalahan","Kepada suamimu minta maafkan","Jikalau suamim tidak maafkan","Inilah menjadi dosa berpanjangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108825,"Jikalau enkau bersuamilah sudah","Setiap hari suami disembah","Perkataan suamimu jangalah dibantah","Walaupun benar ataupun salah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108826,"Wahai anaku yang cemerlang","Nerakalah itu hangat bukan kepalang","Tersentuh ked aging sampai ke tulang","Jerit dan tangis berulang-ulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108827,"Barang siapa melawan suami","Terlalulah sakit rasanya hati","Mukanya hitam seperti peri","Di dalam neraka tempat berhenti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108828,"Wahai anakku yang setiawan","Engkaulah ini anak perempuan","Jikalaulah ada jodoh pertemuan","Suamimu itu jangan dilawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108829,"Kalaulah terang jalan nak mati","Baharulah kita bersuka hati","Kepada Tuhan engkau terpuji","Mendapatlah engkau syurga yang tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108830,"Jikalau mendapat ilmu yang teguh","Engkau amalkan bersungguh-sungguh","Tutuplah pintu neraka yang tujuh","Teranglah jalan seperti suluh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108831,"Jikalau dapat ilmu yang setia","Serta engkau yakin dan percaya","Di dalam akhirat negeri yang mulia","Duduk di dalam pangkuan auliyaa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108832,"Jikalau menuntut engkau mendapat","Terpujilah engkau dunia akhirat","Berhimpun sekelian handai sahabat","Mana yang jauh bertambah dekat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108833,"Pengajaran bapamu jia diikut","Serta dimasukkan ke dalam perut","Bawakan olehmu pergi menuntut","Supaya mendapat apa yang dimaksud", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108834,"Jika sudah engkau pun besar","Pengajaran ibumu hendaklah didengar","Perkataan bapamu janganlah ingkar","Ituah menjadi kota dan pagar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108835,"Jikalau anak tanda bahagia","Amanah dan pesan dipeganglah dia","Walaupun miskin ataupun kaya","Diperbuatnya juga sehabis-habis daya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108836,"Kitab Al Quran dikalau dhabisi","Disuruhlah pula pergi ke haji","Pergi menjejak tanah yang suci","Supaya terbuang perangai yang keji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108837,"Jikalau engkau pandai berkitab","Serta dengan jaw dan Arab","Baharulah ibumu berhati tetap","Makan dan minum baharulah sedap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108838,"Jikalau engka tamat mengaji","Hati ibumu besar sekali","Tidak diberi ke sana sini","Sehinggalah kitab pula dikaji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108839,"Dari kecil dipeliharakan ibumu","Sehinggalah sampai sudaha umurmu","Diserahkan mengaji ke hilir dan ke hulu","Kerana besar hajat ibumu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108840,"Jikalau datang petir dan ribut","Rabunan dibakar engkau dibarut","Di dalam hati terlalu takut","Memeliharakan engkau jangan terkejut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108841,"Dilabuhkan tabir rebut pun lalu","Pelita terpasang di luar kelambu","Sembur dan barut bertalu-talu","Meminta jauhkan syaitan dan hantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108842,"Ayuhai anakku muda bangsawan","Pengajaran ibumu jangan dilawan","Dipeliharakan daripada rebut dan taufan","Takut terkena penyakit sawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108843,"Ayuhai anakku jangan dibantah","Ibumu memeliharakan terlalu susah","Dialih ke kiri kanan pun basah","Habis berlumur kencing dan muntah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108844,"Sudah dipakaikan lalu dikamat","Minta doa supa selamat","Ingat pesan Nabi Muhammad","Di atas dunia mengerjakan syariat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108845,"Tatkala engkau jatuh ke lantai","Dengan segera bidan mencapai","Sudah dimandi lalu dipakai","Tinggallah ibumu lemah gemalai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108846,"Dikandungkan ibumu Sembilan bulan","Nasi dan air tiada tertelan","Memperanakan engkau (anak) berapa kesakitan","Terkadang bercerai nyawa dan badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108847,"Setelah sampai saat dan waktu","Datanglah nyawa lalu bersatu","Di dalam tubuh tempatnya itu","Hawa dan nafas sudah berlaku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108848,"Empatlah bulan sampailah tuan","Sudah menjadi kaki dan tangan","Cukuplah dengan sifat sekelian","Nyawanya lagi yang belum didatangkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108849,"Setelah sampai delapan puluh hari","Alaqah namamu pula diberi","Sehinggalah sampai seratus dua puluh hari","Madghah pula konon dinamaii", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108850,"Setelah turun Rahim bapamu","Ke dalamlah batin Rahim ibumu","Empat puluh hari Nasqah namamu","Di situlah mulai pantang ibumu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108851,"Kus semangat putraku tuan","Jangan tergamang dalam ayunan","Dipanggilkan kami orang sekelian","Ibu bapamu minta ayunkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108852,"Bismillah itu permulaan pertama","Zat dan sifat ada bersama","Keadaan zat mengatakan sama","Kaum kerabat sedialah lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108853,"Akulah bola tergantung tinggi,","Menjadi pujaan seekor burung;","Apabila aku tiada lagi,","Gelap gelita hidup pun murung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108854,"Tegur menegur amalan terpuji,","Hindarakan segala amalan keji,","Jangan mencaci sesama sendiri,","Kelak hidup sentiasa dibenci", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108855,"Tegur menegur amalan terpuji,","Hindarakan segala amalan keji,","Jangan mencaci sesama sendiri,","Kelak hidup sentiasa dibenci", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108856,"Berbilang bangsa berpadu erat,","Jiran tetangga harus sepakat,","Segala usaha biar muafakat,","Agar hidup menjadi berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108857,"Dunia itu yang indah terindah pun ia,","Hina juga ia lagi termulia,","Bahawa yang tiada mengenal dunia,","Bahawa hidupnya itu adalah sia-sia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108858,"Ikan yang berenang dan menyelam,","Menyusuri, merentasi lautan dalam,","Tidak ia masin mahupun masam,","Sedangkan ia dikelilingi air garam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108859,"Ikan-ikan yang berenang di laut,","Tiada takut ia pada pasang dan surut,","Tiada gentar ia pada ajal dan maut,","Meskipun selalu dipukul ribut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108860,"Peri permulaan keadaan insan,","Itulah ingatkan dirimu yang berirfan,","Supaya mengetahui perinya ini,","Dengan takdir lagi dengan ihsan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108861,"Ikan-ikan yang berenang,","Tiada takut ia pada pasang dan surut,","Tiada gentar ia pada ajal dan maut,","Meskipun dipukul ribut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108862,"Panorama di dunia maya,","Maka tercenganglah manusia,","Seebab terkadang ia terhina,","Dan terkadang lagi termulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108863,"Tujuh hari bulan rejangnya tikus,","Tikus jantan masuk penjara,","Kalau terkenang sifat yang bagus,","Hilanglah akal lenyap bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108864,"Tikus jantan masuk penjara,","Penjara besar besikur sani,","Hilanglah akal lenyap bicara,","Dendam siapa selaku ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108865,"Penjara besar-besar besikur sani,","Perbuatan Maharaja Jaya Dewata,","Dendaru siapa selaku ini,","Tidur berendam airnya mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108866,"Gaji pun dipotong kerja-kerja ditambah","Tempat pekerjaan ditukar dipindah","Di manakah hati tak gundah","Kerja yang bertiga seorang sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108867,"Yang Dipertuan Besar negeri Benggala","Yang terlebih mulianya dari segala","Beberapa taklukan rakyat dan bala","Berkuasa di tanah India segala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108868,"Seumpama ribut taufan yang menyala","Kilat dan petir bersama segala","Meniup kapal-kapal perahu-perahu segala","Berhanyutan tiada ketahuan hala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108869,"Gaji pon dipotong kerja-kerja ditambah","Tempat pekerjaan ditukar dipindah","Dimanakah hati sekalian tak gundah","Kerja yang bertiga seorang sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108870,"Mendatangkan perintah dari Benggala","Di atas kita sekalian segala","Selaku api yang amat menyala","Membakarkan hati tidak berkala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108871,"Dengar cakap enggang,","Makan buah beluluk,","Dengar cakap orang,","Terjun masuk lubuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108872,"Dari Hong Kong pergi ke Yaman,","Singgah sebentar ke Inderagiri,","Tinggallah kampung tinggal halaman,","Tinggallah tempat bekasku sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108873,"Di sana padi di sini padi,","Itulah nama sawah dan bendang,","Di sana budi disini budi,","Barulah sempurna bernama orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108874,"Anak merbah terlompat-lompat,","Anak indong ketitiran,","Yang dilelah tiada dapat,","Yang dikendong berciciran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108875,"Limau manis dimakan manis,","Manis sekali rasa isinya,","Dilihat manis dipandang manis,","Manis sekali hati budinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108876,"Buat apa kepuk di ladang,","Kalau tidak berisi padi.","Buat apa berambut panjang,","Kalau tidak berani mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108877,"Buah Rambai dalam padi,","Lazat cita pula rasanya,","Pandai sungguh mengambil hati,","Serta dengan budi bahsanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108878,"Jangan takut dan gentar,","Mati hanya sekali,","Jangan bertukar kisar,","Tetap dilapangan mengendali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108879,"Tiada siapa yang besar,","Tiada siapa yang tinggi,","Tetap sama sedasar,","Tidaklah berbeza lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108880,"Apabila terpelihara kuping","Khabar yang jahat tiadalah damping","Apabila terpelihara lidah","Nescaya dapat daripadanya faedah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108881,"Ambil jerami dekat titi,","Dalam belukar ayam berlaga,","Cinta bersemi dalam hati,","Tetap mekar sampai syurga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108882,"Ambil jerami dekat titi,","Dalam belukar ayam berlaga,","Cinta bersemi dalam hati,","Tetap mekar sampai syurga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108883,"Panas bara sungguh membahang,","Bunga seroja layu di hutan,","Hendak bicara hati bimbang,","Kupendam saja dalam ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108884,"Biru laut nampak bayang,","Kayu ara tengah hutan,","Hati terpaut rasa sayang,","Jiwa gelora ingin pertemuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108885,"Burung pipit pandai terbang,","Hijau sawah amat menenangkan,","Wajah cantik kesayangan abang,","Jagalah maruah jangan lupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108886,"Asam kepayang gugur melata,","Gugur barang ke pinggir gua,","Dahulu sayang sekarang cinta,","Dahulu seorang sekarang berdua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108887,"Menghantar kad hingga ke huma,","Kad jemputan raja sehari,","Lafaz akad senafas cuma,","Menghalalkan ikatan setia dan janji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108888,"Dahan patah dikerat-kerat,","Kerat jangan disenja hari,","Adat nikah majlis beradat,","Menghalalkan hubungan suami isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108889,"Gadis jawa bersanggul tinggi,","Tinggi lagi dayung pedati,","Datang membawa hajat yang tinggi,","Memeterai janji membawa bukti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108890,"Songket Terengganu berbunga padat,","Dipakai kerabat sebagai selendang,","Datang kami datang berhajat,","Menyampai hajat adat bertunang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108891,"Gagah hulubalang memegang panji,","Dayang menari bertingkah irama,","Adat bertunang meterai janji,","Menanti hari disanding bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108892,"Mengisi puan bunga yang mekar,","Disusun rapat berlima-lima,","Datang tuan untuk melamar,","Tidak dapat kami terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108893,"Dari Sarawak datang bertanya,","Di mana ada si bunga raya,","Tinggi sungguh hasrat dibawa,","Apakan daya tidak kesampaian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108894,"Cincin permata tempawan gangsa,","Menjunjung takar puteri bunian,","Berlapik kata berbunga bahasa,","Datang melamar puteri impian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108895,"Dayung pedati mainan dara,","Berdayung dari pagi ke senja,","Berat hati ingin bicara,","Empunya diri belum sedia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108896,"Madu setakar jadi hidangan,","Santapan rasa pengantin bersanding,","Bunga mekar di taman larangan,","Belum masanya untuk disunting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108897,"Kelapa panndan buat kerisik,","Masak hidangan untuk hantaran,","Menanti tuan datang merisik,","Kurang tangan nyiru ditadahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108898,"Anyam jemari berbelang warna,","Tengah berlubang lekang susunan,","Bunga kami belum berpunya,","Menanti kumbang hinggap di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108899,"Pergi memikat burung serindit,","Singgah di Lenga membeli lokan,","Membawa hajat setinggi langit,","Merisik bunga mekar di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108900,"Sirih melata di pinggir laman,","Tumbuh melingkar di atas ampaian,","Cantik jelita anak dara tuan,","Sudahkah dilamar teruna impian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108901,"Dara cantik menjeling mata,","Jelingan nakal seluruh hala;","Adat merisisk si dara jelita,","Untuk mengenal keluarga segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108902,"Apa guna ambal tumis,","Kalau tidak campur asam belimbing,","Apa guna lama menangis,","Tidakkan penuh telaga yang kering.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108903,"Buah pauh jatuh tercampak,","Jatuh mari ke kuala,","Air keruh ikan tak nampak,","Buat penat turun menjala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108904,"Berburu ke Padang Datar,","Dapat rusa bergelang kaki;","Berguru kepalang ajar,","Macam bunga kembang tak jadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108905,"Apa guna berkain batik,","Kalau tidak memakai capal,","Apa guna beristeri cantik,","Kalau tidak pandai menumbuk-sambal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108906,"Buah langsat kuning mencelah,","Keduduk tidak berbunga lagi,","Sudah dapat gading bertuah,","Tanduk tidak berguna lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108907,"Cahaya redup menyegar pagi,","Ayam berkokok mengirai tuah,","Jikalau hidup tidak berbudi,","Umpama pokok tidak bertuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108908,"Orang aceh berniaga papan,","Papan diguna sebagai hiasan,","Remaja gigih menjadi harapan,","Memimpin negara menuju wawasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108909,"Nasi lemak buah bidara,","Sayang selasih saya lurutkan;","Buang emak buang saudara,","sebab kasih saya turut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108910,"Pulau Pandan jauh ke tengah,","Gunung Daik bercabang tiga;","Hancur badan dikandung tanah,","Budi yang baik dikenang juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108911,"Cahaya malam gilang-gemilang,","Kerana disinar bulan purnama;","Gajah mati meninggalkan tulang,","Manusia mati meninggalkan nama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108912,"Sampan kotak mengapa dikayuh,","Hendak belayar ke tanah seberang;","Patah kepak bertongkatkan paruh,","Pantang menyerah di tengah gelanggang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108913,"Buah rambai dalam padi,","Lazat cita pula rasanya;","Pandai sungguh mengambil hati,","Serta dengan budi bahasanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108914,"Padi muda jangan dilurut,","Kalau dilurut patah batangnya;","Hati muda jangan diturut,","Kalau diturut susah datangnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108915,"Patah jarum dalam peti,","Buat menjahit kain bertimbun,","Nasihat sepatah kurang dimengerti,","Rupanya penawar beribu tahun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108916,"Orang mengail di Teluk Bulang,","Apalah umpannya si kulit duku;","Jangan bermain kekasih orang,","Nyawa tersangkut di hujung kuku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108917,"Orang Jawa mencari benang,","Mencari benang di atas bukit;","Orang jauh jangan dikenang,","Lama-lama jadi penyakit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108918,"Bunga Manggar di kanan kiri,","Bunga rampai hiris hirisan,","Pengantin baru raja sehari,","Tambah berseri dengan hiasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108919,"Ayam dara dibuat gulai,","Bungkus bekalan letak di bucu,","Riang gembira kedua mempelai,","Semoga berkekalan ke anak cucu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108920,"Tatang puan tatang cerana,","Tatang biduk Seri Rama,","Datanglah tuan datanglah nyawa,","Datanglah duduk bersama-sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108921,"Amat garang datuk bentara,","Musuh melanggar habis dibenam,","Dulu seorang kini berdua,","Hidup bersama susah senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108922,"Tiup serunai di atass kota,","Uda memupuk bunga melati,","Mula kenal kemudian bercinta,","Cinta tersemai dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108923,"Buah pauh si bunga rampai,","Kilauan bintang sungguh mengasyikkan,","Orang jauh sudahlah sampai,","Selamat datang kami ucapkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108924,"Cik Ungku mudik ke hulu,","Ambil kain basuk nilainya,","Kalau yang itu biarlah dulu,","Kalau yang lain barang bilanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108925,"Sapu tangan jatuh ke laut,","Jatuh dengan kalas-kalasnya,","Panjang tangan sahaya menyambut,","Satu tidak dengan balasnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108926,"Ribu-ribu batang terpanggung,","Akan duduk batang rumbia,","Walau beribu hutangku tanggung,","Akanku pinang idaman dara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108927,"Tali kail panjangnya lima,","Akan pengail ikan tenggiri,","Tujuh tahil sekati lima,","Itulah harga intan di sini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108928,"Rumah kecil paranya lima,","Tempat menyalai ikan kerici,","Alahai Encik saya bertanya,","Berapakah harga intan disini?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108929,"Dari Pauh singgah Permatang,","Singgah merapat papan kemudi,","Dari jauh saya datang,","Karena tuan yang baik budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108930,"Sudah lama langsatnya condong,","BArulah kini batangnya rebah,","Sudah lama niat dikandung,","Baru sekarang diizzinkan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108931,"Pucuk selasih dibawa ke pekan,","Dijual mari di pasar pagi;","Terima kasih kami ucapkan,","Nasihat tuan dijunjung tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108932,"Kampong Melayu Geylang Serai,","Kampung Budaya warisan pusaka;","Sebelum majlis kita bersurai,","Madah tentuan kini dibuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108933,"Padang temu padang baiduri,","Tempat raja membangun kota;","Bijak bertemu dengan jauhari,","Bagai cincin dengan permata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108934,"Kain batik selendang batik,","Mari sebar ke atas pentas;","Adik cantik abang pun cantik,","Bagai dakwat dengan kertas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108935,"Anak itik di celah pandan,","Sambil berenang berdua-dua;","Sama cantik sama padan,","Bagai pinang dibelah dua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108936,"Kuntum kenanga kembang cempaka,","Sayang kemuning gugur bunganya;","Marilah kita doakan mereka,","Hidup bahagia ke akhir hayatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108937,"Asam di adang ikan di laut,","Di dalam belanga sama digulai;","Mata berpandang hati bertaut,","Ke jenjang pelamin akhirnya mempelai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108938,"Ayam selasih di permatang,","Ayam denak hidup di hutan;","Terima kasih tentuan datang,","Budimu kekal dalam ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108939,"Buah betik buah papaya,","Ditanam orang di dalam taman;","Tepung tawar lambang budaya,","Adat leluhur zaman berzaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108940,"Buanglah rokok buang selama,","Sirih adat tinggal-tinggalan;","Bismillahirrahmanirrahim ucapan utama,","Selamat datang ucapan susulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108941,"Bawa joran ke Tasik Cini,","Naik perahu sambil menjala;","Kami hairan orang di sini,","Sudah gaharu cendana pula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108942,"Dari seberang berakit buluh,","Tiada perahu diwaktu petang;","Dah terang lagi bersuluh,","Takkan tak tahu siapa yang datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108943,"Bukit Piatu rumah mak nyonya,","Tidak lama tinggal di sana;","Kalau begitu saya nak tanya,","Dari mana hendak ke mana?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108944,"Orang sekampung tidak berani,","Dengar tembakan waktu petang;","Ingatkan sombong orang disini,","Tidak pedulikan kami yang datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108945,"Naik perahu di tengah malam,","Perahu dilanggar lalu berpusing;","Bukan tak mahu menjawab salam,","Tidak terdengar kerana bising.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108946,"Buah langsat buah kuini,","Buah mempelam tepi sengkuap;","Sibukkah sangat orang disini,","Hinggakan salam tidak berjawab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108947,"Tumbuk lada di atas para,","Ada kasut simpan di peti;","Tepuk dada taya selera,","Apa maksud di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108948,"Tidak pernah rotan merentang,","Kayu cendana dijilat api;","Tidak pernah tuan bertandang,","Tentu ada maksud di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108949,"Malam-malam pasang pelita,","Pelita dipasang atas peti;","Kalau sudah bagai dikata,","Sila terangkan hajat di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108950,"Perahu kolek ke hilir tanjung,","Sarat bermuat tali temali;","Salam tersusun sirih junjung,","Apa hajat sampai ke mari?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108951,"Singgah berenang di persiraman,","Mayang terendam di dalam tasik;","Kumbang merindu bunga di taman,","Bintang merindu cenderawasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108952,"Cenderawasih burung yang sakti,","Singgah hinggap di atas karang;","Kasih berputik di dalam hati,","Dari dahulu sampai sekarang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108953,"Raja Hindu raja di Sailan,","Singgah berenang di persiraman;","Bagai pungguk rindukan bulan,","Kumbang merindu bunga di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108954,"Tinggi-tinggi si matahari,","Anak kerbau mati tertambat;","Sekian lama kami mencari,","Baru kini kami mendapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108955,"Sudah lama langsat condong,","Dahannya rebah ke ampaian;","Sudah lama niat dikandung,","Baru kini kesampaian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108956,"Hendak mangkuk diberi mengkuk","Mangkuk berisi daging punai","Hendak masuk diberi masuk","Masuklah bersama, celahlah inai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108957,"Tinggi bukit si Gunung Daik","Nampak dari Kula Jambi","Wahai pengantin marilah naik","Hamparan dah terbentang di tengah serambi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108958,"Lemah lembut daun lemacang","Terletak di atas peti","Lemah lembut pengantin datang","Naiklah ke rumah, basuhlah kaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108959,"Compodak ditongah laman","Dijolok dengan sepasang kaki","Usahlah lamo togak di laman","Silo naik dan basuhlah kaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108960,"Kayu aro tinggi sobatang","Dahan melambai ke pucuk putih","Apo pasal lambek datang","Kok kami memandang mato berputih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108961,"Cik Minah membeli bunga","Bunga pandan bunga kasturi","Orang yang dinanti sudah tiba","Jemputlah naik wahai raja sehari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108962,"Katang-katang berisis manik","Manik diisi dengan hampeh padi","Semuo yang datang silolah naik","Ketemu tuan rumah yang baik budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108963,"Asam kepayang gugur melata,","Gugur barang ke pinggir gua,","Dahulu sayang sekarang cinta,","Dahulu seorang sekarang berdua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108964,"Menghantar kad hingga ke huma,","Kad jemputan raja sehari,","Lafaz akad senafas cuma,","Menghalalkan ikatan setia dan janji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108965,"Dahan patah dikerat-kerat,","Kerat jangan di senja hari,","Adat nikah majlis beradat,","Menghalalkan hubungan suami isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108966,"Songket Terengganu berbunga padat,","Dipakai kerabat sebagai selendang,","Datang kami membawa hajat,","Menyampai hajat adat bertunang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108967,"Mengisi puan bunga yang mekar,","Disusun rapat berlima-lima,","Datang tuan untuk melamar,","Tidak dapat kami terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108968,"Dari Sarawak datang bertanya,","Di mana ada si bunga raya,","Tinggi sungguh hasrat dibawa,","Apakan daya tidak kesampaian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108969,"Dayung pedati mainan dara,","Berdayung dari pagi hingga ke senja,","Berat hati ingin bicara,","Empunya diri belum sedia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108970,"Madu setakar jadi hidangan,","Santapan rasa pengantin bersanding,","Bunga mekar di taman larangan,","Belum masanya untuk disunting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108971,"Kelapa pandan buat kerisik,","Masak hidangan untuk hantaran,","Menanti tuan datang merisik,","Kurang tangan nyiru ditadahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108972,"Anyam jemari berbelang warna,","Tengah berlubang lekang susunan,","Bunga kami belum berpunya,","Menanti kumbang hinggap di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108973,"Pergi memikat burung serindit,","Singgah di Lenga membeli lokan,","Membawa hajat setinggi langit,","Merisik bunga mekar di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108974,"Sirih melata di pinggir laman,","Tumbuh melingkar di atas ampaian,","Cantik jelita anak dara tuan,","Sudahkah dilamar teruna impian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108975,"Dara cantik menjeling mata,","Jelingan nakal seluruh hala,","Adat merisik si dara jelita,","Untuk mengenal keluarga segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108976,"Buah berangan di rumpun pinang,","Limau kasturi berdaun muda,","Kalau berkenan masuklah meminang,","Tanda diri beradat budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108977,"Ikan berenang di dalam lubuk,","Ikan belida dadanya panjang,","Adat pinang pulang ke tempuk,","Adat sireh pulang ke gagang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108978,"Berserta pula dengan halwa","Dengan ulam pucuk pegaga","Jika tak berpisah jiwa dan raga","Bagaimana kita nak masuk surga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108979,"Mari dibawa dari Rakan","Berserta pula dengan halwa","Jika nyawa badan serahkan","Tidak berpisahkah jiwa dan raga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108980,"Buah beragan rasanya lazat","Mari dibawa dari Rakan","Kalau ada maksud dan hajat","Nyawa dan badan kami serahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108981,"Pucuk pauh ditepi permatang","Buah berangan rasanya lazat","Daripada jauh kami datang","Datang dengan seribu hajat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108982,"Asam kandis amri dihiris","Manis sekali rasa isinya","Dilihat manis dipandang manis","Manis lagi hati budinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108983,"Orang menyungkur di Tanjung Jati","Kuala Pilah darat Melaka","Patah tumbuh hilang berganti","Pusaka berpindah kepada kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108984,"Bersusun sirih gagang berkembar","Beratur-atur berbunga tanjung","Melintang duduk gunung Rembau","Sebagai napuh dihujung tanjung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108985,"Dah patah pokok kedondong","Ditimpa pula pokok delima","Titah datuk saya junjung","Hukum yang baik saya terima", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108986,"Dalam air sungainya landai","Habis hanyut sarang tempua","Bukan saya menunjukkan pandai","Saya khabarkan orang tua-tua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108987,"Pohon lemba di tepi bukit","Pokok pepaya di tepi jalan","Baik makan barang sedikit","Supaya jangan kecewa badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108988,"Orang meracik burung tekukur","Racik benang kait-mengait","Sudikan datuk sirih sekapur","Pinangnya mabuk tembakaunya pahit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108989,"Teliti buah teliti","Terletak mari di atas papan","Tepak sirih sudah menanti","Minta sudikan datuk makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108990,"Dari paua turun ke lembah","Petik pinang dipilih-pilih","Saya sudah mohonkan sembah","Adat meminang bertepak sirih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108991,"Rimba dibakar menanam padi","Makan berulam buahnya petai","Jikalau sudah tulus dan sudii","Berbatalkan bandul bertikarkan lantai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108992,"Orang mengambil siput dilubuk","Airnya dalam banyak lintah","Datang membaiki atap yang tembuk","Hendak mengganti lantai yang patah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108993,"Rumah besar alangnya besar","Rumah Datuk Perdana Menteri","Kalau tidak hajat yang besar","Kami tidak sampai datang kemari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108994,"Saya tidak tahu berebana","Selisih telunjuk bertelekan","Saya tak tahu akan makna","Sirih diunjuk saya makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108995,"Cerita pinang ceriti","Cerana di atas papan","Sirih kami sirih bererti","Sukat makna baharulah makan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108996,"Tatang puan tatang cerana","Tatang biduk Seri Rama","Datang tuan datanglah nyawa","Jemputlah duduk bersama-sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108997,"Dari pauh ke permatang","Tetak tengar papan kemudi","Dari jauh kami datang","Mendengar tuan yang baik hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108998,"Katang-katang berisi manik","Manik berisi hampas padi","Encik-encik datang silalah naik","Inilah air pembasuh kaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (108999,"Budak-budak memakai senglit","Pergi ke kedai membeli timba","Kalau tidak jalan berbelit","Tadi-tadi saya dah tiba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109000,"Cantik memanjat pohon ara","Nampaknya cantik berseri laman","Besar hajat kami tidak terkira","Hendak memetik bunga di taman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109001,"Hilir air sungai landai,","Dalam pulak paras dada,","Bukan pulak menunjukkan cerdik pandai,","Kerana hendak menyambut adat lembaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109002,"Buat pondoh di Durian Tunggal,","Tanam selasih banyak-banyak;","Assalamualaikum dan selamat tinggal,","Terimalah kasih banyak-banyak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109003,"Lebat kuini kerana baja,","Ditanam rapat pohon keranji;","Kami di sini mengikut sahaja,","Harap dapat menunaikan janji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109004,"Gunakan paruh menangkap ikan,","Burung kedidi di tepi lukah;","Wangnya separuh saya berikan,","Bakinya nanti di waktu nikah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109005,"Yatim piatu tidak bertuan,","Setiap hari duduk mengeluh;","Kalau betul katanya tuan,","Berapa nak beri buah penangguh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109006,"Orang memancing di waktu petang,","Dapat seekor ikan tenggiri;","berasa puas orang yang datang,","Barang dicita sudah menjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109007,"Anak Cina pikul kelentung,","Lalu mari di kaki lima;","Pinta datuk kami junjung,","Tiga ribu lima ratus bersih kami terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109008,"Orang cantik tubuhnya tinggi,","Dapat dipandang setiap petang;","Tiga ribu lima ratus diberi cuci,","Orang di sini tak mahu lebih tak mahu kurang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109009,"Letak periuk di atas tungku,","Api menyala ditiuo lama;","Tiga ribu lima ratus serba satu,","Barulah kami dapt terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109010,"Pukul ribut angin selatan,","Takut karam muatan dibuang,","Pinta datuk saya tambahkan,","Lima ratus saya nak bilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109011,"Kepala jendul namanya ceti,","Duduk di bangku sambil berludah;","Kalau dibahagi dua harus tak jadi,","Kalau mahau diminta tambah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109012,"Singapura tidak berlurah,","Tempat orang berlumba kuda;","Saya menawar janganlah marah,","Apa kata, enam ribu dibahagi dua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109013,"Duduk-duduk tepi jambatan,","Pital tali rotan melata;","Pinta datuk saya turutkan,","Harga patut, empat ribu penutup kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109014,"Guru yang mana punya cakap,","Tuan yang mana punya aturan;","Taruhan tuan saya berharap,","Harga yang patut tolong kurangkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109015,"Pecah mangkuk pecah cawan,","Berlaga dengan tudung tekwan;","Empat ribu lima saya letakkan,","Timangan yang baik saya turunkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109016,"Enggang tidak kekek pun tidak,","Singgah bermain di balai peranginan,","Anak ini gedang tidak kecil pun tidak,","Tengah sedap buat permainan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109017,"Lata-lata dalam dulang,","Kaca pecah dalam mangkuk;","Bagi adat saya berbilang,","Timbangan neraca kepada datuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109018,"Tembak rusa jatuhnya kelawar,","Tembak tangan kaki yang patah;","Kalau hendak tawar-menawar,","Janganlah tawar terlalu rendah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109019,"Orang menembak di Tanjung Jati,","Tembak rusa, jatuhnya kelawar;","Datuk letak terlalu tinggi,","Bolehkah saya tawar-menawar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109020,"Perahu elok mudik ke hulu,","Layarnya patah, talinya putus;","Kalau Datuk ingin tahu,","Anak buah saya mahukan lima ribu lima ratus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109021,"Ke laut sudah kedarat pun sudah,","Ke tasik juga yang kita belum;","Berseloka sudah, berpantun pun sudah,","Menetapkan belanja saja yang belum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109022,"Buat juadah untuk dimakan,","Dijual juga dapat modalnya;","Tanda sudah kami serahkan,","Nak tanya juga tentang belanjanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109023,"Di dalam guni daun kelapa,","Diraut licin jadikan lidi;","Kalau di sini hendakkan tanda,","Ambillah cincin tanda jadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109024,"Perbuatan ini zaman dahulu,","Turun-temurun orang dahulu;","Lazim dibuat orang dahulu,","Adat bertunang mahukan tanda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109025,"Berkokok ayam di pagi hari,","Putus tali dari tambatan;","Jika sudah menerima sudi,","Kecil tapak tangan nyiur kami tadahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109026,"Pasang pelita sumbunya panjang,","Cahayanya terang tapi tak lama;","Tak payahlah kita bercerita panjang,","Nampaknya pinangan kami terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109027,"Kita menoleh nama disebut,","Sumpah seranah bila dikata;","Kalau boleh saya menyebut,","Namanya Hasnah anak yang tua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109028,"Daun sirih hijau warnanya,","Putih warna bunga melati;","Kalau boleh saya bertanya,","Yang mana satu idaman hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109029,"Burung kelicap di daun palas,","Tupai memanjat si pohon pinang;","Kami ni cakap kata berkias,","Sebenar hajat hendak meminang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109030,"Anginnya nyaman orang berkayak,","Gunakan galah jaga kemudi;","Bunga ditaman amatlah banyak,","Petik sajalah yang mana sudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109031,"Anginnya nyaman orang berkayak,","Gunakan galah jaga kemudi;","Bunga ditaman amatlah banyak,","Petik sajalah yang mana sudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109032,"Cantik memanjat pohon di para,","Nampaknya cantik berseri laman;","Besar hajat kami tidak terkira,","Hendak memetik bunga di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109033,"Sudah patah pokok sedondong,","Ditimpa pula pokok delima;","Kata-kata datuk saya junjung,","Apa yang baik saya terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109034,"Dalam airnya sungai yang landai,","Habis hanyut sarang tempua;","Bukan saya nak tunjuk pandai,","Nak sampaikan hajat orang muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109035,"Orang mengaji dari alif,","Orang membilang dari satu;","Wahai Datuk orang yang arif,","Cubalah ceritakan satu-persatu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109036,"Cantik rupanya pohon memranti,","Dibuat tugu di dalam taman;","Seksa rasanya duduk menanti,","Macam menunggu jatuh bekuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109037,"Pokok rokan di tepi bukit,","Pokok pepaya di tepi jalan;","Baiklah makan barang sedikit,","Supaya jangan kecewe badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109038,"Orang meracik burung tekukur,","Racik benang kait-mengangait;","Sudikanlah Datuk sirih sekapur,","Pinangnya pahit, tembakaunya pahit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109039,"Belati tali belati,","Letak mari di atas papan;","Tepak sirih sudah menanti,","Minta sudikan Datuk makan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109040,"Batang bukan sebarang datang,","Batang besi tidak berkarat;","Datang bukan sebarang datang,","Datang tuan datang beradat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109041,"Dari payu turun ke lembah,","Petik pinang dipilih-pilih;","Saya suka memohonkan sembah,","Adat meminang bertepak sirih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109042,"Burung pungguk terbang ke paya,","Tiba di paya turun ke tanah;","Tuan tertanya kepada saya,","Sayalah wakil yang punya rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109043,"Besar tubuhnya burung belatuk,","Belatuk kecil turun ke tanah;","Saya bertanya kepada Datuk,","Siapa wakil orang yang punya rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109044,"Jual barang beribu dinar,","Dijual dalam serata benua;","Tolong terangkan hajat sebenar,","Supaya faham kami semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109045,"Buah bacang si buah pauh,","Hendak dibawa buat jualan;","Kami datang dari jauh,","Dengan membawa seribu harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109046,"Sarang tebuan hujung sengkuap,","Ambil jerami bakarkan dahulu;","Soalan tuan boleh dijawab,","Sudikan kami naik dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109047,"Sini sena sana pun sena,","Hidup melata lebat daunnya;","Dari mana hendak ke mana,","Tiba-tiba hendak bertanya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109048,"Pergi berjalan rumah En. Wahab,","Bawa banyak kuih baulu;","Sebelum soalan tuan berjawab,","Saya juga nak bertanya dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109049,"Buah pepaya buah rumia,","Buah kuini rasanya lazat;","Bolehkah saya tumpang bertanya,","Adakah ini rumah En. Maahat?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109050,"Kepala pandan buat kerisik,","Masak hidangan untuk hantaran,","Menanti tuan datang merisik,","Kurang tangan nyiru ditadahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109051,"Anyam jemari berbelang warna,","Tengah berlubang lekang susunan,","Bunga kami belum berpunya,","Menanti kumbang hinggap di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109052,"Asam kandis mari dihiris","Manis sekali rasa isinya","Dilihat manis dipandang manis","Manis lagi hati budinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109053,"Bersusun sirih gagang berkembar","Beraturatur berbunga tanjung","Melintang duduk gunung Rembau","Sebagai napuh di hujung tanjung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109054,"Orang menyungkur di Tanjung Jati","Kuala Pilah darat Melaka","Patah tumbuh hilang berganti","Pusaka berpindah kepada kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109055,"Dah patah pokok kedondong","Ditimpa pula pokok delima","Titah datuk saya junjung","Hukum yang baik saya terima", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109056,"Orang meracik burung tekukur","Racik benang kaitmengait","Sudikan datuk sirih sekapur","Pinangnya mabuk tembakaunya pahit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109057,"Teliti buah teliti","Terletak mari di atas papan","Tepak sirih sudah menanti","Minta sudikan datuk makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109058,"Dari paya turun ke lembah","Petik pinang dipilih-pilih","Saya sudah mohonkan sembah","Adat meminang bertepak sirih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109059,"Rimba dibakar menanam padi","Makan berulam buahnya petai","Jikalau sudah tulus dan sudi","Berbantalkan bandul bertikarkan lantai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109060,"Orang mengambil siput dilubuk","Airnya dalam banyak lintah","Datang membaiki atap yang tembuk","Hendak mengganti lantai yang patah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109061,"Rumah besar alangnya besar","Rumah Datuk Perdana Menteri","Kalau tidak hajat yang besar","Kami tidak sampai datang kemari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109062,"Saya tidak tahu berebana","Selisih telunjuk tertelekan","Saya tak tahu akan makna","Sirih diunjuk saya makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109063,"Cerita pinang ceriti","Cerana di atas papan","Sirih kami sirih bererti","Sukat makna baharulah makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109064,"Cantik memanjat pohon ara","Nampaknya cantik berseri laman","Besar hajat kami tidak terkira","Hendak memetik bunga di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109065,"Raja Hindu raja di Sailan,","Singgah berenang di persiraman,","Bagai pungguk rindukan bulan,","Kumbang merindu bunga di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109066,"Tinggi-tinggisi matahari,","Anak kerbau mati tertambat,","Sekian lama kami mencari,","Baru sekarang kami mendapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109067,"Daun raya di atas bukit,","Tempat raja menanam pala,","Harap kami bukan sedikit,","Sebanyak rambut di atas kepala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109068,"Malam-malam pasaang pelita,","Pelita dipasang atas peti,","Kalau sudah bagai dikata,","Sila terangkan hajat dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109069,"Tidak pernah rotan merentang,","Kayu cendana dijilat api,","Tidak pernah tuan bertandang,","Tentu ada maksud di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109070,"Sudah lama langsatnya condong,","Dahannya rebah ke ampaian,","Sudah lama niat dikandung,","Baru sekarang disampaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109071,"Berapa tinggi pucuk pisang,","Tinggi lagi asap api,","Berapa tinggi Gunung Ledang,","Tinggi lagi harapan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109072,"Kalau gugur buah setandan,","Sampai ke tanah baru tergolek,","Kami bersyukur kepada Tuhan,","Datang kami disambut baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109073,"Dari Pauh singgah Permatang,","SSinggah merapat papan kemudi,","Dari jauh saya datang,","Karena tuan yang baik budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109074,"Sudah lama langsatnya condong,","Barulah kini batangnya rebah.","Sudah lama niat dikandung,","Baru sekarang diizinkan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109075,"Perahu kolek ke hilir tanjung,","Sarat bermuat tali temali.","Salam tersusun sirih junjung,","Apa hajat sampai kemari?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109076,"Tatang puan tatang cerana,","Tatang biduk Seri Rama,","Datang tuan datanglah nyawa,","Jemputlah duduk bersama-sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109077,"Akar menjalar masuk ke huma,","Sering terlangkah akar yang kering,","Hati dibiar merajuk lama,","Kering merekah berkeping keping.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109078,"Kalau tak taat kepada raja,","Kalau tak setia kepada negara,","itulah tanda orang derhaka,","Tempatnya kelak dalam penjara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109079,"Anak Dara dari Melaka,","Memakai celak nampak ceria,","Itulah tanda orang derhaka,","Tempatnya kelak dalam penjara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109080,"Mentari pagi menyinar terang,","Dedaun pokok nampak berseri,","Terasa sunyi duduk seseorang,","Cuba-cuba untuk menghibur diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109081,"Naik sampan membeli sarung,","Sampan pecah baiki di bali,","Pergilah intan pergilah payung,","Pergi usah datang kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109082,"Dijilat api habis rentung,","Kayu alang atapnya ijak,","Buah hati pengarang jantung,","Kalau meradang payah dipujuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109083,"Daun selasih buluh perindu,","Buat sarang silabah-labah,","Dulu kasih dulu kau rindu,","Tapi sekarang kenapa kau berubah.", 4, 6);
