INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109268,"Siapa menyimak makanannya dapat","Siapa paham beroleh berkat","Siapa arif mendapat manfaat","Siapa bijak hidup selamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109269,"Siapa menyimak mendapat pedoman","Siapa cerdik mendapat pelajaran","Siapa pandai terbuka jalan","Siapa bijak mendapat sandaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109270,"Mengajak orang untuk berfikir","Supaya orang tidak pandir","Supaya ketiak tidak berlendir","Supaya hidup tak kena cibir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109271,"Menunjukkan mana halal dan haram","Memberikan suluh siang dan malam","Supaya hidup tiada karam","Kalaupun mati di dalam Islam", 4, 6);
