INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109281,"Ada pesan Baginda Rasulullah","Bagi ummatnya penuh kebahagian","Hiasi suara dengan indahlah","Ketika melantunkan ayat Al Qur'an", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109282,"Ada peta tidak bernama","Ada kotak letak di pinggir","Ada mata melihat cuma","Ada otak tidak berfikir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109283,"Ada petuah juga nasihat","Di api unggun kakek berdiang","Anak anak baiklah ingat","Kesempatan tak sering datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109284,"Ada pijat tuan buangkan","Ada inti pulut direndam","Ada hajat tidak diucapkan","Ada hati kenapa dipendam?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109285,"Ada pintu lompat jendela","Masuk kamar di tengah malam","Lupa waktu lupa aturan","Hidup sudah tidak karuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109286,"Ada pisau buat penyukur","Boleh di gosok di atas kulit","Siang dan malam tiada tidur","Cinta Norma bukan sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109287,"Ada polusi dalam lingkungan","Udara bersih jadi taruhan","Manusia bebas dilahirkan","Dalam hidup jadi tawanan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109288,"Ada pulot berchampor padi","Isi mari di dalam dulang","Bagi mana di mulot begitu di hati","Itu lah tanda berkaseh panjang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109289,"Ada pulot ber-champor padi","Isi mari di dalam dulang","Mana di mulot bergitu di hati","Itu orang chinta sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109290,"Ada pulot ber-champor padi","Isi mari dalam dalang","Mana di mulot bergitu di hati","Itu Manama si anak orang.", 4, 6);
