INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109292,"Ada pun tikar baik tah lampit","Dibawa pengalu ke Junjungan","Akan dibawa perahuku sempit","Tinggallah emas nyawa junjungan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109293,"Ada rezeki ada jodoh","Ada kawan dapat hiburan","Selalu ada seorang bodoh","Dalam setiap perkawinan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109294,"Ada roti di dalam talam","Anak makan sungguhlah lahab","Yang menentang ajaran Islam","Pamannya sendiri Abu Lahab", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109295,"Ada rumah tidak bertingkap","Siap langsir tidak berguna","Tuan peramah fasih bercakap","Saya mendengar terpesona", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109296,"Ada rumah tidak bertingkap","Siap langsir tidak berguna","Tuan rumah fasih bercakap","Tetamu mendengar terpesona", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109297,"Ada rupa ada gaya","Ada hati ada ranggi","Tapi lupa adat budaya","Tidak erti berhemah tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109298,"Ada sa'ekor burong gagak","Petang petang terbang ka hutan","Bukan hangin jadi kan ombak","Dia kroh kan satu lauttan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109299,"Ada sa'ekor burong serindit","Terbang ker-paya makan padi","Kepada orang tamak kan ringgit","Kepada saya kenang kan budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109300,"Ada sambal ada rojak","Sedap juadah si nasi campur","Hujan lebat jalan pun becak","Naik gajah kaki tak kotor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109301,"Ada satu ada dua","Tambah satu menjadi tiga","Bukan batu bukan geliga","Intan terselit di mulut naga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109302,"Ada satu anak Brunei","Cari makan menimba jala","Jangan ikut arus di sungai","Takut terkepung pintu kuala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109303,"Ada satu anak bugis","Di tepi bukit bikin prau","Sunggu saya tak tau menulis","Bacha sedikit saya pun tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109304,"Ada satu anak glanggang","Sari sari turun ka-pantay","Jika sahari tidak ku pandang","Sebagi bunga layu di tangkay", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109305,"Ada satu anak helang","Sehari-hari turun ke pantai","Jika sehari tidak kupandang","Bagai bunga layu di tangkai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109306,"Ada satu anak inchek Dolah","Getah terkukor atas bumbong","Sakit banyak di bagi Allah","Seribu sukor atas junjongan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109307,"Ada satu anak Minangkabau","Cari makan di kebun Cina","Sudah runtuh gunung Rembau","Gunung Ledang ada gantinya", 4, 6);
