INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109309,"Ada satu anak samarang","Sari sari pergi ka pantay","Kalu suda kain slayrang","Tarek di atas bawa tak sampay", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109310,"Ada satu anak seberang","Sapu tangan bungkos beretay","Jangan tak-tahu burong lelang","Dia charik makan menyusoh pantay", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109311,"Ada satu anak sebrang","Sari-sari graji papan","Saya sabagi burong terbang","Di-pukol angin mencharik makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109312,"Ada satu anak Semarang","Tengkat2 ada bodoh-nya","Kalu bergini dunia di-pandang","Rajah berangkat tetap dudok-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109313,"Ada satu binatang melata","Lebar kepala darinya badan","Dengarlah tuan dengan nyata","Ikan tembakul jadi jawaban", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109314,"Ada satu binatang ratu","Tinggalnya aman di gua batu","Kalau tidak angin membantu","Tidak keluar dari situ", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109315,"Ada satu burung dewata","Kukunya panjang seperti senjata;","Wajib ia berkata-kata","Jika duduk di tempat yang rata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109316,"Ada satu burung dewata","Angin bertiup naik angkasa","Badannya besar tiada kepala","Indah menari permainan mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109317,"Ada satu burung pucung","Lehernya panjang bagai teropong","Kalau minta pada tok pekok","Kapal lalu boleh disorong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109318,"Ada satu burung tiung","Turun ke kolam mandikan anak","Bukankah baik beli borong","Timbang kati untung tak banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109319,"Ada satu dewa angkasa","Jalan mari tepi perpat","Suda lah tau serba rasa","Kenapa lah tidak melihat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109320,"Ada satu dewa pahlawan","Hari petang pulang ka-kota","Bukanka langit di kandong awan","Bulan dan bintang mau serata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109321,"Ada satu dua beradek","Suka pakay kain batik","Rusa di tembak kajang terpekek","Sampey di tengok pelandok tergolek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109322,"Ada satu orang Bugis","Kluar di pantay manakin prau","Sunggu sahya tak paham tulis","Tidak banyak s'dikit pun sahya", 4, 6);
