INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109327,"Ada satu panglima Batak","Pandang ke muka bagai perompak","Saya laksana jong tercampak","Pasang surut dipukul ombak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109328,"Ada satu panglima gading","Masok ka hulu handak menjaga","Chaching bertapa lautan kring","Bila kan bolay menjadi naga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109329,"Ada satu panglima garang","Janggut panjang misai bercabang","Pokok pisang boleh tuan tebang","Kepada saya jangan di cadang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109330,"Ada satu si anak Bugis","Blayar mari pergi ka Mecca","Siang malam dudok menangis","Kerna hati dudok berchinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109331,"Ada satu Tlaga batu","Tempat onka berulang mandi","Ada masa ku tika waktu","Barang tak sangka kadang menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109332,"Ada satu ular sawa","Bila ber-bangkit dia ter-gulong","Kalu ada huntong dan ber-tua","Jerki masok s'bagi di sorong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109333,"Ada secawan bunga berembang","Taruk dipetik bertimbun-timbun","Laksana bunga menantikan kumbang","Harap juga setitik embun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109334,"Ada seekor ayam katek","Ekornya panjang bulunya borek","Bila berkokok ia menyerek","Di sepak kawan jatuh tergolek.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109335,"Ada seekor burung dewata","Ia terbang segenap kota","Hinggap ia di tanah rata","Tapaknya boleh berkata-kata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109336,"Ada seekor burung dewata","Kakinya panjang bagai senjata","Kalau duduk tempat yang rata","Boleh ia berkata-kata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109337,"Ada seekor burung geroda","Garuda bini matinya laki","Rasakan belah di dalam dada","Seperti tiada tertahan lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109338,"Ada seekor burung helang","Suka bertenggek di atas carang","Hancur daging bercerai tulang","Budi yang baik baharulah hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109339,"Ada seekor burung pekaka","Hinggap turun di atas batu","Kalau tuan pandai meneka","Apa binatang tulangnya satu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109340,"Ada seekor burung pelatok","Cari makan di kayu buruk","Tuan umpama ayam pungguk","Segan menyakar rajin mematuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109341,"Ada seekor burung serindit","Terbang ke paya makan padi","Kepada orang tamakkan ringgit","Kepada saya kenangkan budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109342,"Ada seekor si anak beruk","Suka bertenggek di kayu buruk","Zahir ketara batin tersorok","Budi yang baik hilang ditaruk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109343,"Ada seekor si burung pucung","Kaki panjang ekornya kontong","Janganlah awak nak tunjuk sombong","Itu binatang bernama sotong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109344,"Ada sejarah cuma direka","Negara mewah rakyatnya payah","Ada ijazah tak mahu kerja","Selesa di bawah ketiak ayah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109345,"Ada sekor biring chek dola","Bawak ka glangang sabong taji","Sakit pening siapa membla","Dudok diam tahan kan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109346,"Ada sekor burong Gak-gak","Dia nak terbang pergi di hutan","Bukan ka angin jadi kan ombak","Di kasi kro satu laot-an", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109347,"Ada sekor ikan slampay","Esi mari didalam prau","Langit tinggi tuan mau chapay","Bumi yang renda tuan tak tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109348,"Ada seorang anak Brunei","Naik tamuai tuju Muara","Jangan mengusung arus di sungai","Takut tersadai karang kuala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109349,"Ada seorang anak Brunei","Turun ke taman bermain tinting","Seekor helang seekor punai","Cuba hilangkan bisa di daging", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109350,"Ada seorang saudagar Keling","Matanya merah seperti cempeling","Pelita di tengah orang keliling","Buluh seruas berganti suling", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109351,"Ada seorang saudagar Keling","Matanya merah seperti cemperling","Pelita di tengah orang keliling","Buluh seruas berganti suling", 4, 6);
