INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109353,"Ada sepasang anak beruk","Suka bertenggek di tunggul buruk","Perahu kita sudah sama buruk","Baiklah berlabuh di tepi teluk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109354,"Ada serangga dan kuman","Di udara laut dan darat","Cari peluang bukan rasa aman","Besi pun bisa berkarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109355,"Ada setengah anasir yang empat","Ada setengahnya cahaya zuriat","Ada setengahnya sahabat yang empat","Waktu mati tiadanya ubat", 4, 6);
