INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109638,"Agar usaha beroleh berkat","Hasil melimpah untung berlipat","Padi berkepuh penuh dan padat","Menepung tawar selalu dibuat", 4, 6);
