INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109640,"Agik dolok mandi di pait","Tapi sekarang mandi di pegung","Agik dolok rokok segeret","Tapi sekarang berokok apung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109641,"Aguang rajo ba-bunyi malam","Urang ma-marik alaman-nyo","Untuang sayo wallahu-alam","Sakik jo sanang di-tangguang-Nyo.", 4, 6);
