INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109717,"Air digunung begitu sejuk","Karena dikelilingi berbagai batu","Masih ada lagi petunjuk","Nantilah di tulis di lain waktu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109718,"Air ditanak di tepi telaga","Singgah memilih buah rambutan","Buang emak buang saudara","Hati kasih saya turutkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109719,"Air ditasik berhening tenang","Bunga teratai nampak berseri","Hati terusik bila terkenang","Ketika bersantai seorang diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109720,"Air dituang betung tumbuh","Diparang anak si Gumanti","Kalau hati sama sungguh","Kering lauatan kita nanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109721,"Air dungun airnya deras","Mari bergalah batang kemuncup;","Muda dengut muda culas","Mari dibujuk sirih kecut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109722,"Air Dungun airnya dua","Tempat cik abang menjemur pukat;","Saya tengok di dalam peta","Abang sudah mencari tempat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109723,"Air Dungun ulu ke paya","Hanyut buluh menikam tebing;","Muda dengut hodoh upacara","Sebelum putus mencari lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109724,"Air halia di bulan puasa","Diminum enak berkali-kali","Masa belia mengisi masa","Berjinak-jinak berjual beli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109725,"Air halia mari di sejat","Warnakan merah pencelup di celpa","Mengapa di mulia pangkat darjat","Sedangkan darah merah serupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109726,"Air halia mari disejat","Warnakan merah pencelup dicelpa","Mengapa dimulia pangkat darjat","Sedangkan darah merah serupa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109727,"Air hangat bunga melati","Bertumbuh enggan hidup pun segan","Kalau idealisme mati","Tersimpan dalam buku catatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109728,"Air hujan lebat mengalir","Hanyut perahu entah ke mana","Berkilat ikan di dalam air","Di mana tahu jantan betina?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109729,"Air hujan sudah di tadah","Jangan disimpan di pelataran","Buruk sangka tiada berfaedah","Dalam menegakkan suatu kebenaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109730,"Air hujan sudah di tadah","Jangan disimpan di pelataran","Buruk sangka tiada berfaedah","Dalam menegakkan suatu kebenaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109731,"Air hujan turun mencurah","Jatuh ke tanah terus ke laut","Binasa badan menahan gundah","Kasih ku curah tiada bersambut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109732,"Air hujan turun menetes","Penuh tempayan tumpah ke bak","Betapapun wajah dioles","Yang jelek pasti juga nampak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109733,"Air jatuh dari pancoran","Percik sampai bunga di taman","Nasib untung dari seorang","Keluarga turut rasakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109734,"Air jernih air telaga","Pelan mengalir sampai muara","Rukun baik dengan tetangga","Hidup anda pasti bahagia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109735,"Air jernih ikannya jinak","Bila mandi bertambah segar","Hati bersih kawanpun banyak","Kemana pergi tuahnya besar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109736,"Air jernih pantainya landai","Tempat bermain si ketam batu","Berunding berbincang dengan pandai","Dua fikiran menjadi satu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109737,"Air jernih sajaknya landai","Jalan raya titian batu","Berunding cerdik dengan pandai","Faham dua menjadi satu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109738,"Air jernih sajaknya landai","Pohon tumbang di tepi batu","Jangan kita berlagak pandai","Jika ilmu hanya sekuku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109739,"Air kelapa jadi minuman","Yang masih muda nikmat rasanya","Itu yang disebut Rukun Iman","Yang di ajarkan Nabi kepada ummatnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109740,"Air keruh bertambah keruh","Musim kemarau semakin panjang","Hatiku rusuh bertambah rusuh","Kerana risau menunggu abang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109741,"Air keruh ikanpun mati","Untuk mandi datang penyakit","Hati rusuh badanpun lesi","Duduk mengaji tentulah sulit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109742,"Air keruh telaga keruh","Tempat Cina menanam padi","Abang jauh adikpun jauh","Kita berjumpa di dalam mimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109743,"Air kolah di dalam ketur","Mari direnjis pohonnya sirih","Kepada Allah saya bersyukur","Untuk majlis berterima kasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109744,"Air kopi didalam gelas","Campurkan susu penyedap rasa","Tercantum dalam surah Al Ikhlas","Hanya Allah Tuhan Yang Esa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109745,"Air kopi didalam gelas","Campurkan susu penyedap rasa","Tercantum dalam surah Al Ikhlas","Hanya Allah Tuhan Yang Esa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109746,"Air lah surut sampan tersakat","Kerana ke tepi ketika pasang","Hatiku hanyut badan melarat","Kerana kekasih lama tak datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109747,"Air laut tenang-tenangan","Sila pandang ke laut lepas","Budi sedikit jadi kenangan","Selagi jiwa masih bernafas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109748,"Air laut tidak dangkal","Tempat mandi anak nelayan","Jadi orang kurang akal","Gampang korban ditipu lawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109749,"Air liur sudah diludah","Jangan ludah jilat kembali","Sudah disanjung sudah dipuja","Jangan sampai melupa diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109750,"Air madu dalam tembikar","Pokok lilang di tengah padang","Rindu malam di bawa tidur","Rindu siang di bawa bertandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109751,"Air mancur di jalan Thamrin","Tempat mandi anak jalanan","Takkan keliru dengan yang lain","Kalau denganmu berhadapan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109752,"Air mancur di kebun raya","Tanam-tanaman banyak yang rindang","Hidup makmur memang impian","Tanggung jawab setiap insan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109753,"Air mancur di tengah kota","Menyiram debu sehari-hari","Mau menegur kehilangan kata","Semua terungkap di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109754,"Air mancur memancar jernih","Orang meminum air kali","Hilang dahaga lenyaplah letih","Kalau engkau muncul di hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109755,"Air manis dengan kopinya","Jualan orang di Kampung Jawa","Mulut manis baik hatinya","Itulah orang berhati mulia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109756,"Air mata buaya","Mengalir ingin melahap","Menangis berpura-pura","Telan korban waktu mendekap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109757,"Air mata duyung dakwat menulis","Mari ditulis dalam daun","Sekecil-kecil kayu tempinis","Terasnya tahan berpuluh tahun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109758,"Air mati memancing kurau","Singgah memungut agar-agar","Buta larang tergagau-gagau (teraba-raba)","Jari hantu di tikam selumbar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109759,"Air mawar di dalam cangkir","Persembahan raja perempuan","Dari awal sampai ke akhir","Kasih terserap padamu tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109760,"Air mawar di dalam ceper","Persembahan kepada raja perempuan","Dari awal sampai ke akhir","Kasih terserah kepadamu tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109761,"Air mawar puaskan dahaga","Daun padi berduri-duri","Tuan hamba berpanah dengan suka","Matilah hamba seorang diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109762,"Air melimpah penuh perigi","Basah batas dan tanah","Tali sumpah ikatan janji","Peti tugas disimpan amanah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109763,"Air melurut ke tepian mandi","Kembang berseri bunga senduduk","Elok diturut resmi padi","Semakin berisi semakin tunduk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109764,"Air mengalir dalam selokan","Jatuhnya dari pancuran","Semua sudah dikorbankan","Baru terakhir kebebasan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109765,"Air mengalir di alur-alur","Pokok lilang di tengah padang","Rindu malam dibawa tidur","Rindu siang dibawa bertandang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109766,"Air nira di dalam gelok","Dicuri minum anak beruang","Kalau suka pada yang elok","Barang buruk jangan dibuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109767,"Air nira kuah serabai","Kuih bangkit si bingka labu","Dunia digila akhirat terabai","Pahala sedikit menjadi abu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109768,"Air pasang ayam berkokok","Penuh kolam penuh perigi","Hendak tahu tingginya pokok","Tanya helang terbangnya tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109769,"Air pasang baru melanda","Sawah dan ladang hampirlah punah","Baru dua bulan dikandungan bunda","Ayah nya Abdullah meninggal di Madinah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109770,"Air pasang berhanyut rakit","Kasihan dara berjalan kaki","Sumur gersang di pinggir bukit","Apa bicara petualang berdaki?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109771,"Air pasang bermain rakit","Sambil berakit memancing patin","Beramal ibadat sebelum sakit","Kaya bersedekah sebelum miskin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109772,"Air pasang bulan mengambang","Hanyut sampan dari Jawa","Jikalau datang hati yang bimbang","Bagaikan hilang rasanya nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109773,"Air pasang bulan pun terang","Hanyutlah sampan dari Jawa","Jika datang hati yang bimbang","Bagaikan hilang rasanya nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109774,"Air pasang dari hulu","Hujan lebat pulang menembak","Fikirkan badan elakkan dulu","Bila kuhampir jangan bergerak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109775,"Air pasang datang menyenak","Sampan berlabuh di tepi tebing","Hati pusang orang pun kemak","Jangan disuruh duduk berunding", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109776,"Air pasang di Bagan Ajam","Pergi memancing ke pintu kuala","Melihat musang berbulu ayam","Kenapa kucing sakit kepala?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109777,"Air pasang hujan di hulu","Makanya mendung malam dan siang","Hendak datang bercampur malu","Jadi berlindung di balik tiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109778,"Air pasang matahari masuk","Banyak orang jual berangan","Jerat dipasang burung tak masuk","Burung biasa makan di tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109779,"Air pasang matahari masuk","Banyak orang jual berangan","Jerat dipasang burung tak masuk","Burung biasa makan di tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109780,"Air pasang matahari masuk","Banyak orang jual berangan","Jerat dipasang burung tak masuk","Burung biasa makan di tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109781,"Air pasang melimpah ruah","Lepas tebing lepas bendang","Sudah dapat gading bertuah","Tanduk lama tetap dipandang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109782,"Air pasang memungut siput","Air surut mencari tiram","Cinta usang duit sejemput","Diikat perut hendak melawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109783,"Air pasang menganjur pagi","Batang Lupar lautnya tenang","Sudah datang si buah hati","Perutku lapar menjadi kenyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109784,"Air pasang menganjur pagi","Perahu belayar pergi Sepinang","Udah datang si buah hati","Perutku lapar menjadi kenyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109785,"Air pasang menjala udang","Hanyut terapung perahu dan sampan","Isteri orang sedap dipandang","Ikan berkampung menolak umpan!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109786,"Air pasang menyelam tiram","Tiram diselam pasang perbani","Jika kuturut hati yang geram","Hilang takut timbul berani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109787,"Air pasang menyelam tiram","Tiram diselam pasang perbani","Jikalau diturut hati yang geram","Hilanglah takut timbul berani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109788,"Air pasang pagi","Surut pukul lima","Nyonya bangun pagi","Siram pokok bunga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109789,"Air pasang pergi ke laut","Menega pengerah Kuala Sempadi","Biar datang ajal dan maut","Mengubah kasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109790,"Air pasang sampah ke tepi","Perahu remuk dipukul arus","Malu pawang serapah tak jadi","Badan gemuk menjadi kurus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109791,"Air pasang sedang ke hulu","Rumah besar bersarang madu","Kalau terkenang masa dahulu","Jantung berperang dengan hempedu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109792,"Air pasang singgahlah dulu","Dapat berhenti di pulau karang","Hatiku bimbang bertambah pilu","Ingat kekasih dirantau orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109793,"Air pasang titi ditempuh","Buah bidara hanyut terapung","Janji usang cintamu rapuh","Hati terdera jiwa terapung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109794,"Air paya rasanya masam","Walaupun masam dapat diminum","Hatiku luka menahan dendam","Dendam berahi berbalas belum", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109795,"Air penawar di dalam kendi","Minum bergilir sebiji cawan","Gerak laku pancaran budi","Baik pekerti hati tertawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109796,"Air penuh hingga ke ruang","Pokok-pokok subur bertumbuh","Agak-agak mengata orang","Supaya cedera tidak tumbuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109797,"Air perigi nampaknya bening","Untuk minuman orang sekampung","Kupandang pipi yang putih kuning","Remuklah tulang pecahlah jantung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109798,"Air perigi tidak tertimba","Hujan lebat hingga ke petang","Jemur padi ayam pun tiba","Bersila rapat makan terhidang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109799,"Air redang bewarna merah","Kerana banyak akar yang mati","Biarpun mati bersimbah darah","Kasihku tidak akan berhenti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109800,"Air sadap di dalam buluh","Pakei bekal pergi beranyi","Kamek arap kasih bersungguh","Kasih kekal hingga ke mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109801,"Air samudera nampaknya tenang","Tempat terubuk dengan tenggiri","Niat bersara hendak bersenang","Tetapi sibuk melanda diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109802,"Air segeluk kelihatan biru","Anak-anak bermain kejaran","Gelisah duduknya si pemburu","Melihat kijang berkeliaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109803,"Air sehingga buku lali","Anak rusa turun berkarang","lanya ada pantang pemali","Tidak boleh dipaksa orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109804,"Air selasih jual di pekan","Ditambah perasa penyedap selera","Sayang dan kasih kita sematkan","Sayangi bangsa kasihkan negara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109805,"Air selasih tolong hidangkan","Untuk di minum penyegar badan","Terima kasih kami ucapkan","Atas perkenan memenuhi undangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109806,"Air serbat dari Serati","Hanyut pandan dari Jawa","Menjadi obat di tangkai hati","Laksana badan dengan nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109807,"Air serbat di dalam cangkir","Persembahan dari dewa di awan","Haram taubat sahaya tak mungkir","Badan terserah kepada tuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109808,"Air serbat di dalam cangkir","Persembahan dewa dari awan","Haram talak saya tak mungkir","Badan berserah kepada tuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109809,"Air serbat di dalam cangkir","Sampai ke Jawa saya curahkan.","Haram tobat saya tak mungkir","Badan dan nyawa saya serahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109810,"Air serbat di dalam cangkir","Sampai di Jawa dicurahkan","Janji diikat takkan dimungkir","Badan dan nyawa diserahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109811,"Air serbat di dalam cangkir","Sampai ke Jawa saya curahkan","Haram taubat saya tak mungkir","Badan dan nyawa saya serahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109812,"Air setalam dua talam","Setahun lamannya orang tapisi","Badan terbangun tengah malam","Dipeluk bantal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109813,"Air sudah bergerak pasang","Lalu hanyut sebatang nibung","Bukannya jelak diberi perangsang","Jikalau hayam boleh disabung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109814,"Air sumur janganlah keruh","Untuk minum supaya sehat","Siang dan malam aku bersimpuh","Mencari rido cucuran rahmat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109815,"Air sungai air perigi","Anak dagang minum selera;","Selamat sungguh abang pergi","Adik dirumah tidak mengapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109816,"Air sungai pasang pagi","Tunggu surut dayung ke hilir","Cintaku satu cuma sekali","Kau yang awal dan terakhir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109817,"Air surut bulan mengambang","Pungguk berbunyi mendayu-dayu","Hendak berpaut di dahan bercabang","Rupanya ada ular menunggu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109818,"Air surut kapal berlayar","Hujan reda layer terkembang","Janji diturut hutang dibayar","Hidup bahagia tanpa bimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109819,"Air surut ke tangga batu","Perahu membawa si atap genting","Malaikat maut di muka pintu","Tali nyawa sedia digunting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109820,"Air surut lumpur tak hilang","Ampai kain biar cermat","Kalau diturut perangai helang","Burung yang lain tidak selamat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109821,"Air surut memungut bayam","Sayur diisi di dalam kantung","Jangan diikut resmi ayam","Bertelur sebiji riuh sekampung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109822,"Air surut memungut bayam","Sayur diisi di dalam kantung","Jangan diikut resmi ayam","Bertelur sebiji riuh sekampung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109823,"Air surut memungut bayam","Sayur diisi di dalam kantung","Jangan diikut resmi ayam","Bertelur sebiji riuh sekampung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109824,"Air surut memungut kerang","Kerang dipungut ikan dikail","Iri pada kejayaan orang","Bakat sendiri belum dicungkil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109825,"Air surut mencari lokan","Lokan dijaja sampai ke pekan","Adat tradisi kembang-kembangkan","Budaya orang dielok-elokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109826,"Air surut mencari lokan","Siput sudu boleh dimakan","Adik bukan isteri pun bukan","Mesra terjalin setakat rakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109827,"Air surut menyelam tiram","Menantikan pasang perbani","Jika kuturut hati yang geram","Hilang takut timbul berani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109828,"Air surut menyelam tiram","Menanti pasang berhari-hari","Jika kuturut hati yang genam","Hilang takut timbul berani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109829,"Air surut orang menyelam","Sambil menjerat si ketam batu","Resam si penakut berjalan malam","Busut jantan disangka hantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109830,"Air surut perahu di tarik","Ada muatan jangan di goyang","Terutama dari kaum yang musyrik","Tetap bertahan dengan agama nenek moyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109831,"Air surut sampan tersakat","Pasang besar baru berlayar","Hati kusut badan pun penat","Kurang sabar tentu bertengkar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109832,"Air surut sungainya tohor","Tebing lekuk dipukul arus","Puas diurut puas dilentur","Hati bengkok tak mahu lurus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109833,"Air surut tanah berlumpur","Kerang tersengih di hutan bakau","Amalan karut jangan dicampur","Syaitan gigih iman dipukau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109834,"Air syarbat di dalam ceper","Sampai ke Jawa sahaya curahkan","Haram tobat sahaya tak mungkir","Badan dan nyawa sahaya serahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109835,"Air tasik beriak tenang","Angin berhembus di hujung daun","Jadikan diri si anak lanang","Gagah perkasa bak harimau mengaum", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109836,"Air telaga beriak-riak","Tempat merpati berulang mandi","Kalau tiada emas sepiak","Semua kerja tidak menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109837,"Air telaga boleh mengail","Ikan jinak menggelepar","Naik tangga untuk berhasil","Berpijak di atas alas mendatar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109838,"Air telaga terasa sejuk","Siapa kesana teruslah mandi","Kupandang muka membawa mabuk","Kudengar suara memutus hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109839,"Air tenang dibawa belayar","Ketika balik jaring direntang","Hati bercabang dada berdebar","Menengok adik dijeling orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109840,"Air tergenang di jajaran","Padi ditanam Sutan Mudo","Tergamang kami ditinggalkan","Kami terbiasa ramai jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109841,"Air timpas pasang tak tiba","Banyaklah kapal bergalah lalu","Kakiku lemas hilang bicara","Hendak berkenal terasa malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109842,"Air turun dari bubungan","Mengalirnya ke rawa rawa","Harapan ada walau biasa","Bisa berubah menjadi nyata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109843,"Air ulama pewaris Nabi","Mengajak orang kejalan Tuhan","Sebut ASMANYA serta di puji","Miskat tersibak dalam kebesaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109844,"Air yang dalam bertambah dalam","Hujan dahulu belumlah teduh","Hati yang dendam bertambah dendam","Dendam dahulu belumlah sembuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109845,"Air yang dalam semakin dalam","Dalamlah lubuk sebelah hulu","Hati geram semakin geram","Geram menengok kekasih lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109846,"Air yang panas taruh perlahan","Dibuatkan kopi untuk kenalan","Bersangka baik dengan tuhan","Subhanallah jadikan amalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109847,"Airku so goyang-goyang","Kutaruh di tapak tangan","Orang memandang terbayang-bayang","Sebut si dayang igau-igauan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109848,"Airku so goyang-goyang","Kutaruh di tapak tangan","Orang memandang terbayang-bayang","Sebut si dayang igau-igauan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109849,"Airlah dalam bertambah dalam","Hujan di hulu belumlah teduh","Hati lah karam bertambah karam","Karam merindu orang yang jauh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109850,"Airnya pasang deras ke barat","Tarik layar ke Melaka","Supaya senang lepas melarat","Kalbu pun tidak menaruh sangka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109851,"Aitu sapi I hakopmo","Sapi okenet ponikanmo","Tuma tina opalaimo","Boine mo' ane sumo aitumo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109852,"Ajaib hairan hati tefekor","Melihat kerja tiada berekor","Murai gila 'nak jadi tekukor","Terbang hinggap di pokok cekor.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109853,"Ajak gua-gua di bukit","Datang di bukit betanam padi","Ajak tua-tualah sedikit","Asal pandei mengambik ati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109854,"Ajak makan di pinggan retak","Bawa balik dodol dan wajik","Angguk tuan maksudnya tidak","Bagai bersolek di cermin mengejek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109855,"Ajaran Islam Nabi Dakwahkan","Akal dan hati juga di lapangkan","Dengan Lembut Nabi menegaskan","Menghindarkan diri dari hal menyesatkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109856,"Ajo ba gala Peto Hakin","Juru tulih jo kiai Matuang","Ba darun aia panalakin","Sinan ta kana bundo kanduang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109857,"Ajuang di lauik dilauti","Si Angkuik namo nakhodonyo","Jauah nan tidak taturuti","Dangakan sajo baritonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109858,"Ajuang jompak di Pulau Aia","Balabuah tantang Taluak Kasai","Taduah ombak kito balaia","Mananti angin nan salasai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109859,"Ajuang jontak di Pulau Aia","Balabuah tantang Taluak Kasai","Taduah ombak pelang balayia","Mandapek angin nan salasai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109860,"Ajuang lauik di lauiki","Karang di surak nangkodo-nyo","Nan jauah indak ta turuit-i","Danga-kan sajo barito-nyo.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109861,"Ajuang perak layianyo tinggi","Pandan dituriah urang Jao","Tujuah minyak campuangkan diri","Badan disisiah urang juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109862,"Jong perak layarnya tinggi","Pandan ditoreh orang Jawa","Tujuh macam minyak rendaman diri","Badan disisih orang jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109863,"Ajuang Siam kamudi Siam","Balabuah auang di Malako","Sungguah jauah tampek diam","Dalam mimpi den pandang nyato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109864,"Jung Siam kemudi Siam","Berlabuh jung di Melaka","Sungguh pun jauh tempat diam","Dalam mimpi kupandang nyata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109865,"Ajuang-ajuang pulau Rumbigo","Sanan si-ponggok makan ikan","Ujuang Tanjuang Pulau nan Tigo","Sinan-lah dagang ba-tangih-an.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109866,"Ajun jasa dalam Supayang","Anak Si Tujuah Banda Dalam","Jan di ubah kasieh sayang","Sadakan juo siang-malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109867,"Ajun jasa di Alahan Panjang","Mangkuto Sutan rang gala-kan","Karam biduak samo ba ranang","Jikok untuang samo sarah-kan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109868,"Ajung batu tumbuh di sungai","Tidak jauh di Burung Pingai","Kisahnya masyhur sebab perangai","Jadi ristaan rakyat Brunei", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109869,"Ajung benang layar kertas","Melintas di gunung api","Putih kuning sudah terlepas","Hitam manis lagi menanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109870,"Ajung landai kemudi landai","Landai mari terheret duri","Adik pandai saya pun pandai","Jangan pula meninggi diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109871,"Ajung rakit belayar kain","Di mana lubuk sana bertimba","Berapa batah bercerai main","Sama duduk jalan bercinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109872,"Ajung rakit di tengah taman","Kayu hidup dahannya lanjar","Sakit mati inda beriman","Selagi hidup inda belajar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109873,"Ajung Siam patah kemudi","Ambil paku lantak di papan","Sungguh diam tidak berbunyi","Di dalam hati bermakan-makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109874,"Ajung Siam patah kemudi","Ambil paku lantak di papan","Sungguh diam tidak berbunyi","Di dalam hati sangat bermakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109875,"Aka banamo aka baha","Bapiuah bagaluang-galuang","Sabab kaduo nan mahilangkan aka","Iyolah karano binguang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109876,"Aka di rimbo bajaraik","Pisau nan tajam ambiak pairih","Nan bak manimbo aia lawik","Baitu gurindam tak namuah abih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109877,"Akad nikah resmilah sudah","Mempelai sampai ke tempat yang indah","Rajinlah sembahyang taat ibadah","Tanamkan tawakkal kepada Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109878,"Akademi Fantasi jadi tarikan","Malaysian Idol juga menawan","Baru aku kebenaran Al-Quran","Bila bercerai roh dari badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109879,"Akal si tua malim bugis","Tetap nya iman jangan kurang","Kapal pun habis lanchang habis","Baru budiman blayar pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109880,"Akan belayar pergi Sipitang","Singgah setagal Kampung Punang","Kekanda berhajat si akan datang","Datang untuk pergi meminang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109881,"Akan berjalan titian licin","Menuju Kampung Tumasib","Pertama dagang kedua miskin","Mengadu untung si dangan nasib", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109882,"Akan berkayuh ke Si Gundang","Akan menaik buah mempelam","Bismillah membuka dundang","Untuk menghibur hati yang dendam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109883,"Akan digantung tiga gantang","Akan disukat tiga sukat","Akan direntang hanyalah panjang","Baik disimpul supaya singkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109884,"Akan doa sekalian pendeta","Berdirilah Daulat Duli Mahkota","Dikurniakan Tuhan alam semesta","Sempurna limpah di atas takhta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109885,"Akan hilir ke Inderagiri","Semalam di Padang panjang","Di mana adat mula berdiri","Di Kuobatu Pariangan Padang panjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109886,"Akan jamuan orang yang datang","Di luar pintu ada paseban","Jadi landasan pagi dan petang","Atau membawa cerek ke jamban", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109887,"Akan makan nasi berkuah","Gawai emas bergiring-giring","Kalau ada untung bertuah","Karam di laut dampar ke siring", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109888,"Akan mati jua hanya kacang","Orang lah payah memirakan","Akan pergi jua kiranya dagang","Orang lah payah menggilakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109889,"Akan menaik buah kelapa","Buah perapat hanyut ke hulu","Kekanda ingin mahu berjumpa","Di mana tempat akan bertemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109890,"Akan mengail di ujung taman","Mari berumpan siar bangkita","Sakit mati inda beriman","Sakit hidup inda berharta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109891,"Akan mengaji Qur'an Istambul","Lambat pandai dipelajari","Kalau berjanji biarlah betul","Jangan bergantung barang sehari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109892,"Akan naik tidak bertangga","Akan terbang tiada bersayap","Tempat membawa ibarat raga","Ibarat api membawa asap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109893,"Akan naik tidak tanaik","Menantang bunga kembang kayapu","Si malai baik dibalas baik","Dibalas jahat mana balaku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109894,"Akan naik tidak tanaik","Menantang bunga kembang kayapu","Si malai baik dibalas baik","Dibalas jahat mana berlaku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109895,"Akan pelecut patah penggalan","Ambil beruk panjat kelapa","Diri terkejut di tengah jalan","Kerbau bertanduk dua-dua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109896,"Akan pembelah buah duku","Duku dibelah jadi kepayang","Kepada Allah berlindung aku","Baik berguru kita sembahyang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109897,"Akan tibalah hari kiamat","Itulah ingatan yang telah diajar","Biar lambat asalkan selamat","Tidak akan lari gunung dikejar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109898,"Akar belantara lilit-melilit","Pohon pala di tanah pinggir","Soalan saudara berbelit-belit","Runsing kepala hendak difikir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109899,"Akar di hutan lilit-melilit","Mari dirongkai buat pengikat","Pantun tuan soalannya sulit","Tak dapat dihurai masa yang singkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109900,"Akar diramu di hutan rimba","Pangkal berbelit di pohon jarak","Jangan di situ kita mencoba","Jalan ditegah oleh syarak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109901,"Akar hidup meresap-resap","Akar mati dalam perahu","Terbakar kampung nampak asap","Terbakar hati siapa tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109902,"Akar kalamba die dalam paraoe","Ole padoepan die atas kona","Toekar die tamba koe belom maoe","Kaloe tieda seroepa nona", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109903,"Akar keladi melilit selasih","Selasih tumbuh di hujung hiliran","Kalungan budi junjungan kasih","Bersama rima mengikut siaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109904,"Akar keladi melilit selasih","Selasih tumbuh di hujung taman","Kalungan budi junjungan kasih","Mesra kenangan sepanjang zaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109905,"Akar keladi melilit selasih","Selasih tumbuh di hujung taman","Kalungan budi junjungan kasih","Mesra kenangan sepanjang zaman", 4, 6);
