INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109907,"Akar keladi melilit selasih","Selasih tumbuh di hujung taman","Kalungan budi junjungan kasih","Mesra kenangan sepanjang zaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109908,"Akar keladi melilit selasih","Selasih tumbuh di hujung taman","Kalungan budi junjungan kasih","Setia kenangan sepanjang zaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109909,"Akar kelambar dalam perahu","Oleh pedupan di atas kona","Tikar ditambah ku belum mahu","Kalau tiada serupa nona", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109910,"Akar keramat akar bertuah","Akar bertampuk di gua batu","Nabi Muhammad bercintakan Allah","Di manakah tuan masa itu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109911,"Akar lalang daun cermai","Buat ubat demam berjangkit","Musuh seorang dikira ramai","Seribu sahabat rasa sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109912,"Akar manau kait-berkait","Manik sambau saya tumpahkan","Di Pariaman baju dijahit","Di Minangkabau baju disudahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109913,"Akar melilit pokok berduri","Akar patah pokok mati","Lihat kulit bukan isi","Lihat wajah bukan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109914,"Akar melilit si batang simpor","Kembang melati harum sa kampong","Jasa sedikit kami menabor","Hasrat hati meninggi gunong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109915,"Akar melurut di pohon badam","Berjela-jela ke pinggir jeram","Maniskah purut masamkah jadam","Masinkah gula maniskah garam?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109916,"Akar nibung meresap resap","Akar mati dalam perahu","Terbakar kampung nampak asap","Terbakar hati siapa tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109917,"Akar nibung meresap-resap","Akar mati dalam perahu","Terbakar kampung kelihatan asap","Terbakar hati siapa tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109918,"Akar seruntun dari seberang","Berangan besar di dalam padi","Pantun tidak mengata orang","Janganlah syak di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109919,"Akar terkapar di atas batu","Pucuk menyubur ke bawah lereng","Kapur adinda kapur tertentu","Bertambah sirih sudahlah kering", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109920,"Akar tunjang pagar selasih","Balong kulit burisi seray","Alang sakit munangong kasih","Tidak kulit tulang bucharay.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109921,"Akar tunjang papar selasih","Balung kulit berisi serai","Alangkah sakit menanggung kasih?","Tiada kulit tulang bercerai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109922,"Akhirnya kamu sampai ke Pudu","Sampailah sudah ke kota raya","Tuntutlah ilmu buat pandu","Moga-moga hidup berjaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109923,"Akin habis tidakkan habis","Percaya diri tiang usaha","Kain tipis tidak berlapis","Dibawa mandi orang menganga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109924,"Akinabahu jauh di awan","Cerita tempatan di bawah bayu","Kita seperahu tidak sehaluan","Kemana daratan hendak dituju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109925,"Akses dan ekuiti membuka peluang","Mempasti tiada yang tercicir kerana tak berwang","Kualiti pengajaran dan pembelajaran sangat diperjuang","Menghasil siswazah gemilang dan terbilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109926,"Aktes-artis terdedah aurat","Terhibur yang hati lalai","Pasti merosakkan asas masyarakat","Tuntutan agama terus terabai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109927,"Aktif dinamis terus berusahalah","Terus di isi sepanjang waktu","Hasil akhir di tangan Allah","Sikap positif di jiwa menyatu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109928,"Aku belauq belauq ku bedaya","Ku sarben gedeng tapen","Munku mauq padan waya","Ku taeq jari penganten", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109929,"Aku bercakap faktanya ada","Sejarah terbuku dalam dada","Bukan lembu kerbau melata","Dicucuk hidung ikut belaka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109930,"Aku betanya pada hati","Cerita baek atau malang","Tidoq malam selalu bermimpi","Mimpi bejumpa dengan tunang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109931,"Aku dudok Taman Melati","Oghang ramai tidak tekira","Tempat elok oghang bebudi","Anak dagang merata-rata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109932,"Aku malak merencam kacang","Iko merencam jagung muda","Aku puas diambat pulang","Iko nak ngarap agung pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109933,"Aku puas merencam kacang","Ini merencam jagung muda","Aku biasa dilanda malang","Ini mengharap agung pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109934,"Aku penanti i keding karang","Sire putjetjengang kulalenen ate","Kusi lalu lintes aku mumanang","Luhku siremonang kusapun rembebe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109935,"Aku pun cuba membantu","Diriku sendiri dan engkau sekalian","Mari kita saling memberitahu","Dalam kebenaran dan kesabaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109936,"Aku sangka tidak berpintu","Berpintu pula pelepah rumbia","Aku sangka tidak begitu","Begitu pula rupanya dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109937,"Aku sangka tidak berpintu","Berpintu pula pelepah rumbia","Aku sangka tidak begitu","Begitu pula rupanya dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109938,"Aku suka-suka","Hang marah-marah","Aku tak tau apa","Apa aku salah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109939,"Aku suka-suka","Hang marah-marah","Aku tak tau apa","Hang ludah-ludah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109940,"Aku tak dak kerja","Pikiaq macam-macam","Banyak oghang tanya","Aku naek geram", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109941,"Aku takot tidak terkira","Kekaseh aku cukop berani","Aku simpan semua rahsia","Kerana aku soghang lelaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109942,"Aku tutuh sebatang pinang","Ditimpa oleh sebatang padi","Ditembak dengan peluru petunang","Baru ditembak ular pun mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109943,"Aku tutuh sebatang pinang","Ditimpa oleh sebatang padi","Ditembak dengan peluru petunang","Serta ditembak ular pun mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109944,"Aku utuh sebatang pinang","Ditimpa oleh sebatang padi","Ditembak dengan peluru petunang","Baru ditembak ular pun mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109945,"Akulah Si Pengemis yang meminta","Rahmat dan redha Pemilik Bejana Emas","Karena ampunan telah menjelma","Dalam cinta hembusan setiap nafas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109946,"Padi di padang sudah diketam","Berkumpul kita bersama-sama","Mengirik padi kita mulakan","Berbalas pantun sesama kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109947,"Tupai melompat di atas dahan","Dahan kayu rantingnya kering","Mengirik padi kita berkawan","Beriring pula kita mengemping", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109948,"Ala payung lah payung ketipang payung","Payung sutera nak raja muda","Alah tolonglah tolong cik abang tolong","Janganlah marah pada adinda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109949,"Alaban pupua bunganyo","Aia mawa sacangkia kaco","Kok anggan sajak mulonyo","Tidak ati saibo nangko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109950,"Alah babunyi katuak-katuak","Bunyi batingkah duo tigo","Janggi sabatang lah bauntuak","Tanah sabingkah lah bapunyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109951,"Alah bakarih samparono","Bingkisan rajo Majo Pahik","Tuah basabab bakarano","Pandai batinggang di nan rumik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109952,"Alah bakarih samparono","Bingkisan Rajo Majopaik","Tuah basabab bakarano","Pandai batenggang din an sampik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109953,"Alah bakarih si Samporono","Buatan rajo Majopahik","Tuah basabab bakarano","Pandai batenggang di nan rahik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109954,"Alah bauriah bak sipasin","Kok bakiak alah bajajak","Walau batuka putaran musim","Sandi adaik jan dianjak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109955,"Alah bauriah bak sipasin","Kok bakiak alah bajajak","Walau batuka putaran musin","Sandi adaik jan dianjak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109956,"Alah datang sikapa api","Pasang mandaru ujuang tiang","Kok datang si rangkai ati","Ati di dalam tidak sanang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109957,"Telah datang kapal api","Pasang menderu di ujung tiang","Jika datang si rangkai hati","Hati di dalam tidaklah tenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109958,"Alah ilia rakik si Ringan","Alah anyuik puntuang suluahnyo","Alah datang rokok kiriman","Bak mano rupo batang tubuahnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109959,"Telah hilir rakit Si Ringan","Telah hanyut puntung suluhnya","Telah datang rokok kiriman","Bagaimana rupa batang tubuhnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109960,"Alah ka ladang urang bakabun","Jatuah badabuak sampai mati","Usah diharok buruang nantun","Balun kabalago musim kini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109961,"Alah lah mujua nan taraiah","Paham saukua nan tacapai","Karuah lah suko dipajaniah","Kusuik lah amuah disalasai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109962,"Alah mo masak padi adiak","Samo manggaro malah kito","Alah mo sarak laki adiak","Sama marando malah kito", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109963,"Telah masak ternyata padi adik","Sama mengusir burung malah kita","Telah cerai ternyata laki adik","Sama menjanda malah kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109964,"Alah patang kiranyo ari","Alah kambang bungo pitulo","Alah datang sirangkai ati","Baru pulang suruah ka muko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109965,"Telah petang kiranya hari","Telah kembang bunga petola","Telah datang si rangkai hati","Baru pulang suruh ke muka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109966,"Alah putuih tali katidiang","Padi tasuruak ka anjalai","Alah pai kawan sairiang","Dagang surang anyo lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109967,"Sudah putus tali ketiding","Padi tersuruk ke anjelai","Sudah pergi kawan seiring","Dagang kini seorang diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109968,"Alah putuih tali katidiang","Tampan batunggang malah padi","Alah dapek kawan sairiang","Tampan tabuang kiranyo kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109969,"Telah putus tali ketiding","Jadi tertunggang malah padi","Telah dapat kawan seining","Jadi terbuang kiranya kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109970,"Alah takanak baju biludu","Batabua ameh salo manyalo","Pandang anak pandang minantu","Lah samo rancak kaduonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109971,"Alahlah limau di binalu","Gulinggang dalam padi Jambi","Harimau tangkoklah Si Anu","Tidak tatahan di ati kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109972,"Kalahlah limau oleh benalu","Gelenggang dalam padi Jambi","Harimau tangkaplah Si Anu","Tidak tertahan di hati kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109973,"Alahmo kambang bungo pandan","Camiahlah padi manuruiki","Alahmo pulang nyawo badan","Camiahlah kami manuruiki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109974,"Telah kembang bunga pandan","Hampir saja padi mengikuti","Telah pulang nyawa badan","Hampir saja kami susuli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109975,"Alahmo kambang bungo pandan","Tatimpo daun katari","Alahmo pulang nyawo badan","Lai ka dapek nan dicari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109976,"Telah kembang bunga pandan","Tertimpa daun ketari","Telah pulang nyawa badan","Adakah dapat yang dicari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109977,"Alai di baruah Batu Mandi","Kundua mintak manjulai pulo","Bangkalai adiak tiok hari","Kok rarak pamulai pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109978,"Alai di baruh Batu Mandi","Kundur minta menjulai pula","Bengkalai adik tiap hari","Kalau rosak mulailah lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109979,"Alai di ulak Batu Mandi","Alah rapek manjulai pulo","Basuo nangko ari kini","Galak pamuloi pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109980,"Alai di ulak Batu Mandi","Tumbuhnya rapat menjulai pula","Pertemuan pada hari ini","Gelak pun pemula pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109981,"Alam Amsah namanya dewa","Tunangan anak Si Puteri Bungsu","Kalau takut badan kecewa","Jangan dituru hawa dan nafsu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109982,"Alam Besar rajanya sakti","Menitahkan Si Alam Bungsu","Bukanku nujum bukanku sakti","Kelip di mata sudahku tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109983,"Alam pura Raja Mahkota","Pergi berperang ke negeri Basanah","Ingat-ingat mengeluarkan kata","Zaman sekarang banyak fitnah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109984,"Alam sekitar yang bersih dan sihat","Kesejahteraan insan dan kemakmuran rakyat","Ekonomi membangun bagi masyarakat","Untuk Malaysia dan dunia sejagat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109985,"Alam sine Tuhan si mineq","Aiq ngalir lengan reban","Polisi nengke ndeq araq si keneq","Awaqne beciq pegaweanne lamban", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109986,"Alamnyo rami ka campuran","Nan tahu di rancak rupo","Dunianyo sunyi jo pangajaran","Kok mantang elok pakaiannyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109987,"Alang alang rendah merunduk","Ditiup angin di pegunungan","Kata kata walaupun muluk","Bisa salah dalam sebutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109988,"Alang ba-sarang di timbarau","Gilo ba-timbo mangkuak juo","Malang ta-tumpang di nan sarau","Gilo ba-ati mabuak juo.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (109989,"Alang di ateh jawi-jawi","Larehkan ba a silaronyo","Kami diadang jo kalahi","Katokan ba a karanonyo", 4, 6);
