INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110079,"Alip ba ta tiga surat","Dapat di dalam kitap mengaji","Tiang di kota chondong ka barat","Tanda alam apa nak jadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110080,"Alip terdiri seperti tiang","Tempat Raja membuat kota","Sudah terpandang si gambar wayang","Hati di dalam sangat berchinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110081,"Alip terdiri seperti tiang","Tempat Raja membuat kota","Sudah terpandang si gambar wayang","Hati di dalam sangat berchinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110082,"Alis putih Seri Brunei","Gual insel di tengah laut","Tubuh putih rendah rambunai","Pinggang ramping bagai diraut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110083,"Allah disembah raja ditaati","Orang yang tua dimuliakan","Harta dijaga nama dihormati","Barang amanah diutamakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110084,"Allah huma malim taubat","Ibarat kayu sepohon labat","Hidup sangsara mati pun lambat","Macam penyakit menanti ubat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110085,"Allah itu juga yang Esa","Buat kami segala bangsa","Celebis ini pula kejadianku","Dari ibu dan bapaku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110086,"Allah itu Maha Esa","Tidak ada permulaan dan tiada kesudahan","Manusia semua akan binasa","Masa saja yang menjadi penentuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110087,"Allah jadikan saraba ampat","Syariat tharikat hakekat ma'rifat","Menjadi satu dalam khalwat","Rasanya nikmat tiada tersurat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110088,"Allah saja yang begitu","Selainya adalah makhlok","Tanpa agama hidup tak menentu","Perangai pun cukup buruk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110089,"Allah taala Tuhan yang esa","Menjadikan memakan sekaliannya","Barang siapa hendak  termasa","Ikhlaskan ibadat kepadaNya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110090,"Allah ucapan lidah","Rabbi pengakuan hati","Abdullah mencari Allah","Allah dicari tak sembunyi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110091,"Almari buruk hilang kunci","Kunci dibuang di sawah padi","Kasih sayang dibalas benci","Miskin emosi miskin budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110092,"Al-Quran di atas peti","Peti dililit besi waja","Hamba berjanji di dalam hati","Cukup nikah sekali saja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110093,"Alu alu di kuala linggi","Di haru sambar sebla prau","Suda tau benda yang tinggi","Kalu tak chobak bila kan tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110094,"Alu alu di tepi bakat","Membawak angin di pantay raja","Kalu ada bersama dekat","Sakit pening bolay menjaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110095,"Alu bertingkah dengan lesung","Padi tumbah ayam yang kenyang","Kita bertelaga sama sekampung","Asyik berbantah orang lain senang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110096,"Alu limpato dipabulek","Baralun-alun tingkah bunyinyo","Painyo duo tingga ciek","Namun bilangan sakitu juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110097,"Alu-alu memakan tunda","Tali ditarik tahan selembar","Kalau tak mau pada adinda","Baik kubalik menahan sabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110098,"Alu-alu memakan tunda","Tali ditarik tahan selembar","Kalau tak mau pada adinda","Baik ku balik menahan sabar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110099,"Alu-aluan rakan sudah diterima","Semoga doa diberkati Allah Taala","Sepakat sehaluan itu yang ku pinta","Memartabatkan lagi UKM di persada dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110100,"Aluan di tontong bergambar burong","Serta laju haylo paras nya","Badan bertua Allah menolong","Chukop dengan harta benda nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110101,"Aluih lumek saik timbakau","Diisok jan ringan-ringan","Buruang jinak tidak tacakau","Lah inggok di tapak tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110102,"Aluih turiahnyo pandan singkia","Dilipek lalu di pakalang","Manih muluiknyo inggan bibia","Ati nan bulek mambalakang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110103,"Alun bagigi lah manggigik","Alun bakuku lah nak manggawik","Bak ibarat pajabat alun babisuluik","Alun tantu bakuaso lah nak maracik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110104,"Alun bakilek lah bakalam","Bulan lah ganok tiga puluah","Alun diliyek alah paham","Sarupo jimat di dalam tubuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110105,"Alun bakilek lah bakalam","Bulan lah ganok tigo puluah","Alun diliek alah paham","Rupo lah jimek dalam tubuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110106,"Alun bakilek lah bakalam","Bulan lah langkok tigo puluah","Alun tasirek lah takalam","Raso lah tibo dalam tubuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110107,"Alun bakilek lah bakalam","Martabat katujuah 'arih' namonyo","Takilek ikan dalam kolam","Lah tantu jantan batinonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110108,"Alun baranak lah ditimang","Di sinan hiduik mangko bedo","Alun tantu labo ka datang","Alah dimabuak kiri-kiro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110109,"Alun bataji lah nak bakukuak","Ayam jantan nan ka gadang-gadangan","Ibarat rang mudo basipat gaduak","Alemu alun kauju-ujuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110110,"Alun dianju lah tataruang","Alamt badan ka binaso","Mulai karajo alun lansuang","Ado halangan nan manimpo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110111,"Alun diimbau alah datang","Alun disuruah alah pai","Bapak mandeh bahati sanang","Kalau anak leh mangarati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110112,"Alun dikinyam lah maraso","Raso nan jimek dalam tubuah","Alun disuok lah badaso","Kalau disuok nak batambuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110113,"Alun dipanjek asok kumayan","Tinggi alah gadang nan balun","Ibarat rang mudo pulo dikatokan","Salamo hiduik babini balun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110114,"Alun duduak alah maunjua","Indak baagak bajangkokan","Sipat pandorong sipat tak batua","Kudian baru jadi sasalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110115,"Alun gelora gumpal-gumpalan","Ombak berolak hingga kuala","Sepakat mesra menambah amalan","Sama mengelak maksiat tercela", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110116,"Alun musik dengan irama","Berlagu dalam rasa","Kegembiraan seperti agama","Jangan coba menganalisa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110117,"Alun pai lah babaliak","Agak-agak ambiak ka bicaro","Mamareso kurang salidiak","Bapacik indak di nan nyato", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110118,"Alun tekilek alah takalam","Bulan lah ganok tigo puluah","Alun diliek alah bakinyam","Raso lah sampai dalam tubuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110119,"Alun-alun gelombang jati","Alun sampei muara selalang","Minta tulong mengubat hati","Hati Loksana alun gelombang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110120,"Alunan biola mengusik hati","Lagu Damak irama dahulu","Masakan pula rumput nak mati","Kalau dah semak orang tak lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110121,"Alunan gurindam hati tertawan","Mawar terlarang idaman kirana","Nurani diandam meranggih gunawan","Impian dikarang gelodaknya mana?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110122,"Alur Keladi di Mukim Chuping","Campak kail bertali benang","Kalaulah jadi di musim mengemping","Kuhantar wakil untuk meminang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110123,"Alur Sena jalan melintang","Anak Cina berjalan kaki","Apa guna yang sanggul lintang","Yang sanggul duduk elok lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110124,"Alur-alur menjadi lubuk","Telor tuntung berapit dua","Biar hancur manjadi bubuk","Asal seuntung kita berdua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110125,"Alus alus larek di raot","Papan di tepi bersangkot plamin","Datang arus tarek ka laot","Putar kemudi mengikot angin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110126,"Alus alus larek di-raot","Papan di-tepi sangkot pelamin","Datang arus tarek ka laot","Pusing kermudi mengikot angin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110127,"Aman dan Ah Seng di tahun tiga","Mereka berkawan dengan Si Baba","Bahaya tidak dapat diduga","Mungkin berlaku secara tiba-tiba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110128,"Aman dan harmoni kehidupan seharian","Berbilang kaum menikmati kemakmuran","Saling memahami menghormati kepelbagaian","Betolak ansur jika ada pertikaian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110129,"Amanah ini tinggi ilmunya","Wang ringgit tak boleh membelinya","Emas intan bukan bandingnya","Permata baiduri bukan padannya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110130,"Amaq Bedul lalo ngawis","Ngawis rebu kadu impan jaran","Luek rakyat lelah nangis","Tianne pede kelaparan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110131,"Amaq cait dengan berita","Lawang bale kenyengkene ngoang","Rakyat jeleng irup menderita","Pejabat atas seneng doing", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110132,"Amaq cait gitaq gelasna","Gelas tetoloq atas peti","Pejabat nane luean ongkatna","Laguq endeq araq terbukti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110133,"Amaq Enal bedue berugaq","Berugaqna leq sedin rurung","Dengan siq maleng sepeda motor selekena lueq","Sengaq araq polisi siq nenulung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110134,"Amaq gelideng lalo mesilaq","Inaq gelindeng leq dalem pakem","Denda tedoq sengaq melilaq","Kakaq dateng  denda demen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110135,"Amaq mbau paoq odaq","Araq baluq lueq buaqna","Bebakti leq dengan toaq","Kewajibante jari anakna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110136,"Amaq Nasir girang lupaq","Lupaq mangan terus beriuk","Inget-inget leq inaq amaq","Jari pegangan dait petunjuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110137,"Amaq Udin talet nyiur","Talet nyiur leq sedin telaga","Ndaq mele jari koruptor","Sengaq koruptor ngerugian Negara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110138,"Amat indah gnung di awan","Trus Madi dapat di lihat","Tidak ku endah cantik rupawan","Jika budi tidak memikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110139,"Amat putih bunga melati","Bunga suntingan putri Bayu.","Jangan tuan merendah hati","Bunga keramat tak mungkin layu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110140,"Ambacang di ateh bukik","Bagolek lalu ka ilalang","Bacancang kilek ateh angin","Guruah manaruah angin layang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110141,"Ambacang di Bukit tinggi","Jatuah sabuah ka subaliak","Bajalan ajo di bulan kini","Musim pabilo ka babaliak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110142,"Embacang di Bukit tinggi","Jatuh sebuah ke sebalik","Berjalan abang di bulan kini","Musim pabila kan berbalik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110143,"Ambacang sabuah bulek","Dimakan sadidiah manih","Urang Padang bakirim surek","Dibaco sadang manangih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110144,"Embacang sebuah bulat","Dimakan seulas manis","Orang Padang berkirim surat","Dibaca sambil menangis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110145,"Ambacang sa-buah bulek","Di-makan sa-irih manih","Tuan panjang ba-kirim surek","Di-baco tumbuah ma-nangih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110146,"Ambar kesturi harum terlebih","Pakaian anak perdana menteri","Membawa persembah sebarang oleh","Sebentuk cincin pemanis jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110147,"Ambar kesturi harum terlebih","Pakaian konon perdana menteri","Membawa persembah sebarang oleh","Sebentuk cincin pemanis jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110148,"Ambe' kan kayu udah ditarah","Untuk di buat lantai tongkang","Lain yang salah lain yang di marah","Balik abah kesuppanan sorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110149,"Ambiak api pangganglah lokan","Anak rajo pulang manjalo","Iduik mati di tangan tuan","Tidak kami duo bicaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110150,"Ambil api pangganglah lokan","Anak raja pulang menjala","Hidup mati di tangan tuan","Tidaklah kami dua bicara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110151,"Ambiak karambia ka dibalah","Kuali sudah dijarangkan","Ya Allah  ya Rasulullah","Rindu di mano dikatokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110152,"Ambil kerambil untuk dibelah","Kuali sudah dijerangkan","Ya Allah ya Rasulullah","Rindu di mana dikatakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110153,"Ambiak pandan pamaga parak","Bang ladiang indak baulu","Janlah badan ditumpalak","Suratan sudah tadahulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110154,"Ambil pandan pemagar parak","Hilang lading tidak berhulu","Janganlah badan ditempelak","Suratan sudah terdahulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110155,"Ambiaklah aie di parian","Kok kurang ambiek di panci","Sajak jalan banyak balarangan","Payah nasibnyo kusie bendi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110156,"Ambik duit sana","Ambik duit sini","Ada lepaih bunga","Tumbok rusok tutoh lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110157,"Ambik keresi dalam puan","Alun-alun tepi jeraya","Sampei hati kekanda tuan","Dibuang lalu kepada saya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110158,"Ambik pelepah di raut nipis","Dianyam rapat menjadi leka","Hati patah bagei diiris","Kasih sik dapat seperti semula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110159,"Ambik penugal menanam padi","Padi segantang bawa ke huma","Adik ditinggal memendam hati","Tuan menghilang tiada berita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110160,"Ambik periuk memasak lempah","Ikan buntal dimasak kari","Agik dolok kamek dah madah","Jangan menyesal belakang belakang hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110161,"Ambik puan dari Merinda","Pandan di Jawa saya robohkan","Jika tuan membawa adinda","Badan dan nyawa saya serahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110162,"Ambik senapang ambik paterom","Memburu landak pokok beringin","Apa dipandang bunga sekuntum","Bunga banyak di tempat lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110163,"Ambik taut buloh","Jangkit umpan katak","Tahan pukoi sepuloh","Boleh burong gagak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110164,"Ambiklah kacip di ambang pintu","Di bawah tingkap biawak melata","Moleklah nasib orang nan bisu","Dibawa bercakap awak tertawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110165,"Ambil air dalam telaga","Jangan lupa cuci embernya","Hak orang lain harus dijaga","Meskipun sedikit ada nilainya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110166,"Ambil air dari perigi","Ada dijinjing ada ditatang","Belum disuruh sudah pergi","Belum dipanggil sudah datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (110167,"Ambil air dari perigi","Air ditimba dalam sekali","Kekasih hati yang beta pilih","Berani rampas hukuman mati", 4, 6);
