INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112431,"Anak urang Koto nan Ampek","Cabiek-cabiek tapi kainnyo","Nan hilang indak kadapek","Cari nan lain kagantinyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112432,"Anak urang Lasi bamain tambua","Cincin parmato jatuah ka kandang","Jiko nasi alah jadi bubua","Aie mato usah dibuang-buang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112433,"Anak urang Pakan Rabaa","Nak pai ka Padang Panjang","Tuan kanduang makanlah baa","Nasi jo samba alah tahidang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112434,"Anak urang Silaiang Tinggi","Dibubuik capo diampehkan","Arok di buruang tabang tinggi","Punai di tangan rang lapehkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112435,"Anak urang Silaieng Tinggi","Dibubuik capo diampehkan","Harok di buruang tabang tinggi","Punai di tangan dilapehkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112436,"Anak urang Sungai Garinggieng","Pai ka balai di hari sanjo","Elok bana balaki sumbieng","Sadang berang tagalak juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112437,"Anak urang Tanjuang Andaleh","Pai kabalai bari sanjo","Bia habih bialah tandeh","Hati den kanai kaba-a juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112438,"Anak uso di dalam somak","Tuwun ka bawuo mamakan padi","Kanapo doso indak ka banyak","Samayang subuo lah tinggi aghi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112439,"Anak wayang hidup dijulang","Cantik pintar layaknya ratu","Adik sayang abang nak pulang","Tunang dihantar hingga ke pintu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112440,"Anak wayang yang menutup aurat","Kesan melakukan ibadah umrah","Ratusan remaja melakukan maksiat","Penyokong Hadhari tiba-tiba marah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112441,"Anak Yam saying berkurik","Tanam chekur dipshon dilam","Dudok diam rasa ditarik","Membuat hanchor hati didalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112442,"Anak Yamtuan di Seberang Nyonya","Ramai pengawas merata-rata","Kepada tuan saya bertanya","Apakah tugas Pegawai Seranta?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112443,"Anak Yamtuan menari joget","Nampak bergaya segak berdandan","Cubalah tuan hanya seringgit","Kalau berjaya tuahlah badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112444,"Anak yatim boleh diasuh","Fakir miskin bawah jembatan","Kenali alat senjata musuh","Taktik ditakuti oleh lawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112445,"Anak yu di sangka tamban","Kalau tamban mana loncatnya?","Adik rindu di sangka demam","Kalau demam mana pucatnya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112446,"Anak yu mati terlentang","Mati ditikam duri sembilang","Pilu hati bukan kepalang","Gunung tinggi menjadi padang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112447,"Anak zaman sekarang","Lain-lain bezanya","Asik dok menentang","Dengan ibu bapa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112448,"Anak-anak bermain gasing","Gasing diuri tali belati","Cantik molek si putih kuning","Si hitam manis menggoda hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112449,"Anak-anak di kayu tinggi","Tempat galah si daun pisang","Anak dagang menumpang di sini","Bajulah basah kering di pinggang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112450,"Anak-anak jahat","Kita kena ajaq","Kalau kita lihat","Lagi kurang ajaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112451,"Anak-anak kato mangaduah","Sabab manuruik sakandak hati","Kabuik tarang hujan lah taduah","Nan hilang patuik dicari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112452,"Anak-anak kena jaga","Maki oghang sana sini","Oghang benci oghang hian","Ibu bapak malu sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112453,"Anak-anak pergi sekolah","Belajar mengira dalam lukisan","Cubalah belajar bersusah-susah","Untuk berjaya di hari kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112454,"Anak-anak segan mandi","Waktu pagi sangat sejok","Ibu bapak turut ati","Waktu mengaji depa mengantok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112455,"Anak-anak segan mandi","Waktu pagi sangat sejuk","Ibu bapa turot ati","Waktu mengaji depa mengantok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112456,"Anak-anak si kayu tinggi","Tempat galah si daun pisang","Anak dagang menumpang di sini","Baju basah kerin di pinggang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112457,"Anak-anak tekun mengaji","Pagi hari mata tak ngantuk","Jaga tajam yang melukai","Jaga duri yang menyusuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112458,"Anak-anak zaman dulu","Tidak sama zaman sekarang","Mereka turot telunjok ibu","Tentang miskin ataupun senang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112459,"Anakanda kedua datang membantu","Ialah bercakap melawan Shah Kobat","Kepada kakanda sudah tertentu","Di dalam kalbu sudah tertambat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112460,"Anak-lah ayam menchakar duri","Mematok-matok seperti puyoh","Bukan ku diam sembunyi diri","Diam untuk menunggu musoh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112461,"Anaklah ular si cinta mani","Gelamaklah mati duak selubok","Selamat ini menahan hati","Gilaku lamak bertambah mabok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112462,"Anaknya rusa mati dijemur","Terpijak duri di depan tangga","Sama dirasa hidup makmur","Negeri sendiri bandingan tiada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112463,"Anam ayam turun sembilan","Mati satu tinggal delapan","Mengeluh risau penuh penyesalan","Takkan lah hidup akan berkecukupan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112464,"Anam denak di buat sabong","Dengar kokok pilu rasa nya","Tuan sempama onggas dan burong","Pokok kayu akan tempat nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112465,"Anam deywa tundok manyumba","Manyumba kaki lantas kurambot","Intan mak-nikam kadua jiwa","Pangku sultan raja di laut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112466,"Anam hari bulan rejang kerboh","Kerboh sa-kawan makan lempoyang","Adinda bangun berpelok tuboh","Pajar menyenseng hari handak siang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112467,"Anam lekor rejang tanglong","Tanglong bermain tanggal kapala","Kerna hati badan ku tanggong","Tanggong kan suda jadi sangsara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112468,"Anam mambawok kapado Cemo","Anggang lalu antah jatuah","Si runciang tanduak iduiknyo seso","Di ma kahilangan inyo tagaduah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112469,"Anam mambawok kapado tuduah","Talalah takaja tatambang taciak","Hindakan diri babuek cabuah","Di tapi tabiang tak elok tagak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112470,"Anaq Amaq Halil teriq rubin luse","Iye nangis kedue naene bakat","Mun ne dateng bulan Puase","Harus te pause kance tunaikan zakat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112471,"Anaq sempi leq tengaq memontong","Tukang ngarat leq bawaq bageq","Lamun temasi jari dengan sombong","Jarang te inget siq batur silaeq laeq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112472,"Anaq sempi leq tengaq memontong","Tukang ngarat leq bawaq bageq","Lamun temasi jari dengan sombong","Jarang te inget siq batur silaeq-laeq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112473,"Anau kaciak anau di lambah","Kurang pantai runduak-runduakkan","Ambo kaciak andak manyambah","Kurang pandai tunjuak-tunjuakkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112474,"Enau kecil enau di lembah","Kurang pantai runduk-rundukkan","Hamba kecil hendak menyembah","Kurang pandai tunjuk-tunjukkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112475,"Anboerkan padie di atas tana","Bakar doepa dari aleoan","Menantiekan toean terlalu lama","Antiekoe tjienta berasa bossen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112476,"Anchek Asan menetak atap","Atap di rendam pangkal bahsa","Segala jemputan datang di adap","Membawak gurindam yang alus bahasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112477,"Andai merekah tanah utara","Tanam selasih sebelah selatan","Guna apakah kain sutera","Benangnya selisih dalam lipatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112478,"Andaikan gagal dalam ikhtiar","Hindarkan diri berputus asa","Jangan pula ibadah buyar","Tetaplah bermohon kepada Yang Kuasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112479,"Andainya gelap tidak berlampu","Tentulah takut membawa peti","Andainya hidup tidak berilmu","Tentulah perut tiada berisi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112480,"Andainya hari bertambah cerah","Eloklah cepat menengok ladang","Andainya kaki tersalah langkah","Eloklah cepat berbalik pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112481,"Andainya hujan lebat mencurah","Eloklah kita membawa payung","Andainya kawan dapat musibah","Eloklah kita segera menolong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112482,"Andainya hujan lebat sekali","Jangan pergi menahan jerat","Andainya kawan mendapat rezeki","Jangan mengiri atau mengumpat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112483,"Andainya kambing tidak berjanggut","Tentulah lain nama diberikan","Andai keningnya tak pernah sujud","Tentulah lenjin dihari kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112484,"Andainya ketupat sudah diisi","Cepat-cepat menjerang kawah","Andainya pangkat sudah tinggi","Ingat-ingat orang di bawah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112485,"Andainya kuda memakan nasi","Tentulah makan dengan tuannya","Andai mudanya tekun mengaji","Tentulah nyaman dihari tuanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112486,"Andainya kuda tak mau besar","Tentulah susah dinaiki orang","Andainya muda tak mau belajar","Tentunya tuanya lebih bertuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112487,"Andainya laut sudah tenang","Cepat-cepat memasang layar","Andainya perut sudah kenyang","Ingat-ingat orang yang lapar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112488,"Andainya makan tidak berlauk","Tentulah sedikit nasi ditelan","Andainya iman tidak dipupuk","Tentulah sakit dihari kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112489,"Andainya mata sudah mengantuk","Cepat-cepat memadamkan lilin","Andainya harta sudah menumpuk","Ingat-ingat orang yang miskin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112490,"Andainya sagu sudah diparut","Tentu berguna bekal berjalan","Andainya ilmu sudah dituntut","Baru berguna bila diamalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112491,"Andainya sagu terus diparut","Banyaklah bekal kemana pergi","Andainya ilmu terus dituntut","Banyaklah bekal hidup dan mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112492,"Andainya sudah memakai kain","Elok sekali mengenakan baju","Andainya sudah menikah kawin","Duduk berdiri menjaga malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112493,"Andainya sudah memakai kain","Jangan lagi berseluar panjang","Andainya sudah menikah kawin","Jangan sekali berlagak bujang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112494,"Andainya tegak di bawah tiang","Baikkan juga mana yang kendur","Andainya awak dituakan orang","Banyakkan jaga daripada tidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112495,"Andainya tegak di bawah tiang","Mana yang patah harus diganti","Andainya awak dituakan orang","Sempurnakan lidah luruskan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112496,"Andainya tegak di bawah tiang","Tengok-tengok tempat yang retak","Andainya awak dituakan orang","Jenguk-jenguk umat yang banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112497,"Andainya tegak di bawah tiang","Tengok-tengokkan lantai yang patah","Andainya awak dituakan orang","Elok-elok menyampaikan petuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112498,"Andainya tekak tak mau makan","Usahakan cari menurut selera","Andainya awak tak tahu jalan","Jangan sekali malu bertanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112499,"Andainya tidak memakai baju","Aiblah diri masuk ke helat","Andainya akhlak bagaikan hantu","Hidup dan mati dikutuk umat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112500,"Andam Dewi bukak-lah peti","Anak Rajo Sutan Kulipah","Elok nan batin ta-sambunyi","Kok laia man-jadi susah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112501,"Andang-andang dipuput ribut","Jatuh ke tasik Anta Permata","Badanku seorang diperebut","Akan menghibur hati gundah-gelana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112502,"Andang-andang padi sang nata","Putus benang jalankan tombak","Timbang-timbang apalah rasa","Lautan tenang gunung berombak.", 4, 6);
