INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112504,"Andapung masuk ke pukat","Pukat Ratu Seri Wangsa","Tidak mengubah tanda muafakat","Kerana tuanku orang berbangsa", 4, 6);
