INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112506,"Andere bergen","Ander gras","Anderen dorpen","Andere zeden", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112507,"Anders is het palmblad","Anders is het varenkruid","Anders ben jij","Anders ben ik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112508,"Andilo au andilo","Pangaraut ni tangga muju","Mangido au mangido","Na nitiktik ni tangan muju", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112509,"Andilo-gras ben ik andilo-gras","Om te binden daarmee jouw ladder","Smeken doe ik je smeken","Om de sirih die je handen bereidden (Pantun Kaum Batak)", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112510,"Andoman anak Siti Dewi","Pergi bertapa di tengah laut","Dendam tak sudah selama ini","Sebagi perahu karam di laut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112511,"Andoman diatas pokok kayu","Pata dahan dengan bunga nya","Singga ka telok indra bayu","Dipakay pana raja laksemana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112512,"Andoman kusangka hantu","Nampak dari kuala Aceh","Rosak iman kerana nafsu","Rosak hati kerana kasih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112513,"Andoman mem belah bukit","Chari obat Seri Rama","Jikalau tidak kenang sedikit","Rasa nak mati bersama sama", 4, 6);
