INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112515,"Andoman membla bukit","Mancharik obat yang bernama","Kalu tidak kenang sdikit","Rasa nak mati bersama-sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112516,"Andoman membongkar bukit","Menchari obat sri rama","Kalu tidak kenang sedikit","Di turut kan mati bersama sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112517,"Andoman monyet kasakti","Anak deri si raja rama","Manja dengan besar hati","Di turut badan mati bersama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112518,"Andoman pergi memeburu","Dapat sekor merpati Jawa","Sekor semot menjadi guru","Merak di awan abis ketawa", 4, 6);
