INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112790,"Anjung Senja Anjung Selera","Tempat berjamu tempat bertemu","Tidak sahaja kita bermesra","Kita bertemu bertukar ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112791,"Anjung tinjau di Bukit Bendera","Tinjau ke kota ibu negeri","Walau hijau tentang negara","Kamilah senjata kami bestari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112792,"Anna Abadi membeli ginseng","Singgah di pasar mencari kari","Jangan ikut budaya samseng","Kelak menyesal di kemudian hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112793,"Annggang lalu atah jatuah","Jatuahnyo tantang marawa","Cupak pangulu kok tasintuah","Anak kamanakan namuah tajua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112794,"Anpa bernang di balek batang","Di olek serta sampay kapal","Bila senang datang datang","Bolay kita sama bersohal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112795,"Anta Beranta pengulu mambang","Undur ke pulau Merencana","Di dalam cinta kalbunya abang","Tidak ternilai barang kemana", 4, 6);
