INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112798,"Antah sapek antah mantilau","Ramo-ramo di dalam gantang","Antah dapek antak moh tido","Kaba lah lamo tak baulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112799,"Antakkan galah ka bubuangan","Rambutan ka labek tidak","Sarahkan bana ka junjungan","Manggih utan ka dapek tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112800,"Hentakkan galah ke bumbungan","Rambutan tidak kan lebat","Serahkan benar pada junjungan","Manggis hutan tidak kan dapat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112801,"Antan diusung di dalam majlis","Tarian seberang rentak berpecah","Akhlak terpesong lantaran penulis","Asyik mengarang cerita lucah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112802,"Antar seggranta","Kepending teega cecoor","Seio neeamba de inche acca","Bantang sappray mari la tidoor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112803,"Antara Bilah dengan Panai","Pulau si Kantan tengah-tengah","Karena ulah dan perangai","Hancur badan kena fitnah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112804,"Antara iman dan tawakkal","Tak boleh berpisah meski sejengkal","Kalau pemahaman terasa dangkal","Belajar dari ujung ke pangkal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112805,"Antara padi dengan semantong","Di situlah sorek ditukarkan","Antara hati dengan jantung","Di situ adik abang tinggalkan.", 4, 6);
