INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112851,"Anyuiklah bawang tigo rimpang","Tarandam lalu ka tanjuang","Jo kok santano ambo tompang","Ka ganti bapak mandeh kanduang", 4, 6);
