INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112880,"Apa bumbulah sayur jantung","Bumbunya lagi ketumbar muda","Kendati saya mati bergantung","Asal kenyang bermain muda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112881,"Apa bumbunya sayur jantung","Bumbunya lagi ketumbar muda","Kendati saya mati tergantung","Asal kenyang bermain muda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112882,"Apa busok di bawa paso","Anak itek mati terlentang","Rupa chantek bagi di baso","Hati prot bagi binatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112883,"Apa cabang tiada bergetah","Embun yang mana tak turun pagi","Bumi tak limbang buluh tak patah","Ular dibalun biarkan mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112884,"Apa daya waelas tinggi","Bepagar besi betiang empat","Apa daya nasibku ini","Pecah kongsi dengan sahabat", 4, 6);
