INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112886,"Apa daya wireless tinggi","Tiang besi bersegi lapan","Apa daya nasibku ini","Patah hati tumbang harapan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112887,"Apa di cari di tengah sawah","Cari kapiting di lubang batu","Apa di cari di badan saya","Orang miskin badan piatu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112888,"Apa di pikir sungu sungu","Di chari hal ber-hari hari","Sala di taksir jangan menungu","Kalu terkilap kena sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112889,"Apa di sesal kepada tudong","Tudong saji trendak Bentan","Apa di sesal kepada untong","Untong suda permintak badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112890,"Apa di tebang di tengah limbah?","Tentulah kayu saya lintangkan.","Bunga setangkai sudah tergubah","Jikalau sudi tuan suntingkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112891,"Apa di tjari di tenga sawa","Tjarie kapitieng di lobang batoe","Apa ditjarie di badan saija","Orang mieskien badan piatoe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112892,"Apa di toemboek apa di tampie","Njang di tampe di dalam doelang","Apa di tonggoe apa dinantie","Njang di nantie la soeda poelang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112893,"Apa diambil di kebun Cina","Membeli lada dengan berangan","Pasang jerat burung tak kena","Burung terbang di atas kayangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112894,"Apa dibeli pergi ke pekan","Membeli wajik dengan penganan","Jangan dipikat burung di hutan","Burung biasa makan di tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112895,"Apa dibuat di Tanjung Papar","Pantai ada untuk berkelah","Jika kuat takwa dan sabar","Akan ada anugerah Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112896,"Apa dicari bayan terbang","Rajawali menyambar dia","Apa dicari dayang berulang-ulang","Orang bestari mendapat dia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112897,"Apa dicari di tengah sana","Cari kepiting di celah batu","Apa dicari di badan saya","Orang miskin badan piatu", 4, 6);
