INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112904,"Apa di-harap kelapa muda","Entah sedap entahkan tidak","Apa di-harap kaseh kanda","Entah sayang entahkan tidak!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112905,"Apa diharap kepada padi","Tinggi petak dari ruang","Apa diharap kepada kami","Emas tiada bangsa pun kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112906,"Apa diharap pada padi","Tinggi petak pada ruang","Apa harap padaku ini","Budi tidak bahasa kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112907,"Apa diharap pada pedati","Pedati ditarik kerbau jalang.","Apa diharap kepada kami","Emas tiada bangsapun kurang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112908,"Apa diharap padi belanda","Tempat semut buat sarang","Apa diharap pada kakanda","Tiada harta bangsa pun kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112909,"Apa diharap selasih di seberang","Habis terbuang ke dalam paya","Apa diharap kekasih orang","Kita menumpang memandang sahaja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112910,"Apa diharap si bunga simpor","Kuning ada berbau tidak","Kelat mata adik nak tidur","Dinding ada kelambu tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112911,"Apa diharap si pauh janggi","Pauh janggi condong ke barat","Apa diharap kepada janji","Sauh besi lagi melarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112912,"Apa dijerok buah pedada","Dipolah sambal sedap sekali","Apa dicarik urang sik ada","Urang yang dekat ada di mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112913,"Apa dijeruk dengan belimbing?","Geranggang mudik muara","Apa di tengok di balik dinding?","Tun Hassan Temenggung anak Bendehara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112914,"Apa dijeruk dengan belimbing","Geranggang mudik muara","Apa ditengok di balik dinding","Tun Hasan Temenggung anak Bendahara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112915,"Apa dikata kepada tudung","Tudung saji dari Melaka","Ributlah orang seisi kampung","Lihat kita jalan berdua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112916,"Apa dikenang kepada tudung","Tudung taji terendak Bantan","Apa dikenang kepada untung","Sudah itu suratan badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112917,"Apa dikenang padi di bukit","Padi dibukit sambang sahaja","Apa dikenang budi sedikit","Budi sedikit terbuang juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112918,"Apa dikenang padi dibukit","Padi di bukit sambang sahaja","Apa dikenang budi sedikit","Budi sedikit terbuang sahaja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112919,"Apa dikerat si batang padi","Pakei seruling anak gembala","Tuan anggerik di pohon berduri","Hendak disunting tidak berdaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112920,"Apa dipikat burung dekut","Kerana sudah patah kaki","Adat orang hendak diikut","Kalau tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112921,"Apa dipotong kaki badak","Badak melompat ke tanah tumbuh","Apa untungnya berlaki budak","Budak dapat disuruh-suruh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112922,"Apa direndang dalam kuali","Ikan todak dari seberang","Apa dipandang kepada kami","Rupa tidak bangsa pun kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112923,"Apa direndang dalam kuali","Pucuk kacang muda-muda","Apa dipandang kepada kami","Sudah miskin papa pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112924,"Apa disesal kayu berdiri","Tiada rebah tiada berguna","Apa disesal semahu diri","Sial tuah sial hujungnya", 4, 6);
