INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112928,"Apa disesal padanya tudung","Tudung saji terendak Bentan","Apa disesal pada untung","Sudah takdir pendapatan badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112929,"Apa disimpan di dalam peti","Buah belimbing dibuat jeruk","Apa diharap kepada kami","Orang miskin rupa pun buruk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112930,"Apa disusah kepada padi","Padi yang sudah digemari","Apa disusah kepada hati","Hati yang sudah diajari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112931,"Apa ditanam semua menjadi","Buah rambutan manis longkah","Bak ayam di kepuk padi","Dapat jemputan pergi ke Makkah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112932,"Apa ditebang di tengah lembah","Tentunya kayu saya lintangkan","Bunga setangkai sudah tergubah","Jikalau sudi tuan suntingkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112933,"Apa ditumbuk apa ditampis","Yang di tampis di dalam dulang","Apa di tunggu apa di nanti","Yang di nanti lah sudah pulang", 4, 6);
