INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112936,"Apa gemuruh di Gunung Ledang","Burung menyambar gugur bulunya","Jangan selalu menaruh bimbang","Menahan sabar banyak gunanya", 4, 6);
