INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112938,"Apa goena manching de Batoo","Panching pata umpang terbang","Apa goena ana peeatoe","Kayn basa cring de pinggang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112939,"Apa guna asam paya","Kalau tidak menggulai ikan","Apa guna lagak dan kaya","Kalau bahasa tidak dihiraukan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112940,"Apa guna baju kebaya","Bertapik batik kain selendang","Apa guna semacam saya","Rupa sik cantik pemandei pun kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112941,"Apa guna baju kebaya","Kain batek kain selendang","Apa guna macamnya saya","Rupa sik cantek pemandei kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112942,"Apa guna banglo cantik","Jika tiada pagar serinya","Apa guna pramugari cantik","Kalau tidak dengan budinya", 4, 6);
