INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112956,"Apa guna berlesong pauh","Lesong pauh menumaph padi","Apa guna berkasih jauh","Berkasih jauh menyusah hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112957,"Apa guna bermain lilin","Lilin asal dari lemak","Apalah guna jeling-menjeling","Tersalah jeling marah emak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112958,"Apa guna berparan panjang","Kalau tidak berani menetak","Apa guna tikar dibentang","Kalau jiran tidak diajak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112959,"Apa guna ber-sanggol sipot","Kalu tidak ber-sunting bunga","Apa guna ter-sebot sebot.","Kalu tidak ber-dengar s'pata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112960,"Apa guna berumah besar","Bilik lapang tingkapnya satu","Apa guna mencari sasar","Kalau senapang tiada peluru!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112961,"Apa guna biduk berenang","Sudah kering lipatan sirih","Apa guna kasih bertunang","Kalau masih Awang bersedih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112962,"Apa guna blayar skochi","Kalu tidak dengan layar-nya","Apa guna memegang konchi","Kalu tidak dengan kuasa-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112963,"Apa guna da bebe terasi","Lamun endag da bede bawang","Apa guna da rajin ngaji","Lamun endeq da rajin sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112964,"Apa guna diledung lagi","Ambil biji belah kayunya","Apa guna di kampung lagi","Orang benci sekampungnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112965,"Apa guna duduk termenung","Elok lakukan kerja kebajikan","Walau harta setinggi gunung","Perintah Allah jangan abaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112966,"Apa guna jarum dait benang","Lamun endeq ta kadu jait kelambi","Apa guna gawek sembahyang","Lamun endekna tao pede ngaji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112967,"Apa guna jintan sulasih","Sayang berjalan ke Singapura","Apa guna berkata kasih","Sadang hatimu berpura-pura", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (112968,"Apa guna kacang direndang","Bila masak direndam lagi","Apa guna abang meminang","Bila sudah beranak isteri", 4, 6);
