INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (113161,"Apa saja pilihan yang dibuat","Faedah dan ganjaran gunalah dengan tepat","Perancangan kewangan haruslah padat","Menjamin kebajikan masa depan dengan baik dan bermanfaat", 4, 6);
