INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (113163,"Apa salahnya Datuk Hang Jebat","Memegang keris di tangan kanan.","Hamba sengaja berkirim surat","Sebagai tanda kita berkenalan.", 4, 6);
