INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (113216,"Apa tanda batang berbuah","Banyak burung hinggap di dahan","Apa tanda orang bertuah","Banyak menolong sesama insan", 4, 6);
