INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115052,"Baju songket songkoknya hitam","Lima butang baju kemeja","Tidak bersepit bukanlah ketam","Malapkan bintang tidak dipuja?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115053,"Baju suasa kain suasa","Potongan Barat tidaklah hebat","Negara kita tanggungjawab kita","Berpegang erat bersatu kuat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115054,"Baju sutera kainnya cindai","Leher bulat pesaknya lebar","Kancil di rimba terasa pandai","Kecil ulat yang dicabar!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115055,"Baju tekua panjang lengan","Terjemur di tepi lebuh","Kuah sudah bergenangan","Nasi pun sudah tertambuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115056,"Baju Toro kancingnya tujuh","Baik di lipat di kaki dulang","Kalau Norma penuju sungguh","Mari ikutlah saya pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115057,"Bajua bamurah-murah","Dibali bari ditimbang","Jiko pamimpin tak samo arah","Di sinan rakyat jadi bimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115058,"Bajuku basah dek hujan panas","Turun lebat sebelah hulu","Jembatan patah buaya mengganas","Bagaimana pula cik puan nak lalau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115059,"Bajulah sudah dari balai","Basamo kito manyaruangkan","Alua juo patuik nan dipakai","Basamo kita manggunokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115060,"Baju-nya sutra merah","Kain-nya kain mastuli","Dengar karangan muka-nya merah","Kiah-sye ketawa tergli-gli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115061,"Baju-nya sutra merah","Kain-nya kain mastuli","Dengar karangan muka-nya merah","Kiah-sye ketawa tergli-gli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115062,"Bak aia di daun teleh","Indak ado katatapannyo","Pandirian indak nan jaleh","Amuah barubah sakutiko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115063,"Bak aia jatuah ka kasiak","Hilang nan tidak maninggakan jajak","Pambarian ado samo dicaliak","Di sipanarimo babakeh indak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115064,"Bak aie di daun taleh","Bak ambun di ujung rumpuik","Bak abu di ateh tunggue","Bak talue di ujuang tanduak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115065,"Bak anau dalam baluka","Malapehkan pucuak masiang-masiang","Balain pandapek basitangka","Mampatahankan pandirian dalam barundiang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115066,"Bak anjiang kadahuluan","Dahulu urang pado awak","Labo lah lamo diharokkan","Dek takudian mandapek indak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115067,"Bak aua dangan tabiang","Hiduik nan sanda manyanda","Jokok bakawan basamo gadang","Batolong-tolongan taguah satia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115068,"Bak aua dihelo sonsang","Kian dihelo kian tasangkuik","Karano banyak cabang jo jipang","Sulik salasai sangkuik pauik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115069,"Bak ayam kanai kapalo","Indak dapek bakutiak lai","Dek tapek lawan bakato","Indak dapek manjawab lai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115070,"Bak ayam kuriak panjang ikua","Rancaklah rupo dipandangi","Urang nan jombang tamusahua","Pandai mamakai mamatuik diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115071,"Bak ayam labiah pangguntiang","Tacampak indak bakamehi","Ibarat urang hiduik tarasiang","Dek bansaik atau kadapatan budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115072,"Bak balam dangan katitiran","Rumik rasonyo kok sabaua","Balaki bini kalau diumpamokan","Mamanggakkan tuah sikua-sikua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115073,"Bak basi baiak diringgiti","Nan rancak batambah elok juo","Tahanlah hati mamuji diri","Jaso nan banyak urang tak lupo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115074,"Bak batuang bulek tak basagi","Pipik jantan tak basarang","Indak bakampuang banagari","Bakapanjangan hiduik tualang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115075,"Bak bungo ditimpo paneh","Layua sabalun jadi buah","Ibarat saurang anak gadih","Rancank hilang ditimpo susah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115076,"Bak cino ba rambut panjang","Ba-uleh jo rambuik suto","Mangko ta-sisieh anak-dagang","Ba-main anak bumi-puto.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115077,"Bak kabau mahelo bendi","Sumbang jo jangga nampak di mato","Hilanglah adat di nagari","Indak malatakkan sasuatu di tampeknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115078,"Bak kacang diabuih ciek","Malonjak-lonjak tangkumari","Itulah urang nan tacacek","Kurang sopan indak babudi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115079,"Bak kaco jatuah ka batu","Tibo di batu hancua luluah","Ibarat urang sadiah jo pilu","Hati nan ramuak hancua luluah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115080,"Bak kambiang diirik ka aia","Bajalan karano dek tapaso","Karajo sarupo nan tasambia","Hati anggan karajo juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115081,"Bak katak di bawah tampuruang","Indak tahu dileba dunia","Alun panah maninggakan kampuang","Pikiran singkek panaglamn tak bara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115082,"Bak kuciang dibawokan lidi","Sangaik takuiknyo ka dilacuik","Taraso lah salah badan diri","Dibawok parkaro takuik tatuntuik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115083,"Bak kuciang kahilangan anak","Kian kumari dicari juo","Nan dikasiahi hilang tak nampak","Alah bacarai mangkonyo seso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115084,"Bak kuciang maintain dendeng","Hati nan harok-harok cameh","Rasaki tampak lah tagendeng","Sabalun dapek alun pueh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115085,"Bak kuciang manjapuik api","Dinanti-nantikan indaknyo tibo","Usah ka dapek nan dicari","Nan mancari pun hilang pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115086,"Bak kundua di ateh dulang","Indak nan tatap di tampeknyo","Indak bapandirian tumbuah di urang","Namuah barubah sakutiko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115087,"Bak langau di ikua gajah","Ka ma dibawok ka ma pai","Suko manuruik tak tantu arah","Pandirian nan indak di dalam diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115088,"Bak mahetong bulu kambiang","Indak ka mungkin sudah-sudahnyo","Walau tumbuah abuak di kaniang","Payah lai badaso tido", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115089,"Bak makan di daun pisang","Abih makan piriang dibuang","Ketek hati bukan kapalang","Karano jaso dilupokan urang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115090,"Bak mambakali budak lari","Baka hilang budak pun hilang","Itu namonyo duo kali rugi","Alah tarambau dihimpik janjang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115091,"Bak mambulalang urang buto","Bak mahariak urang pakak","Dibari nasihat parcuma sajo","Karano manarimo hati lah anggak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115092,"Bak maminum aia bacaciang","Indak katuju diminum juo","Parentah nyato bulek basandiang","Indak satuju dikarajokan juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115093,"Bak manakiak kayu di rimbo","Gatah nan caia bapantingan","Karajo tak baiak dikarajokan juo","Itulah manusia didayo setan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115094,"Bak mandapek durian runtuah","Rasaki datang mauntok","Indak disangko sungguah-sungguah","Labo nan datang taparogok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115095,"Bak mandi di dalam cupak","Di malah badan ka cukuik basah","Hiduik manangguang tagak tak lamak","Sagalo tak sadang mambawok susah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115096,"Bak mangaia kuciang hanyuik","Kok dapek bana apolah guno","Sio-sio indaklah patuik","Paidah karajo indaklah ado", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115097,"Bak mangayuah biduak hilia","Indak dikayuah ka hilia juo","Diparentahkan karajo inyo gumbira","Mukasuik lah nak mangarajokan juo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115098,"Bak maniak putuih talinyo","Bak intan putuih pangarang","Jatuah badarai aia mato","Ditimpo sadiah bukan kapalang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115099,"Bak manuhuak ka dalam lurah","Indak nan sulik ka basuo","Maniayo nan tinggi bakeh nan randah","Itu karajo nan murah sajo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115100,"Bak manyasah kain dapek","Indak baragak saketek juo","Bia lakeh lusuahnyo capek","Rugi indak bapokok tido", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115101,"Bak manyurek di ateh aia","Di ma ka ado bakeh suratan","Nan bak mangecek jo urang pandia","Di ma ka ado bakeh pangaratian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115102,"Bak nangko moh-nyo indayang","Ba-darai mangko ta-timpo","Bak nangko bana kasieh- sayang","Ba-carai mangko ta-cinto.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115103,"Bak palito kahabisan minyak","Habih minyak cahayo hilang","Dek sakik gadih nan rancak","Muko pucek darah lah kurang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115104,"Bak paneh manganduang hujan","Hati nan risau dibawok galak","Atau bak bulan disungkuik awan","Hari tarang cayo tak nampak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115105,"Bak parahu indak bakamudi","Balayia indak bapadoman","Ibarat parusahaan jalan sandiri","Parusahaan tak ado pakai pimpinan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115106,"Bak parang bamato duo","Mato nan tajam kaduonyo","Bapisah urang kaduo-duo","Kauntuangan dapek dari kaduonyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115107,"Bak roda padati","Sakali ka the sakali ka bawah","Hiduik manusia di ateh bumi","Kayo jo bansaik datang basiliah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115108,"Bak santan dangan tangguli","Bakato pandai samo pandai","Bak alu pancukia duri","Bakato pandia samo pandia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115109,"Bak sipontong mandapek cincin","Ka baa manganakkannyo","Mandapek barang tak bias dipagunokan","Akia kalaknyo subao sajo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115110,"Bak siriah pulang ka gagang","Bak pinang pulang ka tampuak","Indak kan ado maraso cangguang","Karano di tampeknyo alah talatak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115111,"Bak Ulando mintak tanah","Mintak nan indak namuah abih","Baitu bana kalau tajajah","Kini panjajah nan lah takikih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115112,"Bak unto manyarah diri","Walaupun apa ka di niat","Kalau sakali manyarah diri","Manyarah nyato indak basyarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115113,"Bak varnis berkilau permai","Ombak terdedah gulung-gulungan","Terasa harmonis dipeluk damai","Dunia yang indah dicemar jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115114,"Bakabek padi dalam buntia","Bawak ka kincia manumbuaknyo","Maupek puji elok bapikia","Di sinan hati taguliciaknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115115,"Bakar api di dalam sekam","Sekam disimpan dalam perahu","Kita berkasih diam-diam","Jangan ada orang yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115116,"Bakar bahulu di dalam loyang","Dimakan sedikit terasa enak","Guru dahulu tegas penyayang","Semua murid dianggap anak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115117,"Bakar bakau menjadi arang","Kayu arang bahan jualan","Melayan rindu kekasih orang","Bagai pungguk hilangkan bulan!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115118,"Bakar capa di pohon lontar","Capa tumbuh di dekat kundur","Tiada lupa barang sebentar","Hanya sementara waktu tidur.", 4, 6);
