INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115120,"Bakar damar di Tanjung Palas","Bilah juga akan suginya","Kami benar serta ikhlas","Allah juga akan saksinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115121,"Bakar damar di Tanjung Palas","Bilah juga akan suginya","Saya benar serta ikhlas","Allah juga akan saksinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115122,"Bakar damar terkilat-kilat","Bilah jua akan suginya","Saya benar di bawa elat","Allah jua akan saksinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115123,"Bakar deopa darie aloen","Tanem temoe di pagar orang","Atie koe tjienta berasa bossen","Soedah ketemoe moeda saorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115124,"Bakar dupa dari haluan","Tanam temu di pagar orang","Hatiku cinta berasa bosan","Sudah ketemu muda seorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115125,"Bakar dupa di pulau Lontar","Capa tumbuh dekat kundur","Tiada lupa barang sebentar","Hanya sementara waktu tidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115126,"Bakar dupa di pulau Lontar","Capa tumbuh dekat kundur","Tidak lupa barang sebentar","Hanya kejap waktu tidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115127,"Bakar dupa di tengah paya","Kunang-kunang di dalam kendi","Jangan lupa kepada saya","Kenangkan tuan yang baik budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115128,"Bakar dupa di tengah paya","Kunang-kunang di dalam gendi","Jangan lupa kepada saya","Kenangkan tuan yang baik budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115129,"Bakar dupa di tengah paya","Kunang-kunang di dalam gendi","Jangan lupa kepada saya","Kenangkan tuan yang baik budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115130,"Bakar gaharu dalam istana","Harumnya sampai keluar kota","Kalau tahu bulan gerhana","Kucari sumbu kupasang pelita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115131,"Bakar huma terbakar kain","Kain batik berlumur arang;","Ia yang mencari lain","Apa peduli kepada orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115132,"Bakar kayu menjadi arang","Arang dicampur si batu kapur","Lama merantau di negeri orang","Pulangnya bak belut ke lumpur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115133,"Bakar kelambu marahkan pijat","Rosak susu kerana nila","Mata kelabu gilakan pangkat","Pemimpin bisu nama tercela", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115134,"Bakar kemenyan di atas bara","Akan perasap nyamuk bersarang","Bukan calang indahnya Singapura","Siang malam terang benderang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115135,"Bakar kertas di dalam cawan","Maligai disambung Raja Wilanda","Sangat pantas raja pahlawan","Memberi bimbang hati kekanda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115136,"Bakar sabut di tengah huma","Buat membaja si pohon cempedak","Kalau rambut sudah nak sama","Kenapa berbedak lelaki tidak?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115137,"Bakar sabut di tengah huma","Kerana hendak membaja cempedak","Kalau rambut sudah nak sama","Kenapa berbedak lelaki tidak?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115138,"Bakar sampah di pinggir rumah","Asap ditiup ke mana-mana","Keluarga sering sengketa","Orang tetangga ikut merasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115139,"Bakareih siganjo erah","Lahia batin pamaga diri","Patah lidah bakeh alah","Patah karieh bakeh mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115140,"Ba-karih tagak ma-nari","Aia ba-riak dalam sumua","Kok arif si-jantuang ati","Pikir-i kato bintang timua.", 4, 6);
