INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115148,"Ba-kudo ka Bukik-Tinggi","Jalan ka suok di kida-kan","Kok datang rodi Gumpani","Nasi di suok di-tingga-kan", 4, 6);
