INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115150,"Bakukuak ayam sabalainyo","Ayam nan dari Bangkahulu","Elok urang di pandainyo","Manembak aram bapiluru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115151,"Berkokok ayam sebalainya","Ayam dari Bangkahulu","Elok orang akan pandainya","Menembak tidak berpeluru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115152,"Bakul besar berisi pulut","Pulut hitam dibuat tapai","Lama bersabar hatiku hanyut","Siang malam badan meragai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115153,"Bakul buluh bakul rotan","Sudah dianyam bawa ke pekan","Memberi makan burung di hutan","Burung di sangkar mati tak makan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115154,"Bakul ketiding nampaknya besar","Diisi segantang menakan penuh","Kupikul kasih dada terbakar","Hendak mengelak hatiku luluh", 4, 6);
