INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115156,"Bakul pandan tikar mengkuang","Banyak dijual di kedai payang","Gara-gara politik wang","Pangkat terbang kerusi melayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115157,"Bakul pembenih orang berladang","Walaupun kecil besar ertinya","Kupikul kasih menjadi hutang","Haram tak ada terima kasihnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115158,"Bakul sikut diisi keladi","Di Inanam berisi juadah","Jangan diikut suara hati","Menyimpan dendam tak sudah-sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115159,"Bakul sirih berisi mempelam","Buat hantaran rumah pengawa","Rakyat berdoa siang dan malam","Agar tuan dipanjangkan usia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115160,"Bakul tampah berisi sekam","Sudah diayak tinggallah beras","Kupikul kasih hatiku karam","Hendak mengelak hatiku belas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115161,"Bakulik alang hari Satu","Hari nan sadang barembang patang","Dubalang baraka bailimu","Sarato barani gadang tulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115162,"Bakumai lagi berbangsi","Bertali nilam pualam","Benar pun sungai tempatku mandi","Dipilih jua tempat menyelam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115163,"Bala datai di Nanga Kumpang","Raja mekat samoa orang","Ngetu belangkau atas tebiang","Dunju ngapa batap kajang", 4, 6);
