INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115165,"Bala ke dudi sama berumban","Rindu berumpak ngalah ka pangan","Sida nerais enda bagineram","Sapanjai hari datai ka malam (Pantun Sarawak)", 4, 6);
