INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115173,"Baladang di Padang Laweh","Malenggang lalu ka tapian","Tuan maadang lauik lapeh","Sayang mananggung paratian", 4, 6);
