INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115202,"Balam tekukur burung di hutan","Asyik berbunyi sepanjang hari","Di dalam tidur aku mimpikan","Adik berjanji datang sendiri", 4, 6);
