INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115210,"Balari-lari ka dalam kabun","Dalam kabun adolah parak","Lai tau adiak di pantun","Dalam pantun ado kaandak", 4, 6);
