INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115214,"Balayia ka Pulau Bangka","Bao sirauik duo tigo","Kalau kaia panjang sajangka","Jangan andak lauik didugo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115215,"Balayia kapa dari Padang","Masuak kualo Pulau Pinang","Alang sakiknyo di ambo surang","Kain basah kariang di pinggang", 4, 6);
